pragma solidity ^0.8.0;

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

import "../access-control/SuAuthenticated.sol";
import "../interfaces/adapters/ILPAdapter.sol";
import "../vendor/curve/ICurveLP.sol";

/**
 * @dev
 * Default - It's pool that is not from stablecoins and is not V2 Factory.
 Oracle for this pool isn't secured from pool manipulations, so we can't create secure getPrice function.
 the example of such pool is tricrypto2 (USDT/WBTC/ETH).
 * STABLE - It's pool based on stablecoins ot eth, like 3pool, crvUSD/USDT ot steth
 * V2 - It's V2 Factory pool that have LP-oracle inside, like TriCRV, TricryptoUSDC
*/
enum CurvePoolType { Default, Stable, V2 }

struct CurveLPInfo {
    address pool;
    CurvePoolType poolType;
    address[] underlyingTokens;
}

/**
 * @title CurveAdapter
 * @notice Adapter for Curve LP token.
 * @dev See ILPAdapter interface for full details.
 */
contract CurveAdapter is SuAuthenticated, ILPAdapter {
    mapping(address => CurveLPInfo) public lps; // Mapping from LP token to pool
    ISuOracle public ORACLE;

    function initialize(address _authControl, address _oracle) public initializer {
        __suAuthenticatedInit(_authControl);
        ORACLE = ISuOracle(_oracle);
    }

    /**
      * @notice Register the given LP token address and set the LP pool
      * @param lp address of LP token
      * @param lpPool address of pool of {lp} LP-token
      * @param nTokens amount of underlyingTokens
     **/
    function registerLP(address lp, address lpPool, uint256 nTokens, CurvePoolType poolType) external onlyAdmin {
        lps[lp].pool = lpPool;
        lps[lp].poolType = poolType;
        lps[lp].underlyingTokens = new address[](nTokens);
        for (uint16 i = 0; i < nTokens; ++i) {
            lps[lp].underlyingTokens[i] = ICurveLP(lpPool).coins(i);
        }
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return lps[asset].pool != address(0);
    }

    /// @notice Is depreceted, only for tests usage
    function getFiatPrice1e18Unsafe(address asset) public view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        CurveLPInfo memory lpInfo = lps[asset];

        uint256 sumBalances = 0;
        uint256 nTokens = lpInfo.underlyingTokens.length;
        for (uint256 i = 0; i < nTokens; ++i) {
            uint256 newTokenBalance = ICurveLP(lpInfo.pool).balances(i);
            sumBalances += newTokenBalance * ORACLE.getFiatPrice1e18(address(lpInfo.underlyingTokens[i]));
        }

        return sumBalances / IERC20(asset).totalSupply();
    }

    function _getFiatPriceStablePool(address asset) internal view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        CurveLPInfo memory lpInfo = lps[asset];

        return ICurveLP(lpInfo.pool).get_virtual_price();
    }

    function _getFiatPricePoolV2(address asset) internal view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        CurveLPInfo memory lpInfo = lps[asset];

        return ICurveLP(lpInfo.pool).lp_price();
    }

    function getFiatPrice1e18(address asset) external view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        CurveLPInfo memory lpInfo = lps[asset];

        if (lpInfo.poolType == CurvePoolType.Stable) {
            return _getFiatPriceStablePool(asset);
        }
        if (lpInfo.poolType == CurvePoolType.V2) {
            return _getFiatPricePoolV2(asset);
        }

        // If lpInfo.poolType == CurvePoolType.Default or not set:
        revert LPOracleNotReady();
    }

    function withdraw(address asset, uint256 amount) external returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        TransferHelper.safeTransferFrom(asset, msg.sender, address(this), amount);
        CurveLPInfo memory lpInfo = lps[asset];

        uint256 nTokens = lpInfo.underlyingTokens.length;
        results = new WithdrawResult[](nTokens);

        if (asset != lpInfo.pool && IERC20(asset).allowance(address(this), lpInfo.pool) < amount) {
            IERC20(asset).approve(lpInfo.pool, amount);
        }

        uint256[] memory balancesBefore = new uint256[](nTokens);
        for (uint16 i = 0; i < nTokens; ++i) {
            balancesBefore[i] = IERC20(lpInfo.underlyingTokens[i]).balanceOf(address(this));
        }

        if (nTokens == 2) {
            uint256[2] memory minAmounts;
            minAmounts[0] = 0;
            minAmounts[1] = 0;
            ICurveLP(lpInfo.pool).remove_liquidity(amount, minAmounts);
        }
        if (nTokens == 3) {
            uint256[3] memory minAmounts;
            minAmounts[0] = 0;
            minAmounts[1] = 0;
            minAmounts[2] = 0;
            ICurveLP(lpInfo.pool).remove_liquidity(amount, minAmounts);
        }

        for (uint16 i = 0; i < nTokens; ++i) {
            address underlyingToken = lpInfo.underlyingTokens[i];
            uint256 underlineTokenAmount = IERC20(underlyingToken).balanceOf(address(this)) - balancesBefore[i];
            results[i] = WithdrawResult({ token: underlyingToken, amount: underlineTokenAmount });
            TransferHelper.safeTransfer(underlyingToken, msg.sender, underlineTokenAmount);
        }
    }

    uint256[45] private __gap;
}
