// CHANGED: in dss all joins contracts are in join.sol. We split and rename them to read this better.
// Added SuAuthenticated instead of authorizedAccounts.

/**
 * @title CoinJoin
 * @notice Adapter contract for converting between internal StablePro coins (SafeEngine) and external ERC20 tokens in the StableUnit protocol.
 * @dev Bridges SafeEngine’s internal RAD-precision coins with external WAD-precision StablePro tokens, handling minting,
 *      burning, and reward system integration (commented out). Supports collateral type mapping for reward tracking.
 *      This contract is a fork of MakerDAO’s join.sol, split and renamed for clarity, with SuAuthenticated access control.
 * @custom:usage
 * - **Initialization**: Deploy with access control, SafeEngine, and StablePro token addresses.
 *   ```solidity
 *   CoinJoin coinJoin = new CoinJoin(accessControl, safeEngine, stablePro);
 *   ```
 * - **Deposit StablePro**: Join external StablePro tokens into a Safe.
 *   ```solidity
 *   coinJoin.join(collateralType, user, cdp, amount);
 *   ```
 * - **Withdraw StablePro**: Exit internal StablePro coins to external tokens.
 *   ```solidity
 *   coinJoin.exit(collateralType, user, lockupPeriod, cdp, amount);
 *   ```
 * - **No-Reward Operations**: Join or exit without reward system interaction.
 *   ```solidity
 *   coinJoin.joinNoReward(cdp, amount);
 *   coinJoin.exitNoReward(cdp, amount);
 *   ```
 * - **Disabling**: Disable the contract to halt operations.
 *   ```solidity
 *   coinJoin.disableContract();
 *   ```
 * @custom:security Only authorized accounts (Admin, DisablerAccess, ProxyOwner) can call state-changing functions.
 *                 Token transfers require user approval, and SafeEngine enforces balance updates. Reward system integration
 *                 is commented out, reducing external dependencies.
 * @custom:changes Split from MakerDAO’s join.sol and renamed to CoinJoin.sol. Replaced authorizedAccounts with
 *                 SuAuthenticated. Commented out RewardChef integration. Added collateral type mapping.
 * @custom:events Emits `Join`, `JoinNoReward`, `Exit`, `ExitNoReward`, `DisableContract`, and commented-out
 *                 `DecreaseAmountFailed`, `IncreaseAmountFailed`, `UpdateRewardChef` for operations and errors.
 */
contract CoinJoin is ICoinJoin, SuAuthenticated, ProxyOwner {
    /*address public rewardChef;*/ // Commented-out RewardChef reference
    /// @notice Reference to the SafeEngine contract for internal coin operations.
    ISafeEngine public safeEngine;
    /// @notice External ERC20 representation of the system coin (StablePro).
    ICoin public systemCoin;
    /// @notice Maps collateral type identifiers to their corresponding token contract addresses.
    mapping (bytes32 => address) public collateralAddress; // CollateralType => address of collateral

    /**
     * @notice Initializes the CoinJoin contract with required parameters.
     * @dev Sets up references, enables the contract, and initializes commented-out RewardChef (if applicable).
     * @param accessControlSingleton Address of the access control singleton.
     * @param safeEngine_ Address of the SafeEngine contract.
     * @param systemCoin_ Address of the external StablePro token contract.
     */
    function initialize(address accessControlSingleton, address safeEngine_, address systemCoin_/*, address rewardChef_*/)
    public initializer {

        __suAuthenticatedInit(accessControlSingleton);

        contractEnabled = 1; // Enable contract operations
        safeEngine = ISafeEngine(safeEngine_); // Initialize SafeEngine reference
        systemCoin = ICoin(systemCoin_); // Initialize StablePro token reference
        //rewardChef = rewardChef_; // Commented-out RewardChef initialization
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice Contract operational status flag (1 = enabled, 0 = disabled).
    uint public contractEnabled;

    /**
     * @notice Disables the contract, halting further operations.
     * @dev Sets `contractEnabled` to 0 and emits an event. Only callable by DisablerAccess.
     */
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0; // Disable contract
        emit DisableContract(); // Notify external systems
    }

    /// @notice RAY precision constant (10^27) for internal system calculations.
    uint constant ONE = 10 ** 27;

    /**
     * @notice Performs safe multiplication with overflow protection.
     * @dev Ensures the product does not overflow by verifying division consistency.
     * @param x First operand.
     * @param y Second operand.
     * @return z Product of x and y.
     */
    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x); // Prevent overflow
    }

    /*function updateRewardChef(address rewardChef_) external onlyDAO {
        rewardChef = rewardChef_; // Commented-out RewardChef update
        emit UpdateRewardChef(); // Notify external systems
    }*/

    /**
     * @notice Associates a collateral type with its token contract address.
     * @dev Maps the collateral type to its address for reward tracking (if enabled). Only callable by Admin.
     * @param _collateralType Unique identifier for the collateral type.
     * @param _collateralAddress Contract address of the collateral token.
     */
    function initializeCollateralType(bytes32 _collateralType, address _collateralAddress) external onlyAdmin {
        collateralAddress[_collateralType] = _collateralAddress; // Map collateral type to address
    }

    /**
     * @notice Converts external StablePro tokens to internal SafeEngine coins with reward adjustments.
     * @dev Burns external tokens, transfers internal coins to the Safe, and updates rewards (commented out).
     *      Requires ProxyOwner authorization for the user.
     * @param collateralType Type of collateral associated with the operation.
     * @param user Address of the user performing the join operation.
     * @param cdp Address to receive the internal coins (Safe handler).
     * @param wad Amount of external StablePro to convert [wad, 18 decimals].
     */
    function join(bytes32 collateralType, address user, address cdp, uint wad) external onlyProxyOwner(user) {
        safeEngine.transferInternalCoins(address(this), cdp, mul(ONE, wad)); // Transfer internal coins (RAD precision)
        systemCoin.burn(msg.sender, wad); // Burn external StablePro tokens

        //address colAddr = collateralAddress[collateralType]; // Commented-out reward logic
        /*(bool success,) = rewardChef.call(
            abi.encodeWithSignature("decreaseAmount(address,address,uint256)", colAddr, user, wad)
        );
        if (!success) {
            emit DecreaseAmountFailed(colAddr, user, wad);
        }*/

        emit Join(cdp, wad); // Log operation
    }

    /**
     * @notice Converts external StablePro tokens to internal SafeEngine coins without reward interaction.
     * @dev Burns external tokens and transfers internal coins to the Safe.
     * @param cdp Address to receive the internal coins (Safe handler).
     * @param wad Amount of external StablePro to convert [wad, 18 decimals].
     */
    function joinNoReward(address cdp, uint wad) external {
        safeEngine.transferInternalCoins(address(this), cdp, mul(ONE, wad)); // Transfer internal coins (RAD precision)
        systemCoin.burn(msg.sender, wad); // Burn external StablePro tokens
        emit JoinNoReward(cdp, wad); // Log operation
    }

    /**
     * @notice Converts internal SafeEngine coins to external StablePro tokens with reward adjustments.
     * @dev Transfers internal coins to this contract, mints external tokens, and updates rewards (commented out).
     *      Requires ProxyOwner authorization and contract to be enabled.
     * @param collateralType Type of collateral associated with the operation.
     * @param user Address of the user performing the exit operation.
     * @param lockupPeriodSeconds Lockup period for reward system integration (if enabled).
     * @param cdp Address to receive the external StablePro tokens.
     * @param wad Amount of internal coins to convert [wad, 18 decimals].
     */
    function exit(
        bytes32 collateralType, address user, uint256 lockupPeriodSeconds, address cdp, uint wad
    ) external onlyProxyOwner(user) {
        require(contractEnabled == 1, "CoinJoin/not-contractEnabled"); // Ensure contract is enabled
        safeEngine.transferInternalCoins(msg.sender, address(this), mul(ONE, wad)); // Transfer internal coins (RAD precision)
        systemCoin.mint(cdp, wad); // Mint external StablePro tokens

        /*address colAddr = collateralAddress[collateralType]; // Commented-out reward logic
        uint256 userLockupPeriod = IRewardChefV2(rewardChef).getUserLockupPeriodsSeconds(colAddr, user);
        if (userLockupPeriod != 0 && userLockupPeriod != lockupPeriodSeconds) revert BadLockupPeriod();
        (bool success,) = rewardChef.call(
            abi.encodeWithSignature(
                "increaseAmount(address,address,uint256,uint256)", colAddr, user, wad, lockupPeriodSeconds
            )
        );
        if (!success) {
            emit IncreaseAmountFailed(colAddr, user, wad, 1);
        }*/

        emit Exit(cdp, wad); // Log operation
    }

    /**
     * @notice Converts internal SafeEngine coins to external StablePro tokens without reward interaction.
     * @dev Transfers internal coins to this contract and mints external tokens. Requires contract to be enabled.
     * @param cdp Address to receive the external StablePro tokens.
     * @param wad Amount of internal coins to convert [wad, 18 decimals].
     */
    function exitNoReward(address cdp, uint wad) external {
        require(contractEnabled == 1, "CoinJoin/not-contractEnabled"); // Ensure contract is enabled
        safeEngine.transferInternalCoins(msg.sender, address(this), mul(ONE, wad)); // Transfer internal coins (RAD precision)
        systemCoin.mint(cdp, wad); // Mint external StablePro tokens
        emit ExitNoReward(cdp, wad); // Log operation
    }

    /**
     * @notice Checks if the contract supports a specific interface.
     * @dev Verifies support for ICoinJoin, IERC165Upgradeable, or access control interfaces.
     * @param interfaceId Interface identifier to check.
     * @return True if the interface is supported, false otherwise.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return interfaceId == type(ICoinJoin).interfaceId || // CoinJoin interface
               interfaceId == type(IERC165Upgradeable).interfaceId || // ERC165 interface
               ACCESS_CONTROL_SINGLETON.supportsInterface(interfaceId); // Access control interfaces
    }
}","keccak256":"0x35a537230063ad91af6914be5f30bbb6c8045c07fbcda01ee1ab37ec02a30d6c","license":"AGPL-3.0-or-later"},"contracts/makerdao/interfaces/ICoin.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

