pragma solidity ^0.8.0;

import "../interfaces/ISafeEngine.sol";
import "../interfaces/IDssCdpManager.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

// CHANGED: interface created and struct and events moved there

/**
 * @title CdpHandler
 * @notice Proxy contract for managing a Safe’s interactions with SafeEngine.
 * @dev Created per Safe to handle collateral and debt operations, granting the DssCdpManager permission to modify it.
 * @custom:usage Deployed automatically by DssCdpManager when creating a new Safe.
 *   ```solidity
 *   CdpHandler handler = new CdpHandler(safeEngine);
 *   ```
 * @custom:security Grants SafeEngine modification rights to the DssCdpManager upon creation.
 */
contract CdpHandler {
    /**
     * @notice Initializes the CdpHandler with a SafeEngine reference.
     * @dev Approves the creator (DssCdpManager) to modify the handler’s Safe in SafeEngine.
     * @param safeEngine Address of the SafeEngine contract.
     */
    constructor(ISafeEngine safeEngine) public {
        safeEngine.approveSAFEModification(msg.sender);
    }
}

/**
 * @title DssCdpManager
 * @notice Manages Safes (CDPs) for users in the StableUnit protocol, facilitating Safe creation and operations.
 * @dev Tracks Safe ownership, permissions, and state via a double-linked list per user. Interacts with SafeEngine
 *      to perform collateral and debt operations through CdpHandler proxies. Supports Safe creation, ownership transfer,
 *      collateral/debt modification, and Safe merging. This contract is a fork of MakerDAO’s DssCdpManager.sol, adapted
 *      for StableUnit with a custom interface and simplified permission model.
 * @custom:usage
 * - **Initialization**: Deploy with SafeEngine address.
 *   ```solidity
 *   DssCdpManager cdpManager = new DssCdpManager(safeEngineAddress);
 *   ```
 * - **Safe Creation**: Open a new Safe for a user with a collateral type.
 *   ```solidity
 *   uint cdpId = cdpManager.openSafe("ETH-A", userAddress);
 *   ```
 * - **Safe Modification**: Adjust collateral or debt for a Safe.
 *   ```solidity
 *   cdpManager.modifySAFECollateralization(cdpId, deltaCollateral, deltaDebt);
 *   ```
 * - **Ownership Transfer**: Transfer a Safe to another user.
 *   ```solidity
 *   cdpManager.transferSafeOwnership(cdpId, newOwner);
 *   ```
 * - **Safe Merging**: Move collateral and debt between Safes or to/from external addresses.
 *   ```solidity
 *   cdpManager.moveSAFE(cdpIdSrc, cdpIdDst); // Merge two Safes
 *   cdpManager.quit(cdpId, dstAddress); // Exit Safe to external address
 *   ```
 * @custom:security Only authorized users (owners or permitted addresses) can operate on Safes or handlers.
 *                 Relies on SafeEngine for core logic and state management.
 *                 CdpHandler proxies ensure isolation of Safe operations.
 * @custom:changes Added `IDssCdpManager` interface, moving structs and events there.
 *                 Simplified permission model with `cdpCan` and `handlerCan` mappings.
 * @custom:events Emits `NewCdp` when creating a Safe; other events are defined in `IDssCdpManager`.
 */
contract DssCdpManager is IDssCdpManager, Initializable {
    /// @notice Reference to the SafeEngine contract for Safe operations.
    ISafeEngine public safeEngine;
    /// @notice Auto-incrementing counter for Safe IDs (CDP IDs).
    uint public cdpi; // Auto incremental
    /// @notice Maps Safe IDs to their CdpHandler proxy addresses.
    mapping (uint => address) public safes; // CDPId => CdpHandler
    /// @notice Maps Safe IDs to their previous and next IDs in the owner’s double-linked list.
    mapping (uint => List) public list; // CDPId => Prev & Next CDPIds (double linked list)
    /// @notice Maps Safe IDs to their owner addresses.
    mapping (uint => address) public owns; // CDPId => Owner
    /// @notice Maps Safe IDs to their collateral types (e.g., "ETH-A").
    mapping (uint => bytes32) public collateralTypes; // CDPId => CollateralType

    /// @notice Maps owner addresses to their first Safe ID.
    mapping (address => uint) public firstCdpId; // Owner => First CDPId
    /// @notice Maps owner addresses to their last Safe ID.
    mapping (address => uint) public lastCdpId; // Owner => Last CDPId
    /// @notice Maps owner addresses to their total number of Safes.
    mapping (address => uint) public cdpCount; // Owner => Amount of CDPs

    /// @notice Maps owners to Safe IDs to allowed addresses with modification permissions.
    mapping (
        address => mapping (
            uint => mapping (
                address => uint
            )
        )
    ) public cdpCan; // Owner => CDPId => Allowed Addr => True/False

    /// @notice Maps CdpHandler addresses to allowed addresses with modification permissions.
    mapping (
        address => mapping (
            address => uint
        )
    ) public handlerCan; // Cdp => Allowed Addr => True/False

    /**
     * @notice Restricts function access to the Safe owner or permitted addresses.
     * @dev Reverts if the caller is neither the owner nor has permission for the Safe.
     * @param cdpId Safe ID to check permissions for.
     */
    modifier cdpAllowed(uint cdpId) {
        require(msg.sender == owns[cdpId] || cdpCan[owns[cdpId]][cdpId][msg.sender] == 1, "cdp-not-allowed");
        _;
    }

    /**
     * @notice Restricts function access to the handler address or permitted addresses.
     * @dev Reverts if the caller is neither the handler nor has permission for the handler.
     * @param urn CdpHandler address to check permissions for.
     */
    modifier handlerAllowed(address urn) {
        require(msg.sender == urn || handlerCan[urn][msg.sender] == 1, "urn-not-allowed");
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initializes the DssCdpManager with a SafeEngine address.
     * @dev Sets the SafeEngine reference for Safe operations.
     * @param safeEngine_ Address of the SafeEngine contract.
     */

    function initialize(address safeEngine_) public initializer {
        safeEngine = ISafeEngine(safeEngine_);
    }

    /**
     * @notice Adds two unsigned integers, reverting on overflow.
     * @dev Ensures safe addition for Safe ID and count calculations.
     * @param x First operand.
     * @param y Second operand.
     * @return z Sum of x and y.
     */
    function _add(uint x, uint y) internal pure returns (uint z) {
        require((z = x + y) >= x);
    }

    /**
     * @notice Subtracts two unsigned integers, reverting on underflow.
     * @dev Ensures safe subtraction for Safe count calculations.
     * @param x First operand.
     * @param y Second operand.
     * @return z Difference of x and y.
     */
    function _sub(uint x, uint y) internal pure returns (uint z) {
        require((z = x - y) <= x);
    }

    /**
     * @notice Converts an unsigned integer to a signed integer.
     * @dev Ensures non-negative input for SafeEngine compatibility.
     * @param x Unsigned integer to convert.
     * @return y Signed integer result.
     */
    function toInt(uint x) internal pure returns (int y) {
        y = int(x);
        require(y >= 0);
    }

    /**
     * @notice Grants or revokes permission for an address to modify a Safe.
     * @dev Updates `cdpCan` mapping for the Safe’s owner. Requires caller to be authorized.
     * @param cdp Safe ID to modify permissions for.
     * @param usr Address to grant/revoke permission.
     * @param ok Permission status (1 for allowed, 0 for denied).
     */
    function cdpAllow(uint cdp, address usr, uint ok) public cdpAllowed(cdp) {
        cdpCan[owns[cdp]][cdp][usr] = ok;
    }

    /**
     * @notice Grants or revokes permission for an address to modify the caller’s CdpHandler.
     * @dev Updates `handlerCan` mapping for the caller’s handler.
     * @param usr Address to grant/revoke permission.
     * @param ok Permission status (1 for allowed, 0 for denied).
     */
    function allowHandler(address usr, uint ok) public {
        handlerCan[msg.sender][usr] = ok;
    }

    /**
     * @notice Creates a new Safe for a user with a specified collateral type.
     * @dev Deploys a CdpHandler, assigns ownership, and updates the double-linked list.
     *      Emits a `NewCdp` event.
     * @param collateralType Unique identifier for the collateral type (e.g., "ETH-A").
     * @param usr Address to own the new Safe.
     * @return Safe ID (CDP ID) of the created Safe.
     */
    function openSafe(bytes32 collateralType, address usr) public returns (uint) {
        require(usr != address(0), "usr-address-0");

        // Increment Safe ID
        cdpi = _add(cdpi, 1);
        // Deploy CdpHandler
        safes[cdpi] = address(new CdpHandler(safeEngine));
        // Assign ownership
        owns[cdpi] = usr;
        collateralTypes[cdpi] = collateralType;

        // Update double-linked list
        if (firstCdpId[usr] == 0) {
            firstCdpId[usr] = cdpi; // Set as first Safe if none exist
        }
        if (lastCdpId[usr] != 0) {
            list[cdpi].prev = lastCdpId[usr]; // Link to previous Safe
            list[lastCdpId[usr]].next = cdpi; // Update previous Safe’s next pointer
        }
        lastCdpId[usr] = cdpi; // Set as last Safe
        cdpCount[usr] = _add(cdpCount[usr], 1); // Increment Safe count

        emit NewCdp(msg.sender, usr, cdpi);
        return cdpi;
    }

    /**
     * @notice Transfers ownership of a Safe to another address.
     * @dev Updates the double-linked list for both the old and new owners. Requires caller to be authorized.
     * @param cdp Safe ID to transfer.
     * @param dst New owner address.
     */
    function transferSafeOwnership(uint cdp, address dst) public cdpAllowed(cdp) {
        require(dst != address(0), "dst-address-0");
        require(dst != owns[cdp], "dst-already-owner");

        // Remove from old owner’s list
        if (list[cdp].prev != 0) {
            list[list[cdp].prev].next = list[cdp].next; // Update previous Safe’s next pointer
        }
        if (list[cdp].next != 0) {
            list[list[cdp].next].prev = list[cdp].prev; // Update next Safe’s previous pointer
        } else {
            lastCdpId[owns[cdp]] = list[cdp].prev; // Update last Safe ID if this was the last
        }
        if (firstCdpId[owns[cdp]] == cdp) {
            firstCdpId[owns[cdp]] = list[cdp].next; // Update first Safe ID if this was the first
        }
        cdpCount[owns[cdp]] = _sub(cdpCount[owns[cdp]], 1); // Decrement old owner’s Safe count

        // Transfer ownership
        owns[cdp] = dst;

        // Add to new owner’s list
        list[cdp].prev = lastCdpId[dst]; // Link to new owner’s last Safe
        list[cdp].next = 0; // Clear next pointer
        if (lastCdpId[dst] != 0) {
            list[lastCdpId[dst]].next = cdp; // Update previous Safe’s next pointer
        }
        if (firstCdpId[dst] == 0) {
            firstCdpId[dst] = cdp; // Set as first Safe if none exist
        }
        lastCdpId[dst] = cdp; // Set as last Safe
        cdpCount[dst] = _add(cdpCount[dst], 1); // Increment new owner’s Safe count
    }

    /**
     * @notice Modifies a Safe’s collateral and debt through SafeEngine.
     * @dev Calls SafeEngine’s `modifySAFECollateralization` using the Safe’s handler. Requires caller to be authorized.
     * @param cdp Safe ID to modify.
     * @param deltaCollateral Amount to add (positive) or remove (negative) collateral [wad].
     * @param deltaDebt Amount to add (positive) or remove (negative) debt [wad].
     */
    function modifySAFECollateralization(
        uint cdp,
        int deltaCollateral,
        int deltaDebt
    ) public cdpAllowed(cdp) {
        address urn = safes[cdp];
        safeEngine.modifySAFECollateralization(
            collateralTypes[cdp],
            urn,
            urn,
            urn,
            deltaCollateral,
            deltaDebt
        );
    }

    /**
     * @notice Transfers collateral from a Safe to an address using the Safe’s collateral type.
     * @dev Calls SafeEngine’s `transferCollateral`. Requires caller to be authorized.
     * @param cdp Safe ID to transfer collateral from.
     * @param dst Destination address to receive collateral.
     * @param wad Amount of collateral to transfer [wad].
     */
    function transferCollateral(
        uint cdp,
        address dst,
        uint wad
    ) public cdpAllowed(cdp) {
        safeEngine.transferCollateral(collateralTypes[cdp], safes[cdp], dst, wad);
    }

    /**
     * @notice Transfers collateral from a Safe to an address using a specified collateral type.
     * @dev Calls SafeEngine’s `transferCollateral`. Requires caller to be authorized.
     * @param ilk Collateral type to use for transfer.
     * @param cdp Safe ID to transfer collateral from.
     * @param dst Destination address to receive collateral.
     * @param wad Amount of collateral to transfer [wad].
     */
    function transferCollateral(
        bytes32 ilk,
        uint cdp,
        address dst,
        uint wad
    ) public cdpAllowed(cdp) {
        safeEngine.transferCollateral(ilk, safes[cdp], dst, wad);
    }

    /**
     * @notice Transfers internal StablePro coins from a Safe to an address.
     * @dev Calls SafeEngine’s `transferInternalCoins`. Requires caller to be authorized.
     * @param cdp Safe ID to transfer coins from.
     * @param dst Destination address to receive coins.
     * @param rad Amount of StablePro coins to transfer [rad].
     */
    function transferInternalCoins(uint cdp, address dst, uint rad) public cdpAllowed(cdp) {
        safeEngine.transferInternalCoins(safes[cdp], dst, rad);
    }

    /**
     * @notice Exits a Safe’s collateral and debt to an external address.
     * @dev Transfers all collateral and debt to the destination address via SafeEngine. Requires caller to be authorized for both Safe and destination handler.
     * @param cdp Safe ID to exit.
     * @param dst Destination address to receive collateral and debt.
     */
    function quit(uint cdp, address dst) public cdpAllowed(cdp) handlerAllowed(dst) {
        (uint lockedCollateral, uint generatedDebt) = safeEngine.safes(collateralTypes[cdp], safes[cdp]);
        safeEngine.transferSAFECollateralAndDebt(
            collateralTypes[cdp],
            safes[cdp],
            dst,
            toInt(lockedCollateral),
            toInt(generatedDebt)
        );
    }

    /**
     * @notice Imports collateral and debt from an external address into a Safe.
     * @dev Transfers all collateral and debt from the source address to the Safe’s handler. Requires caller to be authorized for both source handler and Safe.
     * @param src Source address to transfer collateral and debt from.
     * @param cdp Safe ID to receive collateral and debt.
     */
    function enter(address src, uint cdp) public handlerAllowed(src) cdpAllowed(cdp) {
        (uint lockedCollateral, uint generatedDebt) = safeEngine.safes(collateralTypes[cdp], src);
        safeEngine.transferSAFECollateralAndDebt(
            collateralTypes[cdp],
            src,
            safes[cdp],
            toInt(lockedCollateral),
            toInt(generatedDebt)
        );
    }

    /**
     * @notice Merges collateral and debt from one Safe to another.
     * @dev Transfers all collateral and debt from the source Safe to the destination Safe. Requires both Safes to have the same collateral type and caller to be authorized for both.
     * @param cdpSrc Source Safe ID.
     * @param cdpDst Destination Safe ID.
     */
    function moveSAFE(uint cdpSrc, uint cdpDst) public cdpAllowed(cdpSrc) cdpAllowed(cdpDst) {
        require(collateralTypes[cdpSrc] == collateralTypes[cdpDst], "non-matching-cdps");
        (uint lockedCollateral, uint generatedDebt) = safeEngine.safes(collateralTypes[cdpSrc], safes[cdpSrc]);
        safeEngine.transferSAFECollateralAndDebt(
            collateralTypes[cdpSrc],
            safes[cdpSrc],
            safes[cdpDst],
            toInt(lockedCollateral),
            toInt(generatedDebt)
        );
    }
