/**
 * @title Math
 * @notice Abstract utility contract for precise arithmetic operations in the StableUnit protocol.
 * @dev Provides internal functions for multiplication, addition, subtraction, integer conversion, and precision adjustments
 *      used by DssProxyActions. Supports ray (10^27) and rad (10^45) precision for calculations involving rates and debt.
 *      This contract is inherited by DssProxyActions to ensure safe arithmetic operations.
 * @custom:usage
 * - **Inheritance**: DssProxyActions inherits Math to use its arithmetic functions.
 *   ```solidity
 *   contract DssProxyActions is Math { ... }
 *   ```
 * - **Multiplication**: Use `mul` for overflow-safe multiplication.
 *   ```solidity
 *   uint256 product = mul(a, b); // Safe multiplication
 *   ```
 * - **Precision Conversion**: Use `convertTo18` to adjust collateral amounts to 18 decimals.
 *   ```solidity
 *   uint256 wad = convertTo18(collateralJoin, amount); // Convert to WAD precision
 *   ```
 * @custom:security All functions include overflow/underflow protection to prevent arithmetic errors.
 */
abstract contract Math {
    /// @notice Constant for ray precision (10^27), used in rate calculations.
    uint256 constant RAY = 10 ** 27;

    /**
     * @notice Multiplies two unsigned integers, reverting on overflow.
     * @dev Ensures the product does not exceed the maximum uint256 value.
     * @param x First operand.
     * @param y Second operand.
     * @return z Product of x and y.
     */
    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x, "mul-overflow");
    }

    /**
     * @notice Adds two unsigned integers, reverting on overflow.
     * @dev Ensures the sum does not exceed the maximum uint256 value.
     * @param x First operand.
     * @param y Second operand.
     * @return z Sum of x and y.
     */
    function add(uint x, uint y) internal pure returns (uint z) {
        require((z = x + y) >= x);
    }

    /**
     * @notice Subtracts two unsigned integers, reverting on underflow.
     * @dev Ensures the result is non-negative.
     * @param x First operand.
     * @param y Second operand.
     * @return z Difference of x and y.
     */
    function sub(uint x, uint y) internal pure returns (uint z) {
        require((z = x - y) <= x, "sub-overflow");
    }

    /**
     * @notice Converts an unsigned integer to a signed integer, reverting on negative values.
     * @dev Ensures the input is non-negative for safe conversion.
     * @param x Unsigned integer to convert.
     * @return y Signed integer representation of x.
     */
    function toInt(uint x) internal pure returns (int y) {
        y = int(x);
        require(y >= 0, "int-overflow");
    }

    /**
     * @notice Converts a WAD amount to RAD precision.
     * @dev Multiplies the input by 10^27 to convert from 18 to 45 decimals.
     * @param wad Amount in WAD precision (18 decimals).
     * @return rad Amount in RAD precision (45 decimals).
     */
    function toRad(uint wad) internal pure returns (uint rad) {
        rad = mul(wad, 10 ** 27);
    }

    /**
     * @notice Converts a collateral amount to 18-decimal WAD precision.
     * @dev Adjusts the amount based on the collateral token’s decimals for SafeEngine compatibility.
     * @param collateralJoin Address of the CollateralJoin contract.
     * @param amt Amount in the collateral token’s decimal precision.
     * @return wad Amount in WAD precision (18 decimals).
     */
    function convertTo18(ICollateralJoin collateralJoin, uint256 amt) internal returns (uint256 wad) {
        // Calculate multiplier for decimal conversion
        wad = mul(
            amt,
            10 ** (18 - collateralJoin.decimals())
        );
    }
}

/**
 * @title DssProxyActions
 * @notice Proxy actions contract for user interaction with the StableUnit protocol via a DSProxy.
 * @dev Provides functions to manage collateralized debt positions (Safes) through a user’s proxy, including
 *      locking/unlocking collateral, generating/repaying debt, and querying Safe information. It interacts with
 *      StableUnit contracts like DssCdpManager, SafeEngine, CollateralJoin, CoinJoin, and TaxCollector. This contract
 *      is designed to be called via a DSProxy, reducing complexity for users, and includes view functions for UI integration.
 *      Some functions were removed from the MakerDAO version to optimize deployment size.
 * @custom:warning Functions are intended for DSProxy use; direct calls may be unsafe due to lack of access control.
 * @custom:usage
 * - **Deployment**: Deploy as a stateless contract for DSProxy use.
 *   ```solidity
 *   DssProxyActions proxyActions = new DssProxyActions();
 *   ```
 * - **Lock Collateral**: Deposit collateral into a Safe.
 *   ```solidity
 *   proxyActions.lockTokenCollateral(cdpManager, collateralJoin, cdpId, amount);
 *   ```
 * - **Generate Debt**: Draw StablePro debt from a Safe.
 *   ```solidity
 *   proxyActions.generateDebt(cdpManager, taxCollector, coinJoin, cdpId, amount, lockupPeriod);
 *   ```
 * - **Repay Debt**: Repay StablePro debt to a Safe.
 *   ```solidity
 *   proxyActions.repayDebt(cdpManager, coinJoin, cdpId, amount);
 *   ```
 * - **Combined Operations**: Lock collateral and generate debt in one transaction.
 *   ```solidity
 *   proxyActions.lockTokenCollateralAndGenerateDebt(cdpManager, taxCollector, collateralJoin, coinJoin, cdpId, collateralAmount, debtAmount, lockupPeriod);
 *   ```
 * - **Query Safe Info**: Retrieve Safe details for UI display.
 *   ```solidity
 *   (uint256 lockedCollateral, uint256 generatedDebt) = proxyActions.getCdpInfo(cdpManager, user, collateralType);
 *   ```
 * @custom:security This contract is stateless and relies on DSProxy for access control. Users must approve token transfers
 *                 to the proxy for operations like locking collateral or repaying debt. The SafeEngine enforces collateralization
 *                 and debt limits. Some functions assume collateral tokens have arbitrary decimals, handled via `convertTo18`.
 * @custom:changes Removed functions from MakerDAO’s version (e.g., ETH/GNT support, some Safe management) to reduce deployment size.
 *                 Replaced `vat` with `safeEngine` and updated to StableUnit-specific interfaces.
 */
contract DssProxyActions is Math {
    /**
     * @notice Calculates the delta debt required to generate a specified StablePro amount.
     * @dev Updates stability fees via TaxCollector and computes the debt needed to cover the StablePro amount.
     *      Accounts for existing Safe balance and rate precision.
     * @param safeEngine Address of the SafeEngine contract.
     * @param taxCollector Address of the TaxCollector contract.
     * @param safe Address of the Safe.
     * @param collateralType Unique identifier for the collateral type.
     * @param wad Amount of StablePro to generate [wad].
     * @return deltaDebt Debt adjustment required [in SafeEngine’s debt precision].
     */
    function _getGeneratedDeltaDebt(
        ISafeEngine safeEngine,
        address taxCollector,
        address safe,
        bytes32 collateralType,
        uint wad
    ) internal returns (int deltaDebt) {
        // Update stability fee rate
        uint rate = ITaxCollector(taxCollector).taxSingle(collateralType);
        // Get StablePro balance in SafeEngine
        uint stableProBalance = safeEngine.coinBalance(safe);

        // Calculate additional debt if balance is insufficient
        if (stableProBalance < mul(wad, RAY)) {
            deltaDebt = toInt(sub(mul(wad, RAY), stableProBalance) / rate);
            // Adjust for precision loss
            deltaDebt = mul(uint(deltaDebt), rate) < mul(wad, RAY) ? deltaDebt + 1 : deltaDebt;
        }
    }

    /**
     * @notice Calculates the delta debt to repay a specified StablePro amount.
     * @dev Determines the debt reduction based on the StablePro amount and Safe’s debt, respecting rate precision.
     * @param safeEngine Address of the SafeEngine contract.
     * @param stablePro Amount of StablePro to repay [rad].
     * @param safe Address of the Safe.
     * @param collateralType Unique identifier for the collateral type.
     * @return deltaDebt Debt adjustment to reduce [in SafeEngine’s debt precision].
     */
    function _getRepaidDeltaDebt(
        ISafeEngine safeEngine,
        uint stablePro,
        address safe,
        bytes32 collateralType
    ) internal view returns (int deltaDebt) {
        // Get collateral type rate
        (, uint rate,,,,,,) = safeEngine.collateralTypes(collateralType);
        // Get Safe’s debt
        (, uint safeDebt) = safeEngine.safes(collateralType, safe);

        // Calculate debt reduction
        deltaDebt = toInt(stablePro / rate);
        // Limit to Safe’s total debt
        deltaDebt = uint(deltaDebt) <= safeDebt ? -deltaDebt : -toInt(safeDebt);
    }

    /**
     * @notice Calculates the StablePro amount needed to repay all Safe debt.
     * @dev Computes the StablePro required to clear the Safe’s debt, accounting for rate and precision.
     * @param safeEngine Address of the SafeEngine contract.
     * @param usr Address of the Safe owner.
     * @param safe Address of the Safe.
     * @param collateralType Unique identifier for the collateral type.
     * @return wad Amount of StablePro required [wad].
     */
    function _getRepaidAllWad(
        ISafeEngine safeEngine,
        address usr,
        address safe,
        bytes32 collateralType
    ) internal view returns (uint wad) {
        // Get collateral type rate
        (, uint rate,,,,,,) = safeEngine.collateralTypes(collateralType);
        // Get Safe’s debt
        (, uint safeDebt) = safeEngine.safes(collateralType, safe);
        // Get StablePro balance
        uint stablePro = safeEngine.coinBalance(usr);

        // Calculate required StablePro
        uint rad = sub(mul(safeDebt, rate), stablePro);
        wad = rad / RAY;
        // Adjust for precision dust
        wad = mul(wad, RAY) < rad ? wad + 1 : wad;
    }

    /**
     * @notice Deposits StablePro into the SafeEngine via CoinJoin.
     * @dev Transfers StablePro tokens from the caller, approves CoinJoin, and joins them into the SafeEngine.
     * @param collateralType Unique identifier for the collateral type.
     * @param apt Address of the CoinJoin contract.
     * @param cdp Address to credit the StablePro balance.
     * @param wad Amount of StablePro to deposit [wad].
     */
    function _coinJoin_join(bytes32 collateralType, address apt, address cdp, uint wad) internal {
        // Transfer StablePro from caller
        ICoinJoin(apt).systemCoin().transferFrom(msg.sender, address(this), wad);
        // Approve CoinJoin to spend StablePro
        ICoinJoin(apt).systemCoin().approve(address(apt), wad);
        // Deposit StablePro into SafeEngine
        ICoinJoin(apt).join(collateralType, msg.sender, cdp, wad);
    }

    /**
     * @notice Deposits collateral into the SafeEngine via CollateralJoin.
     * @dev Transfers collateral tokens from the caller, approves CollateralJoin, and joins them into the SafeEngine.
     * @param apt Address of the CollateralJoin contract.
     * @param safe Address to credit the collateral balance.
     * @param amt Amount of collateral to deposit [in token’s decimal precision].
     */
    function _collateralJoin_join(ICollateralJoin apt, address safe, uint amt) internal {
        // Transfer collateral from caller
        apt.collateral().transferFrom(msg.sender, address(this), amt);
        // Approve CollateralJoin to spend collateral
        apt.collateral().approve(address(apt), amt);
        // Deposit collateral into SafeEngine
        apt.join(safe, amt);
    }

    /**
     * @notice Transfers collateral from a Safe to a destination.
     * @dev Calls DssCdpManager to move collateral balance within the SafeEngine.
     * @param manager Address of the DssCdpManager contract.
     * @param cdp Unique identifier of the Safe.
     * @param dst Address to receive the collateral.
     * @param wad Amount of collateral to transfer [wad].
     */
    function transferCollateral(address manager, uint cdp, address dst, uint wad) internal {
        IDssCdpManager(manager).transferCollateral(cdp, dst, wad);
    }

    /**
     * @notice Transfers internal StablePro coins from a Safe to a destination.
     * @dev Calls DssCdpManager to move coin balance within the SafeEngine.
     * @param manager Address of the DssCdpManager contract.
     * @param cdp Unique identifier of the Safe.
     * @param dst Address to receive the coins.
     * @param rad Amount of StablePro to transfer [rad].
     */
    function transferInternalCoins(address manager, uint cdp, address dst, uint rad) internal {
        IDssCdpManager(manager).transferInternalCoins(cdp, dst, rad);
    }

    /**
     * @notice Modifies a Safe’s collateral and debt.
     * @dev Calls DssCdpManager to adjust the Safe’s collateral and debt in the SafeEngine.
     * @param manager Address of the DssCdpManager contract.
     * @param cdp Unique identifier of the Safe.
     * @param deltaCollateral Amount to add/remove collateral [wad].
     * @param deltaDebt Amount to add/remove debt [in SafeEngine’s debt precision].
     */
    function modifySAFECollateralization(address manager, uint cdp, int deltaCollateral, int deltaDebt) internal {
        IDssCdpManager(manager).modifySAFECollateralization(cdp, deltaCollateral, deltaDebt);
    }

    /**
     * @notice Opens a new Safe for a collateral type.
     * @dev Calls DssCdpManager to create a new Safe and returns its ID.
     * @param manager Address of the DssCdpManager contract.
     * @param collateralType Unique identifier for the collateral type.
     * @param usr Address to own the Safe.
     * @return cdp Unique identifier of the newly created Safe.
     */
    function openSafe(address manager, bytes32 collateralType, address usr) internal returns (uint cdp) {
        cdp = IDssCdpManager(manager).openSafe(collateralType, usr);
    }

    /**
     * @notice Locks collateral into a Safe.
     * @dev Deposits collateral via CollateralJoin and updates the Safe’s collateral balance.
     * @param manager Address of the DssCdpManager contract.
     * @param collateralJoin Address of the CollateralJoin contract.
     * @param cdp Unique identifier of the Safe.
     * @param amt Amount of collateral to lock [in token’s decimal precision].
     */
    function lockTokenCollateral(address manager, ICollateralJoin collateralJoin, uint cdp, uint amt) external {
        // Deposit collateral into SafeEngine
        _collateralJoin_join(collateralJoin, address(this), amt);
        // Update Safe’s collateral balance
        IDssCdpManager(manager).safeEngine().modifySAFECollateralization(
            IDssCdpManager(manager).collateralTypes(cdp),
            IDssCdpManager(manager).safes(cdp),
            address(this),
            address(this),
            toInt(convertTo18(collateralJoin, amt)),
            0
        );
    }

    /**
     * @notice Unlocks collateral from a Safe and withdraws it to the caller.
     * @dev Reduces the Safe’s collateral balance and withdraws tokens via CollateralJoin.
     * @param manager Address of the DssCdpManager contract.
     * @param collateralJoin Address of the CollateralJoin contract.
     * @param cdp Unique identifier of the Safe.
     * @param amt Amount of collateral to unlock [in token’s decimal precision].
     */
    function freeTokenCollateral(address manager, ICollateralJoin collateralJoin, uint cdp, uint amt) external {
        uint wad = convertTo18(collateralJoin, amt);
        // Reduce Safe’s collateral balance
        modifySAFECollateralization(manager, cdp, -toInt(wad), 0);
        // Transfer collateral to proxy
        transferCollateral(manager, cdp, address(this), wad);
        // Withdraw collateral to caller
        collateralJoin.exit(msg.sender, amt);
    }

    /**
     * @notice Withdraws collateral from a Safe to the caller.
     * @dev Transfers collateral from the Safe to the proxy and withdraws tokens via CollateralJoin.
     * @param manager Address of the DssCdpManager contract.
     * @param collateralJoin Address of the CollateralJoin contract.
     * @param cdp Unique identifier of the Safe.
     * @param amt Amount of collateral to withdraw [in token’s decimal precision].
     */
    function exitTokenCollateral(address manager, ICollateralJoin collateralJoin, uint cdp, uint amt) external {
        // Transfer collateral to proxy
        transferCollateral(manager, cdp, address(this), convertTo18(collateralJoin, amt));
        // Withdraw collateral to caller
        collateralJoin.exit(msg.sender, amt);
    }

    /**
     * @notice Generates StablePro debt from a Safe.
     * @dev Increases Safe debt, transfers StablePro to the proxy, and withdraws tokens via CoinJoin.
     * @param manager Address of the DssCdpManager contract.
     * @param taxCollector Address of the TaxCollector contract.
     * @param coinJoin Address of the CoinJoin contract.
     * @param cdp Unique identifier of the Safe.
     * @param wad Amount of StablePro to generate [wad].
     * @param lockupPeriod Lockup period for the generated StablePro (protocol-specific).
     */
    function generateDebt(
        address manager,
        address taxCollector,
        address coinJoin,
        uint cdp,
        uint wad,
        uint256 lockupPeriod
    ) external {
        address safe = IDssCdpManager(manager).safes(cdp);
        ISafeEngine safeEngine = IDssCdpManager(manager).safeEngine();
        bytes32 collateralType = IDssCdpManager(manager).collateralTypes(cdp);
        // Generate debt in Safe
        modifySAFECollateralization(
            manager,
            cdp,
            0,
            _getGeneratedDeltaDebt(safeEngine, taxCollector, safe, collateralType, wad)
        );
        // Transfer StablePro to proxy
        transferInternalCoins(manager, cdp, address(this), toRad(wad));
        // Approve CoinJoin to access proxy’s StablePro
        if (safeEngine.cdpRights(address(this), address(coinJoin)) == 0) {
            safeEngine.approveSAFEModification(coinJoin);
        }
        // Withdraw StablePro to caller
        ICoinJoin(coinJoin).exit(collateralType, msg.sender, lockupPeriod, msg.sender, wad);
    }

    /**
     * @notice Repays StablePro debt to a Safe.
     * @dev Deposits StablePro via CoinJoin and reduces Safe debt, handling ownership or permissions.
     * @param manager Address of the DssCdpManager contract.
     * @param coinJoin Address of the CoinJoin contract.
     * @param cdp Unique identifier of the Safe.
     * @param wad Amount of StablePro to repay [wad].
     */
    function repayDebt(address manager, address coinJoin, uint cdp, uint wad) external {
        ISafeEngine safeEngine = IDssCdpManager(manager).safeEngine();
        address safe = IDssCdpManager(manager).safes(cdp);
        bytes32 collateralType = IDssCdpManager(manager).collateralTypes(cdp);

        address own = IDssCdpManager(manager).owns(cdp);
        if (own == address(this) || IDssCdpManager(manager).cdpCan(own, cdp, address(this)) == 1) {
            // Deposit StablePro into SafeEngine
            _coinJoin_join(collateralType, coinJoin, safe, wad);
            // Reduce Safe debt
            modifySAFECollateralization(
                manager,
                cdp,
                0,
                _getRepaidDeltaDebt(safeEngine, safeEngine.coinBalance(safe), safe, collateralType)
            );
        } else {
            // Deposit StablePro into SafeEngine
            _coinJoin_join(collateralType, coinJoin, address(this), wad);
            // Reduce Safe debt directly
            safeEngine.modifySAFECollateralization(
                collateralType,
                safe,
                address(this),
                address(this),
                0,
                _getRepaidDeltaDebt(safeEngine, wad * RAY, safe, collateralType)
            );
        }
    }

    /**
     * @notice Repays all StablePro debt for a Safe.
     * @dev Deposits the required StablePro via CoinJoin and clears all Safe debt, handling ownership or permissions.
     * @param manager Address of the DssCdpManager contract.
     * @param coinJoin Address of the CoinJoin contract.
     * @param cdp Unique identifier of the Safe.
     */
    function repayAllDebt(address manager, address coinJoin, uint cdp) external {
        ISafeEngine safeEngine = IDssCdpManager(manager).safeEngine();
        address safe = IDssCdpManager(manager).safes(cdp);
        bytes32 collateralType = IDssCdpManager(manager).collateralTypes(cdp);
        (, uint safeDebt) = safeEngine.safes(collateralType, safe);

        address own = IDssCdpManager(manager).owns(cdp);
        if (own == address(this) || IDssCdpManager(manager).cdpCan(own, cdp, address(this)) == 1) {
            // Deposit StablePro to cover all debt
            _coinJoin_join(collateralType, coinJoin, safe, _getRepaidAllWad(safeEngine, safe, safe, collateralType));
            // Clear all Safe debt
            modifySAFECollateralization(manager, cdp, 0, -int(safeDebt));
        } else {
            // Deposit StablePro to cover all debt
            _coinJoin_join(
                collateralType,
                coinJoin,
                address(this),
                _getRepaidAllWad(safeEngine, address(this), safe, collateralType)
            );
            // Clear all Safe debt directly
            safeEngine.modifySAFECollateralization(
                collateralType,
                safe,
                address(this),
                address(this),
                0,
                -int(safeDebt)
            );
        }
    }

    /**
     * @notice Locks collateral and generates StablePro debt in a single transaction.
     * @dev Combines collateral deposit and debt generation for efficiency.
     * @param manager Address of the DssCdpManager contract.
     * @param taxCollector Address of the TaxCollector contract.
     * @param collateralJoin Address of the CollateralJoin contract.
     * @param coinJoin Address of the CoinJoin contract.
     * @param cdp Unique identifier of the Safe.
     * @param collateralAmount Amount of collateral to lock [in token’s decimal precision].
     * @param deltaWad Amount of StablePro debt to generate [wad].
     * @param lockupPeriod Lockup period for the generated StablePro (protocol-specific).
     */
    function lockTokenCollateralAndGenerateDebt(
        address manager,
        address taxCollector,
        ICollateralJoin collateralJoin,
        address coinJoin,
        uint cdp,
        uint collateralAmount,
        uint deltaWad,
        uint256 lockupPeriod
    ) public {
        address safeHandler = IDssCdpManager(manager).safes(cdp);
        ISafeEngine safeEngine = IDssCdpManager(manager).safeEngine();
        bytes32 collateralType = IDssCdpManager(manager).collateralTypes(cdp);
        // Deposit collateral into SafeEngine
        _collateralJoin_join(collateralJoin, safeHandler, collateralAmount);
        // Update Safe: add collateral and debt
        modifySAFECollateralization(
            manager,
            cdp,
            toInt(convertTo18(collateralJoin, collateralAmount)),
            _getGeneratedDeltaDebt(safeEngine, taxCollector, safeHandler, collateralType, deltaWad)
        );
        // Transfer StablePro to proxy
        transferInternalCoins(manager, cdp, address(this), toRad(deltaWad));
        // Approve CoinJoin to access proxy’s StablePro
        if (safeEngine.cdpRights(address(this), address(coinJoin)) == 0) {
            safeEngine.approveSAFEModification(coinJoin);
        }
        // Withdraw StablePro to caller
        ICoinJoin(coinJoin).exit(collateralType, msg.sender, lockupPeriod, msg.sender, deltaWad);
    }

    /**
     * @notice Opens a Safe, locks collateral, and generates StablePro debt in a single transaction.
     * @dev Combines Safe creation, collateral deposit, and debt generation for efficiency.
     * @param manager Address of the DssCdpManager contract.
     * @param taxCollector Address of the TaxCollector contract.
     * @param collateralJoin Address of the CollateralJoin contract.
     * @param coinJoin Address of the CoinJoin contract.
     * @param collateralType Unique identifier for the collateral type.
     * @param amtC Amount of collateral to lock [in token’s decimal precision].
     * @param wadD Amount of StablePro debt to generate [wad].
     * @param lockupPeriod Lockup period for the generated StablePro (protocol-specific).
     * @return cdp Unique identifier of the newly created Safe.
     */
    function openLockTokenCollateralAndGenerateDebt(
        address manager,
        address taxCollector,
        ICollateralJoin collateralJoin,
        address coinJoin,
        bytes32 collateralType,
        uint amtC,
        uint wadD,
        uint256 lockupPeriod
    ) external returns (uint cdp) {
        // Create new Safe
        cdp = openSafe(manager, collateralType, address(this));
        // Lock collateral and generate debt
        lockTokenCollateralAndGenerateDebt(
            manager, taxCollector, collateralJoin, coinJoin, cdp, amtC, wadD, lockupPeriod
        );
    }

    /**
     * @notice Repays StablePro debt and unlocks collateral from a Safe in a single transaction.
     * @dev Combines debt repayment and collateral withdrawal for efficiency.
     * @param manager Address of the DssCdpManager contract.
     * @param collateralJoin Address of the CollateralJoin contract.
     * @param coinJoin Address of the CoinJoin contract.
     * @param cdp Unique identifier of the Safe.
     * @param amtC Amount of collateral to unlock [in token’s decimal precision].
     * @param wadD Amount of StablePro to repay [wad].
     */
    function repayDebtAndFreeTokenCollateral(
        address manager,
        ICollateralJoin collateralJoin,
        address coinJoin,
        uint cdp,
        uint amtC,
        uint wadD
    ) external {
        address safe = IDssCdpManager(manager).safes(cdp);
        bytes32 collateralType = IDssCdpManager(manager).collateralTypes(cdp);
        ISafeEngine safeEngine = IDssCdpManager(manager).safeEngine();
        // Deposit StablePro into SafeEngine
        _coinJoin_join(collateralType, coinJoin, safe, wadD);
        uint wadC = convertTo18(collateralJoin, amtC);
        // Reduce debt and unlock collateral
        modifySAFECollateralization(
            manager,
            cdp,
            -toInt(wadC),
            _getRepaidDeltaDebt(
                safeEngine,
                safeEngine.coinBalance(safe),
                safe,
                collateralType
            )
        );
        // Transfer collateral to proxy
        transferCollateral(manager, cdp, address(this), wadC);
        // Withdraw collateral to caller
        collateralJoin.exit(msg.sender, amtC);
    }

    /**
     * @notice Repays all StablePro debt and unlocks collateral from a Safe in a single transaction.
     * @dev Combines full debt repayment and collateral withdrawal for efficiency.
     * @param manager Address of the DssCdpManager contract.
     * @param collateralJoin Address of the CollateralJoin contract.
     * @param coinJoin Address of the CoinJoin contract.
     * @param cdp Unique identifier of the Safe.
     * @param amtC Amount of collateral to unlock [in token’s decimal precision].
     */
    function repayAllDebtAndFreeTokenCollateral(
        address manager,
        ICollateralJoin collateralJoin,
        address coinJoin,
        uint cdp,
        uint amtC
    ) external {
        ISafeEngine safeEngine = IDssCdpManager(manager).safeEngine();
        address safe = IDssCdpManager(manager).safes(cdp);
        bytes32 collateralType = IDssCdpManager(manager).collateralTypes(cdp);
        (, uint safeDebt) = safeEngine.safes(collateralType, safe);

        // Deposit StablePro to cover all debt
        _coinJoin_join(collateralType, coinJoin, safe, _getRepaidAllWad(safeEngine, safe, safe, collateralType));
        uint wadC = convertTo18(collateralJoin, amtC);
        // Clear all debt and unlock collateral
        modifySAFECollateralization(
            manager,
            cdp,
            -toInt(wadC),
            -int(safeDebt)
        );
        // Transfer collateral to proxy
        transferCollateral(manager, cdp, address(this), wadC);
        // Withdraw collateral to caller
        collateralJoin.exit(msg.sender, amtC);
    }

    /**
     * @notice Retrieves the Safe ID for a user’s Safe with a specific collateral type.
     * @dev Iterates through the user’s Safes in DssCdpManager to find the matching collateral type.
     * @param manager Address of the DssCdpManager contract.
     * @param user Address of the Safe owner.
     * @param collateralType Unique identifier for the collateral type.
     * @return cdpId Unique identifier of the Safe, or type(uint256).max if not found.
     */
    function getCdpId(address manager, address user, bytes32 collateralType) public view returns(uint) {
        uint256 cdpCount = IDssCdpManager(manager).cdpCount(user);
        uint256 cdpId = IDssCdpManager(manager).firstCdpId(user);
        IDssCdpManager managerContract = IDssCdpManager(manager);

        // Iterate through user’s Safes
        for (uint256 i; i < cdpCount; ++i) {
            if (managerContract.collateralTypes(cdpId) == collateralType) {
                return cdpId;
            }
            (, uint256 nextCdpId) = managerContract.list(cdpId);
            cdpId = nextCdpId;
        }

        return type(uint256).max;
    }

    /**
     * @notice Retrieves collateral and debt information for a user’s Safe.
     * @dev Queries SafeEngine for the Safe’s locked collateral and generated debt.
     * @param manager Address of the DssCdpManager contract.
     * @param user Address of the Safe owner.
     * @param collateralType Unique identifier for the collateral type.
     * @return lockedCollateral Amount of locked collateral [wad].
     * @return generatedDebt Amount of generated debt [wad].
     */
    function getCdpInfo(address manager, address user, bytes32 collateralType) public view returns(uint, uint) {
        uint256 cdpId = getCdpId(manager, user, collateralType);
        if (cdpId == type(uint256).max) return (0,0);

        ISafeEngine safeEngine = IDssCdpManager(manager).safeEngine();
        address safe = IDssCdpManager(manager).safes(cdpId);
        return safeEngine.safes(collateralType, safe);
    }

    /**
     * @notice Calculates the amount of collateral a user can withdraw from a Safe.
     * @dev Determines excess collateral based on the Safe’s collateralization ratio and safety price.
     * @param manager Address of the DssCdpManager contract.
     * @param user Address of the Safe owner.
     * @param collateralType Unique identifier for the collateral type.
     * @return Amount of collateral available to withdraw [wad].
     */
    function getAvailableToWithdraw(address manager, address user, bytes32 collateralType) external view returns(uint) {
        ISafeEngine safeEngine = IDssCdpManager(manager).safeEngine();

        (uint256 lockedCollateral, uint256 generatedDebt) = getCdpInfo(manager, user, collateralType);
        (, uint rate,,, uint safetyPrice,,,) = safeEngine.collateralTypes(collateralType);

        // Return 0 if safety price is unset
        if (safetyPrice == 0) return 0;

        // Calculate minimum required collateral
        uint256 minCollateralAmount = mul(generatedDebt, rate) / safetyPrice;
        return lockedCollateral > minCollateralAmount ? sub(lockedCollateral, minCollateralAmount) : 0;
    }

    /**
     * @notice Calculates the amount of StablePro a user can borrow from a Safe.
     * @dev Determines borrowing capacity based on collateral value, debt limits, and safety price.
     * @param manager Address of the DssCdpManager contract.
     * @param user Address of the Safe owner.
     * @param collateralType Unique identifier for the collateral type.
     * @return Amount of StablePro available to borrow [wad].
     */
    function getAvailableToBorrow(address manager, address user, bytes32 collateralType) external view returns(uint) {
        ISafeEngine safeEngine = IDssCdpManager(manager).safeEngine();

        (uint256 lockedCollateral, uint256 generatedDebt) = getCdpInfo(manager, user, collateralType);
        (
            uint256 debtAmount, uint256 rate, uint256 debtCeiling,, uint safetyPrice,,,
        ) = safeEngine.collateralTypes(collateralType);

        // Calculate borrowing limit based on collateral value
        uint256 usdLimitE18 = mul(lockedCollateral, safetyPrice) / rate;
        if (usdLimitE18 > generatedDebt) {
            uint256 availableToBorrow = sub(usdLimitE18, generatedDebt);

            // Check global debt ceiling
            uint256 globalAvailableToBorrow = sub(safeEngine.globalDebtCeiling(), safeEngine.globalDebt());
            if (globalAvailableToBorrow < toRad(availableToBorrow)) return globalAvailableToBorrow / RAY;

            // Check collateral type debt ceiling
            uint256 collateralAvailableToBorrow = sub(debtCeiling, toRad(debtAmount));
            if (collateralAvailableToBorrow < toRad(add(debtAmount, availableToBorrow))) {
                return collateralAvailableToBorrow / RAY;
            }

            return availableToBorrow;
        }

        return 0;
    }
}
