pragma solidity ^0.8.0;

/**
 * @title ITaxCollector
 **/
interface ITaxCollector {
    /* ==================== EVENTS ==================== */
    event DisableContract();
    event InitializeCollateralType(bytes32 collateralType);
    event ModifyParameters(bytes32 collateralType, bytes32 what, uint256 data);
    event ModifyParameters(bytes32 what, uint256 data);
    event ModifyParameters(bytes32 what, address data);
    event TaxSingle(bytes32 collateralType, uint256 rate);

    /* ==================== STRUCTS ==================== */

    struct CollateralType {
        uint256 stabilityFee;      // Collateral-specific, per-second stability fee contribution [ray]
        uint256 latestUpdateTime;  // Time of last taxSingle [unix epoch time]
    }


    /* ==================== METHODS ==================== */

    // --- Administration ---
    function initializeCollateralType(bytes32 collateralType) external;
    function modifyParameters(bytes32 collateralType, bytes32 what, uint data) external;
    function modifyParameters(bytes32 what, uint data) external;
    function modifyParameters(bytes32 what, address data) external;


    // --- Stability Fee Collection ---
    /**
     * @notice Collect tax from a single collateral type
     * @param collateralType Collateral type to tax
     */
    function taxSingle(bytes32 collateralType) external returns (uint rate);

    /**
     * @notice Disable this contract (normally called by Global Settlement)
     * @dev When we disable, the contract tries to settle as much debt as possible (if there's any) with
            any surplus that's left in the system. After erasing debt, the contract will either transfer any
            remaining surplus right away (if disableCooldown == 0) or will only record the timestamp when
            it was disabled
    **/
    function disableContract() external;
}
