pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@pendle/core-v2/contracts/oracles/PtYtLpOracle/samples/BoringLpSeller.sol";
import "@pendle/core-v2/contracts/oracles/PtYtLpOracle/PendleLpOracleLib.sol";
import "@pendle/core-v2/contracts/interfaces/IPPYLpOracle.sol";

import "../../access-control/SuAuthenticated.sol";
import "../../interfaces/adapters/IPendleLPAdapter.sol";
import "../../interfaces/oracle/ISuOracleAggregator.sol";

/**
 * @title PendleLPAdapter
 * @notice Adapter for Pendle LP token.
 * @dev See ILPAdapter interface for full details.
 */
contract PendleLPAdapter is SuAuthenticated, IPendleLPAdapter, BoringLpSeller {
    using PendleLpOracleLib for IPMarket;
    using SafeERC20 for IERC20;

    mapping(address => PendleLPInfo) public lps; // Mapping from LP token to pool
    ISuOracleAggregator public ORACLE;
    IPPYLpOracle public PENDLE_ORACLE;

    uint256 internal constant MAX_BPS = 10000; // 100% in basis points
    uint32 public TWAP_WINDOW;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _authControl, address _oracle, address _pendleOracle) public initializer {
        __suAuthenticatedInit(_authControl);
        ORACLE = ISuOracleAggregator(_oracle);
        PENDLE_ORACLE = IPPYLpOracle(_pendleOracle);
        TWAP_WINDOW = 1200; // TWAPDuration is 20min
    }

    function setTwapWindow(uint32 _twapWindow) external onlyAdmin {
        TWAP_WINDOW = _twapWindow;
    }

    function registerLP(address lp, IPMarket market, address mainUnderlyingToken) external onlyAdmin {
        if (IERC20Metadata(lp).decimals() != 18) revert BadDecimals(lp);
        bool wasRegistered = lps[lp].mainUnderlyingToken != address(0);
        // If admin make mistake in this function, for example he pass wrong market,
        // then he should be able to fix it by repeating register call with correct arguments.
        // if (wasRegistered) revert AlreadyRegistered(lp);

        /**
            By default, Pendle markets’ oracles are un-initialized. It could lead to incorrect price calculations
            due to the low cardinality and lack of observations.
            Here we check for oracle initialization before registration
        */
        (bool increaseCardinalityRequired,, bool oldestObservationSatisfied) =
                            PENDLE_ORACLE.getOracleState(address(market), TWAP_WINDOW);

        if (increaseCardinalityRequired || !oldestObservationSatisfied) {
            revert OracleNotReady();
        }

        lps[lp].market = market;
        (lps[lp].SY, /*PT*/, /*YT*/) = market.readTokens();
        lps[lp].underlyingTokens = lps[lp].SY.getTokensOut();

        (,address realMainUnderlyingToken,) = lps[lp].SY.assetInfo();
        if (realMainUnderlyingToken != mainUnderlyingToken) revert BadMainUnderlyingToken();
        lps[lp].mainUnderlyingToken = mainUnderlyingToken;

        if (wasRegistered) {
            emit TokenRegisteredRepeat(lp, address(market), mainUnderlyingToken);
        } else {
            emit TokenRegistered(lp, address(market), mainUnderlyingToken);
        }
    }

    function increaseObservationCardinality(address market, uint16 cardinality) external {
        IPMarket(market).increaseObservationsCardinalityNext(cardinality);
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return lps[asset].underlyingTokens.length != 0;
    }

    /**
     * @notice Idea of this implementation was in Pendle example
    **/
    function getFiatPrice1e18(address asset) external view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        PendleLPInfo memory lpInfo = lps[asset];

        uint256 lpRate = lpInfo.market.getLpToAssetRate(TWAP_WINDOW); // in base 10^d
        uint256 assetPrice = ORACLE.getFiatPrice1e18(lpInfo.mainUnderlyingToken); // in base 10^(36 - d)

        // Decimals check , we should receive decimals = 18:
        // (36 - d) + d - X = 18 => X = 18
        return assetPrice * lpRate / 1e18;
    }

    /**
     * @notice Pendle unwrap LP only to 1 token, but our ILPAdapter is more general,
     * so we need to return here the array of WithdrawResult.
    **/
    function withdraw(
        address asset,
        uint256 amount,
        uint256 maxSlippageBps
    ) external returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);
        PendleLPInfo memory lpInfo = lps[asset];

        uint256 nTokens = lpInfo.underlyingTokens.length;
        results = new WithdrawResult[](1);

        // In other adapters there is the method that unwrap LP to all underlyingTokens,
        // but here we have Pendle library, that can swap LP only for one token.
        // So we need to check what token will have the best USD value. For that we need to multiply token price and
        // amount we could receive after this swap.
        // This swap in depth is doing by lpInfo.SY, so there is previewRedeem function to check this amount after swap.
        address bestTokenOut;
        uint256 bestTokenAmountOut1e18;
        uint256 expectedAmount;
        for (uint256 i = 0; i < nTokens; ++i) {
            address tokenToPreview = lpInfo.underlyingTokens[i];
            if (ORACLE.hasPriceForAsset(tokenToPreview)) {
                // previewRedeem is in base 10^d (in default in returns second arg)
                uint256 previewRedeem =  lpInfo.SY.previewRedeem(tokenToPreview, amount);
                uint256 tokenPrice = ORACLE.getSafeFiatPrice1e18(tokenToPreview); // with base 10^(36 - d)
                uint256 previewAmount1e18 = previewRedeem * tokenPrice / 1e18; // with base 10^(d + 36 - d);
                if (previewAmount1e18 > bestTokenAmountOut1e18) {
                    bestTokenAmountOut1e18 = previewAmount1e18;
                    bestTokenOut = tokenToPreview;
                    expectedAmount = previewRedeem;
                }
            }
        }
        if (bestTokenOut == address(0)) revert TokenOutNotFound();

        uint256 netTokenOut = _sellLpForToken(address(lpInfo.market), amount, bestTokenOut);

        // Calculate minimum acceptable amount based on slippage
        uint256 minAcceptableAmount = (expectedAmount * (MAX_BPS - maxSlippageBps)) / MAX_BPS;
        if (netTokenOut < minAcceptableAmount) {
            revert SlippageExceeded(expectedAmount, netTokenOut);
        }

        results[0] = WithdrawResult({ token: bestTokenOut, amount: netTokenOut });
        IERC20(bestTokenOut).safeTransfer(msg.sender, netTokenOut);

        emit Withdraw(asset, amount);
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for(uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}
