","keccak256":"0x25724ac502fd6418ccd38273b276de4b2fdecd023d723c5d80c9f0a27c407bcd","license":"MIT"},"contracts/exchange/contracts/periphery/contracts/interfaces/adapters/ILPAdapter.sol":{"content":"// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "../oracle/ISuOracle.sol";

struct WithdrawResult {
    address token;
    uint256 amount;
}

interface ILPAdapter is ISuOracle {
    /* ======================== ERRORS ======================== */
    error IsNotLP(address asset);
    error LPOracleNotReady();
    error BadMainUnderlyingToken();
    error BadDecimals(address asset);
    error ZeroRecipient();

    /* ========================= EVENTS ========================= */
    event EmergencyWithdraw(address indexed token, uint256 amount, address receiver);

    /* ==================== MUTABLE METHODS ==================== */
    /**
      * @notice Unwrap LP token with depth 2 (if underlying token is LP, it's also unwrapping) with slippage protection
      * @param asset - address of LP token to withdraw/unwrap
      * @param amount - amount of asset to withdraw/unwrap
      * @param maxSlippageBps Maximum allowed slippage in basis points (1 = 0.01%)
     **/
    function withdraw(address asset, uint256 amount, uint256 maxSlippageBps) external returns (WithdrawResult[] memory);

    /**
     * @notice Withdraw tokens from the adapter in case of emergency.
     * @dev This function is restricted to the admin and allows for emergency withdrawal of tokens.
     * @param tokens List of tokens to withdraw.
     * @param receiver Address of the receiver to receive the tokens.
     */
    function emergencyWithdraw(address[] calldata tokens, address receiver) external;

    /* ====================== VIEW METHODS ==================== */
    /**
      * @notice Returns if asset ig Balancer LP token, registered in our adapter
      * @param asset - address of LP token to check
     **/
    function isAdapterLP(address asset) external returns (bool);
}","keccak256":"0x6b173310065afa386f75f4c548ca483b3db8a2464331da73a5d2092ff0f1aff8","license":"BSL 1.1"},"contracts/exchange/contracts/periphery/contracts/interfaces/adapters/IPendleLPAdapter.sol":{"content":"// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@pendle/core-v2/contracts/interfaces/IPMarket.sol";
import "./ILPAdapter.sol";

/**
 * @title IPendleLPAdapter
 * @notice Interface for PendleLPAdapter contract.
 */
interface IPendleLPAdapter is ILPAdapter {
    /**
     * @title PendleLPInfo
     * @notice Struct with info about each  Pendle LP
     * @param market - address of LPs market (maybe can be removed, because LP address == market address)
     * @param SY - StandardizedYield, we need it to get underlyingTokens and best token to get after unwrap
     * @param underlyingTokens - all tokens that we can receive after unwrapping LP
     * @param mainUnderlyingToken - main LPs underlying token, rate in getFiatPrice is calculated in this token
     */
    struct PendleLPInfo {
        IPMarket market;
        IStandardizedYield SY;
        address[] underlyingTokens;
        address mainUnderlyingToken;
    }

    error OracleNotReady();
    error SlippageExceeded(uint256 expected, uint256 received);
    error TokenOutNotFound();

    event TokenRegisteredRepeat(address lp, address market, address mainUnderlyingToken);
    event TokenRegistered(address lp, address market, address mainUnderlyingToken);
    event Withdraw(address asset, uint256 amount);

    /* ==================== MUTABLE METHODS ==================== */
    /**
      * @notice Register the given LP token address and set the LP info
      * @dev In case when oracle is not ready, you should call market.increaseObservationsCardinalityNext(t)
      *      and wait 20 minutes to register LP
      *      [IMPORTANT] - Before register admin should check that SY contract don't have callbacks.
      *      It should be very rare to have a SY with a callback function, but need to pay attention on it.
      * @param lp address of LP token
      * @param market market for LP token
     **/
    function registerLP(address lp, IPMarket market, address mainUnderlyingToken) external;

    /**
     * @notice Helper function to increases the oracle observation cardinality for a Pendle market
     * @dev This sets the target cardinality that the oracle will grow to as new observations are written.
     * The actual cardinality will increase gradually with each new observation until it reaches this target.
     *
     * This must be called before registering an LP token to ensure enough price observations for TWAP calculations.
     * After increaseObservationCardinality call you must wai at least 20 min to allow the oracle
     * to accumulate enough price observations
     *
     * For 1200 seconds (20 min) cardinality = 1200 / blockTime. Also we can * 1.2 to have buffer for safety.
     * For mainnet (12 seconds for block) cardinality = 120, for Arbitrum (0.3 sec for block) = 4800.
     *
     * @param market The address of the Pendle market whose oracle cardinality should be increased
     * @param cardinality The target number of observations the oracle should maintain
     */
    function increaseObservationCardinality(address market, uint16 cardinality) external;

    /**
     * @notice Set the TWAP window for price calculation
     * @param _twapWindow New TWAP window in seconds
     */
    function setTwapWindow(uint32 _twapWindow) external;
}","keccak256":"0x6181f01354d2a936c9ac8840773ce5625e7311c0cbad03e3664419de879e4fe9","license":"BSL 1.1"},"contracts/exchange/contracts/periphery/contracts/interfaces/oracle/ISuOracle.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

// solhint-disable compiler-version
pragma solidity >=0.7.6;

interface ISuOracle {
    /**
     * @notice WARNING! Read this description very carefully!
     *     function getFiatPrice1e18(address asset) returns (uint256) that:
     *         basicAmountOfAsset * getFiatPrice1e18(asset) / 1e18 === $$ * 1e18
     *     in other words, it doesn't matter what's the erc20.decimals is,
     *     you just multiply token balance in basic units on value from oracle and get dollar amount multiplied on 1e18.
     *
     * different assets have different deviation threshold (errors)
     *     for wBTC it's <= 0.5%, read more https://data.chain.link/ethereum/mainnet/crypto-usd/btc-usd
     *     for other asset is can be larger based on particular oracle implementation.
     *
     * examples:
     *     market price of btc = $30k,
     *     for 0.1 wBTC the unit256 amount is 0.1 * 1e18
     *     0.1 * 1e18 * (price1e18 / 1e18) == $3000 == uint256(3000*1e18)
     *     => price1e18 = 30000 * 1e18;
     *
     *     market price of usdt = $0.97,
     *     for 1 usdt uint256 = 1 * 1e6
     *     so 1*1e6 * price1e18 / 1e18 == $0.97 == uint256(0.97*1e18)
     *     => 1*1e6 * (price1e18 / 1e18) / (0.97*1e18)   = 1
     *     =>  price1e18 = 0.97 * (1e18/1e6) * 1e18
     *
     *    assume market price of wBTC = $31,503.77, oracle error = $158
     *
     *     case #1: small amount of wBTC
     *         we have 0.0,000,001 wBTC that is worth v = $0.00315 ± $0.00001 = 0.00315*1e18 = 315*1e13 ± 1*1e13
     *         actual balance on the asset b = wBTC.balanceOf() =  0.0000001*1e18 = 1e11
     *         oracle should return or = oracle.getFiatPrice1e18(wBTC) <=>
     *         <=> b*or = v => v/b = 315*1e13 / 1e11 = 315*1e2 ± 1e2
     *         error = or.error * b = 1e2 * 1e11 = 1e13 => 1e13/1e18 usd = 1e-5 = 0.00001 usd
     *
     *     case #2: large amount of wBTC
     *         v = 2,000,000 wBTC = $31,503.77 * 2m ± 158*2m = $63,007,540,000 ± $316,000,000 = 63,007*1e24 ± 316*1e24
     *         for calc convenience we increase error on 0.05 and have v = 63,000*24 ± 300*1e24 = (630 ± 3)*1e26
     *         b = 2*1e6 * 1e18 = 2*1e24
     *         or = v/b = (630 ± 3)*1e26 / 2*1e24 = 315*1e2 ± 1.5*1e2
     *         error = or.error * b = 1.5*100 * 2*1e24 = 3*1e26 = 3*1e8*1e18 = $300,000,000 ~ $316,000,000
     *
     *     assume the market price of USDT = $0.97 ± $0.00485,
     *
     *     case #3: little amount of USDT
     *         v = USDT amount 0.005 = 0.005*(0.97 ± 0.00485) = 0.00485*1e18 ± 0.00002425*1e18 = 485*1e13 ± 3*1e13
     *         we rounded error up on (3000-2425)/2425 ~= +24% for calculation convenience.
     *         b = USDT.balanceOf() = 0.005*1e6 = 5*1e3
     *         b*or = v => or = v/b = (485*1e13 ± 3*1e13) / 5*1e3 = 970*1e9 ± 6*1e9
     *         error = 6*1e9 * 5*1e3 / 1e18 = 30*1e12/1e18 = 3*1e-5 = $0,00005
     *
     *     case #4: lot of USDT
     *         v = we have 100,000,000,000 USDT = $97B = 97*1e9*1e18 ± 0.5*1e9*1e18
     *         b = USDT.balanceOf() = 1e11*1e6 = 1e17
     *         or = v/b = (97*1e9*1e18 ± 0.5*1e9*1e18) / 1e17 = 970*1e9 ± 5*1e9
     *         error = 5*1e9 * 1e17 = 5*1e26 = 0.5 * 1e8*1e18
     *
     * @param asset - address of erc20 token contract
     * @return usdPrice1e18 such that asset.balanceOf() * getFiatPrice1e18(asset) / 1e18 == $$ * 1e18
     **/
    function getFiatPrice1e18(address asset) external view returns (uint256);
}
