/// SurplusAuctionHouse.sol -- StableUnit surplus auction module

// Forked from MakerDAO's Flap.sol
// Copyright (C) 2020-2022 Dai Foundation
// Adapted for StableUnit with Reflexer naming and Exchange module functionality (IExchange.addOrder)

pragma solidity ^0.8.0;

import "../interfaces/IAccountingEngine.sol";
import {IExchange, Order} from "../../exchange/contracts/interfaces/IExchange.sol";
import "../../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";
import "../interfaces/ISafeEngine.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/ICoinJoin.sol";
import {Math} from "../utils/Math.sol";

contract SurplusAuctionHouse is SuAuthenticated, Math {
    using SafeERC20 for IERC20;

    // --- Data ---
    struct Bid {
        // Amount of protocol token to sell [rad]
        uint256 amountToSell;
        // Address of the auction initiator
        address sender;
    }

    // Bid data for each separate auction, mapping auction ID to Bid struct
    mapping (uint256 => Bid) public bids;

    // Flag to indicate if the contract is active (1 = enabled, 0 = disabled)
    uint256 public contractEnabled;

    // Address of the protocol's native token (StablePro)
    address public protocolToken;

    // Address of treasury
    address public treasury;

    // Reference to SafeEngine for collateral and stablecoin transfers
    ISafeEngine public safeEngine;

    ICoinJoin public coinJoin; // Adapter to join StablePro

    // --- Events ---
    // Emitted when a new auction starts
    event StartAuction(address treasury, uint256 amountToSell, address sender);

    // Emitted when an auction is settled
    event SettleAuction(uint256 id);
    // Emitted when an auction is canceled
    event CancelAuction(uint256 id);
    // Emitted when parameters are modified
    event ModifyParameters(bytes32 indexed parameter, bytes32 data);
    event ModifyParameters(bytes32 indexed parameter, uint256 data);
    event ModifyParameters(bytes32 indexed parameter, address data);
    // Emitted when the contract is disabled
    event DisableContract();

    // --- Initialization ---
    // Initializes the contract with access control, AccountingEngine, Exchange module, suDAO, and protocol token
    // Forked from MakerDAO Flap.sol: constructor(address vat_, address gem_) public { ... } (line ~54)
    // StableUnit adaptation: Replaces MakerDAO's Vat with AccountingEngine, adds suDAO, and integrates Exchange module
    // @param accessControl Address of the SuAuthenticated access control contract
    // @param protocolToken_ Address of the protocol's native token (StablePro)
    // @param treasury_ Address of the treasury

    function initialize(
        address accessControl,
        address protocolToken_,
        address safeEngine_,
        address coinJoin_,
        address treasury_
    ) public initializer {
        // Set up role-based access control (DAO, Admin, DisablerAccess, LiquidationAccess)
        __suAuthenticatedInit(accessControl);

        // Enable the contract by default
        contractEnabled = 1;

        // Set protocol token address (StablePro)
        protocolToken = protocolToken_;

        treasury = treasury_;

        safeEngine = ISafeEngine(safeEngine_);

        coinJoin = ICoinJoin(coinJoin_);

        safeEngine.approveSAFEModification(coinJoin_); // Approve CoinJoin for SafeEngine modifications
    }

    // Prevents initialization of the implementation contract for upgradeability safety
    // StableUnit addition: Ensures the contract cannot be initialized outside of proxy deployment
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // --- Administration ---
    // Modifies contract parameters such as AccountingEngine, suDAO, or protocol token address
    // Forked from MakerDAO Flap.sol: function file(bytes32 what, address data) public auth { ... } (line ~76)
    // StableUnit adaptation: Updates AccountingEngine, suDAO, or protocolToken address, restricted to DAO role
    // @param what Parameter to modify ("accountingEngine", "suDAO", or "protocolToken")
    // @param data New contract address
    function modifyParameters(bytes32 what, address data) external onlyDAO {
        // Update AccountingEngine address for surplus settlement

        if (what == "protocolToken") {
            protocolToken = address(data);
        }

        if (what == "treasury") {
            treasury = data;
        }

        if (what == "safeEngine") {
            safeEngine = ISafeEngine(data);
        }

        if (what == "coinJoin") {
            coinJoin = ICoinJoin(data);
        }

        // Emit event to log address update
        emit ModifyParameters(what, data);
    }

    // --- Auction Management ---
    // Starts a new surplus auction, integrating Exchange module’s addOrder logic
    // StableUnit adaptation: Creates an order on the Exchange module to sell StablePro for suDAO tokens
    // @param amountToSell Amount of protocol token to sell [rad]
    // @return id Unique auction identifier
    function startAuction(
        uint256 amountToSell,
        uint256
    ) external onlyAuctionAccess returns (uint256 id) {
        // Ensure the contract is enabled to allow new auctions
        require(contractEnabled == 1, "SurplusAuctionHouse/not-enabled");
        // Ensure non-zero amount to prevent null auctions
        require(amountToSell > 0, "SurplusAuctionHouse/zero-collateral");

        safeEngine.transferInternalCoins(msg.sender, address(this), amountToSell * 1e27);

        coinJoin.exitNoReward(address(this), amountToSell);

        IERC20(protocolToken).safeTransfer(treasury, amountToSell);
        // Emit event to log auction start
        emit StartAuction(treasury, amountToSell, msg.sender);
    }

    // Restarts an auction by extending the bid deadline
    // Forked from MakerDAO Flap.sol: function tick(uint id) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    function restartAuction(uint256 id) external {}

    // Increases the bid amount for a fixed collateral amount
    // Forked from MakerDAO Flap.sol: function tend(uint id, uint lot, uint bid) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    // @param collateralAmount Collateral amount (must match auction’s lot)
    // @param bid New bid amount in suDAO tokens [rad]
    function increaseBidSize(uint256 id, uint256 collateralAmount, uint256 bid) external {}

    // Decreases the collateral amount for a fixed bid
    // Forked from MakerDAO Flap.sol: function dent(uint id, uint lot, uint bid) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    // @param collateralAmount New collateral amount (must be less than current lot)
    // @param bid Bid amount (must match current bid and tab) [rad]
    function decreaseSoldAmount(uint256 id, uint256 collateralAmount, uint256 bid) external {}

    // Settles a completed auction, transferring suDAO tokens to AccountingEngine
    // Forked from MakerDAO Flap.sol: function deal(uint id) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    function settleAuction(uint256 id) external {}

    // Cancels an auction during system shutdown, refunding StablePo to the sender
    // Forked from MakerDAO Flap.sol: function yank(uint id) public auth { ... } (line ~137)
    // StableUnit adaptation: Refunds StablePro to the sender via the Exchange module
    // @param id Auction identifier
    function cancelAuction(uint256 id) external onlyLiquidationAccess {}


    // Disables the contract, halting new auctions
    // StableUnit adaptation: Restricted to accounts with DisablerAccess role
    // @notice Only callable by accounts with DisablerAccess role
    function disableContract() external onlyDisablerAccess {
        // Set contract to disabled state
        contractEnabled = 0;
        // Emit event to log contract disablement
        emit DisableContract();
    }
}","keccak256":"0xd8da6d26f219e51042f6a369fc13ca3ae771633b719206410959a45757bbcb22","license":"AGPL-3.0-or-later"},"contracts/makerdao/interfaces/IAccountingEngine.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

/**
 * @title IAccountingEngine
 **/
interface IAccountingEngine {
    /* ==================== EVENTS ===============-===== */
    event StableProSwapped(uint256 amount);
    event DisableContract();

    event AuctionDebt(uint256 indexed id, uint256 totalOnAuctionDebt, uint256 debtBalance);
    event AuctionSurplus(uint256 indexed id, uint256 coinBalance);

    event Initialize(address indexed accountingEngine);
    event PushDebtToQueue(uint256 era, uint256 amountToRaise, uint256 totalQueuedDebt);
    event PopDebtFromQueue(uint256 era, uint256 totalQueuedDebt);
    event SettleDebt(uint256 rad, uint256 coinBalance, uint256 debtBalance);
    event CancelAuctionedDebtWithSurplus(uint256 rad, uint256 totalOnAuctionDebt);
    event JoinStablePro(uint256 amount, uint256 totalOnAuctionDebt);
    event GetRealStablePro(uint256 amount);
    event RefillStablecoin(address stablecoin, uint256 amount);
    event SwapStableProToStablecoin(address stablecoinToken, uint256 stableProAmountE18, uint256 stablecoinAmountEDecimal);
    event AddWhitelistStablecoin(address stablecoin);
    event RemoveWhitelistStablecoin(address stablecoin);
    event SetStablecoinToSwap(address stablecoin);
    event ModifyParameters(bytes32 what, uint256 data);
    event ModifyParameters(bytes32 what, address data);

    /* ==================== ERRORS ===============-===== */
    error ContractDisabled();
    error BadModifyParameters();
    error BadStablecoin(address asset);
    error BadRewardToken();
    error DebtNotZero();
    error SurplusNotZero();
    error InsufficientDebt();
    error InsufficientSurplus();
    error NotEnoughAsh();
    error PopDebtDelayNotFinished();
    error StablecoinNotExist(address asset);
    error StablecoinExists(address asset);

    /* ====================== VARS ===================== */

    function stablecoinToSwap() external view returns (address);

    /* ==================== MUTABLE METHODS ==================== */

    /**
     * @notice Push debt (that the system tries to cover with collateral auctions) to a queue
     * @dev Debt is locked in a queue to give the system enough time to auction collateral
     *      and gather surplus
     * @param amountToRaise Amount of debt to push
     */
    function pushDebtToQueue(uint amountToRaise) external;

    /**
     * @notice A block of debt can be popped from the queue after popDebtDelay seconds passed since it was
     *         added there
     * @param era Timestamp of the block of debt that should be popped out
     */
    function popDebtFromQueue(uint era) external;

    // Debt settlement
    /**
     * @notice Destroy an equal amount of coins and debt
     * @dev We can only destroy debt that is not locked in the queue and also not in a debt auction
     * @param rad Amount of coins/debt to destroy (number with 45 decimals)
    **/
    function settleDebt(uint rad) external;

    /**
     * @notice Use surplus coins to destroy debt that is/was in a debt auction
     * @param rad Amount of coins/debt to destroy (number with 45 decimals)
    **/
    function cancelAuctionedDebtWithSurplus(uint rad) external;

    /**
     * @notice Start a debt auction (we have debt = x$, token2sell with value of y$, where y >= x)
     * @dev We can only auction debt that is not already being auctioned and is not locked in the debt queue.
     * Firstly, we should donate token2sell (it's collateral or stablecoin) by refillStablecoin
    **/
    function auctionDebt(
        address token2sell,
        uint256 amountToSell,
        uint256 auctionDebt
    ) external returns (uint orderId);

    /**
     * @notice Start a surplus auction
     * @dev We can only auction surplus if we wait at least 'surplusAuctionDelay' seconds since the last
     *      auction trigger, if we keep enough surplus in the buffer and if there is no bad debt to settle
    **/
    function auctionSurplus(uint256 surplusAmount) external returns (uint orderId);

    /**
     * @notice Disable this contract (normally called by Global Settlement)
     * @dev When we disable, the contract tries to settle as much debt as possible (if there's any) with
            any surplus that's left in the system. After erasing debt, the contract will either transfer any
            remaining surplus right away (if disableCooldown == 0) or will only record the timestamp when
            it was disabled
    **/
    function disableContract() external;

    /* ================= Stablecoin Whitelist ================== */

    /**
     * @notice Add new stablecoin to whitelist of stablecoins protocol works with
    **/
    function addWhitelistStablecoin(address stablecoin) external;

    /**
     * @notice Remove stablecoin from whitelist of stablecoins protocol works with
    **/
    function removeWhitelistStablecoin(address stablecoin) external;

    /**
     * @notice Check if give token is in stablecoins whitelist
    **/
    function hasWhitelistStablecoin(address stablecoin) external view returns (bool);

    /**
     * @notice Change var stablecoinToSwap, that is used in liquidation process
    **/
    function setStablecoinToSwap(address stablecoin) external;

    /* =================== MANUAL FUNCTIONS ==================== */
    /**
     * @notice Admin function to join protocol using real StablePro (= join in coinJoin)
    **/
    function joinStablePro(uint256 amount) external;

    /**
     * @notice Admin function to get real StablePro tokens instead of records in protocol (= exit from coinJoin)
    **/
    function getRealStablePro(uint256 amount) external;

    /**
     * @notice Function to send stablecoin to AccountingEngine contract
    **/
    function refillStablecoin(address stablecoin, uint256 amount) external;

    /**
     * @notice Admin function to distribute surplus after getting it
    **/
    //function distributeSurplus(uint256 amount) external;

    /**
     * @notice Function to swap sender's stablePro to stablecoin that is in whitelist
    **/
    function swapStableProToStablecoin(address stablecoinToken, uint256 stableProAmountE18) external;

    function totalQueuedDebt() external view returns (uint256);

    function totalOnAuctionDebt() external view returns (uint256);
}
