 */
contract CircuitBreakerUnoptimized is ICircuitBreaker, SuAuthenticated {
    using SafeERC20 for IERC20;

    uint256 public constant WITHDRAWAL_PERIOD = 24 hours;
    /**
     * @dev Here we use sliding window algorithm (https://www.geeksforgeeks.org/window-sliding-technique/)
     * to count sum of funds passed during 1 day
     */
    uint256 public constant SLIDING_WINDOW_PARTS = 4;
    uint256 public constant SLIDING_WINDOW_SIZE = WITHDRAWAL_PERIOD / SLIDING_WINDOW_PARTS;
    uint256 public constant TRIGGER_RATE_LIMIT_LENGTH = 356 days;

    uint256 public fundsFlowThreshold;
    mapping(uint256 => uint256) public liqInPeriod;
    State public state;
    uint256 public triggeredRateLimitEndTimestamp;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /* ==================== MUTABLE METHODS ==================== */
    function initialize(address _authControl) public initializer {
        __suAuthenticatedInit(_authControl);
    }

    // TODO: add onlyRole for our lending protocol
    function controlledTransfer(address token, address to, uint256 amount, uint256 value) external {
        if (state == State.Triggered) {
            if (block.timestamp < triggeredRateLimitEndTimestamp) {
                revert Triggered();
            } else {
                state = State.Ok;
                emit TriggeredRateLimitExpired();
            }
        }
        addFundsFlow(value);
        uint256 totalFundsFlow = totalLiquidityInPeriod();
        if (totalFundsFlow > fundsFlowThreshold) {
            revert ThresholdExceeded();
        } else
            if (totalFundsFlow > fundsFlowThreshold/2) {
                if (state != State.Warning) {
                    state = State.Warning;
                    emit ThresholdWarning();
                }
            }
        IERC20(token).safeTransferFrom(msg.sender, to, amount);
    }

    function addFundsFlow(uint256 value) internal {
        uint256 windowId = getWindowId();
        liqInPeriod[windowId] += value;
    }

    function setFundsFlowThreshold(uint256 _fundsFlowThreshold) external onlyDAO {
        fundsFlowThreshold = _fundsFlowThreshold;
    }

    function reset() external onlyAdmin {
        state = State.Ok;
        // flush funds flow
        uint256 windowId = getWindowId();
        for (uint256 i = 0; i < SLIDING_WINDOW_PARTS; i++) {
            liqInPeriod[windowId-i] = 0;
        }

        emit ResetByAdmin();
    }

    function triggerRateLimit() external onlyAlerter {
        state = State.Triggered;
        triggeredRateLimitEndTimestamp = block.timestamp + TRIGGER_RATE_LIMIT_LENGTH;

        emit RateLimitTriggered();
    }

    /* ==================== VIEW METHODS ==================== */
    function totalLiquidityInPeriod() public view returns (uint256) {
        uint256 windowId = getWindowId();
        uint256 totalFlow;
        for (uint256 i = 0; i < SLIDING_WINDOW_PARTS; i++) {
            totalFlow += liqInPeriod[windowId-i];
        }
        return totalFlow;
    }

    function getWindowId() public view returns (uint256) {
        return block.timestamp / SLIDING_WINDOW_SIZE;
    }
}

