// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import "../interfaces/IArbitrageHelper.sol";
import "../interfaces/IExchange.sol";
import "./CalldataDecoder.sol";
import "../periphery/contracts/interfaces/adapters/ILPAdapter.sol";
import "../interfaces/IVotingEscrow.sol";
import "../interfaces/ITopStakers.sol";

/**
 * @title ArbitrageHelper
 * @notice Helper contract to arbitrage using Exchange module.
 * This is reference implementation that is enough to start arbitrage in Exchange Module.
 * Anyone can update the code and deploy own ArbitrageHelper.
 * @dev See IArbitrageHelper interface for full details.
 */
contract ArbitrageHelper is OwnableUpgradeable, IArbitrageHelper, CalldataDecoder, ReentrancyGuardUpgradeable {
    using SafeERC20 for IERC20;

    IExchange internal EXCHANGE;
    IERC20 internal GOV;
    mapping(address => ILPAdapter) public lpToAdapter; // Mapping LP address to it's adapter to withdraw it

    IVotingEscrow internal VE_GOV;
    ITopStakers internal TOP_STAKERS;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _exchange, address _gov, address _veGov, address _topStakers) public initializer {
        __Ownable_init();
        __ReentrancyGuard_init();

        EXCHANGE = IExchange(_exchange);
        GOV = IERC20(_gov);
        VE_GOV = IVotingEscrow(_veGov);
        TOP_STAKERS = ITopStakers(_topStakers);
    }

    function registerLP(address _lp, address _lpAdapter) public onlyOwner {
        lpToAdapter[_lp] = ILPAdapter(_lpAdapter);
        uint256 allowance = IERC20(_lp).allowance(address(this), _lpAdapter);
        uint256 MAX_UINT = 2**256 - 1;

        if (allowance != MAX_UINT) {
            IERC20(_lp).forceApprove(_lpAdapter, MAX_UINT);
        }

        emit LPRegistered(_lp, _lpAdapter);
    }

    function revokeApprove(address _lp, address _lpAdapter) public onlyOwner {
        uint256 allowance = IERC20(_lp).allowance(address(this), _lpAdapter);

        if (allowance > 0) {
            // We don't need forceApprove because we set allowance to 0
            IERC20(_lp).safeApprove(_lpAdapter, 0);
        }

        emit ApprovalRevoked(_lp, _lpAdapter);
    }

    /**
     * @notice Swap using Paraswap. Sell {amount2sell} token2sell to token using route encoded in paraswapData.data
     * @dev We get that data in bot using ParaSwap API (apiv5.paraswap.io)
     * Docs: https://developers.paraswap.network/api/build-parameters-for-transaction
     * DEPRECATED, we use 1inch here, but it's possible to use Paraswap instead
     */
    //    function swapByParaswap(address token2sell, uint256 amount2sell, ParaswapData memory paraswapData) internal {
    //        // All token transfers are through tokenTransferProxy, so we need to make approve to this contract
    //        IERC20(token2sell).forceApprove(paraswapData.tokenTransferProxy, amount2sell);
    //        // solhint-disable-next-line avoid-low-level-calls
    //        (bool success,) = paraswapData.contractAddress.call(paraswapData.data);
    //        if (!success) {
    //            revert SwapByParaSwapError();
    //        }
    //    }
    /**
     * @notice as in swapByParaswap, look into utils/1inch.ts
     * @dev Docs: https://docs.1inch.io/docs/aggregation-protocol/api/swap-params/
     */
    function swapBy1Inch(address token2sell, uint256 amount2sell, address contractAddress, bytes memory data) internal {
        IERC20(token2sell).forceApprove(contractAddress, amount2sell);

        // solhint-disable-next-line avoid-low-level-calls
        (bool success,) = contractAddress.call(data);
        if (!success) {
            revert SwapBy1InchError();
        }
    }

    function swapWithFlashLoan(
        uint256 orderId,
        uint256 token2sellAmount,
        uint256 minProfitPercent,
        InchData memory inchData
    ) public onlyOwner {
        if (minProfitPercent == 0 || minProfitPercent > 10000) revert InvalidProfitPercent();
        Order memory order = EXCHANGE.getOrder(orderId);

        InchData[] memory inchDataArr = new InchData[](1);
        inchDataArr[0] = inchData;
        // We do a flash-loan of {token2sell} in amount of {token2sellAmount} to our contract.
        // {data} will be decoded in stableunitFillOrderCallback() - the callback after we get flash-loan
        EXCHANGE.fillOrderAsFlashLoan(
            orderId,
            token2sellAmount,
            abi.encode(FlashCallbackData({
                isLP: false,
                orderId: orderId,
                token2sell: order.token2sell,
                token2sellAmount: token2sellAmount,
                minProfitPercent: minProfitPercent,
                minUnwrapSlippage: 0, // we don't need it here
                recipient: msg.sender,
                inchData: inchDataArr
            }))
        );
    }

    function swapLPWithFlashLoan(
        uint256 orderId,
        uint256 token2sellAmount,
        uint256 minProfitPercent,
        uint256 minUnwrapSlippage,
        InchData[] memory inchData
    ) public onlyOwner {
        if (minProfitPercent == 0 || minProfitPercent > 10000) revert InvalidProfitPercent();
        Order memory order = EXCHANGE.getOrder(orderId);

        EXCHANGE.fillOrderAsFlashLoan(
            orderId,
            token2sellAmount,
            abi.encode(FlashCallbackData({
                isLP: true,
                orderId: orderId,
                token2sell: order.token2sell,
                token2sellAmount: token2sellAmount,
                minProfitPercent: minProfitPercent,
                minUnwrapSlippage: minUnwrapSlippage,
                recipient: msg.sender,
                inchData: inchData
            }))
        );
    }

    /**
     * @notice This is callback function from Exchange contract.
     * fillOrderAsFlashLoan call (msg.sender).executeOperation(...) after transferring loan to sender
     * @dev behaviour like in uniswapV3FlashCallback:
     * https://docs.uniswap.org/contracts/v3/guides/flash-integrations/flash-callback
     * and AAVE flash-loan callback:
     * https://github.com/aave/aave-v3-core/blob/master/contracts/flashloan/interfaces/IFlashLoanSimpleReceiver.sol
    **/
    function stableunitFillOrderCallback(
        address returnAsset,
        uint256 amount,
        address sender,
        bytes calldata data
    ) external nonReentrant returns (bool) {
        FlashCallbackData memory decoded = abi.decode(data, (FlashCallbackData));
        address token2sell = decoded.token2sell;
        uint256 token2sellAmount = decoded.token2sellAmount;

        if (msg.sender != address(EXCHANGE)) revert NotAPool(msg.sender);
        if (sender != address(this)) revert BadSender(sender);

        uint256 balanceBefore = IERC20(returnAsset).balanceOf(address(this));

        if (decoded.isLP) {
            if (address(lpToAdapter[token2sell]) == address(0)) revert LPTokenNotRegistered(token2sell);
            if (!lpToAdapter[token2sell].isAdapterLP(token2sell)) revert LPTokenNotSupported(token2sell);

            WithdrawResult[] memory unwrapResult = lpToAdapter[token2sell].withdraw(
                token2sell, token2sellAmount, decoded.minUnwrapSlippage
            );

            // if withdraw don't unwrap to all underlying tokens, then we need to find 1inch data to use in swap
            // else we need to use all 1inch data for each unwrapped tokens
            bool isCorrectUnwrap = decoded.inchData.length == unwrapResult.length;
            for (uint16 i = 0; i < unwrapResult.length; ++i) {
                address underlineToken = unwrapResult[i].token;
                uint256 underlineTokenAmount = unwrapResult[i].amount;

                // If token2buy is underline token of LP-token token2sell => this part of order is done
                if (underlineToken != returnAsset) {
                    if (isCorrectUnwrap) {
                        swapBy1Inch(
                            underlineToken,
                            underlineTokenAmount,
                            decoded.inchData[i].contractAddress,
                            this.changeCalldataAmount(decoded.inchData[i].data, underlineTokenAmount)
                        );
                    } else { // we need to find correct 1inch data for that unwrapped token
                        for (uint16 j = 0; j < decoded.inchData.length; ++j) {
                            if (unwrapResult[i].token == decoded.inchData[j].token) {
                                swapBy1Inch(
                                    underlineToken,
                                    underlineTokenAmount,
                                    decoded.inchData[j].contractAddress,
                                    this.changeCalldataAmount(decoded.inchData[j].data, underlineTokenAmount)
                                );
                                break;
                            }
                        }
                    }
                }
            }
        } else {
            swapBy1Inch(
                token2sell,
                token2sellAmount,
                decoded.inchData[0].contractAddress,
                decoded.inchData[0].data
            );
        }


        uint256 tokenBReceived = IERC20(returnAsset).balanceOf(address(this)) - balanceBefore;

        if (tokenBReceived < amount) revert SwapNotProfitable(amount, tokenBReceived);
        uint256 swapProfit = tokenBReceived - amount;
        uint256 minProfit = (amount * decoded.minProfitPercent) / 10000;
        if (swapProfit < minProfit) revert SwapNotProfitable(minProfit, swapProfit);

        // Return flash-loan debt to Exchange contract
        IERC20(returnAsset).safeTransfer(address(EXCHANGE), amount);

        // Send swapped {returnAsset} minus debt from flash-loan to liquidator (it's user profit)
        IERC20(returnAsset).safeTransfer(decoded.recipient, swapProfit);

        return true;
    }

    // Staker Module Integration

    function approveToVeToken(uint256 amount) external onlyOwner {
        GOV.approve(address(VE_GOV), amount);
    }

    function stakeTokens(uint256 amount, uint256 unlockTime) external onlyOwner {
        VE_GOV.createLock(amount, unlockTime);
    }

    function subscribeInTopStaker() external onlyOwner {
        TOP_STAKERS.subscribe();
    }

    function unsubscribeFromTopStaker() external onlyOwner {
        TOP_STAKERS.unsubscribe();
    }

    function delegateVotingPower(address to) external onlyOwner {
        VE_GOV.delegate(to);
    }

    function emergencyWithdraw(address token, address recipient, uint256 amount) external onlyOwner {
        if (recipient == address(0)) revert ZeroRecipient();
        IERC20(token).safeTransfer(recipient, amount);
        emit EmergencyWithdraw(token, recipient, amount);
    }

    uint256[50] private __gap;
}

