// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

struct SwapDescription {
    address srcToken;
    address dstToken;
    address srcReceiver;
    address dstReceiver;
    uint256 amount;
    uint256 minReturnAmount;
    uint256 flags;
}

/**
 * @notice Helper abstract contract to work with calldata
**/
abstract contract CalldataDecoder {
    /**
     * @notice We need this functions to check their selector and functionSelector in calldata
     * for changeCalldataAmount() function
    **/
    /* solhint-disable no-empty-blocks */
    function uniswapV3Swap(uint256, uint256, uint256[] calldata) public pure {}
    function unoswap(address, uint256, uint256, uint256[] calldata) public pure {}
    function swap(address, SwapDescription calldata, bytes calldata, bytes calldata) public pure {}
    function clipperSwap(
        address clipperExchange,
        address srcToken,
        address dstToken,
        uint256 inputAmount,
        uint256 outputAmount,
        uint256 goodUntil,
        bytes32 r,
        bytes32 vs
    ) public pure {}
    /* solhint-enable no-empty-blocks */

    /**
      * @notice
      * Helper function to change amount parameter on callback that depends on return by 1inch API calldata for swap.
      * We also change minAmount proportionally to newAmount.
      * Route path don't depend on that - this path was calculated previously in backend part and is stored in {data}.
      * @param data - calldata to edit
      * @param newAmount - new amount that should replace amount in {data}
      * @return edited calldata with new amount inside
     **/
    function changeCalldataAmount(bytes calldata data, uint256 newAmount) public pure returns (bytes memory) {
        bytes4 functionSelector = bytes4(data[:4]);
        if (functionSelector == this.uniswapV3Swap.selector) {
            (
                uint256 oldAmount, uint256 oldMinReturn, uint256[] memory pools
            ) = abi.decode(data[4:], (uint256, uint256, uint256[]));
            uint256 newMinReturn = oldAmount > 0 ? (oldMinReturn * newAmount) / oldAmount : 1;
            return abi.encodeWithSignature("uniswapV3Swap(uint256,uint256,uint256[])", newAmount, newMinReturn, pools);
        }
        if (functionSelector == this.unoswap.selector) {
            (
                address token, uint256 oldAmount, uint256 oldMinReturn, uint256[] memory pools
            ) = abi.decode(data[4:], (address,uint256,uint256,uint256[]));
            uint256 newMinReturn = oldAmount > 0 ? (oldMinReturn * newAmount) / oldAmount : 1;
            return abi.encodeWithSignature("unoswap(address,uint256,uint256,uint256[])",
            token,
            newAmount,
            newMinReturn,
            pools);
        }
        if (functionSelector == this.swap.selector) {
            (
                address caller, SwapDescription memory description, bytes memory permit, bytes memory swapData
            ) = abi.decode(data[4:], (address, SwapDescription, bytes, bytes));
            uint256 newMinReturn = description.amount > 0
                ? (description.minReturnAmount * newAmount) / description.amount
                : 1;
            return abi.encodeWithSignature(
                "swap(address,(address,address,address,address,uint256,uint256,uint256),bytes,bytes)",
                caller,
                SwapDescription({
                    srcToken: description.srcToken,
                    dstToken: description.dstToken,
                    srcReceiver: description.srcReceiver,
                    dstReceiver: description.dstReceiver,
                    amount: newAmount,
                    minReturnAmount: newMinReturn,
                    flags: description.flags
                }),
                permit,
                swapData
            );
        }
        if (functionSelector == this.clipperSwap.selector) {
            (
                address clipperExchange,
                address srcToken,
                address dstToken,
                uint256 oldInputAmount,
                uint256 oldOutputAmount,
                uint256 goodUntil,
                bytes32 r,
                bytes32 vs
            ) = abi.decode(data[4:], (address, address, address, uint256, uint256, uint256, bytes32, bytes32));
            uint256 newOutputAmount = oldInputAmount > 0 ?
                (oldOutputAmount * newAmount) / oldInputAmount : 1;
            return abi.encodeWithSignature(
                "clipperSwap(address,address,address,uint256,uint256,uint256,bytes32,bytes32)",
                clipperExchange,
                srcToken,
                dstToken,
                newAmount,
                newOutputAmount,
                goodUntil,
                r,
                vs
            );
        }

        return data;
    }
}

contracts/exchange/contracts/exchange/Ex.content:
// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";

import "../interfaces/IExchange.sol";
import "../interfaces/ITopStakers.sol";
import "../interfaces/IExchangeCallback.sol";
import "../periphery/contracts/interfaces/oracle/ISuOracleAggregator.sol";

interface ICollateralJoin {
    function join(address usr, uint wad) external;
    function exit(address usr, uint wad) external;
}

/**
 * @title Exchange
 * @notice Main contract of Exchange module. Here we can add order or fill it to get profit.
 * @dev See IExchange interface for full details.
 */
contract Exchange is ReentrancyGuard, IExchange, Ownable2Step {
    using SafeERC20 for IERC20;

    // We don't use enum for DiscountType to save gas
    uint8 public constant DISCOUNT_TYPE_AGGRESSIVE = 0;
    uint8 public constant DISCOUNT_TYPE_MIDDLE = 1;
    uint8 public constant DISCOUNT_TYPE_CONSERVATIVE = 2;
    ISuOracleAggregator public ORACLE;
    ITopStakers public TOP_STAKERS;
    address public admin;
    mapping (address => bool) public userWhitelist;
    mapping(address => uint256) private userNonces; // To track user nonces for addOrder
    /**
     * @notice Amount of minimum amountLeft in order. It's measured in fiat.
     * For example dustThreshold = 100 * 1e18 => it's 100 USD
    **/
    uint256 public dustThreshold;
    using EnumerableSet for EnumerableSet.UintSet;
    mapping (uint256 => Order) public orders;
    EnumerableSet.UintSet private orderIdSet;
    /**
     * @notice priceCurve is like price_t = price_0 * (1 - t/priceCurve[0]) - priceCurve[1]
     * from timePassed = 0 to t priceCurve decrease linear, but after t (when discount is 20%), it's constant
    **/
    mapping (uint8 => uint256[2]) public priceCurve;
    uint256 public maxDiscountPercent;

    constructor(address _oracleAddress, address _topStakersAddress) {
        // -20% for 3min/10h/24h
        priceCurve[DISCOUNT_TYPE_AGGRESSIVE] = [180 * 5, 0];
        priceCurve[DISCOUNT_TYPE_MIDDLE] = [36_000 * 5, 0];
        priceCurve[DISCOUNT_TYPE_CONSERVATIVE] = [86_400 * 5, 0];
        dustThreshold = 100 * 1e18;
        maxDiscountPercent = 20;

        ORACLE = ISuOracleAggregator(_oracleAddress);
        TOP_STAKERS = ITopStakers(_topStakersAddress);
    }

    function setMaxDiscountPercent(uint256 newMaxDiscountPercent) external onlyOwner {
        // Sanity check - don't allow more than 90% discount to prevent mistakes
        if (newMaxDiscountPercent > 90) revert MaxDiscountTooHigh();
        uint256 oldMaxDiscount = maxDiscountPercent;
        maxDiscountPercent = newMaxDiscountPercent;
        emit MaxDiscountPercentChanged(oldMaxDiscount, newMaxDiscountPercent);
    }

    function isValidDiscountType(uint8 discountType) internal view returns (bool) {
        return discountType <= DISCOUNT_TYPE_CONSERVATIVE;
    }

    function updateWhitelistUser(address user, bool isWhitelisted) external onlyOwner {
        userWhitelist[user] = isWhitelisted;
        emit WhitelistUpdated(user, isWhitelisted);
    }

    function setPriceCurve(uint8 discountType, uint256[2] calldata newPriceCurve) external onlyOwner {
        if (!isValidDiscountType(discountType)) revert InvalidDiscountType();
        priceCurve[discountType] = newPriceCurve;
        emit PriceCurveChanged();
    }

    function setDustThreshold(uint256 newThreshold) external onlyOwner {
        uint256 oldThreshold = dustThreshold;
        dustThreshold = newThreshold;
        emit DustThresholdChanged(oldThreshold, newThreshold);
    }

    function addOrder(
        address token2sell,
        uint256 amount2sell,
        address token2buy,
        address beneficiary,
        uint8 discountType,
        address collateralJoin // or store this map in exchange module
    ) external nonReentrant returns (uint256 orderId) {
        if (userWhitelist[msg.sender] == false) revert IsNotWhitelisted();
        if (!isValidDiscountType(discountType)) revert InvalidDiscountType();
        if (token2sell == address(0) || token2buy == address(0) || beneficiary == address(0)) revert InvalidAddress();
        if (token2sell == token2buy) revert InvalidTokens();
        if (amount2sell == 0) revert InvalidAmount();

        // @notice The Exchange Module works with explicit real ERC20 tokens balance, so we need to acquire them.
        // If the caller is a user/contract that has some tokens in the protocol,
        // they need to get them out of the protocol via exit.
        // Otherwise, the user/contract just needs to transfer tokens to the Exchange Module.
        if (collateralJoin == address(0)) {
            IERC20(token2sell).safeTransferFrom(msg.sender, address(this), amount2sell);
        } else {
            ICollateralJoin(collateralJoin).exit(address(this), amount2sell);
        }

        // Get current nonce and increment for next time
        uint256 nonce = userNonces[msg.sender]++;
        // Generate a unique order ID using the nonce
        orderId = uint256(
            keccak256(
                abi.encodePacked(block.number, msg.sender, token2sell, amount2sell, token2buy, nonce)
            )
        );

        // With the nonce, this should never happen, but add a check just to be safe
        if (orders[orderId].creationTimestamp != 0) revert OrderExist(orderId);

        // Create the order
        orders[orderId] = Order(
            token2sell,
            token2buy,
            beneficiary,
            msg.sender,
            amount2sell,
            0, // token2buyAmount at start should be 0
            uint64(block.timestamp),
            discountType
        );
        EnumerableSet.add(orderIdSet, orderId);
        emit OrderAdded(orderId);
    }

    function cancelOrder(uint256 orderId) external nonReentrant {
        Order memory order = orders[orderId];

        if (order.creationTimestamp == 0) revert OrderNotExist(orderId);
        if (msg.sender != order.beneficiary && msg.sender != order.caller) revert UserIsNotBeneficiary();

        IERC20(order.token2sell).safeTransfer(msg.sender, order.amountLeft);
        delete orders[orderId];
        EnumerableSet.remove(orderIdSet, orderId);

        emit OrderCanceled(orderId);
    }

    /**
     * @notice Return discounted price of token depending on passed time and type of discount
     * @dev orderPrice = x * 10^18 * 10^(18 - decimals) like in Oracle
    **/
    function getDiscountedTokenPrice(
        uint256 token2sellPrice,
        uint256 timePassed,
        uint8 discountType
    ) internal view returns (uint256) {
        uint256 curveDiv = priceCurve[discountType][0];
        uint256 curveSub = priceCurve[discountType][1];
        uint256 minPrice = token2sellPrice * (100 - maxDiscountPercent) / 100;
        if (timePassed > curveDiv) {
            // if maxDiscountPercent = 20 => we need to get here 80% of token2sellPrice
            return minPrice;
        }

        uint256 calculatedPrice = token2sellPrice - token2sellPrice * timePassed / curveDiv - curveSub;

        return calculatedPrice > minPrice ? calculatedPrice : minPrice;
    }

    function getExpectedOrderOutcome(uint256 orderId, uint256 inAmount) external view returns (uint256 outAmount) {
        Order memory order = orders[orderId];
        uint256 timePassed;
        unchecked {
            timePassed = block.timestamp - order.creationTimestamp;
        }

        // Let's assume that: inAmount = x * 10^d1, discountedTokenPrice = p1 * 10^(36 - d1), priceB = p2 * 10^(36 - d2)
        // Then outAmount = x * 10^d1 * p1 * 10^(36 - d1) / (p2 * 10^(36 - d2)) = (x * p1 / p2) * 10^(d2)
        uint256 discountedTokenPrice = getDiscountedTokenPrice(
            ORACLE.getFiatPrice1e18(order.token2sell),
            timePassed,
            order.discountType
        );
        uint256 priceB = ORACLE.getFiatPrice1e18(order.token2buy);
        if (priceB == 0) revert ZeroPriceError();
        outAmount = inAmount * discountedTokenPrice / priceB;
    }

    function _validateOrderAndCalculateAmount(
        uint256 orderId, uint256 inAmount, Order storage order
    ) internal returns (uint256 outAmount, uint256 newInAmount) {
        newInAmount = inAmount;

        if (order.creationTimestamp == 0) revert OrderNotExist(orderId);

        uint256 timePassed;
        unchecked {
            timePassed = block.timestamp - order.creationTimestamp;
        }

        // grace period
        if (timePassed <= 60) {
            // check that msg.sender is in TopStaker list + it's on position {orderId % x}
            uint256 numberOfStakers = TOP_STAKERS.getTopStakersLength();
            if (numberOfStakers == 0) revert NoStakersAvailable();
            if (!TOP_STAKERS.isTopStaker(msg.sender)) revert IsNotTopStaker(msg.sender);

            // Deterministically assign order to a staker
            address assignedStaker = TOP_STAKERS.getStakerAt(orderId % numberOfStakers);
            if (msg.sender != assignedStaker) {
                revert OrderNotForThisStaker(orderId, msg.sender);
            }
        } else {
            address user = msg.sender;
            if (!TOP_STAKERS.isStaker(user)) revert IsNotStaker(user);
        }

        if (newInAmount > order.amountLeft) {
            newInAmount = order.amountLeft;
        }

        // the same as in getExpectedOrderOutcome, let's think token2sell have decimals = d1, token2buy = d2
        // discountedTokenPrice decimals = 36 - d1, newInAmount decimals = d1
        uint256 discountedTokenPrice = getDiscountedTokenPrice(
            ORACLE.getSafeFiatPrice1e18(order.token2sell),
            timePassed,
            order.discountType
        );
        uint256 priceB = ORACLE.getSafeFiatPrice1e18(order.token2buy); // decimals is 36 - d2
        // outAmount decimals = d1 + 36 - d1 - (36 - d2) = d2
        outAmount = newInAmount * discountedTokenPrice / priceB;

        if (outAmount == 0) {
            revert BadOutAmount();
        }

        order.token2buyAmount += outAmount;
        order.amountLeft -= newInAmount;

        // If in order some token amount left after updating it
        // then it should have value > dustThreshold, counted in fiat
        if (order.amountLeft > 0 && discountedTokenPrice * order.amountLeft / 1e18 <= dustThreshold) {
            revert SmallAmountLeft();
        }

        return (outAmount, newInAmount);
    }

    function _cleanupOrder(uint256 orderId) internal {
        if (orders[orderId].amountLeft == 0) {
            delete orders[orderId];
            EnumerableSet.remove(orderIdSet, orderId);
            emit OrderFilled(orderId);
        } else {
            emit OrderFilledPartially(orderId);
        }
    }

    function fillOrderAsFlashLoan(
        uint256 orderId,
        uint256 inAmount,
        bytes memory data
    ) external nonReentrant returns (uint256 outAmount) {
        Order storage order = orders[orderId];
        uint256 newInAmount;
        (outAmount, newInAmount) = _validateOrderAndCalculateAmount(orderId, inAmount, order);

        address token2sell = order.token2sell;
        address token2buy = order.token2buy;

        IERC20(token2sell).safeTransfer(msg.sender, newInAmount);

        uint256 balanceBefore = IERC20(token2buy).balanceOf(address(this));
        if(!IExchangeCallback(msg.sender).stableunitFillOrderCallback(token2buy, outAmount, msg.sender, data)) {
            revert CallbackError();
        }
        uint256 balanceAfter = IERC20(token2buy).balanceOf(address(this));
        if (balanceAfter < balanceBefore + outAmount) revert NotEnoughReturnValue();

        IERC20(token2buy).safeTransfer(order.beneficiary, outAmount);

        _cleanupOrder(orderId);
    }

    function fillOrderSimple(uint256 orderId, uint256 inAmount) external nonReentrant returns (uint256 outAmount) {
        Order storage order = orders[orderId];
        uint256 newInAmount;
        (outAmount, newInAmount) = _validateOrderAndCalculateAmount(orderId, inAmount, order);

        IERC20(order.token2buy).safeTransferFrom(msg.sender, order.beneficiary, outAmount);
        IERC20(order.token2sell).safeTransfer(msg.sender, newInAmount);

        _cleanupOrder(orderId);
    }

    function getOrder (uint256 orderId) external view returns (Order memory) {
        return orders[orderId];
    }

    function getOrdersCount() external view returns (uint256) {
        return EnumerableSet.length(orderIdSet);
    }

    /**
     * @notice This method can be deleted after we'll add GraphQL to watch orders
    **/
    function getAllOrders(uint256 offset, uint256 limit) external view returns (OrderWithId[] memory) {
        uint256[] memory orderIdValues = EnumerableSet.values(orderIdSet);
        uint256 totalOrders = orderIdValues.length;
        if (totalOrders == 0) return new OrderWithId[](0);

        uint256 end = offset + limit > totalOrders ? totalOrders : offset + limit;
        uint256 len = end > offset ? end - offset : 0;
        OrderWithId[] memory result = new OrderWithId[](len);
        for (uint256 i = offset; i < end; i++) {
            uint256 orderId = orderIdValues[i];
            result[i - offset] = OrderWithId({
                order: orders[orderId],
                orderId: orderId
            });
        }
        return result;
    }

    function getAssignedStaker(uint256 orderId) external view returns (address) {
        Order memory order = orders[orderId];

        // Check if order exists and is within grace period
        if (order.creationTimestamp == 0) return address(0);

        // Only return assigned staker during grace period
        uint256 timePassed;
        unchecked {
            timePassed = block.timestamp - order.creationTimestamp;
        }
        if (timePassed > 60) return address(0);

        // Check if stakers exists
        uint256 numberOfStakers = TOP_STAKERS.getTopStakersLength();
        if (numberOfStakers == 0) return address(0);

        // Calculate assigned staker using the same logic as in _validateOrderAndCalculateAmount
        return TOP_STAKERS.getStakerAt(orderId % numberOfStakers);
    }

    function emergencyWithdraw(address token, address recipient, uint256 amount) external onlyOwner {
        if (recipient == address(0)) revert ZeroRecipient();
        IERC20(token).safeTransfer(recipient, amount);
        emit EmergencyWithdraw(token, recipient, amount);
    }
}

