// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "./IExchangeCallback.sol";

struct FlashCallbackData {
    bool isLP;
    uint256 orderId;
    address token2sell;
    uint256 token2sellAmount;
    uint256 minProfitPercent;
    uint256 minUnwrapSlippage;
    address recipient;
    InchData[] inchData;
}

struct ParaswapData {
    address contractAddress;
    address tokenTransferProxy;
    bytes data;
}

struct InchData {
    address contractAddress;
    bytes data;
    address token; // needed only if adapter unwrap not to all underlying tokens
}

interface IArbitrageHelper is IExchangeCallback {
    /* ===================== ERRORS ===================== */
    error NotAPool(address sender);
    error SwapNotProfitable(uint256 expectedAmount, uint256 realAmount);
    error SwapByParaSwapError();
    error SwapBy1InchError();
    error BadSender(address sender);
    error LPTokenNotSupported(address lpToken);
    error LPTokenNotRegistered(address lpToken);
    error InvalidProfitPercent();
    error ZeroRecipient();

    /* ===================== EVENTS ===================== */
    // event SwapByUniswap(address tokenA, address tokenB);
    event LPRegistered(address indexed lp, address indexed adapter);
    event ApprovalRevoked(address indexed lp, address indexed adapter);
    event EmergencyWithdraw(address indexed token, address indexed recipient, uint256 amount);

    /* ==================== MUTABLE METHODS ==================== */

    /**
      * @notice Register LP to use it adapter to withdraw or get price of LP later in swapLPWithFlashLoan();
      * @param lp - address of LP token
      * @param lpAdapter - address of LPAdapter for this LP token
    **/
    function registerLP(address lp, address lpAdapter) external;

    /**
     * @notice Revoke the approval granted to adapter.
     * @param lp LP token address for which approval should be revoked.
     * @param adapter Address of the adapter whose approval should be revoked.
     */
    function revokeApprove(address lp, address adapter) external;

    /**
      * @notice Reference implementation of Arbitrage bot contract, that use Exchange module to get profit
      * @dev Contract receive loan from exchange module, sell it on 1inch and get profit = received amount - fee
      * @param orderId - Id of order in Exchange module to fill
      * @param token2sellAmount - Amount of token in order to get as flash-loan
      * @param minProfitPercent Minimum required profit in basis points (1 bp = 0.01%)
      * @param inchData - 1inch callData and contract to swap loan
      * @dev - minProfitPercent must be > 0 and <= 10000 (100%)
      *      - Transaction reverts if actual profit is less than minProfitPercent of the flash loan amount
      *      - Higher minProfitPercent provides better protection against sandwich attacks
      *        but may result in fewer successful arbitrages
     **/
    function swapWithFlashLoan(
        uint256 orderId,
        uint256 token2sellAmount,
        uint256 minProfitPercent,
        InchData memory inchData
    ) external;

    /**
      * @notice The same as swapWithFlashLoan, but loan is LP-token that we need to unwrap
      * @dev Contract receive LP-token from exchange module, unwrap it and swap with 1inch all underline tokens
      * to token that exchange module need.
      * @param orderId - Id of order in Exchange module to fill
      * @param token2sellAmount - Amount of token in order to get as flash-loan
      * @param minProfitPercent Minimum required profit in basis points (1 bp = 0.01%)
      * @param minUnwrapSlippage Minimum slippage to unwrap LP, in basis points (1 bp = 0.01%)
      * @param inchData - 1inch callData and contract array to swap every underline token of LP
      * @dev - minProfitPercent must be > 0 and <= 10000 (100%)
      *      - Transaction reverts if actual profit is less than minProfitPercent of the flash loan amount
      *      - Higher minProfitPercent provides better protection against sandwich attacks
      *        but may result in fewer successful arbitrages
     **/
    function swapLPWithFlashLoan(
        uint256 orderId,
        uint256 token2sellAmount,
        uint256 minProfitPercent,
        uint256 minUnwrapSlippage,
        InchData[] memory inchData
    ) external;


    /* ==================== STAKE MODULE  ==================== */

    /**
      * @notice Before stake we need to approve veToken contract to use our Governance tokens
     **/
    function approveToVeToken(uint256 amount) external;

    /**
      * @notice We stake Governance token from contract to receive voting power
      * to make a contract be able to be a top-staker.
     **/
    function stakeTokens(uint256 amount, uint256 unlockTime) external;

    /**
      * @notice Subscribe contract to be a staker in TopStaker contract
     **/
    function subscribeInTopStaker() external;

    /**
     * @notice Unsubscribe contract from stakers list in TopStaker contract
     **/
    function unsubscribeFromTopStaker() external;

    /**
      * @notice Delegate voting power to another contract or user
     **/
    function delegateVotingPower(address to) external;

    /**
      * @notice Emergency withdraw in case contract have some tokens, that can be transferred by default functions
     **/
    function emergencyWithdraw(address token, address recipient, uint256 amount) external;
}

// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

struct FlashCallbackData {
    uint256 orderId;
    address tokenA;
    address tokenB;
    uint256 tokenBAmount;
    address recipient;
    bytes path;
}

interface IArbitrageHelperUniswap {
    /* ===================== ERRORS ===================== */
    error NotAPool(address sender);
    error BadSender(address sender);
    error BadDecodedData();
    error SwapNotProfitable();

    /* ==================== MUTABLE METHODS ==================== */
    function swapWithFlashLoan(
        uint256 orderId,
        address tokenA,
        address tokenB,
        uint256 tokenBAmount,
        bytes memory path
    ) external;
}

// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.3;

interface IBlocklist {
    function isBlocked(address addr) external view returns (bool);
}

// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

struct Order {
    address token2sell;
    address token2buy;
    address beneficiary;
    address caller;
    uint256 amountLeft;
    uint256 token2buyAmount;
    uint64 creationTimestamp;
    uint8 discountType;
}

struct OrderWithId {
    Order order;
    uint256 orderId;
}

interface IExchange {
    /* ===================== ERRORS ===================== */
    error OnlyAdminError();
    error OrderExist(uint256 orderId);
    error OrderNotExist(uint256 orderId);
    error IncorrectToken(address orderToken2buy, address inToken);
    error SmallAmountLeft();
    error BadOutAmount();
    error UserIsNotBeneficiary();
    error IsNotTopStaker(address staker);
    error IsNotStaker(address staker);
    error OrderNotForThisStaker(uint256 orderId, address staker);
    error NotEnoughReturnValue();
    error CallbackError();
    error ZeroPriceError();
    error InvalidDiscountType();
    error InvalidAddress();
    error InvalidTokens();
    error InvalidAmount();
    error IsNotWhitelisted();
    error MaxDiscountTooHigh();
    error NoStakersAvailable();
    error ZeroRecipient();

    /* ======================== EVENTS ========================= */
    event PriceCurveChanged();
    event OrderAdded(uint256 orderId);
    event OrderCanceled(uint256 orderId);
    event OrderFilled(uint256 orderId);
    event OrderFilledPartially(uint256 orderId);
    event LpOrdersAdded(address LPToken, uint256[] orderIds);
    event DustThresholdChanged(uint256 oldValue, uint256 newValue);
    event WhitelistUpdated(address user, bool isWhitelisted);
    event MaxDiscountPercentChanged(uint256 oldMaxDiscountPercent, uint256 newMaxDiscountPercent);
    event EmergencyWithdraw(address indexed token, address indexed recipient, uint256 amount);

    /* ==================== MUTABLE METHODS ==================== */

    /**
      * @notice Add order to swap {token2sell} to {token2buy}.
      * @dev This swap is async, because depends on how arbitrage bots will fill this order. Emits OrderAdded() event.
      * Before addOrder user needs to approve token2sell for exchange contract
      * @param token2sell - Address of token to sell
      * @param amount2sell - Amount of {token2sell} to sell
      * @param token2buy - Address of token to buy
      * @param beneficiary - Address that will receive {token2buy} after filling order.
      * @param discountType - Speed of decreasing swap price. { 0 - aggressive, 1 - middle, 2 - conservative }
      * @param collateralJoin - address of collateral adapter
     **/
    function addOrder (
        address token2sell,
        uint256 amount2sell,
        address token2buy,
        address beneficiary,
        uint8 discountType,
        address collateralJoin
    ) external returns (uint256);

    /**
      * @notice Only user who place order or who is beneficiary can cancelOrder();
      * @param orderId - Id of order to cancel.
     **/
    function cancelOrder (uint256 orderId) external;

    /**
      * @notice Fill order with flash-loan like logic
      * @dev Caller contract receive orders {token2sell} and after that his stableunitFillOrderCallback() is called.
      * At the end of thi callback contract should send some amount of another token back to Exchange contract.
      * @dev We don't need slippage protection because:
      * 1. Prices are determined by Oracle, not DEX market conditions
      * 2. Output amount is calculated deterministically at execution time
      * @param orderId - Id of order to fill.
      * @param inAmount - Amount of {token2sell} to receive
      * @param data - encoded data to pass to callback function
     **/
    function fillOrderAsFlashLoan (
        uint256 orderId,
        uint256 inAmount,
        bytes memory data
    ) external returns (uint256 outAmount);

    /**
      * @notice Fill order without a flash-loan
      * @dev Caller contract send tokens and receive another tokens depends on the order
      * @param orderId - Id of order to fill.
      * @param inAmount - Amount of {token2sell} to receive
     **/
    function fillOrderSimple(uint256 orderId, uint256 inAmount) external returns (uint256 outAmount);

    /**
     * @notice Updates the maximum allowed discount percentage for all order types
     * @dev This function can only be called by the contract owner
     * @param newMaxDiscountPercent New maximum discount value (in percentage points).
     *        Value is bounded to 90% to prevent configuration mistakes
     */
    function setMaxDiscountPercent(uint256 newMaxDiscountPercent) external;

    /**
      * @notice Add or remove user from whitelist
      * @param user - address of user
      * @param isWhitelisted - bool, false if we want to remove, true if we want to add user to whitelist.
     **/
    function updateWhitelistUser(address user, bool isWhitelisted) external;

    /**
      * @notice Function to update price curve - how fast {token2sell} price will decrease/increase
      * @param discountType - discount type to change
      * @param newPriceCurve - parameters of formula
     **/
    function setPriceCurve(uint8 discountType, uint256[2] calldata newPriceCurve) external;

    /**
      * @notice Function to update dust threshold
      * @param newThreshold - new dust threshold
     **/
    function setDustThreshold(uint256 newThreshold) external;

    /* ==================== VIEW METHODS ==================== */
    /**
      * @return The amount of outcome that caller will receive if he will fill {orderId} with {inAmount} of {token2sell}
     **/
    function getExpectedOrderOutcome (uint256 orderId, uint256 inAmount) external view returns (uint256);

    /**
      * @return Order with id = {orderId}
     **/
    function getOrder (uint256 orderId) external view returns (Order memory);

    /**
      * @return The amount of not full-filled orders
     **/
    function getOrdersCount () external view returns (uint256);

    /**
      * @return The array of all not-filled orders. May be deleted after adding GraphQL for orderIds
     **/
    function getAllOrders (uint256 offset, uint256 limit) external view returns (OrderWithId[] memory);

    /**
     * @notice Returns the address of the staker assigned to an order during grace period
     * @param orderId The ID of the order to check
     * @return assignedStaker The address of the assigned staker, or zero address if:
     *         - Order doesn't exist
     *         - Order is past grace period
     *         - No top stakers available
     */
    function getAssignedStaker(uint256 orderId) external view returns (address);

    /**
  * @notice Emergency withdraw in case contract have some tokens, that can be transferred by default functions
     **/
    function emergencyWithdraw(address token, address recipient, uint256 amount) external;
}

// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

interface IExchangeCallback {
    /**
     * @notice Callback that will be called by Exchange module in fillOrderAsFlashLoan()
     * after sending collateral to msg.sender.
     * In the implementation you must pay stablecoin for Exchange contract.
     * @dev Any contract that calls Exchange#fillOrderAsFlashLoan must implement this interface
     * @param returnAsset - The address of the token that should be returned
     * @param amount - The amount of the {token} to return
     * @param initiator The address of the flashloan initiator
     * @return True if the execution of the operation succeeds, false otherwise
     */
    function stableunitFillOrderCallback (
        address returnAsset,
        uint256 amount,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

interface ITopStakers {
    /* ===================== ERRORS ===================== */
    error NotStakerFound();
    error SubscriptionDelayError();
    error AmountMustBeMoreThatMinimum();
    error InsufficientStake(uint256 available, uint256 requested);
    error LockExpired();
    error TokensDelegated();
    error AlreadyInTop();
    error OnlyAdminError();
    error InvalidAddress();
    error OutOfBounds();

    /* ===================== EVENTS ===================== */

    event SubscriptionRequested(address indexed user, uint256 amount, uint256 activationTime);
    event TopStakersMaxCountUpdated(uint256 newTopStakersCount);
    event VeTokenUpdated(address _veToken);
    event MinStakingAmountUpdated(uint256 newAmount);
    event SubscriptionDelayUpdated(uint256 newDelay);
    event SubscriptionRemoved(address user);

    /* ========================= VARS ========================== */

    function maxTopStakersCount() external view returns(uint256);

    /* ==================== MUTABLE METHODS ==================== */

    /**
      * @notice Admin function to change number of top stakers
     **/
    function changeMaxTopStakersCount(uint256 newTopStakersCount) external;

    /**
      * @notice Admin function to change veToken address
     **/
    function changeVeToken(address veToken) external;

    /**
      * @notice Admin function to change minStakingAmount
     **/
    function changeMinStakingAmount(uint256 minStakingAmount) external;

    /**
     * @notice Admin function to change subscriptionDelay
     **/
    function changeSubscriptionDelay(uint256 minStakingAmount) external;

    /**
      * @notice User function to add yourself in stakers list to be able to be topStaker for liquidation
     **/
    function subscribe() external;

    /**
      * @notice User function to remove yourself from stakers list
     **/
    function unsubscribe() external;

    /* ==================== VIEW METHODS ==================== */

    /**
      * @notice Returns if user is top staker or not
     **/
    function isTopStaker(address staker) external view returns(bool);


    /**
      * @notice Returns if user is staker with not expired stake
     **/
    function isStaker(address user) external view returns(bool);

    /**
      * @notice Returns the list of users that are topStakers
     **/
    function getTopStakers() external view returns(address[] memory);

    /**
      * @notice Returns the amount of subscribed users (stakers list length)
     **/
    function getTopStakersLength() external view returns(uint256);

    /**
     * @notice Returns the address of staker in {index} place
     **/
    function getStakerAt(uint256 index) external view returns(address);

    /**
     * @notice Returns the time left in seconds, before user can call subscribe/unsubscribe
     **/
    function getTimeUntilNextSubscribe(address user) external view returns(uint256);
}

// SPDX-License-Identifier: AGPL-3.0-or-later
pragma solidity ^0.8.3;

interface IVotingEscrow {
    function createLock(uint256 _value, uint256 _unlockTime) external;

    function increaseAmount(uint256 _value) external;

    function increaseUnlockTime(uint256 _unlockTime) external;

    function withdraw() external;

    function delegate(address _addr) external;

    function quitLock() external;

    function balanceOf(address _owner) external view returns (uint256);

    function balanceOfAt(address _owner, uint256 _blockNumber) external view returns (uint256);

    function totalSupply() external view returns (uint256);

    function totalSupplyAt(uint256 _blockNumber) external view returns (uint256);

    function forceUndelegate(address _addr) external;
}

