// MockReentrancy.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../exchange/Exchange.sol";
import "../interfaces/IVotingEscrow.sol";
import "../interfaces/ITopStakers.sol";

contract MockReentrancy {
    Exchange public exchange;

    constructor(address _exchange) {
        exchange = Exchange(_exchange);
    }

    function stableunitFillOrderCallback(address token2buy, uint256 amount, address sender, bytes memory data) external returns (bool) {
        uint256 orderId = abi.decode(data, (uint256));
        // Attempt to reenter the fillOrderAsFlashLoan function
        exchange.fillOrderAsFlashLoan(orderId, 1e18, "0x");
        return true;
    }

    function triggerFillOrderAsFlashLoan(uint256 orderId) external {
        bytes memory data = abi.encode(orderId);
        exchange.fillOrderAsFlashLoan(orderId, 1e18, data);
    }

    function becomeTopStaker(IERC20 gov, IVotingEscrow veGov, ITopStakers topStakers, uint256 lockAmount, uint256 duration) external {
        gov.approve(address(veGov), lockAmount);
        veGov.createLock(lockAmount, duration);
        topStakers.subscribe();
    }
}

