// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract MockVeToken is ERC20 {
    struct LockedBalance {
        int128 amount;
        int128 delegated;
        uint256 end;
        address delegatee;
    }

    mapping(address => LockedBalance) private lockedBalances;

    constructor() ERC20("test vested Token", "MockVeToken") {}

    function mint(address _to, uint256 _amount) public {
        _mint(_to, _amount);
        // Initialize locked balance with default end time
        lockedBalances[_to] = LockedBalance({amount: int128(int256(_amount)), end: block.timestamp + 60 * 60 * 24, delegated: 0, delegatee: address(0)});
    }

    function setLockedEnd(address user, uint256 end) public {
        LockedBalance storage balance = lockedBalances[user];
        balance.end = end;
    }

    function locked(address user) public view returns (LockedBalance memory) {
        return lockedBalances[user];
    }
}

