pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC4626.sol";

import "../access-control/SuAuthenticated.sol";
import "../interfaces/adapters/IAdapter4626.sol";

/**
 * @title Adapter4626
 * @notice Adapter for ERC-4626 LP tokens
 * @dev See IAdapter4626 interface for full details.
 */
contract Adapter4626 is SuAuthenticated, IAdapter4626 {
    using SafeERC20 for IERC20;

    mapping(address => address) public vaultToAsset; // vault => underlying asset
    ISuOracle public ORACLE;
    uint256 internal constant MAX_BPS = 10000; // 100% in basis points
    uint256 internal constant MIN_ASSET_VALUE_USD = 10_000 * 1e18; // 10k$

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _authControl, address _oracle) public initializer {
        __suAuthenticatedInit(_authControl);
        ORACLE = ISuOracle(_oracle);
    }

    function registerLP(address vault) external onlyAdmin {
        if (vaultToAsset[vault] != address(0)) revert AlreadyRegistered(vault);

        try IERC4626(vault).asset() returns (address underlying) {
            vaultToAsset[vault] = underlying;
            IERC20(underlying).forceApprove(vault, type(uint256).max);
            emit TokenRegistered(vault, underlying);
        } catch {
            revert BadVault(vault);
        }
    }

    function setAllowance(address vault, uint256 amount) external onlyAdmin {
        if (vaultToAsset[vault] == address(0)) revert IsNotLP(vault);

        address asset = vaultToAsset[vault];
        IERC20(asset).forceApprove(vault, amount);
        emit AllowanceSet(vault, amount);
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return vaultToAsset[asset] != address(0);
    }

    function getFiatPrice1e18(address vault) external view returns (uint256) {
        if (!isAdapterLP(vault)) revert IsNotLP(vault);

        IERC4626 vaultContract = IERC4626(vault);
        address asset = vaultToAsset[vault];
        uint256 assetDecimals = IERC20Metadata(asset).decimals(); // d
        uint256 lpDecimals = IERC20Metadata(vault).decimals(); // d2

        // TODO: use convertToAssets?
        uint256 totalAssets = vaultContract.totalAssets(); // with decimals = d
        uint256 totalSupply = vaultContract.totalSupply(); // with decimals = d2

        if (totalSupply == 0) revert ZeroTotalSupply();

        // Calculate the value of assets in USD
        uint256 assetPrice = ORACLE.getFiatPrice1e18(asset); // in base 10^(36 - d)
        uint256 totalAssetsValueE36 = totalAssets * assetPrice; // decimals: d + (36 - d) = 36

        if (totalAssetsValueE36 / 1e18 < MIN_ASSET_VALUE_USD) {
            revert InsufficientAssetValue(totalAssetsValueE36 / 1e18, MIN_ASSET_VALUE_USD);
        }

        return totalAssetsValueE36 / totalSupply; // decimals = 36 - d2
    }

    function withdraw(
        address vault,
        uint256 amount,
        uint256 maxSlippageBps
    ) public returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(vault)) revert IsNotLP(vault);
        if (maxSlippageBps >= MAX_BPS) revert InvalidSlippage();
        IERC4626 vaultContract = IERC4626(vault);
        IERC20 underlying = IERC20(vaultToAsset[vault]);

        uint256 currentAllowance = underlying.allowance(address(this), vault);
        if (currentAllowance < amount) {
            revert InsufficientAllowance(vault, amount);
        }

        // Transfer LP tokens from caller to adapter
        IERC20(vault).safeTransferFrom(msg.sender, address(this), amount);

        // Calculate expected amount with slippage protection
        uint256 expectedAmount = vaultContract.convertToAssets(amount);
        uint256 minAmountOut = expectedAmount * (10_000 - maxSlippageBps) / 10_000;

        // Execute redeem
        uint256 balanceBefore = underlying.balanceOf(address(this));
        vaultContract.redeem(amount, address(this), address(this));
        uint256 balanceAfter = underlying.balanceOf(address(this));
        uint256 received = balanceAfter - balanceBefore;

        if (received < minAmountOut) revert SlippageExceeded(received, minAmountOut);

        underlying.safeTransfer(msg.sender, received);

        results = new WithdrawResult[](1);
        results[0] = WithdrawResult({ token: address(underlying), amount: received });
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for(uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}

