pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../access-control/SuAuthenticated.sol";
import "../interfaces/adapters/IAuraAdapter.sol";
import "../vendor/aura/IAuraLPToken.sol";
import "../interfaces/adapters/IBalancerAdapter.sol";

/**
 * @title AuraAdapterWeighted
 * @notice Adapter for Aura LP token based on Balancer Weighted LP token.
 * @dev See ILPAdapter interface for full details.
 */
contract AuraAdapterWeighted is SuAuthenticated, IAuraAdapter {
    using SafeERC20 for IERC20;

    mapping(address => address) public lps; // Mapping from LP token to LP info
    IBalancerAdapter public ADAPTER; // Instance of Balancer Adapter to interact with Balancer pools.

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _authControl, address _balancerAdapter) public initializer {
        __suAuthenticatedInit(_authControl);
        ADAPTER = IBalancerAdapter(_balancerAdapter);
    }

    function registerLP(address lp) external onlyAdmin {
        address lpToken = IAuraLPToken(lp).asset();
        if (address(lpToken) == address(0)) revert IsNotLP(lp);
        if (lps[lp] != address(0)) revert AlreadyRegistered(lp);
        if (IERC20Metadata(lp).decimals() != 18) revert BadDecimals(lp);

        // To withdraw we need to approve Balancer Adapter to use our lp tokens
        IERC20(lpToken).approve(address(ADAPTER), type(uint).max);

        lps[lp] = lpToken;

        emit AuraLPRegistered(lp);
    }

    function revokeApprove(address lp) external onlyAdmin {
        uint256 allowance = IERC20(lp).allowance(address(this), address(ADAPTER));

        if (allowance > 0) {
            // We don't need forceApprove because we set allowance to 0
            IERC20(lp).safeApprove(address(ADAPTER), 0);
        }

        emit ApprovalRevoked(lp);
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return lps[asset] != address(0);
    }

    // @dev Aura Vault share price is the amount of assets you can get for that share multiplied by price of an asset
    function getFiatPrice1e18(address asset) external view returns (uint256) {
        address underlying = lps[asset];
        if (underlying == address(0)) revert IsNotLP(asset);

        uint8 auraShareDecimals = IERC20Metadata(asset).decimals();
        // In default balancerLpDecimals should be = 18
        uint8 balancerLpDecimals = IERC20Metadata(underlying).decimals();
        uint256 oneShare = 10 ** auraShareDecimals;

        // Get assets per share in asset's decimals
        uint256 assetsPerShare = IAuraLPToken(asset).convertToAssets(oneShare);
        // Get the price of underlying token (in 1e18)
        uint256 underlyingPrice = ADAPTER.getFiatPrice1e18(underlying);

        return (assetsPerShare * underlyingPrice) / (10 ** balancerLpDecimals);
    }

    function withdraw(
        address asset,
        uint256 amount,
        uint256 maxSlippageBps
    ) public returns (WithdrawResult[] memory results) {
        address lpToken = lps[asset];
        if (lpToken == address(0)) revert IsNotLP(asset);
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);

        uint256 receivedLpTokens = IAuraLPToken(asset).redeem(
            amount,
            address(this),
            address(this)
        );

        results = ADAPTER.withdrawTo(lpToken, receivedLpTokens, maxSlippageBps, msg.sender);
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for(uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}

// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../../vendor/balancer/WeightedPoolUserData.sol";
import "../../access-control/SuAuthenticated.sol";
import "../../interfaces/adapters/IBalancerAdapter.sol";
import "./BalancerOracleUtils.sol";

/**
 * @title BalancerAdapterWeighted
 * @notice Adapter for Balancer Weighted LP token.
 * @dev See IBalancerAdapter and ILPAdapter interface for full details.
 *      IMPORTANT: This adapter intentionally does not support native ETH withdrawals.
 *      Only ERC20 token withdrawals are supported. For ETH-based pools, use the wrapped version (WETH).
 */
contract BalancerAdapterWeighted is SuAuthenticated, BalancerOracleUtils, IBalancerAdapter {
    using SafeERC20 for IERC20;

    uint256 internal constant MAX_BPS = 10000; // 100% in basis points
    mapping(address => LPInfoAdapter) public lps; // Mapping from LP token to pool
    ISuOracle public ORACLE; // Oracle for fetching price information of underlying tokens.
    using WeightedPoolUserData for bytes;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _authControl, address _oracle) public initializer {
        __suAuthenticatedInit(_authControl);
        ORACLE = ISuOracle(_oracle);
    }

    function registerLP(address lp) external onlyAdmin {
        IVault vault = IWeightedPool(lp).getVault();
        if (address(vault) == address(0)) revert IsNotLP(lp);
        if (address(lps[lp].vault) != address(0)) revert AlreadyRegistered(lp);
        if (IERC20Metadata(lp).decimals() != 18) revert BadDecimals(lp);

        bytes32 poolId = IWeightedPool(lp).getPoolId();

        // Retrieve underlying tokens for the given pool ID.
        (IERC20[] memory tokens,,) = vault.getPoolTokens(poolId);

        // We don't work with native ETH, and Balancer pools only contain ERC20 tokens (WETH instead of native ETH).
        // Vault's getPoolTokens() returns actual ERC20 addresses - native ETH (sentinel 0x00) is never stored in pools,
        // only used as input/output flags in swap/join/exit operations.
        // No need for native ETH checks since pool composition is strictly ERC20:
        // https://github.com/balancer/docs-developers/blob/main/helpers/using-native-eth.md

        // Store pool details in the lps mapping.
        lps[lp].poolId = poolId;
        lps[lp].vault = vault;
        lps[lp].underlyingTokens = tokens;

        // make sure getFiatPrice1e18 does not revert and not return 0
        if(getFiatPrice1e18(lp) == 0) revert NoPriceAfterRegister();

        emit TokenRegistered(lp);
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return address(lps[asset].vault) != address(0);
    }

    function checkUnderlyingTokens(address lp) public view returns (bool) {
        if (!isAdapterLP(lp)) return false;

        LPInfoAdapter memory lpInfo = lps[lp];

        (IERC20[] memory currentTokens,,) = lpInfo.vault.getPoolTokens(lpInfo.poolId);

        if (currentTokens.length != lpInfo.underlyingTokens.length) return false;

        uint256 len = currentTokens.length;
        for (uint256 i = 0; i < len; i++) {
            if (address(currentTokens[i]) != address(lpInfo.underlyingTokens[i])) {
                return false;
            }
        }

        return true;
    }

    function updateUnderlyingTokens(address lp) public onlyAdmin {
        if (!isAdapterLP(lp)) revert IsNotLP(lp);

        LPInfoAdapter storage lpInfo = lps[lp];

        (IERC20[] memory currentTokens,,) = lpInfo.vault.getPoolTokens(lpInfo.poolId);

        lpInfo.underlyingTokens = currentTokens;

        emit UnderlyingTokensUpdated();
    }

    function getFiatPrice1e18(address asset) public view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        LPInfoAdapter memory lpInfo = lps[asset];

        // getTimeWeightedAverage is deprecated, so for any weighted LP token we should use this approximation
        return _getFiatPriceWeightedComplex(asset, lpInfo.poolId, lpInfo.vault, lpInfo.underlyingTokens, ORACLE);
    }

    /**
     * @notice Internal function to handle the withdrawal logic for LP tokens.
     * @dev This function interacts with Balancer's Vault to perform token withdrawals.
     *      IMPORTANT: This function intentionally does not support native ETH withdrawals.
     *      When working with pools containing ETH, use the wrapped version (WETH).
     *      This is a design decision to maintain consistent ERC20-based processing across all adapters.
     *      This check is in first step - registerLP() function
     * @param asset Address of the LP token to withdraw.
     * @param amount Amount of the LP token to withdraw.
     * @return results Array of `WithdrawResult` containing details of withdrawn tokens.
     */
    function _withdrawInternal(address asset, uint256 amount) internal returns (WithdrawResult[] memory results) {
        LPInfoAdapter memory lpInfo = lps[asset];
        VaultReentrancyLib.ensureNotInVaultContext(lpInfo.vault);
        uint256 nTokens = lpInfo.underlyingTokens.length;
        address vaultAddress = address(lpInfo.vault);
        if (
            asset != vaultAddress &&
            IERC20(asset).allowance(address(this), vaultAddress) < amount
        ) {
            IERC20(asset).approve(vaultAddress, amount);
        }

        uint256[] memory balancesBefore = new uint256[](nTokens);
        for (uint256 i = 0; i < nTokens; i++) {
            balancesBefore[i] = lpInfo.underlyingTokens[i].balanceOf(address(this));
        }

        IAsset[] memory assets = new IAsset[](nTokens);
        uint256[] memory minAmountsOut = new uint[](nTokens);
        for (uint256 i = 0; i < nTokens; i++) {
            assets[i] = IAsset(address(lpInfo.underlyingTokens[i]));
            minAmountsOut[i] = 0;
        }
        lpInfo.vault.exitPool(
            lpInfo.poolId,
            payable(address(this)),
            payable(address(this)),
            IVault.ExitPoolRequest({
                assets: assets,
                minAmountsOut: minAmountsOut,
                userData: abi.encode(WeightedPoolUserData.ExitKind.EXACT_BPT_IN_FOR_TOKENS_OUT, amount),
                toInternalBalance: false
            })
        );

        // For some LPs vault returns the first address in underlyingTokens as the LP token address
        if (address(lpInfo.underlyingTokens[0]) == asset) {
            results = new WithdrawResult[](nTokens - 1);
            for (uint256 i = 1; i < nTokens; i++) {
                uint256 underlineTokenAmount =
                    lpInfo.underlyingTokens[i].balanceOf(address(this)) - balancesBefore[i];
                results[i - 1] = WithdrawResult({
                    token: address(lpInfo.underlyingTokens[i]),
                    amount: underlineTokenAmount
                });
            }
        } else {
            results = new WithdrawResult[](nTokens);
            for (uint256 i = 0; i < nTokens; i++) {
                uint256 underlineTokenAmount =
                    lpInfo.underlyingTokens[i].balanceOf(address(this)) - balancesBefore[i];
                results[i] = WithdrawResult({
                    token: address(lpInfo.underlyingTokens[i]),
                    amount: underlineTokenAmount
                });
            }
        }
    }

    function withdrawTo(
        address asset, uint256 amount, uint256 maxSlippageBps, address receiver
    ) public returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        if (maxSlippageBps >= MAX_BPS) revert InvalidSlippage();
        // Calculate expected value of LP tokens being withdrawn and minimum acceptable value based on slippage
        uint256 lpTokenPrice = getFiatPrice1e18(asset);
        uint256 expectedValueUSD = (amount * lpTokenPrice) / 1e18;
        uint256 minValueUSD = (expectedValueUSD * (MAX_BPS - maxSlippageBps)) / MAX_BPS;

        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);

        WithdrawResult[] memory resultsExt = _withdrawInternal(asset, amount);

        uint256 receivedValueUSD;
        uint256 len = resultsExt.length;
        results = new WithdrawResult[](len);
        uint256 resultsIndex = 0;
        for (uint256 i = 0; i < len; i++) {
            address underlyingToken = resultsExt[i].token;
            if (underlyingToken != address(0)) {
                uint256 tokenAmount = resultsExt[i].amount;

                // Calculate USD value of received token
                uint256 tokenPrice = ORACLE.getFiatPrice1e18(underlyingToken);
                receivedValueUSD += (tokenAmount * tokenPrice) / 1e18;

                // Store result and transfer token
                IERC20(underlyingToken).safeTransfer(receiver, tokenAmount);

                results[resultsIndex++] = resultsExt[i];
            }
        }

        // Check slippage
        if (receivedValueUSD < minValueUSD) {
            revert SlippageExceeded(expectedValueUSD, receivedValueUSD);
        }

        emit Withdraw(asset, amount, receiver);
    }

    function withdraw(
        address asset,
        uint256 amount,
        uint256 maxSlippageBps
    ) external returns (WithdrawResult[] memory results) {
        return withdrawTo(asset, amount, maxSlippageBps, msg.sender);
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for(uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}

// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@prb/math/contracts/PRBMathSD59x18.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import "../../interfaces/adapters/IBalancerOracleUtils.sol";
import "../../interfaces/adapters/IWeightedPool.sol";
// import "@balancer-labs/v2-pool-utils/contracts/lib/VaultReentrancyLib.sol";
// We use custom copy of VaultReentrancyLib because Balancer need IVault that use his ERC20 implementation,
// so we need to copy IVault and VaultReentrancyLib
import "../../vendor/balancer/VaultReentrancyLib.sol";
import {ISuOracle} from "../../interfaces/oracle/ISuOracle.sol";

/**
 * @title BalancerOracle
 */
abstract contract BalancerOracleUtils is IBalancerOracleUtils {
    using PRBMathSD59x18 for *;

    function _getPoolInvariant(address asset) internal view returns (uint256) {
        // Try both old and new methods to get the invariant
        try IWeightedPool(asset).getLastPostJoinExitInvariant() returns (uint256 newInvariant) {
            return newInvariant;
        } catch {
            try IWeightedPool(asset).getLastInvariant() returns (uint256 oldInvariant) {
                return oldInvariant;
            } catch {
                revert PoolInvariantError();
            }
        }
    }

    /**
     * @notice To determine the supply there are several functions to get it:
     *         - getActualSupply: is used by the most recent versions of Weighted and Stable Pools.
     *         It accounts for pre-minted BPT as well as due protocol fees.
     *         - totalSupply: is used to be called only by older legacy pools since those doesn’t have pre-minted BPT.
     */
    function _getTotalPoolSupply(address pool) internal view returns (uint256) {
        try IWeightedPool(address(pool)).getActualSupply() returns (uint256 totalSupply) {
            return totalSupply;
        } catch {
            try IWeightedPool(pool).totalSupply() returns (uint256 oldTotalSupply) {
                return oldTotalSupply;
            } catch {
                revert TotalSupplyError();
            }
        }
    }

    // solhint-disable max-line-length
    /**
     * @notice Internal function to calculate the fiat price of a weighted LP token.
     * @dev Uses the Oracle to fetch prices of underlying tokens and computes the weighted price,
            assumes all tokens have <= 18 decimals. Base on:
            [Revest Finance Blog](https://revestfinance.medium.com/dev-blog-on-the-derivation-of-a-safe-price-formula-for-balancer-pool-tokens-33e8993455d0)
     * @dev Balancer has a known read-only reentrancy, which can lead to price manipulation attacks:
            - Oracle with the same implementation was hacked using this bug: [Sentiment Blog](https://hackmd.io/@ sentimentxyz/SJCySo1z2)
            - This bug is kinda old, most of the vulnerable pools were redeployed or has low TVL now. But it is still
            recommended to have reentrancy checks.
            - Calling ensureNotInVaultContext(vault) will perform a noop call to the Vault that will fail if an attacker
            is attempting a read-only reentrancy attack.
     * @return The fiat price of the LP token scaled to 1e18.
     */
    // solhint-enable max-line-length
    function _getFiatPriceWeightedComplex(
        address asset,
        bytes32 poolId,
        IVault vault,
        IERC20[] memory underlyingTokens,
        ISuOracle oracle
    ) internal view returns (uint256) {
        VaultReentrancyLib.ensureNotInVaultContext(vault);

        (IERC20[] memory tokens, uint256[] memory balances, ) = vault.getPoolTokens(poolId);
        uint256[] memory weights = IWeightedPool(asset).getNormalizedWeights();

        int256 totalPi = PRBMathSD59x18.fromInt(1e18);
        uint256[] memory prices = new uint256[](tokens.length);

        uint256 len = tokens.length;
        for (uint256 i = 0; i < len; i++) {
            if (weights[i] == 0) revert ZeroWeight();

            address underlyingTokenAddress = address(underlyingTokens[i]);
            uint8 decimals = IERC20Metadata(underlyingTokenAddress).decimals();
            if (decimals > 18) revert DecimalsTooHigh();
            uint256 decimalAdjustment = 10**(18 - decimals);

            balances[i] = balances[i] * decimalAdjustment;
            prices[i] = oracle.getFiatPrice1e18(underlyingTokenAddress) / decimalAdjustment;

            int256 val = int256(prices[i]).div(int256(weights[i]));
            int256 indivPi = val.pow(int256(weights[i]));

            totalPi = totalPi.mul(indivPi);
        }

        int256 invariant = int256(_getPoolInvariant(asset));
        int256 numerator = totalPi.mul(invariant);
        int256 totalSupply = int256(_getTotalPoolSupply(asset));

        return uint256(numerator.toInt().div(totalSupply));
    }
}

// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../access-control/SuAuthenticated.sol";
import "../interfaces/adapters/IBeefyAdapter.sol";
import "../vendor/beefy/IBeefyVaultV7.sol";

contract BeefyAdapter is SuAuthenticated, IBeefyAdapter {
    using SafeERC20 for IERC20;

    struct BeefyLPInfo {
        address mainToken; // Main underlying token
        address lpAdapter; // Adapter for the mainToken if it's an LP, address(0) if not
    }

    mapping(address => BeefyLPInfo) public lps;
    ISuOracle public ORACLE;
    uint256 internal constant MAX_BPS = 10000; // 100% in basis points

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _authControl, address _oracle) public initializer {
        __suAuthenticatedInit(_authControl);
        ORACLE = ISuOracle(_oracle);
    }

    function registerLP(address lp, address mainToken, address lpAdapter) external onlyAdmin {
        // BeefyVaultV7 always have decimals = 18
        if (IERC20Metadata(lp).decimals() != 18) revert BadDecimals(lp);
        if (lps[lp].mainToken != address(0)) revert AlreadyRegistered(lp);

        // Verify the vault is valid and uses the correct want token
        IBeefyVaultV7 beefyVault = IBeefyVaultV7(lp);
        if (address(beefyVault.want()) != mainToken) revert BadMainUnderlyingToken();

        lps[lp] = BeefyLPInfo({mainToken: mainToken, lpAdapter: lpAdapter});
        if (lpAdapter != address(0)) {
            // Infinite approve to adapter for future withdraw
            IERC20(mainToken).approve(lpAdapter, type(uint256).max);
        }

        emit TokenRegistered(lp, mainToken, lpAdapter);
    }

    function setUnderlyingLPAdapter(address asset, address adapter) external onlyAdmin {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        BeefyLPInfo storage lpInfo = lps[asset];

        // Reset allowance if changing adapter
        if (lpInfo.lpAdapter != address(0)) {
            IERC20(lpInfo.mainToken).approve(lpInfo.lpAdapter, 0);
        }

        if (adapter != address(0) && !ILPAdapter(adapter).isAdapterLP(lpInfo.mainToken)) {
            revert AdapterDoesNotSupportToken(adapter, lpInfo.mainToken);
        }

        lpInfo.lpAdapter = adapter;
        emit UnderlyingLPAdapterSet(asset, adapter);
    }

    function setAllowance(address asset, uint256 amount) external onlyAdmin {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        BeefyLPInfo memory lpInfo = lps[asset];

        if (lpInfo.lpAdapter == address(0)) revert AdapterNotSet(asset);

        IERC20(lpInfo.mainToken).approve(lpInfo.lpAdapter, amount);

        emit AllowanceSet(asset, lpInfo.mainToken, lpInfo.lpAdapter, amount);
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return lps[asset].mainToken != address(0);
    }

    /**
     * @dev This function is safe against flash loan attacks because the share price calculation
     * maintains constant ratio regardless of deposit/withdrawal size.
     *
     * Mathematical proof:
     * Let's define:
     * - b = total balance of underlying tokens in vault
     * - s = total supply of shares
     * - p = price of underlying token in USD
     *
     * Initial state (price per share):
     * pps₁ = (b/s) * p
     *
     * After flash loan deposit of amount 'a':
     * pps₂ = ((b+a)/(s+s_new)) * p
     * where s_new = (a*s)/b
     *
     * Substituting s_new:
     * pps₂ = ((b+a)/(s+(a*s)/b)) * p
     * = ((b+a)*b)/(s*b+a*s) * p
     * = ((b+a)*b)/((b+a)*s) * p
     * = (b/s) * p
     *
     * When flash loan is repaid (amount 'a' withdrawn):
     * pps₃ = ((b+a-a)/(s+(a*s)/b-(a*s)/b)) * p
     * pps₃ = (b/s) * p
     * pps₃ = pps₁
     *
     * Therefore: pps₁ = pps₃, proving flash loan resistance
     */
    function getFiatPrice1e18(address asset) external view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        BeefyLPInfo memory lpInfo = lps[asset];

        IBeefyVaultV7 vault = IBeefyVaultV7(asset);
        uint256 pricePerShare = vault.getPricePerFullShare(); // decimals = 18
        uint256 tokenDecimals = IERC20Metadata(lpInfo.mainToken).decimals(); // d
        uint256 mainTokenPrice = ORACLE.getFiatPrice1e18(lpInfo.mainToken); // decimals = 36 - d

        // LP have decimals = 18 => we need to return result with decimals = 36-18-18
        // In result we have decimals = 18 + (36 - d) - (36 - d) = 18
        return (pricePerShare * mainTokenPrice) / 10 ** (36 - tokenDecimals);
    }

    function withdraw(
        address asset,
        uint256 amount,
        uint256 maxSlippageBps
    ) external returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        if (maxSlippageBps >= MAX_BPS) revert InvalidSlippage();

        BeefyLPInfo memory lpInfo = lps[asset];
        IBeefyVaultV7 vault = IBeefyVaultV7(asset);

        // Calculate expected output based on current price per share
        uint256 expectedAmount = (amount * vault.balance()) / vault.totalSupply();
        uint256 minAcceptableAmount = (expectedAmount * (MAX_BPS - maxSlippageBps)) / MAX_BPS;

        // TODO: add allowance check
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);

        // First step: withdraw from Beefy vault
        uint256 balanceBefore = IERC20(lpInfo.mainToken).balanceOf(address(this));
        vault.withdraw(amount);
        uint256 balanceAfter = IERC20(lpInfo.mainToken).balanceOf(address(this));

        uint256 receivedAmount = balanceAfter - balanceBefore;
        if (receivedAmount < minAcceptableAmount) {
            revert SlippageExceeded(expectedAmount, receivedAmount);
        }

        // Second step: if underlying token is LP and has adapter, withdraw it too
        if (lpInfo.lpAdapter != address(0)) {
            if (IERC20(lpInfo.mainToken).allowance(address(this), lpInfo.lpAdapter) < receivedAmount) {
                revert InsufficientAllowance(asset);
            }
            // Withdraw underlying LP using its adapter
            results = ILPAdapter(lpInfo.lpAdapter).withdraw(lpInfo.mainToken, receivedAmount, maxSlippageBps);
            // Transfer all resulting tokens to user
            for (uint256 i = 0; i < results.length; i++) {
                IERC20(results[i].token).safeTransfer(msg.sender, results[i].amount);
            }
        } else {
            // If no nested LP, return single result
            results = new WithdrawResult[](1);
            results[0] = WithdrawResult({token: lpInfo.mainToken, amount: receivedAmount});
            IERC20(lpInfo.mainToken).safeTransfer(msg.sender, receivedAmount);
        }

        emit Withdraw(asset, amount);
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for (uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}

