    /**
      * @notice Register the given LP token address and set the LP underlyingTokens
      * @param lp address of LP token
     **/
    function registerLP(address lp) external onlyAdmin {
        lps[lp].underlyingTokens[0] = IUniswapV2Pair(lp).token0();
        lps[lp].underlyingTokens[1] = IUniswapV2Pair(lp).token1();
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return lps[asset].underlyingTokens[0] != address(0);
    }

    function getFiatPrice1e18(address asset) external view returns (uint256) {
        revert LPOracleNotReady();
        // Below it's unsafe oracle implementation
//        if (!isAdapterLP(asset)) revert IsNotLP(asset);
//        QuickSwapLPInfo memory lpInfo = lps[asset];
//
//        (uint112 _reserve0, uint112 _reserve1,) = IUniswapV2Pair(asset).getReserves();
//        uint256 sumBalances = _reserve0 * ORACLE.getFiatPrice1e18(lpInfo.underlyingTokens[0])
//            + _reserve1 * ORACLE.getFiatPrice1e18(lpInfo.underlyingTokens[1]);
//
//        return sumBalances / IUniswapV2Pair(asset).totalSupply();
    }

    function withdraw(address asset, uint256 amount, uint256 _maxSlippageBps) external returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);
        QuickSwapLPInfo memory lpInfo = lps[asset];

        results = new WithdrawResult[](2);

        if (IUniswapV2Pair(asset).allowance(address(this), ROUTER) < amount) {
            IERC20(asset).approve(ROUTER, amount);
        }

        uint256[2] memory balancesBefore = [
            IERC20(lpInfo.underlyingTokens[0]).balanceOf(address(this)),
            IERC20(lpInfo.underlyingTokens[1]).balanceOf(address(this))
        ];

        IUniswapV2Router01(ROUTER).removeLiquidity(
