        pts[pt].underlyingTokens = pts[pt].SY.getTokensOut();

        (,address realMainUnderlyingToken,) = pts[pt].SY.assetInfo();
        if (realMainUnderlyingToken != mainUnderlyingToken) revert BadMainUnderlyingToken();
        pts[pt].mainUnderlyingToken = mainUnderlyingToken;

        if (wasRegistered) {
            emit TokenRegisteredRepeat(pt, address(market), mainUnderlyingToken);
        } else {
            emit TokenRegistered(pt, address(market), mainUnderlyingToken);
        }
    }

    function increaseObservationCardinality(address market, uint16 cardinality) external onlyAdmin {
        IPMarket(market).increaseObservationsCardinalityNext(cardinality);
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return pts[asset].underlyingTokens.length != 0;
    }

    /**
     * @notice Idea of this implementation was in Pendle example and Gearbox PendleTWAPPTPriceFeed
    **/
    function getFiatPrice1e18(address asset) external view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        PendlePTInfo memory lpInfo = pts[asset];

        uint256 lpRate = lpInfo.market.getPtToAssetRate(TWAP_WINDOW); // in base 10^d
        uint256 assetPrice = ORACLE.getFiatPrice1e18(lpInfo.mainUnderlyingToken); // in base 10^(36 - d)

        // Decimals check , we should receive decimals = 18:
        // (36 - d) + d - X = 18 => X = 18
        return assetPrice * lpRate / 1e18;
    }

    /**
     * @notice Preview how much of a token would be received for redeeming PT
     * @dev Estimates the output of selling PT for a token through the entire conversion path
     * @param ptToken PT token to sell
     * @param ptAmount Amount of PT token to sell
     * @param outputToken Desired token to receive
     * @return expectedAmount Estimated amount of output token
     */
    function previewPtToToken(
        address ptToken,
        uint256 ptAmount,
        address outputToken
    ) internal view returns (uint256) {
        PendlePTInfo memory ptInfo = pts[ptToken];

        // Estimate PT to SY conversion:
        // If PT is expired, we can redeem it directly
        // When PT is not expired, we use the market's PT/SY rate from oracle
        uint256 estimatedSyOut = IPPrincipalToken(ptToken).isExpired()
            ? ptAmount
            : ptAmount * ptInfo.market.getPtToSyRate(TWAP_WINDOW) / 1e18;

        // Estimate SY to output token conversion
        return ptInfo.SY.previewRedeem(outputToken, estimatedSyOut);
    }

    function withdraw(
        address asset,
        uint256 amount,
        uint256 maxSlippageBps
    ) public returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);
        PendlePTInfo memory ptInfo = pts[asset];

        uint256 nTokens = ptInfo.underlyingTokens.length;
        results = new WithdrawResult[](1);

        // In other adapters there is the method that unwrap LP to all underlyingTokens,
        // but here we have Pendle library, that can swap PT only for one token.
        // So we need to check what token will have the best USD value. For that we need to multiply token price and
        // amount we could receive after this swap.
        // This swap in depth is doing by ptInfo.SY, so there is previewRedeem function to check this amount after swap.
        address bestTokenOut;
        uint256 bestTokenAmountOut1e18;
        uint256 expectedAmount;
        for (uint256 i = 0; i < nTokens; ++i) {
            address tokenToPreview = ptInfo.underlyingTokens[i];
            if (ORACLE.hasPriceForAsset(tokenToPreview)) {
                // previewRedeem is in base 10^d (in default in returns second arg)
                uint256 previewRedeem =  previewPtToToken(asset, amount, tokenToPreview);
                uint256 tokenPrice = ORACLE.getSafeFiatPrice1e18(tokenToPreview); // with base 10^(36 - d)
                uint256 previewAmount1e18 = previewRedeem * tokenPrice / 1e18; // with base 10^(d + 36 - d);
                if (previewAmount1e18 > bestTokenAmountOut1e18) {
                    bestTokenAmountOut1e18 = previewAmount1e18;
                    bestTokenOut = tokenToPreview;
                    expectedAmount = previewRedeem;
                }
            }
        }
        if (bestTokenOut == address(0)) revert TokenOutNotFound();

        uint256 netTokenOut = _sellPtForToken(address(ptInfo.market), amount, bestTokenOut);

        // Calculate minimum acceptable amount based on slippage
        uint256 minAcceptableAmount = (expectedAmount * (MAX_BPS - maxSlippageBps)) / MAX_BPS;
        if (netTokenOut < minAcceptableAmount) {
            revert SlippageExceeded(expectedAmount, netTokenOut);
        }

        results[0] = WithdrawResult({ token: bestTokenOut, amount: netTokenOut });
        IERC20(bestTokenOut).safeTransfer(msg.sender, netTokenOut);

        emit Withdraw(asset, amount);
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for(uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}

