pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../access-control/SuAuthenticated.sol";
import "../interfaces/adapters/UniswapV3/IUniswapV3Adapter.sol";
import "../interfaces/oracle/ISuOracle.sol";
import "../vendor/uniswap/OracleLibrary.sol";
import "../vendor/uniswap/LiquidityAmounts.sol";
import "../vendor/uniswap/PoolAddress.sol";
import "../vendor/uniswap/TickMath.sol";
import "../vendor/uniswap/interfaces/IUniswapV3PositionManager.sol";
import "../vendor/uniswap/interfaces/IUniswapV3Factory.sol";
import "../vendor/uniswap/FixedPoint128.sol";

// adapter is erc 721 holder, implements onERC721Received

import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

/**
 * @title UniswapV3Adapter
 * @dev Each position accounts for accumulated fees, as well as the current amounts in tokensOwned0 and tokensOwned1.
 * When the liquidity of a position changes (increased or decreased),
 * any earned fees are calculated and added to tokensOwned0 and tokensOwned1.
 * As a result, after unwinding or unwrapping the position,
 * the total amount received is likely to be higher due to these accumulated fees.
 */
contract UniswapV3Adapter is SuAuthenticated, ERC721Holder, IUniswapV3Adapter {
    using SafeERC20 for IERC20;
    uint128 private constant  MAX_UINT128 = type(uint128).max;

    mapping(address => mapping(address => mapping(uint24 => bool))) public lps; // Mapping for lps
    ISuOracle public ORACLE;
    IUniswapV3PositionManager public positionManager;
    IUniswapV3Factory public uniswapV3Factory;

    uint256 internal constant MAX_BPS = 10000; // 100% in basis points

    uint16 internal TWAP_PERIOD;
    uint16 internal MIN_BLOCKS_FOR_OBSERVATIONS;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _authControl,
        address _oracle,
        address _positionManager,
        address _uniswapV3Factory,
        uint16 blocksPerMinute
    ) public initializer {
        __suAuthenticatedInit(_authControl);

        ORACLE = ISuOracle(_oracle);

        // 1. init nft position manager of uniswap v3 periphery

        positionManager = IUniswapV3PositionManager(_positionManager);

        // 2. unit factory for getting pool address by params
        uniswapV3Factory = IUniswapV3Factory(_uniswapV3Factory);


        // 3. init twap and min blocks per observation for set uniswap v3 oracle
        TWAP_PERIOD = 60 * 30; // 30 minutes TWAP
        MIN_BLOCKS_FOR_OBSERVATIONS = (TWAP_PERIOD / 60) * blocksPerMinute;
    }


    /**
     * @notice Validates and ensures sufficient cardinality for the pool
     * The cardinality represents the number of observations that can be stored by the pool for TWAP calculations.
     * Docs: https://uniswapv3book.com/milestone_5/price-oracle.html#observations-and-cardinality
     * @param pool The Uniswap V3 pool to check
     */
    function validateAndIncreaseCardinality(IUniswapV3Pool pool) internal {
        (
            /*sqrtPriceX96*/,
            /*tick*/,
            /*observationIndex*/,
            uint16 observationCardinality,
            /*observationCardinalityNext*/,
            /*feeProtocol*/,
            /*unlocked*/
        ) = pool.slot0();

        (,,, bool initialized) = pool.observations(0);
        if (!initialized) revert PoolInitializationFailed();

        // Verify existing cardinality, if cardinality is insufficient - increase it
        if (observationCardinality < MIN_BLOCKS_FOR_OBSERVATIONS) {
            pool.increaseObservationCardinalityNext(MIN_BLOCKS_FOR_OBSERVATIONS);
        }
    }

    /* 3. register lp as token0, token1 and fee paramers.
        Pool can be with different params for fee and it will different pools
    */
    /**
     * @notice register lp, nft position
     * @param token0 address of token0 of position
     * @param token1 address of token1 of position
     * @param fee position fee
     */
    function registerLp(
        address token0,
        address token1,
        uint24 fee
        ) external onlyAdmin {
        if (address(token0) == address(0) || address(token1) == address(0) || fee == 0)
            revert IsNotLP(token0, token1, fee);

        if (lps[token0][token1][fee]) revert AlreadyRegistered(token0, token1, fee);

        address pool = uniswapV3Factory.getPool(token0, token1, fee);

        // 4. check pool exists
        if (uniswapV3Factory.getPool(token0, token1, fee) == address(0)) revert IsNotLP(token0, token1, fee);

        validateAndIncreaseCardinality(IUniswapV3Pool(pool));

        lps[token0][token1][fee] = true;
        lps[token1][token0][fee] = true;

        emit UniswapV3PairRegistered(token0, token1, fee);
    }


    function getFiatPrice1e18(address asset) external view returns (uint256) {
        revert FunctionIsNotAllowed();
    }

    /**
     * @notice get price of pisition
     * @param positionId position id
     */
     function getFiatPrice1e18(uint256 positionId) external view returns (uint256) {

        // 5. get information about position
        (
        ,,
        address token0,
        address token1,
        uint24 fee,int24
        tickLower,
        int24 tickUpper,
        uint128  liquidity,,,
        uint128 token0Owned,
        uint128 token1Owned) = positionManager.positions(positionId);

        if (!isAdapterLP(token0, token1, fee)) revert IsNotLP(token0, token1, fee);

        IUniswapV3Pool pool = IUniswapV3Pool(getPool(token0, token1, fee));

        // 6. calculate amounts of reserves in position using uniswap v3 position and oracle
        (uint256 amount0, uint256 amount1) = getAmounts(pool, tickLower, tickUpper, liquidity);

        uint256 token0Price = ORACLE.getFiatPrice1e18(token0);  // decimals = 36 - d1

        uint256 token1Price = ORACLE.getFiatPrice1e18(token1); // decimals = 36 - d2

        // 7. get fiat price and calculate reserve in usdt for token0 price
        // (36 - d1) + d1  - 18 = 18
        token0Price = token0Price * (amount0 + uint256(token0Owned)) /  10 ** 18;

        // 8. get fiat price and calculate reserve in usdt for token1 price

        // (36 - d2) + d2  - 18 = 18
        token1Price = token1Price * (amount1 + uint256(token1Owned)) / 10 ** 18;

        // 9. get sum of reserves in position in fiat
        return (token0Price + token1Price);
    }

    function getExpectedAmounts(uint256 positionId) external view returns (uint256 amount0, uint256 amount1) {
         (
        ,,
        address token0,
        address token1,
        uint24 fee,
        int24 tickLower,
        int24 tickUpper,
        uint128  liquidity,
        ,
        ,
        uint128 tokensOwed0,
        uint128 tokensOwed1
        ) = positionManager.positions(positionId);

        if (!isAdapterLP(token0, token1, fee)) revert IsNotLP(token0, token1, fee);

        IUniswapV3Pool pool = IUniswapV3Pool(getPool(token0, token1, fee));

        uint160 sqrtPriceX96 =  getCurrentSqrtPrice(pool);

        uint160 sqrtRatioX96A = TickMath.getSqrtRatioAtTick(tickLower);

        uint160 sqrtRatioX96B =TickMath.getSqrtRatioAtTick(tickUpper);

        if (sqrtRatioX96A  > sqrtRatioX96B)  {
            (sqrtRatioX96A, sqrtRatioX96B ) = (sqrtRatioX96B , sqrtRatioX96A);
        }

        (amount0, amount1) = LiquidityAmounts.getAmountsForLiquidity(
            sqrtPriceX96,
            sqrtRatioX96A,
            sqrtRatioX96B,
            liquidity
        );

        amount0 += uint256(tokensOwed0);

        amount1 += uint256(tokensOwed1);
    }

    function getCurrentSqrtPrice(IUniswapV3Pool pool) internal view returns (uint160 sqrtPriceX96) {
        (sqrtPriceX96,,,,,,) =  pool.slot0();
    }

    function getAmounts(
        IUniswapV3Pool pool,
        int24 tickLower,
        int24 tickUpper,
        uint128 liquidity) internal view returns (uint256 amount0, uint256 amount1) {

        // 10. get tick from oracle
        uint32[] memory secondsAgos = new uint32[](2);

        uint32 secondsAgo = uint32(TWAP_PERIOD);

        secondsAgos[0] = secondsAgo;
        secondsAgos[1] = 0;

        (int56[] memory tickCumulatives, ) = pool.observe(secondsAgos);

        int56 tickCumulativesDelta = tickCumulatives[1] - tickCumulatives[0];

        int24 tick = int24(tickCumulativesDelta / int56(uint56(secondsAgo)));

        // Always round to negative infinity
        if (tickCumulativesDelta < 0 && (tickCumulativesDelta % int56(uint56(secondsAgo)) != 0)) tick--;


        // 11. calculate sqrt ratio at tick and ranges
        uint160 sqrtRatioX96 = TickMath.getSqrtRatioAtTick(tick);

        uint160 sqrtRatioX96A = TickMath.getSqrtRatioAtTick(tickLower);

        uint160 sqrtRatioX96B =TickMath.getSqrtRatioAtTick(tickUpper);

        // 12. using liquidity library get amount0 and amount1 reserves at position
        (amount0, amount1) = LiquidityAmounts.getAmountsForLiquidity(
            sqrtRatioX96,
            sqrtRatioX96A,
            sqrtRatioX96B,
            liquidity
        );
    }

    function getPool(address token0, address token1, uint24 fee) internal view returns (address pool) {
        pool = uniswapV3Factory.getPool(token0, token1, fee);
    }


    // 13. change isAdapterLp for checking pools in our registry
    /**
     * @notice check adapter is registered
     * @param token0 address of token0 of position
     * @param token1 address of token1 of position
     * @param fee position fee
    */
    function isAdapterLP(address token0, address token1, uint24 fee) public view returns (bool) {
        return lps[token0][token1][fee];
    }

    /**
     * @notice withdraw from position
     * @param positionId position id
     * @param liquidity liquidity of position, part of whole
     * @param maxSlippageBps slippage bps
    */
    function withdraw(
        uint256 positionId,
        uint128 liquidity, //how is much liquidity we want to remove from position
        uint256 maxSlippageBps
    ) public returns (WithdrawResult[] memory results) {

        // 14. get information about position
        (
            ,,
            address token0,
            address token1,
            uint24 fee,
            int24 tickLower,
            int24 tickUpper,,,,,) = positionManager.positions(positionId);

        // 15. check pool in our registry
        if (!isAdapterLP(token0, token1, fee)) revert IsNotLP(token0, token1, fee);


        IUniswapV3Pool pool = IUniswapV3Pool(getPool(token0, token1, fee));

        (uint256 amount0, uint256 amount1) = getAmounts(pool, tickLower, tickUpper, liquidity);

        // 16. transfer NFT from owner to balance
        positionManager.safeTransferFrom(msg.sender, address(this), positionId);

        // 17. remove liquidity

        positionManager.decreaseLiquidity(IUniswapV3PositionManager.DecreaseLiquidityParams({
            tokenId: positionId,
            liquidity: liquidity,
            amount0Min: (amount0 * (MAX_BPS - maxSlippageBps)) / MAX_BPS,
            amount1Min: (amount1 * (MAX_BPS - maxSlippageBps)) / MAX_BPS,
            deadline: block.timestamp
        }));


        // 18. after removing liquidity collect amounts from position manager to recipient (msg.sender)
        (amount0, amount1) = positionManager.collect(IUniswapV3PositionManager.CollectParams({
            tokenId: positionId,
            recipient: msg.sender, // send token to msg.sender
            amount0Max: MAX_UINT128,
            amount1Max: MAX_UINT128
        }));

        results = new WithdrawResult[](2);

        // 12. update results
        results[0] = WithdrawResult({token: token0, amount: amount0});
        results[1] = WithdrawResult({token: token1, amount: amount1});

        emit Withdraw(positionId, liquidity, token0, token1, amount0, amount1);
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for (uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}

