    error BadFeed(); // In case if we the feed for asset is not specified
    error BadETHPrice(); // In case if there is no price for ETH or it's 0
    error BadDecimals(); // In case if price decimals is >77 that overflow uint for 10^78
    error StalePrice(); // If price from feed is not actual
    error L2SequencerUnavailable(); // If sequencer for L2 is not working
    error AssetNotRegistered(); // If asset is not registered during updating staleness threshold

    /* ===================== EVENTS ===================== */
    event StalenessThresholdUpdated(address indexed asset, uint256 threshold);
    event AssetRegistered(
        address indexed asset,
        address indexed feed,
        uint8 decimals,
        bool isPriceInETH,
        uint256 threshold
    );

    /* ====================== VARS ====================== */
    /**
     * @notice Data feeds addresses from https://docs.chain.link/docs/ethereum-addresses/#Goerli%20Testnet
     * each chainlink data feed has own decimals, see the link above
     **/
    function assetToFeed (address asset) external view returns ( address );
    function assetToFeedDecimals (address asset) external view returns ( uint8 );
    function isPriceInETH (address asset) external view returns ( bool );

    /* ==================== METHODS ==================== */
    /**
     * @notice Register asset - Add ChainLink data for asset and
     *         set if the price for `asset` is in ETH or not (in USDT otherwise)
     * @dev The call can be made more than once, but only by the admin
     * @param asset Address of asset
     * @param chainlinkDataFeed Address of asset feed to receive latestRoundData() in getFiatPrice
     * @param chainlinkDataFeedDecimals Decimals in ChainLink answer of feed
     * @param isPriceInETH Boolean, true if the price of `asset` is in ETH, false if is in USDT
     * @param stalenessThreshold Staleness threshold for the asset in seconds
     **/
    function registerAsset(
        address asset,
        address chainlinkDataFeed,
        uint8 chainlinkDataFeedDecimals,
        bool isPriceInETH,
        uint256 stalenessThreshold
    ) external;

    /**
     * @notice Updates the staleness threshold for a specific asset
     * @dev This threshold should be set based on the Chainlink feed's heartbeat
     * For example:
     * - ETH/USD: 3600s (1 hour) heartbeat -> set threshold to 3600
     * - STETH/USD: 3600s heartbeat -> set threshold to 3600
     * - wstETH/USD: 86400s (24 hour) heartbeat -> set threshold to 86400
     * @param asset The address of the asset to update
     * @param _stalenessThreshold New staleness threshold in seconds
     */
    function updateStalenessThreshold(address asset, uint256 _stalenessThreshold) external;
}

