pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../adapters/Pendle/PendleLPAdapter.sol";

contract MockPendleLPAdapter is PendleLPAdapter {
    /// @notice Unsafe price calculation that could be manipulated during a flash loan attack
    /// @dev This function calculates the price based on current balance state, which can be manipulated
    function getFiatPrice1e18Unsafe(address asset) external view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        PendleLPInfo memory lpInfo = lps[asset];

        // Get total supply of the LP token
        uint256 totalSupply = IERC20(asset).totalSupply();
        if (totalSupply == 0) return 0;

        // Get the main underlying token price
        uint256 mainTokenPrice = ORACLE.getFiatPrice1e18(lpInfo.mainUnderlyingToken);

        // Calculate the direct balance of underlying assets
        uint256 underlyingTokensValue = 0;
        for (uint16 i = 0; i < lpInfo.underlyingTokens.length; i++) {
            address token = lpInfo.underlyingTokens[i];
            uint256 tokenBalance = IERC20(token).balanceOf(address(lpInfo.market));
            uint256 tokenPrice = ORACLE.getFiatPrice1e18(token);
            uint256 decimals = IERC20Metadata(token).decimals();

            underlyingTokensValue += tokenBalance * tokenPrice / 10**decimals;
        }

        // Also add value of any reward tokens if they exist
        address[] memory rewardTokens = lpInfo.market.getRewardTokens();
        for (uint16 i = 0; i < rewardTokens.length; i++) {
            address token = rewardTokens[i];
            uint256 tokenBalance = IERC20(token).balanceOf(address(lpInfo.market));
            uint256 tokenPrice = ORACLE.getFiatPrice1e18(token);
            uint256 decimals = IERC20Metadata(token).decimals();

            underlyingTokensValue += tokenBalance * tokenPrice / 10**decimals;
        }

        // Calculate price per LP token
        return underlyingTokensValue * 1e18 / totalSupply;
    }
}

