contract MockPendlePTAdapter is PendlePTAdapter {
    using PendlePYOracleLib for IPMarket;
    /// @notice AUnsafe price calculation for Pendle Principal Tokens that could be manipulated
    function getFiatPrice1e18Unsafe(address asset) external view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        PendlePTInfo memory ptInfo = pts[asset];

        uint256 ptToAssetRate = ptInfo.market.getPtToAssetRate(1);
        uint256 mainTokenPrice = ORACLE.getFiatPrice1e18(ptInfo.mainUnderlyingToken);

