// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {AggregatorV2V3Interface} from "@chainlink/contracts/src/v0.8/interfaces/AggregatorV2V3Interface.sol";

import "../interfaces/oracle/ISuChainlinkOracle.sol";
import "../access-control/SuAuthenticated.sol";
import "../access-control/Pausable.sol";

/**
 * @title SuChainlinkOracle
 * @notice Oracle that use Chainlink prices
 * @dev See ISuChainlinkOracle interface for full details.
 */
contract SuChainlinkOracle is ISuChainlinkOracle, SuAuthenticated, Pausable {
    mapping (address => address) public assetToFeed;
    // some decimals are different from real token decimals, so we need to store them
    mapping (address => uint8) public assetToFeedDecimals;
    // We need to know is the feed price in USD or in ETH
    mapping (address => bool) public isPriceInETH;
    uint256 internal constant GRACE_PERIOD_TIME = 3600;
    // Staleness threshold for each asset (in seconds). It could be different for each asset,
    // so here we store staleness threshold to check correctness of latestRoundData later in getFiatPrice1e18().
    mapping(address => uint256) public assetStalenessThreshold;
    address internal sequencerUptimeFeed;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
    * For a list of available Sequencer Uptime Feed proxy addresses, see:
    * https://docs.chain.link/docs/data-feeds/l2-sequencer-feeds
    */
    function initialize(address _authControl, address _sequencerUptimeFeed) public initializer {
        __suAuthenticatedInit(_authControl);
        sequencerUptimeFeed = _sequencerUptimeFeed;
    }

    function registerAsset(
        address asset,
        address chainlinkDataFeed,
        uint8 chainlinkDataFeedDecimals,
        bool _isPriceInETH,
        uint256 _stalenessThreshold
    ) external whenNotPaused onlyAdmin {
        assetToFeed[asset] = chainlinkDataFeed;
        assetToFeedDecimals[asset] = chainlinkDataFeedDecimals;
        isPriceInETH[asset] = _isPriceInETH;
        assetStalenessThreshold[asset] = _stalenessThreshold;
        emit AssetRegistered(asset, chainlinkDataFeed, chainlinkDataFeedDecimals, _isPriceInETH, _stalenessThreshold);
    }

    function updateStalenessThreshold(address asset, uint256 _stalenessThreshold) external whenNotPaused onlyAdmin {
        if (assetToFeed[asset] == address(0)) revert AssetNotRegistered();
        assetStalenessThreshold[asset] = _stalenessThreshold;
        emit StalenessThresholdUpdated(asset, _stalenessThreshold);
    }

    /**
     * @notice Chainlink recommends that all Optimistic L2 oracles consult the Sequencer Uptime Feed to ensure
     * that the sequencer is live before trusting the data returned by the oracle.
     * If the L2 Sequencer goes down, oracle data will not be kept up to date, and thus could become stale.
     * Reference implementation: https://docs.chain.link/data-feeds/l2-sequencer-feeds#example-code
     */
    function isSequencerActive() internal view returns (bool) {
        if (sequencerUptimeFeed == address(0)) return true; // if not set in constructor => we are not in L2

        (, int256 answer, uint256 startedAt,,) = AggregatorV2V3Interface(sequencerUptimeFeed).latestRoundData();
        if (block.timestamp - startedAt <= GRACE_PERIOD_TIME || answer == 1)
            return false;
        return true;
    }

    function getFiatPrice1e18(address asset) public view whenNotPaused returns (uint256) {
        address feed = assetToFeed[asset];
        if (feed == address(0)) revert BadFeed();
        if (!isSequencerActive()) revert L2SequencerUnavailable();
        uint8 decimals = assetToFeedDecimals[asset];
        (
            /*uint80 roundId*/,
            int256 answer,
            /*uint256 startedAt*/,
            uint256 updatedAt,
            /*uint80 answeredInRound*/
        ) = AggregatorV3Interface(feed).latestRoundData();
        // answeredInRound in Chainlink is used to indicate in which round the current price (answer) was recorded.
        // According to Chainlink’s documentation, this variable is marked "deprecated", so we use updatedAt variable
        if (block.timestamp - updatedAt >= assetStalenessThreshold[asset]) revert StalePrice();
        if (answer <= 0) revert BadPriceAnswer();
        // examples of answers and what we want to get:
        // ETH/USD = 134165000000 = 1341.65*1e8 =>(want) 1341*1e18
        // BTC/USD = 1928068190485 = 19,280.68190485*1e8 =>(want) 19k*1e18 * (18-8)[for asset decimals]
        // USDC/USD = 99990000 = 0.99990000*1e8 =>(want) => 0.9(9) * 1e18 * (18-6)[for asset decimals]
        uint256 priceFeed1e18 = scaleToE18(uint256(answer), decimals);
        if (asset == address(0) || asset == 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE) {
            return priceFeed1e18;
        } else {
            uint8 assetDecimals = IERC20Metadata(asset).decimals();
            uint256 scaledPrice1e18 = scaleToE18(priceFeed1e18, assetDecimals);
            if (isPriceInETH[asset]) {
                // address(0) mean ETH, that should be added in chainlink config
                uint256 ethPrice = getFiatPrice1e18(address(0));
                if (ethPrice == 0) revert BadETHPrice();
                return scaledPrice1e18 * ethPrice / 1e18;
            } else {
                return scaledPrice1e18;
            }
        }
    }

    /**
     * @dev Adjusts the `price` to have 18 decimals.
     * @param price The original price to adjust.
     * @param priceDecimals The number of decimals in the original price.
     * @return The price adjusted to the desired number of decimals.
     */
    function scaleToE18(uint256 price, uint8 priceDecimals) internal pure returns (uint256) {
        if (price == 0) return 0;
        // This is because 10 ** 78 exceeds uint256's maximum value.
        if (priceDecimals >= 77) revert BadDecimals();

        if (priceDecimals < 18) {
            return price * (10 ** (18 - priceDecimals));
        } else if (priceDecimals > 18) {
            return price / (10 ** (priceDecimals - 18));
        }
        return price;
    }

    uint256[50] private __gap;
}

