/*
    OracleAggregator aggregates several oracles inside, such as: getUsdPrice asset -> id -> call to implementation
    We have several oracle implementations of ISuOracle (they are inside this folder)
*/

import "../access-control/SuAuthenticated.sol";
import "../interfaces/oracle/ISuOracleAggregator.sol";
import "../access-control/Pausable.sol";

/**
 * @title SuChainlinkOracle
 * @notice Oracle that aggregate all oracles, like router
 * @dev See ISuOracleAggregator interface for full details.
 */
contract SuOracleAggregator is ISuOracleAggregator, SuAuthenticated, Pausable {
    mapping (address => uint256) public assetToOracle;
    mapping (uint256 => ISuOracle) public oracleImplementations;
    mapping (address => AssetLimitation) public limitations;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _authControl) public initializer {
        __suAuthenticatedInit(_authControl);
    }

    function setLowerBound(address asset, uint256 newLowerBound) external whenNotPaused onlyAdmin {
        limitations[asset].lowerBound = newLowerBound;

        emit OracleLowerBoundUpdated(asset, newLowerBound);
    }
    function setUpperBound(address asset, uint256 newUpperBound) external whenNotPaused onlyAdmin {
        limitations[asset].upperBound = newUpperBound;

        emit OracleUpperBoundUpdated(asset, newUpperBound);
    }

    function hasPriceForAsset(address asset) public view returns (bool) {
        return assetToOracle[asset] != 0;
    }

    function getFiatPrice1e18(address asset) public view whenNotPaused returns (uint256) {
        uint256 oracleId = assetToOracle[asset];
        if (oracleId == 0) revert NoOracleFound(asset);
        ISuOracle oracleImplementation = oracleImplementations[oracleId];
        if (address(oracleImplementation) == address(0)) revert NoOracleImplementation();
        return oracleImplementation.getFiatPrice1e18(asset);
    }

    function getSafeFiatPrice1e18(address asset) external whenNotPaused returns (uint256) {
        uint256 price = getFiatPrice1e18(asset);

        if (price < limitations[asset].lowerBound) {
            emit OracleLowerBoundWarning(asset, price, limitations[asset].lowerBound);
            revert PriceBelowLowerBound(asset, price, limitations[asset].lowerBound);
        }
        if (price > limitations[asset].upperBound) {
            emit OracleUpperBoundWarning(asset, price, limitations[asset].upperBound);
            revert PriceAboveUpperBound(asset, price, limitations[asset].upperBound);
        }

        return price;
    }

    function setOracleImplementation(uint256 oracleId, ISuOracle oracleImplementation) external whenNotPaused onlyDAO {
        if (oracleId == 0) revert BadOracleId();
        if (address(oracleImplementation) == address(0)) revert NoOracleImplementation();
        oracleImplementations[oracleId] = oracleImplementation;
    }

    function setOracleIdForAssets(address[] memory assets, uint256 oracleId) external whenNotPaused onlyDAO {
        if (address(oracleImplementations[oracleId]) == address(0)) revert NoOracleImplementation();

        uint256 len = assets.length;
        for (uint256 i; i < len;) {
            assetToOracle[assets[i]] = oracleId;
            unchecked { ++i; }
        }
    }

    uint256[50] private __gap;
}

