pragma solidity ^0.8.0;

import { IUniswapV3Pool } from "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "../vendor/uniswap/PoolAddress.sol";
import "../vendor/uniswap/OracleLibrary.sol";
import "../interfaces/oracle/ISuUniV3Oracle.sol";
import "../access-control/SuAuthenticated.sol";
import "../access-control/Pausable.sol";

/**
 * @notice check SuUniV3Oracle section in ORACLE_ARCHITECTURE.md
 * @dev Here we calculate only the price of asset in USDT, not USD.
 * Before that we need to enableAssetPool, to know what pool use for that asset (what fees).
 * See ISuUniV3Oracle interface for full details.
**/
contract SuUniV3Oracle is SuAuthenticated, ISuUniV3Oracle, Pausable {
    mapping (address => IUniswapV3Pool) public override assetToPool;

    address public UNISWAP_FACTORY;
    address public USDT;
    address public ORACLE;
    uint16 internal TWAP_PERIOD;
    uint16 internal MIN_BLOCKS_FOR_OBSERVATIONS;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _authControl,
        address _UNISWAP_FACTORY,
        address _USDT,
        address _ORACLE,
        uint16 blocksPerMinute
    ) public initializer {
        __suAuthenticatedInit(_authControl);
        UNISWAP_FACTORY = _UNISWAP_FACTORY;
        USDT = _USDT;
        ORACLE = _ORACLE;
        TWAP_PERIOD = 60 * 30; // 30 minutes TWAP
        MIN_BLOCKS_FOR_OBSERVATIONS = (TWAP_PERIOD / 60) * blocksPerMinute;
    }

    /**
     * @notice Validates and ensures sufficient cardinality for the pool
     * The cardinality represents the number of observations that can be stored by the pool for TWAP calculations.
     * Docs: https://uniswapv3book.com/milestone_5/price-oracle.html#observations-and-cardinality
     * @param pool The Uniswap V3 pool to check
     */
    function validateAndIncreaseCardinality(IUniswapV3Pool pool) internal {
        (
            /*sqrtPriceX96*/,
            /*tick*/,
            /*observationIndex*/,
            uint16 observationCardinality,
            /*observationCardinalityNext*/,
            /*feeProtocol*/,
            /*unlocked*/
        ) = pool.slot0();

        (,,, bool initialized) = pool.observations(0);
        if (!initialized) revert PoolInitializationFailed();

        // Verify existing cardinality, if cardinality is insufficient - increase it
        if (observationCardinality < MIN_BLOCKS_FOR_OBSERVATIONS) {
            pool.increaseObservationCardinalityNext(MIN_BLOCKS_FOR_OBSERVATIONS);
        }
    }

    function getPool(address _asset, uint24 _fee) internal view returns (IUniswapV3Pool pool) {
        pool = IUniswapV3Pool(PoolAddress.computeAddress(UNISWAP_FACTORY, PoolAddress.getPoolKey(_asset, USDT, _fee)));
    }

    function enableAssetPool(address _asset, uint24 _fee) external override whenNotPaused onlyAdmin {
        IUniswapV3Pool pool = getPool(_asset, _fee);
        validateAndIncreaseCardinality(pool);
        assetToPool[_asset] = pool;
    }

    /**
     * @notice estimateAmountOut returns price of asset in USDT, so we need multiply it on USDT price
     **/
    function getFiatPrice1e18(address asset) public override view whenNotPaused returns (uint256) {
        return
            ISuOracle(ORACLE).getFiatPrice1e18(USDT) *
            estimateAmountOut(assetToPool[asset], asset, 1e18, uint32(TWAP_PERIOD)) / 1e18;
    }

    function estimateAmountOut(
        IUniswapV3Pool pool,
        address tokenIn,
        uint128 amountIn,
        uint32 secondsAgo
    ) internal view returns (uint256 amountOut) {
        address token0 = pool.token0();
        address token1 = pool.token1();
        if (tokenIn != token0 && tokenIn != token1) revert InvalidToken();

        address tokenOut = tokenIn == token0 ? token1 : token0;

        // (int24 tick, ) = OracleLibrary.consult(pool, secondsAgo);
        // Gas optimization: code copied from OracleLibrary.sol, consult()
        uint32[] memory secondsAgos = new uint32[](2);
        secondsAgos[0] = secondsAgo;
        secondsAgos[1] = 0;

        (int56[] memory tickCumulatives, ) = pool.observe(secondsAgos);

        int56 tickCumulativesDelta = tickCumulatives[1] - tickCumulatives[0];

        int24 tick = int24(tickCumulativesDelta / int56(uint56(secondsAgo)));
        // Always round to negative infinity
        if (tickCumulativesDelta < 0 && (tickCumulativesDelta % int56(uint56(secondsAgo)) != 0)) tick--;

        amountOut = OracleLibrary.getQuoteAtTick(
            tick,
            amountIn,
            tokenIn,
            tokenOut
        );
    }

    uint256[50] private __gap;
}

