// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "../interfaces/ITopStakers.sol";

struct LockedBalance {
    int128 amount;
    int128 delegated;
    uint256 end;
    address delegatee;
}

interface IVeToken {
    function balanceOf(address _owner) external view returns (uint256);
    function locked(address user) external view returns (LockedBalance memory);
}

/**
  * @title TopStakers
  * @notice Contract that have functionality to work with Top Stakers.
  * They need to have veToken and call subscribe to participate in this list.
 **/
contract TopStakers is ITopStakers, Ownable2Step {
    address public admin;
    address[] public topStakers;
    uint256 public maxTopStakersCount; // Max number of stakers we want to store on the top
    uint256 public minStakingAmount; // minimal amount of staked token to be able to subscribe in the list
    uint256 public subscriptionDelay;
    IVeToken public veToken; // Address(interface) of vested escrow token
    mapping(address => uint256) public lastOperationTime;
    mapping(address => bool) public isStaker;

    constructor(address _veToken) {
        veToken = IVeToken(_veToken);
        maxTopStakersCount = 5;
        minStakingAmount = 0;
        subscriptionDelay = 30;
    }

    modifier checkDelay() {
        if (block.timestamp <= lastOperationTime[msg.sender] + subscriptionDelay) revert SubscriptionDelayError();
        _;
        lastOperationTime[msg.sender] = block.timestamp;
    }

    function changeMaxTopStakersCount(uint256 newMaxTopStakersCount) external onlyOwner {
        maxTopStakersCount = newMaxTopStakersCount;
        emit TopStakersMaxCountUpdated(newMaxTopStakersCount);
    }

    function changeVeToken(address _veToken) external onlyOwner {
        veToken = IVeToken(_veToken);
        emit VeTokenUpdated(_veToken);
    }

    function changeMinStakingAmount(uint256 _minStakingAmount) external onlyOwner {
        minStakingAmount = _minStakingAmount;
        emit MinStakingAmountUpdated(_minStakingAmount);
    }

    function changeSubscriptionDelay(uint256 newDelay) external onlyOwner {
        subscriptionDelay = newDelay;
        emit SubscriptionDelayUpdated(newDelay);
    }

    /**
      * @notice Finds an expired staker to replace or the staker with minimum balance if no expired stakers found
      * @dev Important: Returns immediately upon finding first expired staker.
      *      Expired stakers are not automatically removed from the list - they remain until:
      *      1. They are replaced by a new staker (they are given priority for replacement), or
      *      2. They manually unsubscribe
      *      If no expired stakers are found, returns position of the staker with minimum balance.
      * @param user Address to check against the top stakers list
      * @return minPosition Position of either:
      *         - First found expired staker (priority for replacement)
      *         - Minimum balance holder (if no expired stakers)
      * @return minBalance The balance (0 if expired staker found, otherwise minimum balance among active stakers)
      */
    function _findMinStakerAndCheckUser(address user) internal view returns(uint256 minPosition, uint256 minBalance) {
        uint256 len = topStakers.length;
        minBalance = len == 0 ? 0 : type(uint256).max;
        minPosition = 0;

        for (uint256 i = 0; i < len; i++) {
            if (user == topStakers[i]) revert AlreadyInTop();

            LockedBalance memory lock = veToken.locked(topStakers[i]);
            if (lock.end < block.timestamp) {
                return (i, 0);
            }

            uint256 stakerBalance = veToken.balanceOf(topStakers[i]);
            if (stakerBalance < minBalance) {
                minBalance = stakerBalance;
                minPosition = i;
            }
        }
    }

    function subscribe() external checkDelay {
        LockedBalance memory userLock = veToken.locked(msg.sender);
        uint256 amount = veToken.balanceOf(msg.sender);
        if (amount <= minStakingAmount) revert AmountMustBeMoreThatMinimum();
        // Check that lock is not expired
        if (userLock.end < block.timestamp) revert LockExpired();
        // Check that tokens are not delegated to another person
        if (userLock.delegatee != address(0) && userLock.delegatee != msg.sender) revert TokensDelegated();

        uint256 len = topStakers.length;
        if (len < maxTopStakersCount) {
            topStakers.push(msg.sender);

            isStaker[msg.sender] = true;
            emit SubscriptionRequested(msg.sender, amount, block.timestamp);
            return;
        }

        (uint256 minPosition, uint256 minBalance) = _findMinStakerAndCheckUser(msg.sender);
        if (amount > minBalance) {
                emit SubscriptionRemoved(topStakers[minPosition]);
                topStakers[minPosition] = msg.sender;
        }

        isStaker[msg.sender] = true;
        emit SubscriptionRequested(msg.sender, amount, block.timestamp);
    }

    function _unsubscribe(address user) internal {
        if (topStakers.length == 0 || user == address(0)) revert NotStakerFound();

        uint256 len = topStakers.length;
        for (uint256 i = 0; i < len; i++) {
            if (topStakers[i] == user) {
                // Swap the element with the last one and remove the last element
                topStakers[i] = topStakers[len - 1];
                topStakers.pop();
                isStaker[user] = false;
                emit SubscriptionRemoved(user); return;
            }
        }

        revert NotStakerFound();
    }

    function unsubscribe() external checkDelay {
        _unsubscribe(msg.sender);
    }

    /* ==================== VIEW METHODS ==================== */

    function isTopStaker(address staker) external view returns (bool) {
        uint256 len = topStakers.length;
        for (uint256 i = 0; i < len; i++) {
            if (topStakers[i] == staker) return true;
        }
        return false;
    }

    function getTopStakers() external view returns (address[] memory) {
        return topStakers;
    }

    function getTopStakersLength() external view returns (uint256) {
        return topStakers.length;
    }

    function getStakerAt(uint256 index) external view returns (address) {
        if (index >= topStakers.length) revert OutOfBounds();
        return topStakers[index];
    }

    function getTimeUntilNextSubscribe(address user) external view returns (uint256) {
        uint256 nextOperationTime = lastOperationTime[user] + subscriptionDelay;
        if (block.timestamp >= nextOperationTime) {
            return 0;
        }
        return nextOperationTime - block.timestamp;
    }
}

contracts/exchange/contracts/staking/Vot.content:
// SPDX-License-Identifier: AGPL-3.0-or-later
pragma solidity ^0.8.3;

import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { IVotingEscrow } from "../interfaces/IVotingEscrow.sol";
import { IBlocklist } from "../interfaces/IBlocklist.sol";

/// @title  Delegated Voting Escrow
/// @notice An ERC20 token that allocates users a virtual balance depending
/// on the amount of tokens locked and their remaining lock duration. The
/// virtual balance decreases linearly with the remaining lock duration.
/// This is the locking mechanism known from veCRV with additional features:
/// - Delegation of lock and voting power
/// - Quit an existing lock and pay a penalty
/// - Optimistic approval of SmartWallets through Blocklist
/// - Reduced pointHistory array size and, as a result, lifetime of the contract
/// - Removed public deposit_for and Aragon compatibility (no use case)
/// @dev Builds on Curve Finance's original VotingEscrow implementation
/// (see https://github.com/curvefi/curve-dao-contracts/blob/master/contracts/VotingEscrow.vy)
/// and mStable's Solidity translation thereof
/// (see https://github.com/mstable/mStable-contracts/blob/master/contracts/governance/IncentivisedVotingLockup.sol)
/// Usage of this contract is not safe with all tokens, specifically:
/// - Contract does not support tokens with maxSupply>2^128-10^[decimals]
/// - Contract does not support fee-on-transfer tokens
/// - Contract may be unsafe for tokens with decimals<6

//// @audit: We copied this implementation from https://github.com/BarnBridge/veToken
//// while user add or remove delegation
//// and method to add/change address of topStaker (setTopStakerContract)
//// TODO: also we can use implementation from Pendle https://github.com/pendle-finance/pendle-core-v2-public/blob/main/contracts/LiquidityMining/VotingEscrow/VotingEscrowPendleMainchain.sol
//// or https://github.com/chimera-defi/SharedDeposit/blob/main/contracts/governance/voteEscrow.sol
//// or from Cream.finance, Fixed Forex, Iron.finance, Firebird.finance, Pickle.finance
contract VotingEscrow is IVotingEscrow, ReentrancyGuard {
    using SafeERC20 for IERC20;
    // Shared Events
    event Deposit(
        address indexed provider,
        uint256 value,
        uint256 locktime,
        LockAction indexed action,
        uint256 ts
    );
    event Withdraw(
        address indexed provider,
        uint256 value,
        LockAction indexed action,
        uint256 ts
    );
    event TransferOwnership(address indexed owner);
    event UpdateBlocklist(address indexed blocklist);
    event UpdatePenaltyRecipient(address indexed recipient);
    event CollectPenalty(uint256 amount, address indexed recipient);
    event Unlock();

    // Shared global state
    IERC20 public immutable token;
    uint256 public constant WEEK = 7 days;
    uint256 public constant MAXTIME = 730 days;
    uint256 public constant MULTIPLIER = 1e18;
    address public owner;
    address public penaltyRecipient; // receives collected penalty payments
    uint256 public maxPenalty = 1e18; // penalty for quitters with MAXTIME remaining lock
    uint256 public penaltyAccumulated; // accumulated and unwithdrawn penalty payments
    address public blocklist;
    uint256 public supply;

    // Lock state
    uint256 public globalEpoch;
    Point[1000000000000000000] public pointHistory; // 1e9 * userPointHistory-length, so sufficient for 1e9 users
    mapping(address => Point[1000000000]) public userPointHistory;
    mapping(address => uint256) public userPointEpoch;
    mapping(uint256 => int128) public slopeChanges;
    mapping(address => LockedBalance) public locked;

    // Voting token
    string public name;
    string public symbol;
    uint256 public decimals;

    // Structs
    struct Point {
        int128 bias;
        int128 slope;
        uint256 ts;
        uint256 blk;
    }
    struct LockedBalance {
        int128 amount;
        int128 delegated;
        uint96 end;
        address delegatee;
    }

    // Miscellaneous
    enum LockAction {
        CREATE,
        INCREASE_AMOUNT,
        INCREASE_AMOUNT_AND_DELEGATION,
        INCREASE_TIME,
        WITHDRAW,
        QUIT,
        DELEGATE,
        UNDELEGATE
    }

    /// @notice Initializes state
    /// @param _owner Is assumed to be a timelock contract
    /// @param _penaltyRecipient The recipient of penalty paid by lock quitters
    /// @param _token The token locked in order to obtain voting power
    /// @param _name The name of the voting token
    /// @param _symbol The symbol of the voting token
    constructor(
        address _owner,
        address _penaltyRecipient,
        address _token,
        string memory _name,
        string memory _symbol
    ) {
        token = IERC20(_token);
        pointHistory[0] = Point({
            bias: int128(0),
            slope: int128(0),
            ts: block.timestamp,
            blk: block.number
        });

        decimals = IERC20Metadata(_token).decimals();
        require(decimals <= 18, "Exceeds max decimals");

        name = _name;
        symbol = _symbol;
        owner = _owner;
        penaltyRecipient = _penaltyRecipient;
    }

    modifier checkBlocklist() {
        require(
            !IBlocklist(blocklist).isBlocked(msg.sender),
            "Blocked contract"
        );
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~~ ///
    ///       Owner Functions       ///
    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~~ ///

    /// @notice Transfers ownership to a new owner
    /// @param _addr The new owner
    /// @dev Owner is assumed to be a timelock contract
    function transferOwnership(address _addr) external onlyOwner {
        owner = _addr;
        emit TransferOwnership(_addr);
    }

    /// @notice Updates the blocklist contract
    function updateBlocklist(address _addr) external onlyOwner {
        blocklist = _addr;
        emit UpdateBlocklist(_addr);
    }

    /// @notice Updates the recipient of the accumulated penalty paid by quitters
    function updatePenaltyRecipient(address _addr) external onlyOwner {
        penaltyRecipient = _addr;
        emit UpdatePenaltyRecipient(_addr);
    }

    /// @notice Removes quitlock penalty by setting it to zero
    /// @dev This is an irreversible action and is assumed to be used in
    /// a migration to a new VotingEscrow contract only
    function unlock() external onlyOwner {
        maxPenalty = 0;
        emit Unlock();
    }

    /// @notice Remove delegation for blocked contract
    /// @param _addr user to which voting power is delegated
    /// @dev Only callable by the blocklist contract
    function forceUndelegate(address _addr) external override {
        require(msg.sender == blocklist, "Only Blocklist");
        LockedBalance memory locked_ = locked[_addr];
        address delegatee = locked_.delegatee;
        int128 value = locked_.amount;

        if (delegatee != _addr && value > 0) {
            LockedBalance memory fromLocked;
            locked_.delegatee = _addr;
            fromLocked = locked[delegatee];
            locked_.end = fromLocked.end;
            _delegate(delegatee, fromLocked, value, LockAction.UNDELEGATE);
            _delegate(_addr, locked_, value, LockAction.DELEGATE);
        }
    }

    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~~ ///
    ///       LOCK MANAGEMENT       ///
    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~~ ///

    /// @notice Returns a lock's expiration
    /// @param _addr The address of the lock owner
    /// @return Expiration of the lock
    function lockEnd(address _addr) external view returns (uint256) {
        return locked[_addr].end;
    }

    /// @notice Returns a lock's last available user point
    /// @param _addr The address of the lock owner
    /// @return bias The last recorded virtual balance
    /// @return slope The last recorded linear decay
    /// @return ts The last recorded timestamp
    function getLastUserPoint(address _addr)
    external
    view
    returns (
        int128 bias,
        int128 slope,
        uint256 ts
    )
    {
        uint256 uepoch = userPointEpoch[_addr];
        if (uepoch == 0) {
            return (0, 0, 0);
        }
        Point memory point = userPointHistory[_addr][uepoch];
        return (point.bias, point.slope, point.ts);
    }

    /// @notice Records a checkpoint of both individual and global slope
    /// @param _addr The address of the lock owner, or address(0) for only global
    /// @param _oldLocked Old amount that user had locked, or null for global
    /// @param _newLocked New amount that user has locked, or null for global
    function _checkpoint(
        address _addr,
        LockedBalance memory _oldLocked,
        LockedBalance memory _newLocked
    ) internal {
        Point memory userOldPoint;
        Point memory userNewPoint;
        int128 oldSlopeDelta = 0;
        int128 newSlopeDelta = 0;
        uint256 epoch = globalEpoch;

        if (_addr != address(0)) {
            // Calculate slopes and biases
            // Kept at zero when they have to
            // Casting in the next blocks is safe given that MAXTIME is a small
            // positive number and we check for _oldLocked.end>block.timestamp
            // and _newLocked.end>block.timestamp
            if (_oldLocked.end > block.timestamp && _oldLocked.delegated > 0) {
                userOldPoint.slope =
                    _oldLocked.delegated /
                    int128(int256(MAXTIME));
                userOldPoint.bias =
                    userOldPoint.slope *
                    int128(int256(_oldLocked.end - block.timestamp));
            }
            if (_newLocked.end > block.timestamp && _newLocked.delegated > 0) {
                userNewPoint.slope =
                    _newLocked.delegated /
                    int128(int256(MAXTIME));
                userNewPoint.bias =
                    userNewPoint.slope *
                    int128(int256(_newLocked.end - block.timestamp));
            }

            // Moved from bottom final if statement to resolve stack too deep err
            // start {
            // Now handle user history
            uint256 uEpoch = userPointEpoch[_addr];

            userPointEpoch[_addr] = uEpoch + 1;
            userNewPoint.ts = block.timestamp;
            userNewPoint.blk = block.number;
            userPointHistory[_addr][uEpoch + 1] = userNewPoint;

            // } end

            // Read values of scheduled changes in the slope
            // oldLocked.end can be in the past and in the future
            // newLocked.end can ONLY by in the FUTURE unless everything expired: than zeros
            oldSlopeDelta = slopeChanges[_oldLocked.end];
            if (_newLocked.end != 0) {
                if (_newLocked.end == _oldLocked.end) {
                    newSlopeDelta = oldSlopeDelta;
                } else {
                    newSlopeDelta = slopeChanges[_newLocked.end];
                }
            }
        }

        Point memory lastPoint =
                        Point({
                bias: 0,
                slope: 0,
                ts: block.timestamp,
                blk: block.number
            });
        if (epoch > 0) {
            lastPoint = pointHistory[epoch];
        }
        uint256 lastCheckpoint = lastPoint.ts;

        // initialLastPoint is used for extrapolation to calculate block number
        // (approximately, for *At methods) and save them
        // as we cannot figure that out exactly from inside the contract
        Point memory initialLastPoint =
                        Point({ bias: 0, slope: 0, ts: lastPoint.ts, blk: lastPoint.blk });
        uint256 blockSlope = 0; // dblock/dt
        if (block.timestamp > lastPoint.ts) {
            blockSlope =
                (MULTIPLIER * (block.number - lastPoint.blk)) /
                (block.timestamp - lastPoint.ts);
        }
        // If last point is already recorded in this block, slope=0
        // But that's ok b/c we know the block in such case

        // Go over weeks to fill history and calculate what the current point is
        uint256 iterativeTime = _floorToWeek(lastCheckpoint);
        for (uint256 i; i < 255; ) {
            // Hopefully it won't happen that this won't get used in 5 years!
            // If it does, users will be able to withdraw but vote weight will be broken
            iterativeTime = iterativeTime + WEEK;
            int128 dSlope = 0;
            if (iterativeTime > block.timestamp) {
                iterativeTime = block.timestamp;
            } else {
                dSlope = slopeChanges[iterativeTime];
            }
            int128 biasDelta =
                lastPoint.slope *
                int128(int256((iterativeTime - lastCheckpoint)));
            lastPoint.bias = lastPoint.bias - biasDelta;
            lastPoint.slope = lastPoint.slope + dSlope;
            // This can happen
            if (lastPoint.bias < 0) {
                lastPoint.bias = 0;
            }
            // This cannot happen - just in case
            if (lastPoint.slope < 0) {
                lastPoint.slope = 0;
            }
            lastCheckpoint = iterativeTime;
            lastPoint.ts = iterativeTime;
            lastPoint.blk =
                initialLastPoint.blk +
                (blockSlope * (iterativeTime - initialLastPoint.ts)) /
                MULTIPLIER;

            // when epoch is incremented, we either push here or after slopes updated below
            epoch = epoch + 1;
            if (iterativeTime == block.timestamp) {
                lastPoint.blk = block.number;
                break;
            } else {
                pointHistory[epoch] = lastPoint;
            }
            unchecked { ++i; }
        }

        globalEpoch = epoch;
        // Now pointHistory is filled until t=now

        if (_addr != address(0)) {
            // If last point was in this block, the slope change has been applied already
            // But in such case we have 0 slope(s)
            lastPoint.slope =
                lastPoint.slope +
                userNewPoint.slope -
                userOldPoint.slope;
            lastPoint.bias =
                lastPoint.bias +
                userNewPoint.bias -
                userOldPoint.bias;
            if (lastPoint.slope < 0) {
                lastPoint.slope = 0;
            }
            if (lastPoint.bias < 0) {
                lastPoint.bias = 0;
            }
        }

        // Record the changed point into history
        pointHistory[epoch] = lastPoint;

        if (_addr != address(0)) {
            // Schedule the slope changes (slope is going down)
            // We subtract new_user_slope from [new_locked.end]
            // and add old_user_slope to [old_locked.end]
            if (_oldLocked.end > block.timestamp) {
                // oldSlopeDelta was <something> - userOldPoint.slope, so we cancel that
                oldSlopeDelta = oldSlopeDelta + userOldPoint.slope;
                if (_newLocked.end == _oldLocked.end) {
                    oldSlopeDelta = oldSlopeDelta - userNewPoint.slope; // It was a new deposit, not extension
                }
                slopeChanges[_oldLocked.end] = oldSlopeDelta;
            }
            if (_newLocked.end > block.timestamp) {
                if (_newLocked.end > _oldLocked.end) {
                    newSlopeDelta = newSlopeDelta - userNewPoint.slope; // old slope disappeared at this point
                    slopeChanges[_newLocked.end] = newSlopeDelta;
                }
                // else: we recorded it already in oldSlopeDelta
            }
        }
    }

    /// @notice Records a new global checkpoint
    function checkpoint() external {
        LockedBalance memory empty;
        _checkpoint(address(0), empty, empty);
    }

    /// @notice Creates a new lock
    /// @param _value Amount of token to lock
    /// @param _unlockTime Expiration time of the lock
    /// @dev `_value` is (unsafely) downcasted from `uint256` to `int128`
    /// and `_unlockTime` is (unsafely) downcasted from `uint256` to `uint96`
    /// assuming that the values never reach the respective max values
    function createLock(uint256 _value, uint256 _unlockTime)
    external
    override
    nonReentrant
    checkBlocklist
    {
        uint256 unlock_time = _floorToWeek(_unlockTime); // Locktime is rounded down to weeks
        LockedBalance memory locked_ = locked[msg.sender];
        // Validate inputs
        require(_value != 0, "Only non zero amount");
        require(locked_.amount == 0, "Lock exists");
        require(unlock_time >= locked_.end, "Only increase lock end"); // from using quitLock, user should increaseAmount instead
        require(unlock_time > block.timestamp, "Only future lock end");
        require(unlock_time <= block.timestamp + MAXTIME, "Exceeds maxtime");
        // Update total supply of token deposited
        supply = supply + _value;
        // Update lock and voting power (checkpoint)
        // Casting in the next block is safe given that we check for _value>0 and the
        // totalSupply of tokens is generally significantly lower than the int128.max
        // value (considering the max precision of 18 decimals enforced in the constructor)
        locked_.amount = locked_.amount + int128(int256(_value));
        locked_.end = uint96(unlock_time);
        locked_.delegated = locked_.delegated + int128(int256(_value));
        locked_.delegatee = msg.sender;
        locked[msg.sender] = locked_;
        _checkpoint(msg.sender, LockedBalance(0, 0, 0, address(0)), locked_);
        // Deposit locked tokens
        token.safeTransferFrom(msg.sender, address(this), _value);
        emit Deposit(
            msg.sender,
            _value,
            unlock_time,
            LockAction.CREATE,
            block.timestamp
        );
    }

    /// @notice Locks more tokens in an existing lock
    /// @param _value Amount of tokens to add to the lock
    /// @dev Does not update the lock's expiration
    /// Does record a new checkpoint for the lock
    /// `_value` is (unsafely) downcasted from `uint256` to `int128` assuming
    /// that the max value is never reached in practice
    function increaseAmount(uint256 _value)
    external
    override
    nonReentrant
    checkBlocklist
    {
        LockedBalance memory locked_ = locked[msg.sender];
        // Validate inputs
        require(_value != 0, "Only non zero amount");
        require(locked_.amount > 0, "No lock");
        require(locked_.end > block.timestamp, "Lock expired");
        // Update total supply of token deposited
        supply = supply + _value;
        // Update lock
        address delegatee = locked_.delegatee;
        uint256 unlockTime = locked_.end;
        LockAction action = LockAction.INCREASE_AMOUNT;
        LockedBalance memory newLocked;
        // Casting in the next block is safe given that we check for _value>0 and the
        // totalSupply of tokens is generally significantly lower than the int128.max
        // value (considering the max precision of 18 decimals enforced in the constructor)
        if (delegatee == msg.sender) {
            // Undelegated lock
            action = LockAction.INCREASE_AMOUNT_AND_DELEGATION;
            newLocked = _copyLock(locked_);
            newLocked.amount = newLocked.amount + int128(int256(_value));
            newLocked.delegated = newLocked.delegated + int128(int256(_value));
            locked[msg.sender] = newLocked;
        } else {
            // Delegated lock, update sender's lock first
            locked_.amount = locked_.amount + int128(int256(_value));
            locked[msg.sender] = locked_;
            // Then, update delegatee's lock and voting power (checkpoint)
            locked_ = locked[delegatee];
            require(locked_.amount > 0, "Delegatee has no lock");
            require(locked_.end > block.timestamp, "Delegatee lock expired");
            newLocked = _copyLock(locked_);
            newLocked.delegated = newLocked.delegated + int128(int256(_value));
            locked[delegatee] = newLocked;
            emit Deposit(
                delegatee,
                _value,
                newLocked.end,
                LockAction.DELEGATE,
                block.timestamp
            );
        }
        // Checkpoint only for delegatee
        _checkpoint(delegatee, locked_, newLocked);
        // Deposit locked tokens
        token.safeTransferFrom(msg.sender, address(this), _value);
        emit Deposit(msg.sender, _value, unlockTime, action, block.timestamp);
    }

    /// @notice Extends the expiration of an existing lock
    /// @param _unlockTime New lock expiration time
    /// @dev Does not update the amount of tokens locked
    /// Does record a new checkpoint for the lock
    /// `_unlockTime` is (unsafely) downcasted from `uint256` to `uint96`
    /// assuming that the max value is never reached in practice
    function increaseUnlockTime(uint256 _unlockTime)
    external
    override
    nonReentrant
    checkBlocklist
    {
        LockedBalance memory locked_ = locked[msg.sender];
        uint256 unlock_time = _floorToWeek(_unlockTime); // Locktime is rounded down to weeks
        // Validate inputs
        require(locked_.amount > 0, "No lock");
        require(locked_.end > block.timestamp, "Lock expired");
        require(unlock_time > locked_.end, "Only increase lock end");
        require(unlock_time <= block.timestamp + MAXTIME, "Exceeds maxtime");
        // Update lock
        uint256 oldUnlockTime = locked_.end;
        locked_.end = uint96(unlock_time);
        locked[msg.sender] = locked_;
        if (locked_.delegated > 0) {
            // Lock with non-zero virtual balance
            LockedBalance memory oldLocked = _copyLock(locked_);
            oldLocked.end = uint96(oldUnlockTime);
            _checkpoint(msg.sender, oldLocked, locked_);
        }
        emit Deposit(
            msg.sender,
            0,
            unlock_time,
            LockAction.INCREASE_TIME,
            block.timestamp
        );
    }

    /// @notice Withdraws the tokens of an expired lock
    /// Delegated locks need to be undelegated first
    function withdraw() external override nonReentrant {
        LockedBalance memory locked_ = locked[msg.sender];
        // Validate inputs
        require(locked_.amount > 0, "No lock");
        require(locked_.end <= block.timestamp, "Lock not expired");
        require(locked_.delegatee == msg.sender, "Lock delegated");
        // Update total supply of token deposited
        uint256 value = uint256(uint128(locked_.amount));
        supply = supply - value;
        // Update lock
        LockedBalance memory newLocked = _copyLock(locked_);
        newLocked.amount = 0;
        newLocked.end = 0;
        newLocked.delegated = newLocked.delegated - locked_.amount;
        newLocked.delegatee = address(0);
        locked[msg.sender] = newLocked;
        newLocked.delegated = 0;
        // oldLocked can have either expired <= timestamp or zero end
        // currentLock has only 0 end
        // Both can have >= 0 amount
        _checkpoint(msg.sender, locked_, newLocked);
        // Send back deposited tokens
        token.safeTransfer(msg.sender, value);
        emit Withdraw(msg.sender, value, LockAction.WITHDRAW, block.timestamp);
    }

    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~ ///
    ///         DELEGATION         ///
    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~ ///

    /// @notice Delegate lock and voting power to another lock
    /// The receiving lock needs to have a longer lock duration
    /// The delegated lock will inherit the receiving lock's expiration
    /// @param _addr The address of the lock owner to which to delegate
    function delegate(address _addr)
    external
    override
    nonReentrant
    checkBlocklist
    {
        LockedBalance memory locked_ = locked[msg.sender];

        // Different restrictions apply to undelegation
        if (_addr == msg.sender) {
            _undelegate();
            return;
        }
        // Validate inputs
        require(!IBlocklist(blocklist).isBlocked(_addr), "Blocked contract");
        require(locked_.amount > 0, "No lock");
        require(locked_.end > block.timestamp, "Lock expired");
        require(locked_.delegatee != _addr, "Already delegated");
        // Update locks
        int128 value = locked_.amount;
        address delegatee = locked_.delegatee;
        LockedBalance memory toLocked = locked[_addr];
        locked_.delegatee = _addr;
        if (delegatee != msg.sender) {
            locked[msg.sender] = locked_;
            locked_ = locked[delegatee];
        }
        require(toLocked.amount > 0, "Delegatee has no lock");
        require(toLocked.end > block.timestamp, "Delegatee lock expired");
        require(toLocked.end >= locked_.end, "Only delegate to longer lock");
        _delegate(delegatee, locked_, value, LockAction.UNDELEGATE);
        _delegate(_addr, toLocked, value, LockAction.DELEGATE);
    }

    // Undelegates sender's lock
    // Can be executed on expired locks too
    // Owner inherits delegatee's unlockTime if it exceeds owner's
    function _undelegate() internal {
        LockedBalance memory locked_ = locked[msg.sender];
        // Validate inputs
        require(locked_.amount > 0, "No lock");
        require(locked_.delegatee != msg.sender, "Already undelegated");
        // Update locks
        int128 value = locked_.amount;
        address delegatee = locked_.delegatee;
        LockedBalance memory fromLocked = locked[delegatee];
        locked_.delegatee = msg.sender;
        if (locked_.end < fromLocked.end) {
            locked_.end = fromLocked.end;
        }
        _delegate(delegatee, fromLocked, value, LockAction.UNDELEGATE);
        _delegate(msg.sender, locked_, value, LockAction.DELEGATE);
    }

    // Delegates from/to lock and voting power
    function _delegate(
        address addr,
        LockedBalance memory _locked,
        int128 value,
        LockAction action
    ) internal {
        LockedBalance memory newLocked = _copyLock(_locked);
        if (action == LockAction.DELEGATE) {
            newLocked.delegated = newLocked.delegated + value;
            emit Deposit(
                addr,
                uint256(int256(value)),
                newLocked.end,
                action,
                block.timestamp
            );
        } else {
            newLocked.delegated = newLocked.delegated - value;
            emit Withdraw(
                addr,
                uint256(int256(value)),
                action,
                block.timestamp
            );
        }
        locked[addr] = newLocked;
        if (newLocked.amount > 0) {
            // Only if lock (from lock) hasn't been withdrawn/quitted
            _checkpoint(addr, _locked, newLocked);
        }
    }

    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~ ///
    ///         QUIT LOCK          ///
    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~ ///

    /// @notice Quit an existing lock by withdrawing all tokens less a penalty
    /// Use `withdraw` for expired locks
    /// @dev Quitters lock expiration remains in place because it might be delegated to
    function quitLock() external override nonReentrant {
        LockedBalance memory locked_ = locked[msg.sender];
        // Validate inputs
        require(locked_.amount > 0, "No lock");
        require(locked_.end > block.timestamp, "Lock expired");
        require(locked_.delegatee == msg.sender, "Lock delegated");
        // Update total supply of token deposited
        uint256 value = uint256(uint128(locked_.amount));
        supply = supply - value;
        // Update lock
        LockedBalance memory newLocked = _copyLock(locked_);
        newLocked.amount = 0;
        newLocked.delegated = newLocked.delegated - locked_.amount;
        newLocked.delegatee = address(0);
        locked[msg.sender] = newLocked;
        newLocked.end = 0;
        newLocked.delegated = 0;
        // oldLocked can have either expired <= timestamp or zero end
        // currentLock has only 0 end
        // Both can have >= 0 amount
        _checkpoint(msg.sender, locked_, newLocked);
        // apply penalty
        uint256 penaltyRate = _calculatePenaltyRate(locked_.end);
        uint256 penaltyAmount = (value * penaltyRate) / 1e18; // quitlock_penalty is in 18 decimals precision
        penaltyAccumulated = penaltyAccumulated + penaltyAmount;
        uint256 remainingAmount = value - penaltyAmount;
        // Send back remaining tokens
        token.safeTransfer(msg.sender, remainingAmount);
        emit Withdraw(msg.sender, value, LockAction.QUIT, block.timestamp);
    }

    /// @notice Returns the penalty rate for a given lock expiration
    /// @param end The lock's expiration
    /// @return The penalty rate applicable to the lock
    /// @dev The penalty rate decreases linearly at the same rate as a lock's voting power
    /// in order to compensate for votes unlocked without committing to the lock expiration
    function getPenaltyRate(uint256 end) external view returns (uint256) {
        return _calculatePenaltyRate(end);
    }

    // Calculate penalty rate
    // Penalty rate decreases linearly at the same rate as a lock's voting power
    // in order to compensate for votes used
    function _calculatePenaltyRate(uint256 end)
    internal
    view
    returns (uint256)
    {
        // We know that end > block.timestamp because expired locks cannot be quitted
        return ((end - block.timestamp) * maxPenalty) / MAXTIME;
    }

    /// @notice Collect accumulated penalty from lock quitters
    /// Everyone can collect but penalty is sent to `penaltyRecipient`
    function collectPenalty() external {
        uint256 amount = penaltyAccumulated;
        penaltyAccumulated = 0;
        address recipient = penaltyRecipient;
        token.safeTransfer(recipient, amount);
        emit CollectPenalty(amount, recipient);
    }

    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~ ///
    ///            GETTERS         ///
    /// ~~~~~~~~~~~~~~~~~~~~~~~~~~ ///

    // Creates a copy of a lock
    function _copyLock(LockedBalance memory _locked)
    internal
    pure
    returns (LockedBalance memory)
    {
        return
            LockedBalance({
            amount: _locked.amount,
            end: _locked.end,
            delegatee: _locked.delegatee,
            delegated: _locked.delegated
        });
    }

    // Floors a timestamp to the nearest weekly increment
    function _floorToWeek(uint256 _t) internal pure returns (uint256) {
        return (_t / WEEK) * WEEK;
    }

    // Uses binarysearch to find the most recent point history preceeding block
    // Find the most recent point history before _block
    // Do not search pointHistories past _maxEpoch
    function _findBlockEpoch(uint256 _block, uint256 _maxEpoch)
    internal
    view
    returns (uint256)
    {
        // Binary search
        uint256 min = 0;
        uint256 max = _maxEpoch;
        // Will be always enough for 128-bit numbers
        for (uint256 i; i < 128; ) {
            if (min >= max) break;
            uint256 mid = (min + max + 1) / 2;
            if (pointHistory[mid].blk <= _block) {
                min = mid;
            } else {
                max = mid - 1;
            }
            unchecked { ++i; }
        }
        return min;
    }

    // Uses binarysearch to find the most recent user point history preceeding block
    // _addr is the lock owner for which to search
    // Find the most recent point history before _block
    function _findUserBlockEpoch(address _addr, uint256 _block)
    internal
    view
    returns (uint256)
    {
        uint256 min = 0;
        uint256 max = userPointEpoch[_addr];
        for (uint256 i; i < 128; ) {
            if (min >= max) {
                break;
            }
            uint256 mid = (min + max + 1) / 2;
            if (userPointHistory[_addr][mid].blk <= _block) {
                min = mid;
            } else {
                max = mid - 1;
            }
            unchecked { ++i; }
        }
        return min;
    }

    /// @notice Get a lock's current voting power
    /// @param _owner The address of the lock owner for which to return voting power
    /// @return Voting power of the lock
    function balanceOf(address _owner) public view override returns (uint256) {
        uint256 epoch = userPointEpoch[_owner];
        if (epoch == 0) {
            return 0;
        }
        // Casting is safe given that checkpoints are recorded in the past
        // and are more frequent than every int128.max seconds
        Point memory lastPoint = userPointHistory[_owner][epoch];
        lastPoint.bias =
            lastPoint.bias -
            (lastPoint.slope * int128(int256(block.timestamp - lastPoint.ts)));
        if (lastPoint.bias < 0) {
            lastPoint.bias = 0;
        }
        return uint256(uint128(lastPoint.bias));
    }

    /// @notice Get a lock's voting power at a given block number
    /// @param _owner The address of the lock owner for which to return voting power
    /// @param _blockNumber The block at which to calculate the lock's voting power
    /// @return uint256 Voting power of the lock
    function balanceOfAt(address _owner, uint256 _blockNumber)
    public
    view
    override
    returns (uint256)
    {
        require(_blockNumber <= block.number, "Only past block number");

        // Get most recent user Point to block
        uint256 userEpoch = _findUserBlockEpoch(_owner, _blockNumber);
        if (userEpoch == 0) {
            return 0;
        }
        Point memory upoint = userPointHistory[_owner][userEpoch];

        // Get most recent global Point to block
        uint256 maxEpoch = globalEpoch;
        uint256 epoch = _findBlockEpoch(_blockNumber, maxEpoch);
        Point memory point0 = pointHistory[epoch];

        // Calculate delta (block & time) between user Point and target block
        // Allowing us to calculate the average seconds per block between
        // the two points
        uint256 dBlock = 0;
        uint256 dTime = 0;
        if (epoch < maxEpoch) {
            Point memory point1 = pointHistory[epoch + 1];
            dBlock = point1.blk - point0.blk;
            dTime = point1.ts - point0.ts;
        } else {
            dBlock = block.number - point0.blk;
            dTime = block.timestamp - point0.ts;
        }
        // (Deterministically) Estimate the time at which block _blockNumber was mined
        uint256 blockTime = point0.ts;
        if (dBlock != 0) {
            blockTime =
                blockTime +
                ((dTime * (_blockNumber - point0.blk)) / dBlock);
        }
        // Current Bias = most recent bias - (slope * time since update)
        // Casting is safe given that checkpoints are recorded in the past
        // and are more frequent than every int128.max seconds
        upoint.bias =
            upoint.bias -
            (upoint.slope * int128(int256(blockTime - upoint.ts)));
        if (upoint.bias >= 0) {
            return uint256(uint128(upoint.bias));
        } else {
            return 0;
        }
    }

    // Calculate total supply of voting power at a given time _t
    // _point is the most recent point before time _t
    // _t is the time at which to calculate supply
    function _supplyAt(Point memory _point, uint256 _t)
    internal
    view
    returns (uint256)
    {
        Point memory lastPoint = _point;
        // Floor the timestamp to weekly interval
        uint256 iterativeTime = _floorToWeek(lastPoint.ts);
        // Iterate through all weeks between _point & _t to account for slope changes
        for (uint256 i; i < 255; ) {
            iterativeTime = iterativeTime + WEEK;
            int128 dSlope = 0;
            // If week end is after timestamp, then truncate & leave dSlope to 0
            if (iterativeTime > _t) {
                iterativeTime = _t;
            }
            // else get most recent slope change
            else {
                dSlope = slopeChanges[iterativeTime];
            }

            // Casting is safe given that lastPoint.ts < iterativeTime and
            // iteration goes over 255 weeks max
            lastPoint.bias =
                lastPoint.bias -
                (lastPoint.slope *
                    int128(int256(iterativeTime - lastPoint.ts)));
            if (iterativeTime == _t) {
                break;
            }
            lastPoint.slope = lastPoint.slope + dSlope;
            lastPoint.ts = iterativeTime;

            unchecked { ++i; }
        }

        if (lastPoint.bias < 0) {
            lastPoint.bias = 0;
        }
        return uint256(uint128(lastPoint.bias));
    }

    /// @notice Calculate current total supply of voting power
    /// @return Current totalSupply
    function totalSupply() public view override returns (uint256) {
        uint256 epoch_ = globalEpoch;
        Point memory lastPoint = pointHistory[epoch_];
        return _supplyAt(lastPoint, block.timestamp);
    }

    /// @notice Calculate total supply of voting power at a given block number
    /// @param _blockNumber The block number at which to calculate total supply
    /// @return totalSupply of voting power at the given block number
    function totalSupplyAt(uint256 _blockNumber)
    public
    view
    override
    returns (uint256)
    {
        require(_blockNumber <= block.number, "Only past block number");

        uint256 epoch = globalEpoch;
        uint256 targetEpoch = _findBlockEpoch(_blockNumber, epoch);

        Point memory point = pointHistory[targetEpoch];

        // If point.blk > _blockNumber that means we got the initial epoch & contract did not yet exist
        if (point.blk > _blockNumber) {
            return 0;
        }

        uint256 dTime = 0;
        if (targetEpoch < epoch) {
            Point memory pointNext = pointHistory[targetEpoch + 1];
            if (point.blk != pointNext.blk) {
                dTime =
                    ((_blockNumber - point.blk) * (pointNext.ts - point.ts)) /
                    (pointNext.blk - point.blk);
            }
        } else if (point.blk != block.number) {
            dTime =
                ((_blockNumber - point.blk) * (block.timestamp - point.ts)) /
                (block.number - point.blk);
        }
        // Now dTime contains info on how far are we beyond point
        return _supplyAt(point, point.ts + dTime);
    }
}

contracts/gain-erc20/GainErc20v3Upgradea.content:
// SPDX-License-Identifier: MIT
// OpenZeppelin Contracts (last updated v4.5.0) (token/ERC20/ERC20.sol)

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeCastUpgradeable.sol";
import "../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";
import "../interfaces/IGainableERC20v3.sol";

contract GainERC20v3Upgradeable is ContextUpgradeable, SuAuthenticated, IGainableERC20v3 {
    using SafeCastUpgradeable for uint256;
    using SafeCastUpgradeable for uint128;
    using SafeCastUpgradeable for uint64;
    using SafeCastUpgradeable for int256;

    uint256 private constant ACC_REWARD_TOKEN_PRECISION = 1e12; // Constant precision factor
    uint256 public constant REWARD_PRECISION = 1e14; // Minimum reward unit, so min reward is 1e-4 unit cents
    uint128 public accRewardPerShare; // Reward-related data: accumulated rewards per share, same as in MasterChef
    uint64 public lastRewardBlock; // Block number of last block when reward was distributed

    mapping(address => IAccount) private _accounts; // Stores user balances and reward debts.
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply; // Total supply of the token

    string private _name;
    string private _symbol;

    uint256 public rewardPerBlock; // Amount of tokens distributed per block.
    uint256 public rewardEndBlock; // Block number when rewards end.

    uint256 private _blocksPerMinute; // Default value, can be changed by setter

    function setBlocksPerMinute(uint256 blocksPerMinute_) external onlyAdmin {
        require(blocksPerMinute_ > 0, "blocksPerMinute must be positive");
        _blocksPerMinute = blocksPerMinute_;
    }

    function getBlocksPerMinute() external view returns (uint256) {
        return _blocksPerMinute;
    }

    function refillReward(uint256 amount, uint64 endBlock) public onlyRewardOrAdminAccess {
        if (block.number >= endBlock) revert BadEndBlock();
        updateRewardPool();

        _transfer(msg.sender, address(this), amount, true);

        uint256 rewardLeftAmount;
        // if there are active rewards leftovers and we call refillReward before old endBlock ends
        if (rewardEndBlock > 0 && block.number < rewardEndBlock) {
            rewardLeftAmount = rewardPerBlock * (rewardEndBlock - block.number);
        }

        rewardPerBlock = (rewardLeftAmount + amount) / (endBlock - block.number);
        if (rewardPerBlock == 0) revert InsufficientRewardAmount();

        rewardEndBlock = endBlock;
    }

    /**
     * @dev Calculates total amount of rewards allocated to the all pools on the rage (startBlock, endBlock]
     * i.e. excluding startBlock but including endBlock
     * @return The total amount of rewards allocated since the last update
     */
    function getTotalRewardsSinceLastUpdate() internal view returns (uint256) {
        uint256 blockNumber = block.number;
        if (blockNumber <= lastRewardBlock || rewardEndBlock == 0) {
            return 0;
        }

        if (rewardEndBlock <= lastRewardBlock) {
            return 0;
        }

        return blockNumber < rewardEndBlock
            ? (blockNumber - lastRewardBlock) * rewardPerBlock
            : (rewardEndBlock - lastRewardBlock) * rewardPerBlock;
    }

    function updateRewardPool() public {
        uint64 blockNumber = block.number.toUint64();
        if (blockNumber > lastRewardBlock) {
            if (_totalSupply > 0) {
                accRewardPerShare = accRewardPerShare +
                            (getTotalRewardsSinceLastUpdate() * ACC_REWARD_TOKEN_PRECISION / _totalSupply).toUint128();
            }
            lastRewardBlock = blockNumber;
        }
    }

    function pendingRewards(address account) public view returns (uint256) {
        uint256 currentAccRewardPerShare = accRewardPerShare;
        if (block.number > lastRewardBlock && _totalSupply > 0) {
            currentAccRewardPerShare = currentAccRewardPerShare +
                (getTotalRewardsSinceLastUpdate() * ACC_REWARD_TOKEN_PRECISION / _totalSupply);
        }

        // simulate harvest()
        IAccount memory user = _accounts[account];
        int256 accountReward = (user.balance * currentAccRewardPerShare / ACC_REWARD_TOKEN_PRECISION).toInt256();
        uint256 pending = (accountReward - user.rewardDebt).toUint256();

        // Number prettier
        // on metamask the balanceOf sometimes gives too many digits after the comma
        // so balance looks ugly, for example: 12507.259801909711051452 StablePro
        // Since in any known fiat currency sub-cent values are essentially worthless, let's round them up if original
        // balance doesn't have many decimals - any number of additions of rounded values doesn't increase it
        return (pending / REWARD_PRECISION) * REWARD_PRECISION;
    }

    function harvest(address account) public {
        IAccount storage user = _accounts[account];
        updateRewardPool();
        int256 accRewards = (user.balance * accRewardPerShare / ACC_REWARD_TOKEN_PRECISION).toInt256();
        uint256 _pendingRewards = (accRewards - user.rewardDebt).toUint256();
        _pendingRewards = (_pendingRewards / REWARD_PRECISION) * REWARD_PRECISION;

        // Effects
        user.rewardDebt = accRewards;

        // Interactions
        if (_pendingRewards != 0 && account != address(this)) {
            if (user.redirectYieldTo == address(0)) {
                // to reduce amount of useless harvests
                _transfer(address(this), account, _pendingRewards, false);
            } else {
                _transfer(address(this), user.redirectYieldTo, _pendingRewards, true);
            }
        }
    }

    function redirectYieldAndHarvest(address to) public {
        IAccount storage user = _accounts[msg.sender];

        harvest(msg.sender);
        if (to != address(0)) {
            harvest(to);
        }

        user.redirectYieldTo = to;
    }

    function cancelRedirectYieldAndHarvest() external {
        redirectYieldAndHarvest(address(0));
    }

    function redirectYieldAndHarvestByDAO(address from, address to) external onlyDAO {
        if (_accounts[from].balance == 0) revert ZeroFromBalance();
        harvest(from);
        if (to != address(0)) {
            harvest(to);
        }
        _accounts[from].redirectYieldTo = to;
    }

    function getYieldAPR() external view returns (uint256) {
        if (block.number > rewardEndBlock) {
            return 0;
        }
        uint256 blocksPerYear = _blocksPerMinute * 60 * 24 * 365;

        return (rewardPerBlock * blocksPerYear * 1e18) / _totalSupply;
    }

    function getAccount(address account) external view returns (IAccount memory) {
        return _accounts[account];
    }

    //===================================================================
    // We need to disable custom-errors rule to behave like standard ERC20 token
    /* solhint-disable custom-errors */

    // solhint-disable-next-line func-name-mixedcase
    function __ERC20_init(address authControl_, string memory name_, string memory symbol_) internal onlyInitializing {
        __ERC20_init_unchained(authControl_, name_, symbol_);
    }

    // solhint-disable-next-line func-name-mixedcase
    function __ERC20_init_unchained(
        address authControl_,
        string memory name_,
        string memory symbol_
    ) internal onlyInitializing {
        _name = name_;
        _symbol = symbol_;
        _blocksPerMinute = 5;
        __suAuthenticatedInit(authControl_);
    }

    // TODO: add function to change name and symbol for onlyDAO
    function name() public view virtual returns (string memory) {
        // return _name;
        return "StableUnit";
    }

    function symbol() public view virtual returns (string memory) {
        // return _symbol;
        return "USD Pro";
    }

    function decimals() public view virtual returns (uint8) {
        return 18;
    }

    function totalSupply() public view virtual returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual returns (uint256) {
        IAccount memory user = _accounts[account];

        return user.redirectYieldTo == address(0) ? user.balance + pendingRewards(account) : user.balance;
    }

    function transfer(address to, uint256 amount) public virtual returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, amount, true);
        return true;
    }

    function allowance(address owner, address spender) public view virtual returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }

    function transferFrom(address from, address to, uint256 amount) public virtual returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount, true);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, _allowances[owner][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        address owner = _msgSender();
        uint256 currentAllowance = _allowances[owner][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(owner, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function _transfer(address from, address to, uint256 amount, bool needHook) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if (needHook) {
            _beforeTokenTransfer(from, to, amount);
        }

        uint256 fromBalance = _accounts[from].balance;
        require(fromBalance >= amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            _accounts[from].balance = fromBalance - amount;
            _accounts[from].rewardDebt = (
                _accounts[from].balance * accRewardPerShare / ACC_REWARD_TOKEN_PRECISION
            ).toInt256();
        }
        _accounts[to].balance += amount;
        _accounts[to].rewardDebt = (
            _accounts[to].balance * accRewardPerShare / ACC_REWARD_TOKEN_PRECISION
        ).toInt256();

        emit Transfer(from, to, amount);

        if (needHook) {
            _afterTokenTransfer(from, to, amount);
        }
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _accounts[account].balance += amount;
        _accounts[account].rewardDebt = (
            _accounts[account].balance * accRewardPerShare / ACC_REWARD_TOKEN_PRECISION
        ).toInt256();
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _accounts[account].balance;
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _accounts[account].balance = accountBalance - amount;
            _accounts[account].rewardDebt = (
                _accounts[account].balance * accRewardPerShare / ACC_REWARD_TOKEN_PRECISION
            ).toInt256();
        }
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _spendAllowance(address owner, address spender, uint256 amount) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(currentAllowance >= amount, "ERC20: insufficient allowance");
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    /**
     * @dev Hook that is called before any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * will be transferred to `to`.
     * - when `from` is zero, `amount` tokens will be minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens will be burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(address from, address to, uint256) internal {
        if (from != address(0)) {
            harvest(from);
        }
        if (to != address(0)) {
            harvest(to);
        }
    }

    /**
     * @dev Hook that is called after any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * has been transferred to `to`.
     * - when `from` is zero, `amount` tokens have been minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens have been burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    // solhint-disable-next-line no-empty-blocks
    function _afterTokenTransfer(address from, address to, uint256 amount) internal virtual {}

    /* solhint-enable custom-errors */
    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[44] private __gap;
}

contracts/interfaces/ICircuitBreaker.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "../reward/RewardChefV2.sol";

/**
 * @dev
 * Ok - protocol is working well, there is no abnormal withdrawals
 * Warning - users withdraws more than half of threshold, dev team should be notified about that
 * Triggered - admin trigger rate limit, so protocol can't withdraw tokens.
 * After some time it could be reset automatically, or manually by admin/DAO
*/
enum State { Ok, Warning, Triggered }

/**
 * @title Circuit Breaker
 * @dev This contract is a circuit breaker designed for financial safety in DeFi protocols.
 * It acts as a middleware, monitoring the flow of funds through itself and triggering an interrupt
 * if the flow of funds exceeds a certain threshold within a defined period. The threshold could be set
 * as a percentage of the Total Value Locked (TVL) per day, for example, 15% of TVL. Upon an interrupt,
 * there are three potential outcomes:
 *
 * 1. The admin manually resets the circuit breaker. In this case, all held funds are released immediately.
 *
 * 2. No action is taken by the admin or the DAO within the threshold period. In this case, all held funds
 * are released automatically when the threshold period expires.
 *
 * 3. The admin escalates the alarm. In this case, the DAO must resolve the situation via voting.
 *
 * To prevent sudden halts in fund flow, the contract emits a warning event when it is halfway to
 * reaching the interrupt condition. This warning can be caught by an admin to spot potential interrupt
 * scenarios in advance.
 */
interface ICircuitBreaker {
    /* ====================== VARS ====================== */

    // how much funds passed in selected window
    function liqInPeriod (uint256 windowId) external view returns ( uint256 );

    /* ===================== EVENTS ===================== */
    event ThresholdWarning();
    event TriggeredRateLimitExpired();
    event ResetByAdmin();
    event RateLimitTriggered();

    /* ===================== ERRORS ===================== */
    error Triggered();
    error ThresholdExceeded();

    /* ==================== MUTABLE METHODS ==================== */

    /**
     * @notice  Transfer funds to a specified address.
     *          If the threshold is exceeded, the transfer will be interrupted.
     * @dev This function will revert if the contract state is interrupted.
     * @param token The address of the erc20 token to transfer
     * @param to The address to transfer to.
     * @param amount The amount to be transferred.
     * @param value The total value of the transfer, in fiat amount, for ex. $100 should be passed as 100, not 100*1e18.
     *        value is needed to avoid expensive call to the oracle inside of the transfer code.
     */
    function controlledTransfer(address token, address to, uint256 amount, uint256 value) external;

    function setFundsFlowThreshold(uint256 _fundsFlowThreshold) external;

    function reset() external;

    function triggerRateLimit() external;
    /* ==================== VIEW METHODS ==================== */

    function totalLiquidityInPeriod() external view returns (uint256);

    function getWindowId() external view returns (uint256);
}

contracts/interfaces/IGainableERC20v3.so.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";

/**
 * @title IGainableERC20v3
 * @notice This is fork on openzeppelin ERC20Upgradeable v4.5.0
 * @dev Implementation of the {IERC20} interface.
 **/
interface IGainableERC20v3 is IERC20MetadataUpgradeable {
    /* ======================== STRUCT ========================= */
    struct IAccount {
        uint256 balance;
        int256 rewardDebt;
        address redirectYieldTo;
        // bool isGainDisabled;
    }

    struct IRewardPool {
        uint128 accRewardPerShare;
        uint64 lastRewardBlock;
    }

    /* ======================== ERRORS ========================= */
    error BadEndBlock();
    error InsufficientRewardAmount();
    error ZeroFromBalance();

    /* ==================== MUTABLE METHODS ==================== */

    /**
     * @notice Refills the "moneybox" with a specified amount of tokens
     * @param amount The amount of tokens to add to the reward pool
     * @param endBlock The block number at which the rewards will end
     * @dev This function updates the reward per block and end block for the reward distribution
     */
    function refillReward(uint256 amount, uint64 endBlock) external;

    /**
     * @notice Updates the reward pool state
     * @dev This function should be called to update the accumulated rewards per share and the last reward block
     */
    function updateRewardPool() external;

    /**
     * @notice Harvests rewards for a specified account
     * @param account The address of the account to harvest rewards for
     * @dev This function calculates and transfers pending rewards
     * to the account or its designated yield recipient.
     * We won't make it private, because if user will send 1wei
     * to any account, then harvest(to) will be called
     */
    function harvest(address account) external;

    /**
     * @notice Redirects yield to a specified address and harvests current rewards
     * @param to The address to redirect yield to (from msg.sender)
     * @dev This function allows users to redirect their yield to another address and
     * immediately harvest current rewards.
     * To remove redirect just pass address(0) in argument
     */
    function redirectYieldAndHarvest(address to) external;

    /**
     * @notice Cancels yield redirection and harvests current rewards
     * @dev This function stops redirecting yield and harvests the current rewards for the caller
     */
    function cancelRedirectYieldAndHarvest() external;

    /**
     * @notice Allows the DAO to redirect yield for a user and harvest their rewards
     * @param from The address of the user whose yield is being redirected
     * @param to The address to redirect the yield to
     * @dev This function can only be called by the DAO and allows for administrative yield redirection
     */
    function redirectYieldAndHarvestByDAO(address from, address to) external;

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(address spender, uint256 addedValue) external returns (bool);

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     * - `spender` must have allowance for the caller of at least
     * `subtractedValue`.
     */
    function decreaseAllowance(address spender, uint256 subtractedValue) external returns (bool);

    /**
     * @notice Sets the number of blocks per minute for APR calculation
     * @param blocksPerMinute_ The number of blocks produced per minute (network-dependent)
     * @dev Only callable by DAO. Used to adjust APR calculation for different networks.
     */
    function setBlocksPerMinute(uint256 blocksPerMinute_) external;

    /* ===================== VIEW METHODS ====================== */

    /**
     * @notice Calculates the pending rewards for a given account
     * @param account The address of the account to check pending rewards for
     * @return The amount of pending rewards for the account
     * @dev This function calculates rewards without updating the state
     */
    function pendingRewards(address account) external view returns (uint256);

    /**
     * @notice Calculates the current yield Annual Percentage Rate (APR)
     * @return The current yield APR expressed as a percentage with 18 decimal places
     * @dev This function provides an estimate of the annual yield rate based on current rewards, especially for UI
     */
    function getYieldAPR() external view returns (uint256);

    /**
     * @notice View function for UI
     * @return Info of account stored in stablecoin (user balances, rewardDebt, redirectYield)
     */
    function getAccount(address account) external view returns (IAccount memory);

    /**
     * @notice Returns the current number of blocks per minute used for APR calculation
     * @return The number of blocks per minute
     * @dev Used in APR calculation to adapt to different network block times
     */
    function getBlocksPerMinute() external view returns (uint256);
}

contracts/interfaces/IRewardChefV2.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "../exchange/contracts/periphery/contracts/interfaces/oracle/ISuOracle.sol";

/**
 * @title RewardChefV2
 * @notice fork of MasterChefV2(13/05/2023) https://etherscan.io/address/0xef0881ec094552b2e128cf945ef17a6752b4ec5d#code
 * @dev This contract is based on MVC2, but uses "virtual" balances instead of storing real ERC20 tokens
 * and uses address of this assets instead of pid.
 * Rewards that are distributed have to be deposited using refillReward(uint256 amount, uint64 endBlock)
 **/
interface IRewardChefV2 {
    /**
      * @notice Info of each reward pool.
      * @param accSushiPerShare -
      * @param lastRewardBlock -
      * @param allocPoint - The amount of allocation points assigned to the pool.
      * Also known as the amount of REWARD_TOKEN to distribute per block.
      * @param lpSupply -
     **/
    struct PoolInfo {
        uint128 accSushiPerShare;
        uint64 lastRewardBlock;
        uint64 allocPoint;
        uint256 lpSupply;
    }

    /**
      * @notice Info of each user.
      * @param amount - token amount the user has provided.
      * @param rewardDebt - The amount of rewards entitled to the user.
      * @param multiplicator1e18 - deposit amount multiplicator for current user, that depends on his lockupPeriod
      * @param lockupPeriodSeconds - users lockupPeriod in seconds
      * @param lockupStartTimestamp - timestamp when user start lockup
     **/
    struct UserInfo {
        uint256 amount;
        int256 rewardDebt;

        uint256 multiplicator1e18;
        uint256 lockupPeriodSeconds;
        uint256 lockupStartTimestamp;
    }

    /**
      * @notice Lockup info struct for a view method getPossibleLockupPeriodsSeconds()
      * @param lockupPeriodSeconds - available lockupPeriod in seconds
      * @param multiplicator1e18 - deposit amount multiplicator for this lockup period
     **/
    struct ILockupPeriod {
        uint256 lockupPeriodSeconds;
        uint256 multiplicator1e18;
    }

    // =======================================EVENTS=============================================
    event VirtualDeposit(address indexed user, address indexed asset, uint256 amount);
    event VirtualWithdraw(address indexed user, address indexed asset, uint256 amount);
    event ResetAmount(
        address indexed user, address indexed asset, address indexed to, uint256 amount, uint256 lockupPeriodSeconds
    );
    event Harvest(address indexed user, address indexed asset, uint256 amount);
    event LogPoolAddition(address indexed asset, uint256 allocPoint);
    event LogSetPool(address indexed asset, uint256 allocPoint);
    event LogUpdatePool(address indexed asset, uint64 lastRewardBlock, uint256 lpSupply, uint256 accSushiPerShare);

    // =======================================ERRORS=============================================
    error BadEndBlockForRefill();
    error ZeroLockupPeriod();
    error ZeroMultiplicator();
    error PoolExist();
    error PoolNotExist();
    error DifferentLockup();
    error HarvestBeforeLockup();

    // =========================================VARS=============================================
    /**
     *  @dev Total allocation points. Must be the sum of all allocation points in all pools.
     * The good practice, to always keep this variable is equal 1000.
     */
    function totalAllocPoint() external view returns ( uint256 );

    // =======================================REWARDER=============================================
    // solhint-disable-next-line func-name-mixedcase
    function REWARD_TOKEN() external view returns ( IERC20Upgradeable );
    // solhint-disable-next-line func-name-mixedcase
    function ORACLE() external view returns ( ISuOracle );
    function rewardPerBlock() external view returns ( uint256 );
    function rewardEndBlock() external view returns ( uint256 );

    /**
     * @dev admin transfer REWARD_TOKEN to contract to increase rewards per block
     */
    function refillReward(uint256 amount, uint64 endBlock) external;
    /**
     * @dev returns total amount of rewards allocated to the all pools on the rage (startBlock, endBlock]
     * i.e. excluding startBlock but including endBlock
     */
    function rewardsBetweenBlocks(uint256 startBlock, uint256 endBlock) external returns ( uint256 );

    //=======================================LOCKUP LOGIC===========================================
    /**
     * @notice view method that return all lockupPeriods in ILockupPeriod struct
     */
    function getPossibleLockupPeriodsSeconds() external view returns (ILockupPeriod[] memory);

    function getUserLockupPeriodsSeconds(address asset, address user) external
        view returns (uint256 lockupPeriodSeconds);

    /**
     * @notice method to add new LockupPeriods with it multiplicator
     * @dev If multiplicator1e18 == 0 => we remove lockupPeriodSeconds else we add it
     * @param lockupPeriodSeconds Amount of seconds of lockupPeriod we want to add/remove
     * @param multiplicator1e18 Multiplicator for that lockupPeriod
     */
    function setPossibleLockupPeriodsSeconds(uint256 lockupPeriodSeconds, uint256 multiplicator1e18) external;

    //================================CORE REWARD CHEF METHODS======================================
    /**
     * @notice Add a new reward pool. Can only be called by the admin. Can't add the same LP token more than once.
     * @param allocPoint AP of the new pool.
     * @param _asset Address of the ERC-20 token.
     */
    function add(uint256 allocPoint, address _asset) external;

    /**
     * @notice Update the given pool's REWARD_TOKEN allocation point. Can only be called by the admin.
     * Can't be called for asset that was not added by add() function.
     * @param _asset Address of the ERC-20 token.
     * @param _allocPoint New AP of the pool.
     */
    function set(address _asset, uint256 _allocPoint) external;

    /**
     * @notice View function to see pending REWARD_TOKEN on frontend.
     * @param _asset Address of the ERC-20 token.
     * @param _user Address of user.
     * @return pending REWARD_TOKEN reward for a given user.
     */
    function pendingSushi(address _asset, address _user) external view returns ( uint256 );

    /**
     * @notice Update reward variables of the given pool.
     * @param asset Asset address
     * @return updated pool info
     */
    function updatePool(address asset) external returns ( PoolInfo memory );

    /**
     * @notice Update reward variables for all pools. Be careful of gas spending!
     */
    function updateAllPools() external;

    /**
     * @notice analogues to MCV2 Deposit method, but can be called only by trusted address (onlyRewardAccess)
     * that is trusted to honestly calc how many "virtual" tokens have to be allocated for each user.
     * @param asset Asset address
     * @param to User for whom we will increase the virtual balance
     * @param amountEDecimal Quantity without taking into account the multiplier that we will add
     * @param lockupPeriodSeconds Current deposit lockup period. Can't increase amount with 2 different lockupPeriods
     */
    function increaseAmount(address asset, address to, uint256 amountEDecimal, uint256 lockupPeriodSeconds) external;

    /**
     * @notice Analogues to MVC2 Withdraw method, that can be called only by trusted address (onlyRewardAccess)
     * that is trusted to honestly calc how many "virtual" tokens have to be allocated for each user.
     * @param asset Asset address
     * @param to User for whom we will subtract the virtual balance
     * @param amountEDecimal Quantity without taking into account the multiplier that we will subtract
     */
    function decreaseAmount(address asset, address to, uint256 amountEDecimal) external;

    /**
     * @notice View method that return penalty for user that will decrease amount by {amountEDecimal}
     * @param asset Asset address
     * @param to User who will decrease his deposit amount
     * @param amountEDecimal Quantity without taking into account the multiplier that we will subtract
     */
    function getDecreaseAmountRewardPenalty(
        address asset,
        address to,
        uint256 amountEDecimal
    ) external view returns (uint256);

    /**
     * @notice Harvest proceeds for transaction sender to {to}.
     * @dev Here we send msg.sender pendingSushi to {to} and update lockup period.
     * @param asset Asset address
     * @param to Receiver of REWARD_TOKEN rewards.
     */
    function harvest(address asset, address to, uint256 newLockupPeriodSeconds) external;

    /**
     * @notice Withdraw without caring about rewards. EMERGENCY ONLY.
     * @param asset Asset address
     * @param to The address of the user whose information will be cleared
     */
    function resetAmount(address asset, address to) external;

    //================================VIEW METHODS======================================
    /**
     * @notice Method for frontend that return pool APR in base of 1e18
     * @param asset Asset address
     */
    function getPoolApr(address asset) external view returns (uint256);

    /**
     * @notice Reward tokens can be locked for certain period of time.
     * So user have to wait until at least timestamp to be able to harvest them.
     * There's no lock or any restriction on withdrawing principle.
     * This function return end timestamp for that lock.
     * @param asset Asset address
     * @param user User address
     */
    function getHarvestLockupEndTimestamp(address asset, address user) external view returns (uint256);
}

contracts/interfaces/ISuPausable.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

interface ISuPausable {
    /* ===================== EVENTS ===================== */
    /**
     * @dev Emitted when the pause is triggered by `account` for the "collateral".
     **/
    event PausedCollateral(address account, address collateral);

    /**
     * @dev Emitted when the pause is triggered by `account` for the "_contract".
     **/
    event PausedContract(address account, address _contract);

    /**
     * @dev Emitted when the pause is lifted by `account` for the "collateral".
     **/
    event UnpausedCollateral(address account, address collateral);

    /**
     * @dev Emitted when the pause is lifted by `account` for the "_contract".
     **/
    event UnpausedContract(address account, address _contract);

    /* ===================== ERRORS ===================== */
    error AlreadyPaused();
    error NotPaused();

    /* ==================== MUTABLE METHODS ==================== */
    /**
     * @dev Triggers stopped state for the "collateral".
     **/
    function pauseCollateral(address collateral) external;

    /**
     * @dev Triggers stopped state for all "collaterals".
     **/
    function batchPauseCollateral(address[] calldata collaterals) external;

    /**
     * @dev Triggers stopped state for the "_contract".
     **/
    function pauseContract(address _contract) external;

    /**
     * @dev Triggers stopped state for the protocol.
     **/
    function pauseAll() external;

    /**
     * @dev Returns "collateral" to normal state.
     **/
    function unpauseCollateral(address collateral) external;

    /**
     * @dev Returns all "collaterals" to normal state.
     **/
    function batchUnpauseCollateral(address[] calldata collaterals) external;

    /**
     * @dev Returns "_contract" to normal state.
     **/
    function unpauseContract(address _contract) external;

    /**
     * @dev Returns protocol to normal state. All previous stopped collaterals and contracts are in the same state.
     **/
    function unpauseAll() external;

    /* ==================== VIEW METHODS ==================== */
    /**
     * @dev Returns true if the contract for the "collateral" is paused, and false otherwise.
     **/
    function isPaused(address collateral) external view returns (bool);
}

