pragma solidity ^0.8.0;

import "../../interfaces/ICollateralJoin.sol";
import "../../interfaces/ISafeEngine.sol";
import "../../../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";

// CHANGED: In CollateralJoin (GemJoin) I only added events.
// Added SuAuthenticated instead of authorizedAccounts

/**
 * @title CollateralJoin
 * @notice Adapter contract for depositing and withdrawing collateral tokens with 18 decimals into/from the StableUnit protocol.
 * @dev Bridges external ERC20 collateral tokens with the SafeEngine’s internal collateral balances, assuming the collateral
 *      token has exactly 18 decimals. Handles token transfers and SafeEngine balance updates for Safe operations. This
 *      contract is a fork of MakerDAO’s GemJoin.sol, adapted for StableUnit with added events and SuAuthenticated access
 *      control.
 * @custom:usage
 * - **Initialization**: Deploy with access control, SafeEngine, collateral type, and collateral token addresses.
 *   ```solidity
 *   CollateralJoin collateralJoin = new CollateralJoin(accessControl, safeEngine, "ETH-A", collateralToken);
 *   ```
 * - **Deposit Collateral**: Join collateral tokens into a Safe.
 *   ```solidity
 *   collateralJoin.join(user, amount);
 *   ```
 * - **Withdraw Collateral**: Exit collateral tokens from a Safe.
 *   ```solidity
 *   collateralJoin.exit(user, amount);
 *   ```
 * - **Disabling**: Disable the contract to halt operations.
 *   ```solidity
 *   collateralJoin.disableContract();
 *   ```
 * @custom:security Only authorized accounts (DisablerAccess) can disable the contract. Token transfers require user
 *                 approval, and SafeEngine enforces balance updates. The contract assumes 18-decimal collateral tokens.
 * @custom:changes Renamed from GemJoin.sol to CollateralJoin.sol. Added `Join` and `Exit` events. Replaced MakerDAO’s
 *                 authorizedAccounts with SuAuthenticated for access control.
 * @custom:events Emits `Join`, `Exit`, and `DisableContract` for state changes and operations.
 */
contract CollateralJoin is ICollateralJoin, SuAuthenticated {
    /// @notice Reference to the SafeEngine contract for internal collateral operations.
    ISafeEngine public safeEngine;
    /// @notice Unique identifier for the collateral type (e.g., "ETH-A").
    bytes32 public collateralType;
    /// @notice Reference to the external ERC20 collateral token contract.
    IERC20Metadata public collateral;      // Actual collateral token contract
    /// @notice Number of decimals for the collateral token (must be 18).
    uint public decimals;        // How many decimals the collateral token has

    /**
     * @notice Initializes the CollateralJoin contract with required parameters.
     * @dev Sets up references, validates collateral token decimals, and enables the contract.
     * @param accessControlSingleton Address of the access control singleton.
     * @param safeEngine_ Address of the SafeEngine contract.
     * @param collateralType_ Unique identifier for the collateral type.
     * @param collateral_ Address of the collateral token contract.
     */
    function initialize(address accessControlSingleton, address safeEngine_, bytes32 collateralType_, address collateral_)
        public initializer{

        __suAuthenticatedInit(accessControlSingleton);

        contractEnabled = 1; // Enable contract operations
        safeEngine = ISafeEngine(safeEngine_); // Initialize SafeEngine reference
        collateralType = collateralType_; // Set collateral type identifier
        collateral = IERC20Metadata(collateral_); // Initialize collateral token reference
        decimals = collateral.decimals(); // Retrieve token decimals
        require(decimals == 18, "CollateralJoin/bad-decimals"); // Ensure 18 decimals
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice Flag indicating if the contract is enabled (1) or disabled (0).
    uint public contractEnabled;

    /**
     * @notice Disables the contract, halting further operations.
     * @dev Sets `contractEnabled` to 0 and emits an event. Only callable by DisablerAccess.
     */
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0; // Disable contract
        emit DisableContract(); // Notify external systems
    }

    /**
     * @notice Deposits collateral tokens into the SafeEngine for a user’s Safe.
     * @dev Transfers tokens from the caller and increases the SafeEngine’s collateral balance.
     *      Requires the contract to be enabled and the amount to be non-negative.
     * @param usr Address to credit the collateral balance (Safe handler).
     * @param wad Amount of collateral to deposit [wad, 18 decimals].
     */
    function join(address usr, uint wad) external {
        require(contractEnabled == 1, "CollateralJoin/not-contractEnabled"); // Ensure contract is enabled
        require(int(wad) >= 0, "CollateralJoin/overflow"); // Prevent negative amounts
        safeEngine.modifyCollateralBalance(collateralType, usr, int(wad)); // Increase SafeEngine balance
        require(collateral.transferFrom(msg.sender, address(this), wad), "CollateralJoin/failed-transfer"); // Transfer tokens
        emit Join(usr, wad); // Log operation
    }

    /**
     * @notice Withdraws collateral tokens from the SafeEngine to a user.
     * @dev Decreases the SafeEngine’s collateral balance and transfers tokens to the user.
     *      Ensures the amount does not cause overflow in SafeEngine.
     * @param usr Address to receive the collateral tokens.
     * @param wad Amount of collateral to withdraw [wad, 18 decimals].
     */
    function exit(address usr, uint wad) external {
        require(wad <= 2 ** 255, "CollateralJoin/overflow"); // Prevent overflow in SafeEngine
        safeEngine.modifyCollateralBalance(collateralType, msg.sender, -int(wad)); // Decrease SafeEngine balance
        require(collateral.transfer(usr, wad), "CollateralJoin/failed-transfer"); // Transfer tokens
        emit Exit(usr, wad); // Log operation
    }
}

