// it under the terms of the GNU Affero General Public License as published by
// the Free Software Foundation, either version 3 of the License, or
// (at your option) any later version.
//
// This program is distributed in the hope that it will be useful,
// but WITHOUT ANY WARRANTY; without even the implied warranty of
// MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the
// GNU Affero General Public License for more details.
//
// You should have received a copy of the GNU Affero General Public License
// along with this program.  If not, see <https://www.gnu.org/licenses/>.

// -pragma solidity ^0.6.12;
pragma solidity ^0.8.0;

import {Math} from "../utils/Math.sol";
import {ISafeEngine} from "../interfaces/ISafeEngine.sol";
import "../../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";
import "../../exchange/contracts/periphery/contracts/interfaces/oracle/ISuOracleAggregator.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

// FIXME: This contract was altered compared to the production version.
// It doesn't use LibNote anymore.
// New deployments of this contract will need to include custom events (TO DO).

// CHANGED: added interface, moved structs there.
// Maybe we'll need to add events in each method (need to discuss, in another branch dss has LogNote for that)
// Added SuAuthenticated instead of authorizedAccounts
// Added updating safetyPrice

/**
 * @title SafeEngine
 * @notice Core accounting and state management contract for collateralized debt positions (Safes) in the StableUnit protocol.
 * @dev Manages collateral, debt, and StablePro coin balances for Safes, tracks global and per-collateral-type debt limits,
 *      and enforces collateralization ratios using safety prices from an oracle. Supports operations like collateral
 *      deposit/withdrawal, debt generation/repayment, liquidation, and settlement. Uses SuAuthenticatedNonUpgradeable for
 *      access control and integrates with ISuOracleAggregator for price feeds. Can be disabled by authorized accounts to
 *      halt operations. This contract is a fork of MakerDAO’s Vat.sol, adapted for StableUnit with features like dynamic
 *      safety price updates and redemption price.
 * @custom:usage
 * - **Initialization**: Deploy with access control and oracle addresses.
 *   ```solidity
 *   SafeEngine safeEngine = new SafeEngine(accessControlAddress, oracleAddress);
 *   ```
 * - **Collateral Type Setup**: Initialize a collateral type with its token address.
 *   ```solidity
 *   safeEngine.initializeCollateralType("ETH-A", ethTokenAddress);
 *   ```
 * - **Parameter Modification**: DAO updates debt ceilings, redemption price, or liquidation ratios.
 *   ```solidity
 *   safeEngine.modifyParameters("globalDebtCeiling", newCeiling); // Update global debt ceiling
 *   safeEngine.modifyParameters("ETH-A", "debtCeiling", newCeiling); // Update collateral debt ceiling
 *   ```
 * - **Safe Operations**: Users or adapters modify Safe collateral/debt via DssProxyActions or direct calls.
 *   ```solidity
 *   safeEngine.modifySAFECollateralization("ETH-A", safe, collateralSrc, debtDst, deltaCollateral, deltaDebt);
 *   ```
 * - **Price Updates**: Trigger safety price updates for a collateral type.
 *   ```solidity
 *   safeEngine.updateSafetyPrice("ETH-A"); // Refresh safety price from oracle
 *   ```
 * - **Disabling**: DisablerAccess accounts can disable the contract.
 *   ```solidity
 *   safeEngine.disableContract(); // Halt operations
 *   ```
 * @custom:security Only enabled contracts (`contractEnabled == 1`) allow state-changing operations.
 *                 Administrative functions are restricted by SuAuthenticatedNonUpgradeable roles (DAO, Admin, DisablerAccess).
 *                 Only authorized users/contracts (via `cdpRights`) can modify specific Safes or balances.
 *                 Safety price updates rely on ISuOracleAggregator, which must provide reliable price feeds.
 * @custom:changes Replaced MakerDAO’s `authorizedAccounts` with `SuAuthenticatedNonUpgradeable` for access control.
 *                 Removed `LibNote` for event logging, requiring custom events (TODO).
 *                 Added `ISuOracleAggregator` integration for dynamic safety price updates.
 *                 Introduced `redemptionPrice`, `safetyPriceValidBlockAmount`, and `defaultLiquidationRatio`.
 *                 Moved structs to `ISafeEngine` interface.
 * @custom:events No events are defined; custom events need to be added (as noted in FIXME).
 *                Example placeholder events are included in comments but not emitted due to lack of definition.
 */
contract SafeEngine is SuAuthenticated, Math, ISafeEngine {
    // --- Disable ---
    /// @notice Tracks whether the contract is enabled (1) or disabled (0).
    uint public contractEnabled;

    /**
     * @notice Disables the contract, preventing further state-changing operations.
     * @dev Sets `contractEnabled` to 0 and emits a placeholder event. Only callable by DisablerAccess accounts.
     * @custom:access OnlyDisablerAccess
     */
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0;

        emit DisableContract();
    }

    // --- Auth ---
    // -// --- Auth ---
    // -mapping (address => uint) public wards;
    // -function rely(address usr) external auth { require(live == 1, "Vat/not-live"); wards[usr] = 1; }
    // -function deny(address usr) external auth { require(live == 1, "Vat/not-live"); wards[usr] = 0; }
    // -modifier auth {
    // -    require(wards[msg.sender] == 1, "Vat/not-authorized");
    // -    _;
    // -}

    // Who can transfer collateral & debt in/out of a CDP
    /// @notice Maps Safe owners to operators with permission to modify their Safes.
    mapping(address => mapping (address => uint)) public cdpRights;
    // -mapping(address => mapping (address => uint)) public can;

    /**
     * @notice Grants a user permission to modify the caller’s Safe.
     * @dev Sets `cdpRights[msg.sender][usr] = 1`.
     * @param usr Address to grant modification rights.
     */
    function approveSAFEModification(address usr) external {
        cdpRights[msg.sender][usr] = 1;
    }
    // -function hope(address usr) external { can[msg.sender][usr] = 1; }

    /**
     * @notice Revokes a user’s permission to modify the caller’s Safe.
     * @dev Sets `cdpRights[msg.sender][usr] = 0`.
     * @param usr Address to revoke modification rights.
     */
    function denySAFEModification(address usr) external {
        cdpRights[msg.sender][usr] = 0;
    }
    // -function nope(address usr) external { can[msg.sender][usr] = 0; }

    /**
     * @notice Checks if an account can modify a Safe.
     * @dev Returns true if the account is the Safe owner or has explicit permission.
     * @param cdp Address of the Safe.
     * @param account Address to check.
     * @return True if the account can modify the Safe.
     */
    function canModifySAFE(address cdp, address account) internal view returns (bool) {
        return either(cdp == account, cdpRights[cdp][account] == 1);
    }
    // -function wish(address bit, address usr) internal view returns (bool) {
    // -    return either(bit == usr, can[bit][usr] == 1);
    // -}

    /// Total User Debt (drawn StablePro + stability fee accrued) = SAFE.generatedDebt * CollateralType.accumulatedRate

    // --- Data ---
    // -struct Ilk {
    // -    uint256 Art;   // Total Normalised Debt     [wad]
    // -    uint256 rate;  // Accumulated Rates         [ray]
    // -    uint256 spot;  // Price with Safety Margin  [ray]
    // -    uint256 line;  // Debt Ceiling              [rad]
    // -    uint256 dust;  // Urn Debt Floor            [rad]
    // -}
    // -struct Urn {
    // -    uint256 ink;   // Locked Collateral  [wad]
    // -    uint256 art;   // Normalised Debt    [wad]
    // -}

    // Data about each collateral type
    /// @notice Maps collateral types to their configuration (e.g., debt ceiling, safety price).
    mapping (bytes32 => CollateralType) public collateralTypes;
    // -mapping (bytes32 => Ilk)                       public ilks;

    // Data about each SAFE
    /// @notice Maps collateral types and Safe addresses to their state (collateral and debt).
    mapping (bytes32 => mapping (address => SAFE)) public safes;
    // -mapping (bytes32 => mapping (address => Urn )) public urns;

    // Balance of each collateral type
    /// @notice Tracks collateral balances per collateral type and user [wad].
    mapping (bytes32 => mapping (address => uint)) public tokenCollateral; // [wad]
    // -mapping (bytes32 => mapping (address => uint)) public gem;  // [wad]

    // Internal balance of pegged-coins
    /// @notice Tracks internal StablePro coin balances per user [rad].
    mapping (address => uint256) public coinBalance; // [rad]
    // -mapping (address => uint256)                   public dai;  // [rad]

    // Amount of debt held by an account. Coin & debt are like matter and antimatter. They nullify each other
    /// @notice Tracks debt balances per user, offsetting coin balances [rad].
    mapping (address => uint256) public debtBalance; // [rad]
    // -mapping (address => uint256)                   public sin;  // [rad]

    /// @notice Oracle aggregator providing collateral price feeds.
    ISuOracleAggregator public ORACLE; // Total amount of debt (coins) currently issued [rad]
    /// @notice Total debt issued across all Safes [rad].
    uint256 public globalDebt; // Total amount of debt (coins) currently issued [rad]
    // -uint256 public debt;  // Total Dai Issued    [rad]

    /// @notice Total unbacked debt not covered by collateral [rad].
    uint256 public globalUnbackedDebt; // 'Bad' debt that's not covered by collateral [rad]
    // -uint256 public vice;  // Total Unbacked Dai  [rad]

    /// @notice Maximum debt allowed across all Safes [rad].
    uint256 public globalDebtCeiling; // Maximum amount of debt that can be issued [rad]
    // -uint256 public Line;  // Total Debt Ceiling  [rad]

    /// @notice Reference price per StablePro, defaulting to 1e27 (1:1 USD) [ray].
    uint256 public redemptionPrice; // Reference value per StablePro, as default = 1e27, that mean 1:1 to USD [ray]
    /// @notice Number of blocks a safety price remains valid.
    uint256 public safetyPriceValidBlockAmount; // Amount of blocks during which safetyPrice is valid
    /// @notice Default liquidation ratio set for new collateral types (e.g., 150%) [ray].
    uint256 public defaultLiquidationRatio; // Liquidation ratio that is set in initializeCollateralType
    // -uint256 public live;  // Active Flag


    // --- Init ---
    /**
     * @notice Initializes the SafeEngine with access control and oracle addresses.
     * @dev Sets initial state: contract enabled, redemption price to 1e27, safety price validity to 1 block,
     *      and default liquidation ratio to 150%. Inherits access control from SuAuthenticatedNonUpgradeable.
     * @param accessControl Address of the access control singleton contract.
     * @param oracle Address of the ISuOracleAggregator contract.
     */
    function initialize(address accessControl, address oracle) public initializer {
        __suAuthenticatedInit(accessControl);

        contractEnabled = 1;
        redemptionPrice = ONE; // 1e27
        safetyPriceValidBlockAmount = 1; // 2 = this block and 1 block after, 1 = only this block
        defaultLiquidationRatio = ONE * 15 / 10; // 150%
        ORACLE = ISuOracleAggregator(oracle);
    }
    // -constructor() public {
    // -    wards[msg.sender] = 1;
    // -    live = 1;
    // -}

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // --- Math ---
    // -function _add(uint x, int y) internal pure returns (uint z) {
    // -    z = x + uint(y);
    // -    require(y >= 0 || z <= x);
    // -    require(y <= 0 || z >= x);
    // -}
    // -function _sub(uint x, int y) internal pure returns (uint z) {
    // -    z = x - uint(y);
    // -    require(y <= 0 || z <= x);
    // -    require(y >= 0 || z >= x);
    // -}
    // -function _mul(uint x, int y) internal pure returns (int z) {
    // -    z = int(x) * y;
    // -    require(int(x) >= 0);
    // -    require(y == 0 || z / y == int(x));
    // -}
    // -function _add(uint x, uint y) internal pure returns (uint z) {
    // -    require((z = x + y) >= x);
    // -}
    // -function _sub(uint x, uint y) internal pure returns (uint z) {
    // -    require((z = x - y) <= x);
    // -}
    // -function _mul(uint x, uint y) internal pure returns (uint z) {
    // -    require(y == 0 || (z = x * y) / y == x);
    // -}

    // --- Administration ---
    /**
     * @notice Initializes a new collateral type with its token address.
     * @dev Sets accumulated rate to 1e27 and assigns the collateral token address and default liquidation ratio.
     *      Reverts if already initialized. Only callable by Admin.
     * @param collateralType Unique identifier for the collateral type (e.g., "ETH-A").
     * @param collateralAddress Address of the collateral token contract.
     * @custom:access OnlyAdmin
     */
    function initializeCollateralType(bytes32 collateralType, address collateralAddress) external onlyAdmin {
        require(collateralTypes[collateralType].accumulatedRate == 0, "SafeEngine/collateral-type-already-init");
        collateralTypes[collateralType].accumulatedRate = ONE; // 1e27
        collateralTypes[collateralType].collateralAddress = collateralAddress;
        collateralTypes[collateralType].liquidationRatio = defaultLiquidationRatio; // 150%

        emit InitializeCollateralType(collateralType);
    }
    // -function init(bytes32 ilk) external auth {
    // -    require(ilks[ilk].rate == 0, "Vat/ilk-already-init");
    // -    ilks[ilk].rate = 10 ** 27;
    // -}

    /**
     * @notice Modifies global protocol parameters.
     * @dev Updates parameters like global debt ceiling, redemption price, default liquidation ratio, or safety price validity.
     *      Requires contract to be enabled. Only callable by DAO.
     * @param what Parameter to modify (e.g., "globalDebtCeiling", "redemptionPrice").
     * @param data New value for the parameter.
     * @custom:access OnlyDAO
     */
    function modifyParameters(bytes32 what, uint data) external onlyDAO {
        require(contractEnabled == 1, "SafeEngine/not-contractEnabled");
        if (what == "globalDebtCeiling") globalDebtCeiling = data;
        else if (what == "redemptionPrice") redemptionPrice = data;
        else if (what == "defaultLiquidationRatio") defaultLiquidationRatio = data;
        else if (what == "safetyPriceValidBlockAmount") safetyPriceValidBlockAmount = data;
        else revert("SafeEngine/modifyParameters-unrecognized-param");

        emit ModifyParameters(what, data);
    }
    // -function file(bytes32 what, uint data) external auth {
    // -    require(live == 1, "Vat/not-live");
    // -    if (what == "Line") Line = data;
    // -    else revert("Vat/file-unrecognized-param");
    // -}

    /**
     * @notice Modifies parameters for a specific collateral type.
     * @dev Updates parameters like debt ceiling, debt floor, or liquidation ratio.
     *      Requires contract to be enabled. Only callable by DAO.
     * @param collateralType Unique identifier for the collateral type.
     * @param what Parameter to modify (e.g., "debtCeiling", "liquidationRatio").
     * @param data New value for the parameter.
     * @custom:access OnlyDAO
     */
    function modifyParameters(bytes32 collateralType, bytes32 what, uint data) external onlyDAO {
        require(contractEnabled == 1, "SafeEngine/not-contractEnabled");
        //if (what == "safetyPrice") collateralTypes[collateralType].safetyPrice = data; // Commented out
        if (what == "debtCeiling") collateralTypes[collateralType].debtCeiling = data;
        else if (what == "debtFloor") collateralTypes[collateralType].debtFloor = data;
        else if (what == "liquidationRatio") collateralTypes[collateralType].liquidationRatio = data;
        else revert("SafeEngine/modifyParameters-unrecognized-param");

        emit ModifyParameters(collateralType, what, data);
    }
    // -function file(bytes32 ilk, bytes32 what, uint data) external auth {
    // -    require(live == 1, "Vat/not-live");
    // -    if (what == "spot") ilks[ilk].spot = data;
    // -    else if (what == "line") ilks[ilk].line = data;
    // -    else if (what == "dust") ilks[ilk].dust = data;
    // -    else revert("Vat/file-unrecognized-param");
    // -}

    // -function cage() external auth {
    // -    live = 0;
    // -}

    /**
     * @notice Updates the safety price for a collateral type using oracle data.
     * @dev Fetches the collateral’s fiat price from ISuOracleAggregator, adjusts for token decimals,
     *      and calculates the safety price as (realPrice / redemptionPrice / liquidationRatio).
     *      Only updates if the current safety price is unset or expired based on `safetyPriceValidBlockAmount`.
     * @param collateralType Unique identifier for the collateral type.
     */
    function updateSafetyPrice(bytes32 collateralType) public {
        // Skip update if safety price is valid
        if (collateralTypes[collateralType].safetyPriceBlockNumber == 0) {
            collateralTypes[collateralType].safetyPriceBlockNumber = block.number;
        } else if (
            collateralTypes[collateralType].safetyPriceBlockNumber + safetyPriceValidBlockAmount >= block.number
        ) return;

        address collateralAddress = collateralTypes[collateralType].collateralAddress;
        uint256 decimals = IERC20Metadata(collateralAddress).decimals();
        // Adjust price for token decimals: realPriceE18 = oraclePrice / 10^(18-decimals)
        uint256 realPriceE18 = decimals == 18
            ? ORACLE.getSafeFiatPrice1e18(collateralAddress)
            : ORACLE.getSafeFiatPrice1e18(collateralAddress) / (10 ** (18 - decimals));
        // Safety price = (realPrice * 1e9 / redemptionPrice) / liquidationRatio [ray]
        collateralTypes[collateralType].safetyPrice = _rdiv(
            _rdiv(realPriceE18 * 1e9, redemptionPrice),
            collateralTypes[collateralType].liquidationRatio
        );
        collateralTypes[collateralType].safetyPriceBlockNumber = block.number;

        emit UpdateSafetyPrice(collateralType, collateralTypes[collateralType].safetyPrice);
    }

    // --- Fungibility ---
    /**
     * @notice Modifies a user’s collateral balance for a collateral type.
     * @dev Adds or subtracts collateral tokens, typically called by CollateralJoin adapters.
     *      Requires caller to have CDP access permissions.
     * @param collateralType Unique identifier for the collateral type.
     * @param usr Address to modify the collateral balance for.
     * @param wad Amount to add (positive) or subtract (negative) [wad].
     * @custom:access OnlyCDPAccess
     */
    function modifyCollateralBalance(bytes32 collateralType, address usr, int256 wad) external onlyCDPAccess {
        tokenCollateral[collateralType][usr] = _add(tokenCollateral[collateralType][usr], wad);

        emit ModifyCollateralBalance(collateralType, usr, wad);
    }
    // -function slip(bytes32 ilk, address usr, int256 wad) external auth {
    // -    gem[ilk][usr] = _add(gem[ilk][usr], wad);
    // -}

    /**
     * @notice Transfers collateral between two addresses for a collateral type.
     * @dev Requires the caller to have permission to modify the source address’s Safe.
     * @param collateralType Unique identifier for the collateral type.
     * @param src Source address to debit collateral from.
     * @param dst Destination address to credit collateral to.
     * @param wad Amount of collateral to transfer [wad].
     */
    function transferCollateral(bytes32 collateralType, address src, address dst, uint256 wad) external {
        require(canModifySAFE(src, msg.sender), "SafeEngine/not-allowed");
        tokenCollateral[collateralType][src] = _sub(tokenCollateral[collateralType][src], wad);
        tokenCollateral[collateralType][dst] = _add(tokenCollateral[collateralType][dst], wad);

        emit TransferCollateral(collateralType, src, dst, wad);
    }
    // -function flux(bytes32 ilk, address src, address dst, uint256 wad) external {
    // -    require(wish(src, msg.sender), "Vat/not-allowed");
    // -    gem[ilk][src] = _sub(gem[ilk][src], wad);
    // -    gem[ilk][dst] = _add(gem[ilk][dst], wad);
    // -}

    /**
     * @notice Transfers internal StablePro coins between two addresses.
     * @dev Requires the caller to have permission to modify the source address’s Safe.
     * @param src Source address to debit coins from.
     * @param dst Destination address to credit coins to.
     * @param rad Amount of StablePro coins to transfer [rad].
     */
    function transferInternalCoins(address src, address dst, uint256 rad) external {
        require(canModifySAFE(src, msg.sender), "SafeEngine/not-allowed");
        coinBalance[src] = _sub(coinBalance[src], rad);
        coinBalance[dst] = _add(coinBalance[dst], rad);

        emit TransferInternalCoins(src, dst, rad);
    }
    // -function move(address src, address dst, uint256 rad) external {
    // -    require(wish(src, msg.sender), "Vat/not-allowed");
    // -    dai[src] = _sub(dai[src], rad);
    // -    dai[dst] = _add(dai[dst], rad);
    // -}

    // -function either(bool x, bool y) internal pure returns (bool z) {
    // -    assembly{ z := or(x, y)}
    // -}

    // -function both(bool x, bool y) internal pure returns (bool z) {
    // -    assembly{ z := and(x, y)}
    // -}

    // --- CDP Manipulation ---
    /**
     * @notice Modifies a Safe’s collateral and debt, ensuring collateralization requirements.
     * @dev Updates Safe state, checks debt ceilings, collateralization ratio, and permissions.
     *      Requires contract to be enabled and collateral type to be initialized.
     * @param collateralType Unique identifier for the collateral type.
     * @param cdp Address of the Safe to modify.
     * @param collateralSource Address to debit/credit collateral.
     * @param debtDestination Address to debit/credit debt.
     * @param deltaCollateral Amount to add (positive) or remove (negative) collateral [wad].
     * @param deltaDebt Amount to add (positive) or remove (negative) debt [wad].
     */
    function modifySAFECollateralization(
        bytes32 collateralType,
        address cdp,
        address collateralSource,
        address debtDestination,
        int deltaCollateral,
        int deltaDebt
    ) external {
        // Check contract is enabled
        require(contractEnabled == 1, "SafeEngine/not-contractEnabled");

        SAFE memory safe_ = safes[collateralType][cdp];

        // Refresh safety price
        updateSafetyPrice(collateralType);
        CollateralType memory collateralType_ = collateralTypes[collateralType];
        // Check collateral type is initialized
        require(collateralType_.accumulatedRate != 0, "SafeEngine/collateral-type-not-init");

        // Update Safe state
        safe_.lockedCollateral = _add(safe_.lockedCollateral, deltaCollateral);
        safe_.generatedDebt = _add(safe_.generatedDebt, deltaDebt);
        collateralType_.debtAmount = _add(collateralType_.debtAmount, deltaDebt);

        // Calculate debt impact
        int dAmountToRaise = _mul(collateralType_.accumulatedRate, deltaDebt);
        uint amountToRaise = _mul(collateralType_.accumulatedRate, safe_.generatedDebt);
        globalDebt = _add(globalDebt, dAmountToRaise);

        // Validate debt ceilings
        require(
            either(
                deltaDebt <= 0,
                both(
                    _mul(collateralType_.debtAmount, collateralType_.accumulatedRate) <= collateralType_.debtCeiling,
                    globalDebt <= globalDebtCeiling
                )
            ),
            "SafeEngine/ceiling-exceeded"
        );
        // Validate collateralization ratio
        require(
            either(
                both(deltaDebt <= 0, deltaCollateral >= 0),
                amountToRaise <= _mul(safe_.lockedCollateral, collateralType_.safetyPrice)
            ),
            "SafeEngine/not-safe"
        );

        // Validate permissions
        require(
            either(both(deltaDebt <= 0, deltaCollateral >= 0), canModifySAFE(cdp, msg.sender)),
            "SafeEngine/not-allowed-u"
        );
        require(either(deltaCollateral <= 0, canModifySAFE(collateralSource, msg.sender)), "SafeEngine/not-allowed-v");
        require(either(deltaDebt >= 0, canModifySAFE(debtDestination, msg.sender)), "SafeEngine/not-allowed-w");

        // Validate debt floor
        require(either(safe_.generatedDebt == 0, amountToRaise >= collateralType_.debtFloor), "SafeEngine/debtFloor");

        // Update balances
        tokenCollateral[collateralType][collateralSource] =
                        _sub(tokenCollateral[collateralType][collateralSource], deltaCollateral);
        coinBalance[debtDestination] = _add(coinBalance[debtDestination], dAmountToRaise);

        // Save state
        safes[collateralType][cdp] = safe_;
        collateralTypes[collateralType] = collateralType_;

        emit ModifySAFECollateralization(
            collateralType,
            cdp,
            collateralSource,
            debtDestination,
            deltaCollateral,
            deltaDebt,
            safe_.lockedCollateral,
            safe_.generatedDebt,
            globalDebt
        );
    }
    // -function frob(bytes32 i, address u, address v, address w, int dink, int dart) external {
    // -    // system is live
    // -    require(live == 1, "Vat/not-live");
    // -
    // -    Urn memory urn = urns[i][u];
    // -    Ilk memory ilk = ilks[i];
    // -    // ilk has been initialised
    // -    require(ilk.rate != 0, "Vat/ilk-not-init");
    // -
    // -    urn.ink = _add(urn.ink, dink);
    // -    urn.art = _add(urn.art, dart);
    // -    ilk.Art = _add(ilk.Art, dart);
    // -
    // -    int dtab = _mul(ilk.rate, dart);
    // -    uint tab = _mul(ilk.rate, urn.art);
    // -    debt     = _add(debt, dtab);
    // -
    // -    // either debt has decreased, or debt ceilings are not exceeded
    // -    require(either(dart <= 0, both(_mul(ilk.Art, ilk.rate) <= ilk.line, debt <= Line)), "Vat/ceiling-exceeded");
    // -    // urn is either less risky than before, or it is safe
    // -    require(either(both(dart <= 0, dink >= 0), tab <= _mul(urn.ink, ilk.spot)), "Vat/not-safe");
    // -
    // -    // urn is either more safe, or the owner consents
    // -    require(either(both(dart <= 0, dink >= 0), wish(u, msg.sender)), "Vat/not-allowed-u");
    // -    // collateral src consents
    // -    require(either(dink <= 0, wish(v, msg.sender)), "Vat/not-allowed-v");
    // -    // debt dst consents
    // -    require(either(dart >= 0, wish(w, msg.sender)), "Vat/not-allowed-w");
    // -
    // -    // urn has no debt, or a non-dusty amount
    // -    require(either(urn.art == 0, tab >= ilk.dust), "Vat/dust");
    // -
    // -    gem[i][v] = _sub(gem[i][v], dink);
    // -    dai[w]    = _add(dai[w],    dtab);
    // -
    // -    urns[i][u] = urn;
    // -    ilks[i]    = ilk;
    // -}

    // --- CDP Fungibility ---
    /**
     * @notice Transfers collateral and debt between two Safes.
     * @dev Updates source and destination Safe states, ensuring collateralization and debt floor requirements.
     *      Requires caller to have permission for both Safes.
     * @param collateralType Unique identifier for the collateral type.
     * @param src Address of the source Safe.
     * @param dst Address of the destination Safe.
     * @param deltaCollateral Amount to transfer collateral [wad].
     * @param deltaDebt Amount to transfer debt [wad].
     */
    function transferSAFECollateralAndDebt(
        bytes32 collateralType,
        address src,
        address dst,
        int deltaCollateral,
        int deltaDebt
    ) external {
        // Refresh safety price
        updateSafetyPrice(collateralType);
        SAFE storage srcCDP = safes[collateralType][src];
        SAFE storage dstCDP = safes[collateralType][dst];
        CollateralType storage col = collateralTypes[collateralType];

        // Transfer collateral and debt
        srcCDP.lockedCollateral = _sub(srcCDP.lockedCollateral, deltaCollateral);
        srcCDP.generatedDebt = _sub(srcCDP.generatedDebt, deltaDebt);
        dstCDP.lockedCollateral = _add(dstCDP.lockedCollateral, deltaCollateral);
        dstCDP.generatedDebt = _add(dstCDP.generatedDebt, deltaDebt);

        // Calculate debt values
        uint srcAmountToRaise = _mul(srcCDP.generatedDebt, col.accumulatedRate);
        uint dstAmountToRaise = _mul(dstCDP.generatedDebt, col.accumulatedRate);

        // Validate permissions
        require(both(canModifySAFE(src, msg.sender), canModifySAFE(dst, msg.sender)), "SafeEngine/not-allowed");

        // Validate collateralization
        require(srcAmountToRaise <= _mul(srcCDP.lockedCollateral, col.safetyPrice), "SafeEngine/not-safe-src");
        require(dstAmountToRaise <= _mul(dstCDP.lockedCollateral, col.safetyPrice), "SafeEngine/not-safe-dst");

        // Validate debt floor
        require(either(srcAmountToRaise >= col.debtFloor, srcCDP.generatedDebt == 0), "SafeEngine/debtFloor-src");
        require(either(dstAmountToRaise >= col.debtFloor, dstCDP.generatedDebt == 0), "SafeEngine/debtFloor-dst");

        emit TransferSAFECollateralAndDebt(
            collateralType,
            src,
            dst,
            deltaCollateral,
            deltaDebt,
            srcCDP.lockedCollateral,
            srcCDP.generatedDebt,
            dstCDP.lockedCollateral,
            dstCDP.generatedDebt
        );
    }
    // -function fork(bytes32 ilk, address src, address dst, int dink, int dart) external {
    // -    Urn storage u = urns[ilk][src];
    // -    Urn storage v = urns[ilk][dst];
    // -    Ilk storage i = ilks[ilk];
    // -
    // -    u.ink = _sub(u.ink, dink);
    // -    u.art = _sub(u.art, dart);
    // -    v.ink = _add(v.ink, dink);
    // -    v.art = _add(v.art, dart);
    // -
    // -    uint utab = _mul(u.art, i.rate);
    // -    uint vtab = _mul(v.art, i.rate);
    // -
    // -    // both sides consent
    // -    require(both(wish(src, msg.sender), wish(dst, msg.sender)), "Vat/not-allowed");
    // -
    // -    // both sides safe
    // -    require(utab <= _mul(u.ink, i.spot), "Vat/not-safe-src");
    // -    require(vtab <= _mul(v.ink, i.spot), "Vat/not-safe-dst");
    // -
    // -    // both sides non-dusty
    // -    require(either(utab >= i.dust, u.art == 0), "Vat/dust-src");
    // -    require(either(vtab >= i.dust, v.art == 0), "Vat/dust-dst");
    // -}

    // --- CDP Confiscation ---
    /**
     * @notice Confiscates collateral and debt from a Safe during liquidation.
     * @dev Transfers collateral and debt to counterparties, typically for auctions. Requires CDP access permissions.
     * @param collateralType Unique identifier for the collateral type.
     * @param cdp Address of the Safe to confiscate.
     * @param collateralCounterparty Address to debit collateral from.
     * @param debtCounterparty Address to debit debt from.
     * @param deltaCollateral Amount to confiscate collateral [wad].
     * @param deltaDebt Amount to confiscate debt [wad].
     * @custom:access OnlyCDPAccess
     */
    function confiscateSAFECollateralAndDebt(
        bytes32 collateralType,
        address cdp,
        address collateralCounterparty,
        address debtCounterparty,
        int deltaCollateral,
        int deltaDebt
    ) external onlyCDPAccess {
        SAFE storage safe_ = safes[collateralType][cdp];
        CollateralType storage collateralType_ = collateralTypes[collateralType];

        // Update Safe state
        safe_.lockedCollateral = _add(safe_.lockedCollateral, deltaCollateral);
        safe_.generatedDebt = _add(safe_.generatedDebt, deltaDebt);
        collateralType_.debtAmount = _add(collateralType_.debtAmount, deltaDebt);

        // Calculate debt impact
        int deltaTotalIssuedDebt = _mul(collateralType_.accumulatedRate, deltaDebt);

        // Update balances
        tokenCollateral[collateralType][collateralCounterparty] =
                        _sub(tokenCollateral[collateralType][collateralCounterparty], deltaCollateral);
        debtBalance[debtCounterparty] = _sub(debtBalance[debtCounterparty], deltaTotalIssuedDebt);
        globalUnbackedDebt = _sub(globalUnbackedDebt, deltaTotalIssuedDebt);

        emit ConfiscateSAFECollateralAndDebt(
            collateralType,
            cdp,
            collateralCounterparty,
            debtCounterparty,
            deltaCollateral,
            deltaDebt,
            globalUnbackedDebt
        );
    }
    // -function grab(bytes32 i, address u, address v, address w, int dink, int dart) external auth {
    // -    Urn storage urn = urns[i][u];
    // -    Ilk storage ilk = ilks[i];
    // -
    // -    urn.ink = _add(urn.ink, dink);
    // -    urn.art = _add(urn.art, dart);
    // -    ilk.Art = _add(ilk.Art, dart);
    // -
    // -    int dtab = _mul(ilk.rate, dart);
    // -
    // -    gem[i][v] = _sub(gem[i][v], dink);
    // -    sin[w]    = _sub(sin[w],    dtab);
    // -    vice      = _sub(vice,      dtab);
    // -}

    // --- Settlement ---
    /**
     * @notice Settles debt using available StablePro coin balance.
     * @dev Reduces debt and coin balances for the caller, typically used by AccountingEngine.
     * @param rad Amount of debt to settle [rad].
     */
    function settleDebt(uint rad) external {
        address account = msg.sender;
        debtBalance[account] = _sub(debtBalance[account], rad);
        coinBalance[account] = _sub(coinBalance[account], rad);
        globalUnbackedDebt = _sub(globalUnbackedDebt, rad);
        globalDebt = _sub(globalDebt, rad);

        emit SettleDebt(account, rad, debtBalance[account], coinBalance[account], globalUnbackedDebt, globalDebt);
    }
    // -function heal(uint rad) external {
    // -    address u = msg.sender;
    // -    sin[u] = _sub(sin[u], rad);
    // -    dai[u] = _sub(dai[u], rad);
    // -    vice   = _sub(vice,   rad);
    // -    debt   = _sub(debt,   rad);
    // -}

    /**
     * @notice Creates unbacked debt, typically for bad debt handling.
     * @dev Increases debt and coin balances for destination addresses. Requires CDP access permissions.
     * @param debtDestination Address to assign the unbacked debt.
     * @param coinDestination Address to assign the StablePro coins.
     * @param rad Amount of unbacked debt to create [rad].
     * @custom:access OnlyCDPAccess
     */
    function createUnbackedDebt(address debtDestination, address coinDestination, uint rad) external onlyCDPAccess {
        debtBalance[debtDestination] = _add(debtBalance[debtDestination], rad);
        coinBalance[coinDestination] = _add(coinBalance[coinDestination], rad);
        globalUnbackedDebt = _add(globalUnbackedDebt, rad);
        globalDebt = _add(globalDebt, rad);

         emit CreateUnbackedDebt(
            debtDestination,
            coinDestination,
            rad,
            debtBalance[debtDestination],
            coinBalance[coinDestination],
            globalUnbackedDebt,
            globalDebt
        );
    }
    // -function suck(address u, address v, uint rad) external auth {
    // -    sin[u] = _add(sin[u], rad);
    // -    dai[v] = _add(dai[v], rad);
    // -    vice   = _add(vice,   rad);
    // -    debt   = _add(debt,   rad);
    // -}

    // --- Rates ---
    /**
     * @notice Updates the accumulated stability fee rate for a collateral type.
     * @dev Adjusts the collateral type’s rate and credits surplus to the destination address.
     *      Requires contract to be enabled and CDP access permissions.
     * @param collateralType Unique identifier for the collateral type.
     * @param surplusDst Address to credit surplus StablePro coins.
     * @param accumulatedRate Change in accumulated rate [ray].
     * @custom:access OnlyCDPAccess
     */
    function updateAccumulatedRate(
        bytes32 collateralType,
        address surplusDst,
        int accumulatedRate
    ) external onlyCDPAccess {
        require(contractEnabled == 1, "SafeEngine/not-contractEnabled");
        CollateralType storage collateralType_ = collateralTypes[collateralType];
        collateralType_.accumulatedRate = _add(collateralType_.accumulatedRate, accumulatedRate);
        int deltaSurplus = _mul(collateralType_.debtAmount, accumulatedRate);
        coinBalance[surplusDst] = _add(coinBalance[surplusDst], deltaSurplus);
        globalDebt = _add(globalDebt, deltaSurplus);

        emit UpdateAccumulatedRate(collateralType, surplusDst, accumulatedRate, globalDebt);
    }
    // -function fold(bytes32 i, address u, int rate) external auth {
    // -    require(live == 1, "Vat/not-live");
    // -    Ilk storage ilk = ilks[i];
    // -    ilk.rate = _add(ilk.rate, rate);
    // -    int rad  = _mul(ilk.Art, rate);
    // -    dai[u]   = _add(dai[u], rad);
    // -    debt     = _add(debt,   rad);
    // -}
}

