// it under the terms of the GNU Affero General Public License as published by
// the Free Software Foundation, either version 3 of the License, or
// (at your option) any later version.
//
// This program is distributed in the hope that it will be useful,
// but WITHOUT ANY WARRANTY; without even the implied warranty of
// MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the
// GNU Affero General Public License for more details.
//
// You should have received a copy of the GNU Affero General Public License
// along with this program.  If not, see <https://www.gnu.org/licenses/>.

pragma solidity ^0.8.0;

import "../interfaces/ISafeEngine.sol";
import "../interfaces/ITaxCollector.sol";
import "../../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

// FIXME: This contract was altered compared to the production version.
// It doesn't use LibNote anymore.
// New deployments of this contract will need to include custom events (TO DO).

// CHANGED: added interface and moved struct there.
// Added SuAuthenticated instead of authorizedAccounts

/**
 * @title TaxCollector
 * @notice Manages stability fees for collateral types in the StableUnit protocol.
 * @dev Calculates and applies per-second stability fees to Safes, updating accumulated rates in SafeEngine.
 *      Supports a global base fee and per-collateral-type fees, with surplus directed to a primary tax receiver
 *      (e.g., AccountingEngine). Uses SuAuthenticated for access control. Integrates with SafeEngine
 *      to adjust debt balances based on accrued fees. This contract is a fork of MakerDAO’s TaxCollector.sol, adapted
 *      for StableUnit with simplified math and custom access control.
 * @custom:usage
 * - **Initialization**: Deploy with access control and SafeEngine addresses.
 *   ```solidity
 *   TaxCollector taxCollector = new TaxCollector(accessControlAddress, safeEngineAddress);
 *   ```
 * - **Collateral Type Setup**: Initialize a collateral type’s fee parameters.
 *   ```solidity
 *   taxCollector.initializeCollateralType("ETH-A");
 *   ```
 * - **Fee Collection**: Trigger fee calculation for a collateral type.
 *   ```solidity
 *   uint256 newRate = taxCollector.taxSingle("ETH-A"); // Update fees for ETH-A
 *   ```
 * - **Parameter Modification**: DAO updates stability fees, base fee, or tax receiver.
 *   ```solidity
 *   taxCollector.modifyParameters("ETH-A", "stabilityFee", newFee); // Update collateral fee
 *   taxCollector.modifyParameters("base", newBaseFee); // Update global base fee
 *   taxCollector.modifyParameters("primaryTaxReceiver", newReceiver); // Update tax receiver
 *   ```
 * - **Disabling**: DisablerAccess accounts can disable the contract.
 *   ```solidity
 *   taxCollector.disableContract(); // Halt operations
 *   ```
 * @custom:security Only authorized accounts (DAO, Admin, DisablerAccess) can modify parameters or disable the contract.
 *                 Fee calculations rely on SafeEngine’s rate updates, and `taxSingle` requires up-to-date timestamps.
 *                 The contract assumes `primaryTaxReceiver` is a valid address (e.g., AccountingEngine).
 * @custom:changes Replaced MakerDAO’s `authorizedAccounts` with `SuAuthenticated`.
 *                 Removed `LibNote` for event logging, requiring custom events (TODO).
 *                 Added `ITaxCollector` interface and moved `CollateralType` struct there.
 *                 Simplified math functions (`_rpow`, `_rmul`, `_add`, `_diff`) for gas efficiency.
 * @custom:events No events are defined; custom events need to be added (as noted in FIXME).
 *                Placeholder events (e.g., `DisableContract`) are included in comments but not defined.
 */
contract TaxCollector is ITaxCollector, SuAuthenticated {
    /// @notice Maps collateral types to their stability fee configurations.
    mapping (bytes32 => CollateralType) public collateralTypes;
    /// @notice Reference to the SafeEngine contract for rate updates.
    ISafeEngine public safeEngine; // CDP Engine
    /// @notice Address receiving collected stability fees (e.g., AccountingEngine).
    address public primaryTaxReceiver; // Debt Engine
    /// @notice Global per-second stability fee contribution applied to all collateral types [ray].
    uint256 public base; // Global, per-second stability fee contribution [ray]


    // --- Init ---
    /**
     * @notice Initializes the TaxCollector with access control and SafeEngine addresses.
     * @dev Sets the SafeEngine reference and inherits access control from SuAuthenticated.
     * @param accessControl Address of the access control singleton contract.
     * @param safeEngine_ Address of the SafeEngine contract.
     */
    function initialize(address accessControl, address safeEngine_) public initializer {
        __suAuthenticatedInit(accessControl);

        safeEngine = ISafeEngine(safeEngine_);
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // --- Disable ---
    /// @notice Tracks whether the contract is enabled (0) or disabled (non-zero).
    uint public contractEnabled;

    /**
     * @notice Disables the contract, preventing further state-changing operations.
     * @dev Sets `contractEnabled` to 0 and emits a placeholder event. Only callable by DisablerAccess accounts.
     * @custom:access OnlyDisablerAccess
     */
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0;
        // TODO: Define and emit custom event
        emit DisableContract();
    }

    // --- Math ---
    /**
     * @notice Calculates x raised to the power n, divided by base b, with precision handling.
     * @dev Uses assembly for gas-efficient exponentiation, suitable for stability fee calculations.
     *      Reverts on overflow or division errors.
     * @param x Base value [ray].
     * @param n Exponent (time delta in seconds).
     * @param b Denominator for precision scaling [ray].
     * @return z Result of (x^n) / b [ray].
     */
    function _rpow(uint x, uint n, uint b) internal pure returns (uint z) {
      assembly {
        switch x case 0 {switch n case 0 {z := b} default {z := 0}}
        default {
          switch mod(n, 2) case 0 { z := b } default { z := x }
          let half := div(b, 2) // for rounding.
          for { n := div(n, 2) } n { n := div(n,2) } {
            let xx := mul(x, x)
            if iszero(eq(div(xx, x), x)) { revert(0,0) } // Check for overflow
            let xxRound := add(xx, half)
            if lt(xxRound, xx) { revert(0,0) } // Check for rounding overflow
            x := div(xxRound, b)
            if mod(n,2) {
              let zx := mul(z, x)
              if and(iszero(iszero(x)), iszero(eq(div(zx, x), z))) { revert(0,0) } // Check for overflow
              let zxRound := add(zx, half)
              if lt(zxRound, zx) { revert(0,0) } // Check for rounding overflow
              z := div(zxRound, b)
            }
          }
        }
      }
    }

    /// @notice Constant for ray precision (10^27).
    uint256 constant ONE = 10 ** 27;

    /**
     * @notice Adds two unsigned integers, reverting on overflow.
     * @dev Ensures safe addition for stability fee calculations.
     * @param x First operand.
     * @param y Second operand.
     * @return z Sum of x and y.
     */
    function _add(uint x, uint y) internal pure returns (uint z) {
        z = x + y;
        require(z >= x);
    }

    /**
     * @notice Subtracts two unsigned integers, returning the signed difference.
     * @dev Ensures non-negative inputs for rate difference calculations.
     * @param x First operand.
     * @param y Second operand.
     * @return z Difference of x and y (signed).
     */
    function _diff(uint x, uint y) internal pure returns (int z) {
        z = int(x) - int(y);
        require(int(x) >= 0 && int(y) >= 0);
    }

    /**
     * @notice Multiplies two unsigned integers in ray precision, scaling down by ONE.
     * @dev Ensures safe multiplication and division for rate calculations.
     * @param x First operand [ray].
     * @param y Second operand [ray].
     * @return z Product of x and y, divided by ONE [ray].
     */
    function _rmul(uint x, uint y) internal pure returns (uint z) {
        z = x * y;
        require(y == 0 || z / y == x); // Check for overflow
        z = z / ONE;
    }

    // --- Administration ---
    /**
     * @notice Initializes a new collateral type for stability fee collection.
     * @dev Sets initial stability fee to ONE (no fee) and records the current timestamp.
     *      Reverts if already initialized. Only callable by Admin.
     * @param collateralType Unique identifier for the collateral type (e.g., "ETH-A").
     * @custom:access OnlyAdmin
     */
    function initializeCollateralType(bytes32 collateralType) external onlyAdmin {
        CollateralType storage i = collateralTypes[collateralType];
        require(i.stabilityFee == 0, "TaxCollector/collateralType-already-init");
        i.stabilityFee = ONE; // 1e27 (no fee)
        i.latestUpdateTime = block.timestamp;

        emit InitializeCollateralType(collateralType);
    }

    /**
     * @notice Modifies stability fee for a specific collateral type.
     * @dev Updates the stability fee, requiring the collateral type’s timestamp to be up-to-date.
     *      Only callable by DAO.
     * @param collateralType Unique identifier for the collateral type.
     * @param what Parameter to modify (must be "stabilityFee").
     * @param data New stability fee value [ray].
     * @custom:access OnlyDAO
     */
    function modifyParameters(bytes32 collateralType, bytes32 what, uint data) external onlyDAO {
        require(
            block.timestamp == collateralTypes[collateralType].latestUpdateTime,
            "TaxCollector/latestUpdateTime-not-updated"
        );
        if (what == "stabilityFee") collateralTypes[collateralType].stabilityFee = data;
        else revert("TaxCollector/modifyParameters-unrecognized-param");

        emit ModifyParameters(collateralType, what, data);
    }

    /**
     * @notice Modifies global protocol parameters.
     * @dev Updates the global base stability fee. Only callable by DAO.
     * @param what Parameter to modify (must be "base").
     * @param data New base fee value [ray].
     * @custom:access OnlyDAO
     */
    function modifyParameters(bytes32 what, uint data) external onlyDAO {
        if (what == "base") base = data;
        else revert("TaxCollector/modifyParameters-unrecognized-param");

        emit ModifyParameters(what, data);
    }

    /**
     * @notice Modifies the primary tax receiver address.
     * @dev Updates the address receiving collected fees (e.g., AccountingEngine). Only callable by DAO.
     * @param what Parameter to modify (must be "primaryTaxReceiver").
     * @param data New receiver address.
     * @custom:access OnlyDAO
     */
    function modifyParameters(bytes32 what, address data) external onlyDAO {
        if (what == "primaryTaxReceiver") primaryTaxReceiver = data;
        else revert("TaxCollector/modifyParameters-unrecognized-param");
    }

    /**
     * @notice Collects stability fees for a collateral type and updates SafeEngine rates.
     * @dev Calculates the new accumulated rate based on elapsed time and combined stability fees
     *      (base + collateral-specific). Updates SafeEngine and records the current timestamp.
     * @param collateralType Unique identifier for the collateral type.
     * @return rate New accumulated rate for the collateral type [ray].
     */
    function taxSingle(bytes32 collateralType) external returns (uint rate) {
        require(
            block.timestamp >= collateralTypes[collateralType].latestUpdateTime,
            "TaxCollector/invalid-block-timestamp"
        );
        (, uint prevRate,,,,,,) = safeEngine.collateralTypes(collateralType);
        // Calculate new rate: prevRate * (1 + (base + stabilityFee))^(time delta)
        rate = _rmul(
            _rpow(
                _add(base, collateralTypes[collateralType].stabilityFee),
                block.timestamp - collateralTypes[collateralType].latestUpdateTime,
                ONE
            ),
            prevRate
        );
        // Update SafeEngine with rate difference
        safeEngine.updateAccumulatedRate(collateralType, primaryTaxReceiver, _diff(rate, prevRate));
        // Record update time
        collateralTypes[collateralType].latestUpdateTime = block.timestamp;

        emit TaxSingle(collateralType, rate);
    }
}

