import "../../interfaces/IRewardChefV2.sol";
// +  import
// Difference: StableUnit includes (but comments out) IRewardChefV2 for potential reward distribution, not present in MakerDAO.

import "../../exchange/contracts/periphery/contracts/interfaces/oracle/ISuOracle.sol";
// +  import
// Difference: StableUnit uses ISuOracle for price feeds in stablecoin swaps.

import "../../exchange/contracts/periphery/contracts/interfaces/oracle/ISuOracleAggregator.sol";
// +  import
// Difference: StableUnit uses ISuOracleAggregator for price feeds in auctions.

import "../../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";
// +  import (uses wards mapping)
// Difference: StableUnit uses SuAuthenticated for role-based access control (DAO, Admin, etc.), replacing MakerDAO's wards.

import "../../exchange/contracts/interfaces/IExchange.sol";
// +  import (uses FlapLike, FlopLike)
// Difference: StableUnit uses IExchange for auctions, replacing MakerDAO's FlapLike and FlopLike.

// - // FIXME: This contract was altered compared to the production version. It doesn't use LibNote anymore.
// Difference: Both note the removal of LibNote for event logging, but StableUnit explicitly plans custom events.

/**
 * @title AccountingEngine
 * @notice Manages surplus and debt settlement in the StableUnit protocol.
 * @dev Handles debt queuing, surplus auctions, debt auctions, and stablecoin swaps. Integrates with SafeEngine for debt
 *      settlement, Exchange for auctions, and CoinJoin for StablePro operations. Supports a whitelist of stablecoins for
 *      swaps and a surplus buffer to ensure protocol solvency. This contract is a fork of MakerDAO’s AccountingEngine.sol,
 *      adapted for StableUnit with an Exchange module, stablecoin whitelist, and commented-out RewardChef integration.
 * @custom:usage
 * - **Initialization**: Deploy with required contract addresses.
 *   ```solidity
 *   AccountingEngine accountingEngine = new AccountingEngine(accessControl, safeEngine, exchange, oracle, suDAO, stablePro, coinJoin);
 *   ```
 * - **Debt Queuing**: Push bad debt to the queue for auctioning.
 *   ```solidity
 *   accountingEngine.pushDebtToQueue(amountToRaise);
 *   ```
 * - **Debt Auction**: Auction governance tokens to cover debt.
 *   ```solidity
 *   uint orderId = accountingEngine.auctionDebt(tokenToSell, amountToSell, discountType, auctionDebt);
 *   ```
 * - **Surplus Auction**: Auction StablePro for governance tokens to burn.
 *   ```solidity
 *   uint orderId = accountingEngine.auctionSurplus(surplusAmount, discountType);
 *   ```
 * - **Stablecoin Swap**: Swap StablePro for a whitelisted stablecoin.
 *   ```solidity
 *   accountingEngine.swapStableProToStablecoin(stablecoin, stableProAmount);
 *   ```
 * - **Disabling**: Disable the contract and settle remaining debt.
 *   ```solidity
 *   accountingEngine.disableContract();
 *   ```
 * @custom:security Only authorized accounts (DAO, Admin, DisablerAccess, LiquidationAccess) can call state-changing functions.
 *                 Relies on SafeEngine for debt tracking and Exchange for auctions. Stablecoin whitelist ensures trusted tokens.
 * @custom:changes Replaced MakerDAO’s auction houses with Exchange module. Removed LibNote for event logging, requiring
 *                 custom events (TODO). Added stablecoin whitelist and swap functions. Commented out RewardChef integration.
 * @custom:events Emits `StablecoinRefilled`, `StableProSwapped` for manual operations; custom events need definition (TODO).
 */


interface DebtAuctionHouseLike {
    function startAuction(address,uint256 amountToSell,address tokenToSell)   external returns (uint256);
    function protocolToken() external view returns (address);
    function disableContract() external;
}

interface  SurplusAuctionHouseLike {
    function startAuction(uint256 amountToSell,uint256) external returns (uint256);
    function protocolToken() external view returns (address);
}

 // - contract Vow {
contract AccountingEngine is SuAuthenticated, Math, IAccountingEngine {
    // --- Data ---
    /// @notice Reference to the SafeEngine contract for debt and surplus tracking.
    // - VatLike public vat;        // CDP Engine
    ISafeEngine public safeEngine; // CDP Engine
    // Difference: Renamed to safeEngine and uses ISafeEngine interface for type safety and clarity.

    /// @notice Reference to the Exchange module for auctions.
    // - FlapLike public flapper;   // Surplus Auction House
    // - FlopLike public flopper;   // Debt Auction House
    IExchange public exchange; // Exchange module
    // Difference: StableUnit uses a single Exchange module for both surplus and debt auctions, replacing MakerDAO's separate FlapLike and FlopLike.

    /// @notice Reference to the oracle for price feeds.
    // +
    ISuOracleAggregator public oracle; // Oracle
    // Difference: StableUnit adds oracle for dynamic price feeds in auctions and swaps.

    /// @notice Reference to the governance token (suDAO).
    // +
    IERC20 public suDAO; // Protocol governance token
    // Difference: StableUnit explicitly tracks suDAO for governance token auctions.

    /// @notice Reference to the system coin (StablePro).
    // +
    IERC20 public stablePro; // System coin
    // Difference: StableUnit tracks stablePro for StablePro operations.

    /// @notice Reference to the RewardChef contract (commented out).
    // +
    // IRewardChefV2 public rewardChef; // RewardModule
    // Difference: StableUnit includes (but comments out) rewardChef for potential reward distribution.

    /// @notice Reference to the CoinJoin contract for StablePro operations.
    // +
    ICoinJoin public coinJoin; // Adapter to join StablePro
    // Difference: StableUnit adds coinJoin for StablePro joining/exiting.

    /// @notice Maps addresses to their stablecoin whitelist status.
    // +
    mapping(address => bool) internal stablecoinWhitelist;
    // Difference: StableUnit introduces a stablecoin whitelist for swap operations.

    /// @notice Stablecoin address for collateral liquidation swaps.
    // +
    address public stablecoinToSwap; // Stablecoin to which the collateral will be exchanged during liquidation
    // Difference: StableUnit adds stablecoinToSwap for liquidation swaps.

    /// @notice Maps timestamps to queued debt amounts [rad].
    // - mapping (uint256 => uint256) public sin;  // debt queue
    mapping (uint256 => uint256) public debtQueue; // debt queue
    // Difference: Renamed to debtQueue for clarity, but functionality is identical.

    /// @notice Total debt queued for auctioning [rad].
    // - uint256 public Sin;   // Queued debt [rad]
    uint256 public totalQueuedDebt; // Queued debt [rad]
    // Difference: Renamed to totalQueuedDebt for clarity, but functionality is identical.

    /// @notice Total debt currently being auctioned [rad].
    // - uint256 public Ash;   // On-auction debt [rad]
    uint256 public totalOnAuctionDebt; // On-auction debt [rad]
    // Difference: Renamed to totalOnAuctionDebt for clarity, but functionality is identical.

    /// @notice Delay before debt can be popped from the queue [seconds].
    // - uint256 public wait;  // Flop delay [seconds]
    uint256 public popDebtDelay; // DebtAuctionHouse delay [seconds]
    // Difference: Renamed to popDebtDelay for clarity, but functionality is identical.

    // - uint256 public dump;  // Flop initial lot size [wad]
    // - uint256 public sump;  // Flop fixed bid size [rad]
    // Difference: StableUnit removes dump and sump, as auction sizes are handled by Exchange module parameters.

    /// @notice Minimum surplus to maintain before auctioning [rad].
    // - uint256 public bump;  // Flap fixed lot size [rad]
    // - uint256 public hump;  // Surplus buffer [rad]
    uint256 public surplusBuffer; // Surplus buffer [rad]
    // Difference: Combines MakerDAO's bump and hump into surplusBuffer, focusing on maintaining a surplus buffer.

    /// @notice Flag indicating if the contract is enabled (1) or disabled (0).
    // - uint256 public live;  // Active Flag
    uint256 public contractEnabled; // Active Flag
    // Difference: Renamed to contractEnabled for clarity, but functionality is identical.

    /// @notice Default reward end block for RewardChef (if enabled) [block number].
    // +
    uint256 public defaultRewardEndBlock; // Active Flag


    // Contract that handles auctions for surplus stability fees (sell coins for sudao tokens that are then burned)
    SurplusAuctionHouseLike    public surplusAuctionHouse;
    /**
      Contract that handles auctions for debt that couldn't be covered by collateral
      auctions (it prints protocol tokens in exchange for coins that will settle the debt)
    **/
    DebtAuctionHouseLike       public debtAuctionHouse;

    // Difference: StableUnit adds defaultRewardEndBlock for potential RewardChef integration.


    // --- Init ---
    /**
     * @notice Initializes the AccountingEngine with required contract addresses.
     * @dev Sets immutable references, approves CoinJoin for SafeEngine modifications, and configures initial state.
     *      RewardChef integration is commented out.
     * @param accessControl_ Address of the access control singleton.
     * @param safeEngine_ Address of the SafeEngine contract.
     * @param exchange_ Address of the Exchange module.
     * @param oracle_ Address of the oracle aggregator.
     * @param suDAO_ Address of the governance token.
     * @param stablePro_ Address of the StablePro token.
     * @param coinJoin_ Address of the CoinJoin contract.
     */
    // - constructor(address vat_, address flapper_, address flopper_) public { ... }
    function initialize(
        address accessControl_,
        address safeEngine_,
        address exchange_,
        address oracle_,
        address suDAO_,
        //address rewardChef_,
        address stablePro_,
        address coinJoin_
    ) public initializer {
        // - wards[msg.sender] = 1;
        __suAuthenticatedInit(accessControl_); // Set up role-based access control
        // - vat = VatLike(vat_);
        safeEngine = ISafeEngine(safeEngine_); // Assign SafeEngine contract
        // - flapper = FlapLike(flapper_);
        // - flopper = FlopLike(flopper_);
        exchange = IExchange(exchange_); // Assign Exchange module
        oracle = ISuOracleAggregator(oracle_); // Assign oracle for price feeds
        suDAO = IERC20(suDAO_); // Assign governance token
        stablePro = IERC20(stablePro_); // Assign StablePro token
        //rewardChef = IRewardChefV2(rewardChef_); // Assign RewardChef (commented out)
        coinJoin = ICoinJoin(coinJoin_); // Assign CoinJoin contract
        // - vat.hope(flapper_);
        safeEngine.approveSAFEModification(coinJoin_); // Approve CoinJoin for SafeEngine modifications
        // - live = 1;
        contractEnabled = 1; // Enable the contract
        defaultRewardEndBlock = 172800; // Set default reward end block (1 month)
        // Difference: StableUnit uses initializer for upgradeability, SuAuthenticated for access control, and supports additional contracts (oracle, suDAO, stablePro, coinJoin).
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    // +
    constructor() {
        _disableInitializers(); // Disable initializers for safety
        // Difference: StableUnit adds constructor for upgradeability, not present in MakerDAO.
    }

    // --- Math ---
    // - function add(uint x, uint y) internal pure returns (uint z) { require((z = x + y) >= x); }
    // - function sub(uint x, uint y) internal pure returns (uint z) { require((z = x - y) <= x); }
    // - function min(uint x, uint y) internal pure returns (uint z) { return x <= y ? x : y; }
    // Difference: StableUnit moves math operations to Math.sol, using _add, _sub, and min for safe arithmetic.

    // --- Auth ---
    // - mapping (address => uint) public wards;
    // - function rely(address usr) external auth { require(live == 1, "Vow/not-live"); wards[usr] = 1; }
    // - function deny(address usr) external auth { wards[usr] = 0; }
    // - modifier auth { require(wards[msg.sender] == 1, "Vow/not-authorized"); _; }
    // Difference: StableUnit replaces wards-based auth with SuAuthenticated's role-based access control (onlyDAO, onlyAdmin, etc.).

    // --- Administration ---
    /**
     * @notice Modifies protocol parameters.
     * @dev Updates timing or buffer parameters. Only callable by DAO.
     * @param what Parameter to modify ("popDebtDelay", "surplusBuffer", or "defaultRewardEndBlock").
     * @param data New parameter value.
     */
    // - function file(bytes32 what, uint data) external auth { ... }
    function modifyParameters(bytes32 what, uint data) external onlyDAO {
        // - if (what == "wait") wait = data;
        if (what == "popDebtDelay") popDebtDelay = data; // Update debt queue delay
        // - else if (what == "bump") bump = data;
        // - else if (what == "sump") sump = data;
        // - else if (what == "dump") dump = data;
        // - else if (what == "hump") hump = data;
        else if (what == "surplusBuffer") surplusBuffer = data; // Update surplus buffer
        else if (what == "defaultRewardEndBlock") defaultRewardEndBlock = data; // Update reward end block
        // - else revert("Vow/file-unrecognized-param");
        else revert BadModifyParameters(); // Revert for unrecognized parameter
        // Difference: StableUnit renames parameters (wait to popDebtDelay, hump to surplusBuffer) and adds defaultRewardEndBlock. Uses onlyDAO instead of auth.
        emit ModifyParameters(what, data);
    }

    /**
     * @notice Modifies contract addresses.
     * @dev Updates references to Exchange, oracle, suDAO, or CoinJoin. RewardChef update is commented out. Only callable by DAO.
     * @param what Parameter to modify ("exchange", "oracle", "suDAO", or "coinJoin").
     * @param data New contract address.
     */
    // - function file(bytes32 what, address data) external auth { ... }
    function modifyParameters(bytes32 what, address data) external onlyDAO {
        // - if (what == "flapper") { vat.nope(address(flapper)); flapper = FlapLike(data); vat.hope(data); }
        if (what == "exchange") exchange = IExchange(data); // Update Exchange module
        // - else if (what == "flopper") flopper = FlopLike(data);
        else if (what == "oracle") oracle = ISuOracleAggregator(data); // Update oracle
        else if (what == "suDAO") suDAO = IERC20(data); // Update governance token
        else if (what == "coinJoin") coinJoin = ICoinJoin(data); // Update CoinJoin contract

        else if (what == "surplusAuctionHouse") {
            safeEngine.denySAFEModification(address(surplusAuctionHouse));
            surplusAuctionHouse = SurplusAuctionHouseLike(data);
            safeEngine.approveSAFEModification(data);
        } else if (what == "debtAuctionHouse") {
            debtAuctionHouse = DebtAuctionHouseLike(data);
        }
        else revert BadModifyParameters();

        emit ModifyParameters(what, data);
        // Difference: StableUnit supports additional contracts (oracle, suDAO, coinJoin) and uses Exchange instead of flapper/flopper. RewardChef update is commented out.
    }

    /**
     * @notice Queues debt for auctioning.
     * @dev Adds debt to the queue at the current timestamp. Only callable by LiquidationAccess.
     * @param amountToRaise Debt amount to queue [rad].
     */
    // - function fess(uint tab) external auth { sin[now] = add(sin[now], tab); Sin = add(Sin, tab); }
    function pushDebtToQueue(uint amountToRaise) external onlyLiquidationAccess {
        // - sin[now] = add(sin[now], tab);
        debtQueue[block.timestamp] = _add(debtQueue[block.timestamp], amountToRaise); // Add debt to queue
        // - Sin = add(Sin, tab);
        totalQueuedDebt = _add(totalQueuedDebt, amountToRaise); // Update total queued debt
        // Difference: Renamed to pushDebtToQueue, uses onlyLiquidationAccess instead of auth, and uses block.timestamp instead of now.
        emit PushDebtToQueue(block.timestamp, amountToRaise, totalQueuedDebt);
    }

    /**
     * @notice Removes debt from the queue after the delay period.
     * @dev Clears debt from the specified era if the delay has passed.
     * @param era Timestamp of the debt queue entry.
     */
    // - function flog(uint era) external { require(add(era, wait) <= now, "Vow/wait-not-finished"); Sin = sub(Sin, sin[era]); sin[era] = 0; }
    function popDebtFromQueue(uint era) external {
        // - require(add(era, wait) <= now, "Vow/wait-not-finished");
        if (_add(era, popDebtDelay) > block.timestamp) revert PopDebtDelayNotFinished(); // Ensure delay has passed
        // - Sin = sub(Sin, sin[era]);
        totalQueuedDebt = _sub(totalQueuedDebt, debtQueue[era]); // Reduce total queued debt
        // - sin[era] = 0;
        debtQueue[era] = 0; // Clear debt queue entry
        // Difference: Renamed to popDebtFromQueue, uses popDebtDelay, and replaces now with block.timestamp.
        emit PopDebtFromQueue(era, totalQueuedDebt);
    }

    /**
     * @notice Settles debt using surplus StablePro.
     * @dev Reduces debt and surplus in SafeEngine, ensuring sufficient surplus and debt availability.
     * @param rad Debt amount to settle [rad].
     */
    // - function heal(uint rad) external { ... }
    function settleDebt(uint rad) public {
        // - require(rad <= vat.dai(address(this)), "Vow/insufficient-surplus");
        if (rad > safeEngine.coinBalance(address(this))) revert InsufficientSurplus(); // Ensure sufficient surplus
        // - require(rad <= sub(sub(vat.sin(address(this)), Sin), Ash), "Vow/insufficient-debt");
        if (rad > _sub(_sub(safeEngine.debtBalance(address(this)), totalQueuedDebt), totalOnAuctionDebt))
            revert InsufficientDebt(); // Ensure sufficient debt
        // - vat.heal(rad);
        safeEngine.settleDebt(rad); // Settle debt in SafeEngine
        // Difference: Renamed to settleDebt, uses safeEngine and StableUnit terminology (coinBalance, debtBalance).
        emit SettleDebt(rad, safeEngine.coinBalance(address(this)), safeEngine.debtBalance(address(this)));
    }

    /**
     * @notice Cancels auctioned debt using surplus StablePro.
     * @dev Reduces auctioned debt and settles it with surplus, ensuring sufficient surplus and auctioned debt.
     * @param rad Debt amount to cancel [rad].
     */
    // - function kiss(uint rad) external { ... }
    function cancelAuctionedDebtWithSurplus(uint rad) external {
        // - require(rad <= Ash, "Vow/not-enough-ash");
        if (rad > totalOnAuctionDebt) revert NotEnoughAsh(); // Ensure sufficient auctioned debt
        // - require(rad <= vat.dai(address(this)), "Vow/insufficient-surplus");
        if (rad > safeEngine.coinBalance(address(this))) revert InsufficientSurplus(); // Ensure sufficient surplus
        // - Ash = sub(Ash, rad);
        totalOnAuctionDebt = _sub(totalOnAuctionDebt, rad); // Reduce auctioned debt
        // - vat.heal(rad);
        safeEngine.settleDebt(rad); // Settle debt in SafeEngine
        // Difference: Renamed to cancelAuctionedDebtWithSurplus, uses StableUnit terminology and safeEngine.
        emit CancelAuctionedDebtWithSurplus(rad, totalOnAuctionDebt);
    }

    /**
     * @notice Auctions governance tokens to cover debt.
     * @dev Creates an Exchange order to sell governance tokens for StablePro, increasing auctioned debt.
     * @param token2sell Address of the token to sell (suDAO).
     * @param amountToSell Amount of tokens to sell [token decimals].
     * @param auctionDebt Debt amount to cover [rad].
     * @return id Unique identifier of the Exchange order.
     */
    // - function flop() external returns (uint id) { ... }
    function auctionDebt(address token2sell, uint256 amountToSell, uint256 auctionDebt)
        external onlyAdmin returns (uint id)
    {

        require(stablecoinWhitelist[token2sell], "AccountingEngine/is-not-whitelisted");

        settleDebt(safeEngine.coinBalance(address(this)));

        require(safeEngine.coinBalance(address(this)) == 0, "AccountingEngine/surplus-not-zero");
        require(debtAuctionHouse.protocolToken() != address(0), "AccountingEngine/debt-auction-house-null-prot");

        // - require(sump <= sub(sub(vat.sin(address(this)), Sin), Ash), "Vow/insufficient-debt");
        // - require(vat.dai(address(this)) == 0, "Vow/surplus-not-zero");
        // - Ash = add(Ash, sump);


        //check that amountToSell cover auctionDebt and auctinDebt is not in queue

        totalOnAuctionDebt = _add(totalOnAuctionDebt, auctionDebt); // Increase auctioned debt
        // - id = flopper.kick(address(this), dump, sump);
        uint256 decimals = IERC20Metadata(token2sell).decimals();

        TransferHelper.safeTransfer(token2sell, address(debtAuctionHouse), amountToSell);

        id = debtAuctionHouse.startAuction(address(this), amountToSell, token2sell); // Create Exchange order

        emit AuctionDebt(id, totalOnAuctionDebt, safeEngine.debtBalance(address(this)));
        // Difference: Renamed to auctionDebt, uses Exchange module instead of flopper, and adds oracle-based pricing.
    }

    /**
     * @notice Auctions surplus StablePro for governance tokens to distribute via rewardChef
     * @dev Creates an Exchange order to sell StablePro, ensuring sufficient surplus and no outstanding debt.
     * @param surplusAmount Amount of StablePro to auction [wad].
     * @return id Unique identifier of the Exchange order.
     * @dev Fail: i think its deprecated function, because we dont have rewardChef.
     */
    // - function flap() external returns (uint id) { ... }
    function auctionSurplus(uint256 surplusAmount) external onlyAdmin returns (uint id) {
        // - require(vat.dai(address(this)) >= add(add(vat.sin(address(this)), bump), hump), "Vow/insufficient-surplus");
        uint256 coinBalance = safeEngine.coinBalance(address(this)); // Get surplus balance
        uint256 debtBalance = safeEngine.debtBalance(address(this)); // Get debt balance
        uint256 realBalance = IERC20(stablePro).balanceOf(address(this)); // Get real StablePro balance
        if (
            coinBalance < surplusBuffer ||
            _add(coinBalance, realBalance * 1e27) < _add(_add(debtBalance, surplusAmount * 1e27), surplusBuffer)
        ) revert InsufficientSurplus(); // Ensure sufficient surplus
        // - require(sub(sub(vat.sin(address(this)), Sin), Ash) == 0, "Vow/debt-not-zero");
        if (_sub(_sub(debtBalance, totalQueuedDebt), totalOnAuctionDebt) != 0) revert DebtNotZero(); // Ensure no outstanding debt

        require(surplusAuctionHouse.protocolToken() != address(0), "AccountingEngine/surplus-auction-house-null-prot");
        // - id = flapper.kick(bump, 0);

        id = surplusAuctionHouse.startAuction(surplusAmount, 0);

        // Difference: Renamed to auctionSurplus, uses Exchange module instead of flapper, and checks real StablePro balance.

        emit AuctionSurplus(id, coinBalance);
    }

    /**
     * @notice Disables the contract and settles remaining debt.
     * @dev Clears queued and auctioned debt, settles available debt with surplus, and disables the contract.
     */
    // - function cage() external auth { ... }
    function disableContract() external onlyDisablerAccess {
        // - require(live == 1, "Vow/not-live");
        if (contractEnabled == 0) revert ContractDisabled(); // Ensure contract is enabled
        // - live = 0;
        contractEnabled = 0; // Disable the contract
        // - Sin = 0;
        totalQueuedDebt = 0; // Clear queued debt
        // - Ash = 0;
        totalOnAuctionDebt = 0; // Clear auctioned debt
        // - flapper.cage(vat.dai(address(flapper)));
        // - flopper.cage();
        // - vat.heal(min(vat.dai(address(this)), vat.sin(address(this))));
        debtAuctionHouse.disableContract();
        safeEngine.settleDebt(min(safeEngine.coinBalance(address(this)), safeEngine.debtBalance(address(this)))); // Settle available debt
        // Difference: Renamed to disableContract, uses onlyDisablerAccess, and removes flapper/flopper cage calls.
        emit DisableContract();
    }

    // --- Manual functions ---
    /**
     * @notice Deposits StablePro into the SafeEngine via CoinJoin.
     * @dev Reduces auctioned debt and joins StablePro without rewards. Only callable by Admin.
     * @param amount Amount of StablePro to deposit [wad].
     */
    // +
    function joinStablePro(uint256 amount) external {
        totalOnAuctionDebt = totalOnAuctionDebt < amount ? 0 : _sub(totalOnAuctionDebt, amount); // Reduce auctioned debt
        coinJoin.joinNoReward(address(this), amount); // Join StablePro without rewards
        // Difference: StableUnit adds joinStablePro for manual StablePro deposits via CoinJoin.
        emit JoinStablePro(amount, totalOnAuctionDebt);
    }

    /**
     * @notice Withdraws StablePro from the SafeEngine via CoinJoin.
     * @dev Exits StablePro without rewards. Only callable by Admin.
     * @param amount Amount of StablePro to withdraw [wad].
     */
    // +
    function getRealStablePro(uint256 amount) external onlyAdmin {
        coinJoin.exitNoReward(address(this), amount); // Exit StablePro without rewards
        // Difference: StableUnit adds getRealStablePro for manual StablePro withdrawals.
        emit GetRealStablePro(amount);
    }

    /**
     * @notice Deposits a stablecoin into the contract.
     * @dev Transfers stablecoin from the caller and emits an event.
     * @param stablecoin Address of the stablecoin.
     * @param amount Amount of stablecoin to deposit [token decimals].
     */
    // +
    function refillStablecoin(address stablecoin, uint256 amount) external {
        TransferHelper.safeTransferFrom(stablecoin, msg.sender, address(this), amount); // Transfer stablecoin from caller
        // Difference: StableUnit adds refillStablecoin for stablecoin deposits.
        emit RefillStablecoin(stablecoin, amount);
    }

    // +
    // Difference: StableUnit includes (but comments out) distributeSurplus for potential RewardChef integration.

    /**
     * @notice Swaps StablePro for a whitelisted stablecoin.
     * @dev Transfers StablePro from the caller, sends equivalent stablecoin, and emits an event.
     * @param stablecoinToken Address of the whitelisted stablecoin.
     * @param stableProAmountE18 Amount of StablePro to swap [wad].
     */
    // +
    function swapStableProToStablecoin(address stablecoinToken, uint256 stableProAmountE18) public {
        if (!stablecoinWhitelist[stablecoinToken]) revert BadStablecoin(stablecoinToken); // Ensure stablecoin is whitelisted
        uint256 stablecoinAmountEDecimal = stableProAmountE18 * 1e18 / oracle.getFiatPrice1e18(stablecoinToken); // Calculate stablecoin amount
        TransferHelper.safeTransferFrom(address(stablePro), msg.sender, address(this), stableProAmountE18); // Transfer StablePro
        TransferHelper.safeTransfer(stablecoinToken, msg.sender, stablecoinAmountEDecimal); // Transfer stablecoin
        // Difference: StableUnit adds swapStableProToStablecoin for stablecoin swaps using oracle pricing.
        emit SwapStableProToStablecoin(stablecoinToken, stableProAmountE18, stablecoinAmountEDecimal);
    }

    // --- Stablecoin Whitelist ---
    /**
     * @notice Adds a stablecoin to the whitelist.
     * @dev Approves the Exchange module to spend the stablecoin. Only callable by DAO.
     * @param stablecoin Address of the stablecoin to whitelist.
     */
    // +
    function addWhitelistStablecoin(address stablecoin) external onlyDAO {
        if (stablecoinWhitelist[stablecoin]) revert StablecoinExists(stablecoin); // Ensure stablecoin is not already whitelisted
        stablecoinWhitelist[stablecoin] = true; // Add to whitelist
        TransferHelper.safeApprove(stablecoin, address(debtAuctionHouse), type(uint256).max); // Approve Exchange module
        // Difference: StableUnit adds stablecoin whitelist management.
        emit AddWhitelistStablecoin(stablecoin);
    }

    /**
     * @notice Removes a stablecoin from the whitelist.
     * @dev Revokes Exchange module approval. Only callable by DAO.
     * @param stablecoin Address of the stablecoin to remove.
     */
    // +
    function removeWhitelistStablecoin(address stablecoin) external onlyDAO {
        if (!stablecoinWhitelist[stablecoin]) revert StablecoinNotExist(stablecoin); // Ensure stablecoin is whitelisted
        stablecoinWhitelist[stablecoin] = false; // Remove from whitelist
        TransferHelper.safeApprove(stablecoin, address(debtAuctionHouse), 0); // Revoke Exchange approval
        // Difference: StableUnit adds stablecoin whitelist management.
        emit RemoveWhitelistStablecoin(stablecoin);
    }

    /**
     * @notice Checks if a stablecoin is whitelisted.
     * @param stablecoin Address of the stablecoin to check.
     * @return True if the stablecoin is whitelisted, false otherwise.
     */
    // +
    function hasWhitelistStablecoin(address stablecoin) external view returns (bool) {
        return stablecoinWhitelist[stablecoin]; // Return whitelist status
        // Difference: StableUnit adds stablecoin whitelist checking.
    }

    /**
     * @notice Sets the stablecoin for collateral liquidation swaps.
     * @dev Ensures the stabdisableContractlecoin is whitelisted. Only callable by Admin.
     * @param stablecoin Address of the whitelisted stablecoin.
     */
    // +
    function setStablecoinToSwap(address stablecoin) external onlyAdmin {
        if (!stablecoinWhitelist[stablecoin]) revert BadStablecoin(stablecoin); // Ensure stablecoin is whitelisted
        stablecoinToSwap = stablecoin; // Set stablecoin for liquidation swaps
        // Difference: StableUnit adds stablecoinToSwap for liquidation swaps.
        emit SetStablecoinToSwap(stablecoin);
    }
}

