// SPDX-License-Identifier: AGPL-3.0-or-later

/// CollateralAuctionHouse.sol -- StableUnit collateral auction module

// Forked from MakerDAO's Flip.sol
// Copyright (C) 2020-2022 Dai Foundation
// Adapted for StableUnit with Reflexer naming and Exchange module functionality (IExchange.addOrder)

pragma solidity ^0.8.0;

import "../interfaces/ISafeEngine.sol";
import "../interfaces/ICollateralJoin.sol";
import "../interfaces/IAccountingEngine.sol";
import "../interfaces/ILiquidationEngine.sol";
import {IExchange, Order} from "../../exchange/contracts/interfaces/IExchange.sol";
import "../../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";
import {Math} from "../utils/Math.sol";

contract CollateralAuctionHouse is SuAuthenticated, Math {
    // --- Data ---
    struct Bid {
        //initial collateral amount
        uint256 collateralAmount;
        // How much collateral is sold in an auction
        uint256 amountToSell;

        // address liquidator
        address incentivesReceiver;
        // liquidator reward
        uint256 liquidatorReward; // [rad]

        bytes32 collateralType;
    }


    // Bid data for each separate auction
    mapping (uint256 => Bid) public bids;

    // Reference to SafeEngine for collateral and stablecoin transfers
    ISafeEngine public safeEngine;

    // Reference to AccountingEngine for debt settlement
    IAccountingEngine public accountingEngine;

    // Reference to LiquidationEngine
    ILiquidationEngine public liquidationEngine;

    // Reference to Exchange Module
    IExchange public exchange;

    // DiscountType, that will be used in liquidations
    uint256 public liquidationDiscountType;

    // Active Flag
    uint256 public contractEnabled;

    uint256 public liquidatorReward; // Reward in percent (1e18 == 1) for liquidation [wad]

    // --- Events ---
    // Emitted when a new auction starts
    event StartAuction(uint256 id, uint256 collateralAmount, uint256 amountToSell,
        address incentivesReceiver, uint256 liquidatorReward);

    // Emitted when an auction is settled
    event SettleAuction(uint256 id);
    // Emitted when an auction is canceled
    event CancelAuction(uint256 id);
    // Emitted when parameters are modified
    event ModifyParameters(bytes32 indexed parameter, bytes32 data);
    event ModifyParameters(bytes32 indexed parameter, uint256 data);
    event ModifyParameters(bytes32 indexed parameter, address data);
    // Emitted when the contract is disabled
    event DisableContract();

    // --- Initialization ---
    // MakerDAO Flip.sol: constructor(address vat_, address gem_) public { ... }
    // Initializes the contract with access control, SafeEngine, AccountingEngine, and collateral token
    // @param accessControl Address of the SuAuthenticated access control contract
    // @param safeEngine_ Address of the SafeEngine contract
    // @param accountingEngine_ Address of the AccountingEngine contract
    // @param liquidationEngine_ Address of the LiquidationEngine contract
    // @param exchange_ Address of the exchange module

    function initialize(
      address accessControl,
      address safeEngine_,
      address accountingEngine_,
      address liquidationEngine_,
      address exchange_
    ) public initializer {
        // Set up role-based access control (DAO, Admin, DisablerAccess, LiquidationAccess)
        __suAuthenticatedInit(accessControl);
        // Assign SafeEngine for collateral and stablecoin operations
        safeEngine = ISafeEngine(safeEngine_);
        // Assign AccountingEngine for debt settlement
        accountingEngine = IAccountingEngine(accountingEngine_);
        // Set exchange module address
        exchange = IExchange(exchange_);

        liquidationEngine = ILiquidationEngine(liquidationEngine_);

        liquidationDiscountType = 0;

        contractEnabled = 1;
    }

    // MakerDAO Flip.sol: No equivalent (line ~N/A)
    // Prevents initialization of the implementation contract for upgradeability safety
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // MakerDAO Flip.sol: function file(bytes32 what, address data) public auth { ... } (line ~76)
    // Modifies the AccountingEngine, CollateralJoin, Exchange address
    // @param what Parameter to modify
    // @param data New contract address
    function modifyParameters(bytes32 what, address data) external onlyDAO {
        // Original Flip.sol: if (what == "vat") vat = data; else revert("Flip/file-unrecognized-param"); (lines 77-79)
        // Updates AccountingEngine address for debt settlement
        if (what == "accountingEngine") {
            accountingEngine = IAccountingEngine(data);
        } else if (what == "exchange") {
            exchange = IExchange(data);
        } else {
            revert("CollateralAuctionHouse/modify-unrecognized-param");
        }
        // Emit event to log address update
        emit ModifyParameters(what, data);
    }

    // MakerDAO Flip.sol: function file(bytes32 what, uint256 data) public auth { ... }
    // Modifies auction parameters ("liquidatorReward", "liquidationDiscountType")
    // @param what Parameter to modify ("liquidatorReward", "liquidationDiscountType")
    // @param data New parameter value
    function modifyParameters(bytes32 what, uint256 data) external onlyDAO {
        if (what == "LiquidatorReward") {
            liquidatorReward = data;
        } else if (what == "liquidationDiscountType") {
            liquidationDiscountType = data;
        } else {
            revert("CollateralAuctionHouse/modify-unrecognized-param");
        }

        emit ModifyParameters(what, data);
    }

    // --- Auction Management ---
    // MakerDAO Flip.sol: function kick(address urn, address gal, uint tab, uint lot, uint bid) public auth returns (uint id) { ... } (line ~82)
    // Starts a new auction, integrating Exchange module’s addOrder logic
    // @param collateralType that can be liquidated
    // @param debtReceiver Address to receive stablecoin (AccountingEngine)
    // @param collateralAmount Amount of collateral to sell [wad]
    // @param incentivesReceiver incentives receiver after liquidation
    // @return id Unique auction identifier
    function startAuction(
        bytes32 collateralType,
        ICollateralJoin collateralJoin,
        address debtReceiver,
        uint256 collateralAmount,
        address incentivesReceiver
    ) external onlyLiquidationAccess returns (uint256 id) {
        // Original Flip.sol: require(live, "Flip/not-live"); ... id = ++kicks; bids[id] = Bid(bid, lot, msg.sender, uint48(now + tau), uint48(now + ttl), urn, gal, tab); (lines 83-87)
        // Exchange module integration: Maps IExchange.addOrder(collateral, collateral2sell, stablecoin, debtReceiver, liquidationDiscountType, collateralJoin) to auction creation
        // Ensure contract is enabled to allow new auctions
        require(contractEnabled == 1, "CollateralAuctionHouse/not-enabled");
        // Ensure non-zero collateral amount to prevent null auctions
        require(collateralAmount > 0, "CollateralAuctionHouse/zero-collateral");


        safeEngine.transferCollateral(collateralType, msg.sender, address(this), collateralAmount);

        uint256  liquidatorReward_ = _mul(collateralAmount, liquidatorReward);

        uint256 amountToSell = (_sub(collateralAmount, liquidatorReward_));

        collateralJoin.exit(address(this), amountToSell);

        collateralJoin.collateral().approve(address(exchange), amountToSell);

        id = exchange.addOrder(
            address(collateralJoin.collateral()),
            amountToSell,
            accountingEngine.stablecoinToSwap(),
            debtReceiver,
            uint8(liquidationDiscountType),
            address(0)
        );

        bids[id] = Bid({
          collateralAmount: collateralAmount,
          amountToSell: amountToSell,
          incentivesReceiver: incentivesReceiver,
          liquidatorReward: liquidatorReward_,
          collateralType: collateralType
        });

        // Emit event to log auction start
        emit StartAuction(id,collateralAmount, amountToSell, incentivesReceiver, liquidatorReward);
    }

    // MakerDAO Flip.sol: function tick(uint id) public { ... } (line ~94)
    // Restarts an auction by extending the bid deadline
    // @param id Auction identifier
    function restartAuction(uint256 id) external {}

    // MakerDAO Flip.sol: function tend(uint id, uint lot, uint bid) public { ... } (line ~99)
    // Increases the bid amount for a fixed collateral amount
    // @param id Auction identifier
    // @param collateralAmount Collateral amount (must match auction’s lot)
    // @param bid New bid amount in stablecoin [rad]
    function increaseBidSize(uint256 id, uint256 collateralAmount, uint256 bid) external {}

    // MakerDAO Flip.sol: function dent(uint id, uint lot, uint bid) public { ... } (line ~114)
    // Decreases the collateral amount for a fixed bid
    // @param id Auction identifier
    // @param collateralAmount New collateral amount (must be less than current lot)
    // @param bid Bid amount (must match current bid and tab) [rad]
    function decreaseSoldAmount(uint256 id, uint256 collateralAmount, uint256 bid) external {}

    // MakerDAO Flip.sol: function deal(uint id) public { ... }
    // Settles a completed auction, transferring collateral to the winning bidder and stablecoin to AccountingEngine
    // @param id Auction identifier

    /**
     * @notice Settle/finish an auction
     * @param id ID of the auction to settle
     */
    function settleAuction(uint256 id) external {
        require(bids[id].amountToSell != 0, "CollateralAuctionHouse/not-finished");
        Order memory order = exchange.getOrder(id);

        require(order.amountLeft == 0, "CollateralAuctionHouse/not-executed");

        safeEngine.transferCollateral(bids[id].collateralType, address(this), bids[id].incentivesReceiver, bids[id].liquidatorReward);
        liquidationEngine.reduceActiveLiquidationAmount(bids[id].collateralType, bids[id].amountToSell);

        delete bids[id];
        emit SettleAuction(id);
    }

    // MakerDAO Flip.sol: function yank(uint id) public auth { ... } (line ~137)
    // Cancels an auction during system shutdown, refunding collateral and stablecoin
    // @param id Auction identifier
    function cancelAuction(uint256 id, ICollateralJoin collateralJoin) external onlyLiquidationAccess {
        // Original Flip.sol: require(!live, "Flip/still-live"); ... emit Yank(id);
        // Exchange module: Refunds collateral to CDP and stablecoin to bidder
        // Ensure contract is disabled (system shutdown)
        Order memory order = exchange.getOrder(id);

        require(contractEnabled == 0, "DebtAuctionHouse/still-enabled");
        require(order.amountLeft != 0, "DebtAuctionHouse/executed");
        require(bids[id].amountToSell != 0, "DebtAuctionHouse/executed");

        exchange.cancelOrder(id);

        collateralJoin.join(msg.sender, order.amountLeft);

        // Refund collateral to the original CDP
        safeEngine.transferCollateral(bids[id].collateralType, address(this), msg.sender, order.amountLeft);

        emit CancelAuction(id);
    }

    // --- Disable ---
    // Disables the contract, halting new auctions
    // Only callable by accounts with DisablerAccess role
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0;
        emit DisableContract();
    }
}

contracts/makerdao/externalContracts/Deb.content:
// SPDX-License-Identifier: AGPL-3.0-or-later

/// DebtAuctionHouse.sol -- StableUnit debt auction module

// Forked from MakerDAO's Flop.sol
// Copyright (C) 2020-2022 Dai Foundation
// Adapted for StableUnit with Reflexer naming and Exchange module functionality (IExchange.addOrder)

pragma solidity ^0.8.0;

import "../interfaces/IAccountingEngine.sol";
import {IExchange, Order} from "../../exchange/contracts/interfaces/IExchange.sol";
import "../../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Math} from "../utils/Math.sol";

contract DebtAuctionHouse is SuAuthenticated, Math {
    using SafeERC20 for IERC20;

    // --- Data ---
    struct Bid {
        // Address of the token to sell in the auction (e.g., USDT)
        address tokenToSell;
        // Amount of token to sell [rad]
        uint256 amountToSell;
        // Address of the auction initiator
        address sender;
    }

    // Bid data for each separate auction, mapping auction ID to Bid struct
    mapping (uint256 => Bid) public bids;

    // Reference to AccountingEngine for debt settlement operations
    IAccountingEngine public accountingEngine;

    // Reference to Exchange Module for order creation and management
    IExchange public exchange;

    // Flag to indicate if the contract is active (1 = enabled, 0 = disabled)
    uint256 public contractEnabled;

    // Address of the protocol's native token (StablePro)
    address public protocolToken;

    // Discount type for auctions (0, default)
    uint256 public discountType;

    // --- Events ---
    // Emitted when a new auction starts
    event StartAuction(uint256 id, address tokenToSell, uint256 amountToSell, address sender);

    // Emitted when an auction is settled
    event SettleAuction(uint256 id);
    // Emitted when an auction is canceled
    event CancelAuction(uint256 id);

    // Emitted when parameters are modified
    event ModifyParameters(bytes32 indexed parameter, uint256 data);

    // Emitted when parameters are modified
    event ModifyParameters(bytes32 indexed parameter, address data);
    // Emitted when the contract is disabled
    event DisableContract();

    // --- Initialization ---
    // Initializes the contract with access control, AccountingEngine, Exchange module, and protocol token
    // Forked from MakerDAO Flop.sol: constructor(address vat_, address gem_) public { ... } (line ~54)
    // StableUnit adaptation: Replaces MakerDAO's Vat with AccountingEngine and integrates Exchange module
    // @param accessControl Address of the SuAuthenticated access control contract
    // @param accountingEngine_ Address of the AccountingEngine contract
    // @param exchange_ Address of the Exchange module
    // @param protocolToken_ Address of the protocol's native token (StablePro)
    function initialize(
        address accessControl,
        address accountingEngine_,
        address exchange_,
        address protocolToken_
    ) public initializer {
        // Set up role-based access control (DAO, Admin, DisablerAccess, LiquidationAccess)
        __suAuthenticatedInit(accessControl);

        // Assign AccountingEngine for debt settlement operations
        accountingEngine = IAccountingEngine(accountingEngine_);

        // Set Exchange module address for order creation
        exchange = IExchange(exchange_);

        // Enable the contract by default
        contractEnabled = 1;

        // Set default discount type to aggressive (0)
        discountType = 0;

        // Set protocol token address (StablePro)
        protocolToken = protocolToken_;
    }

    // Prevents initialization of the implementation contract for upgradeability safety
    // StableUnit addition: Ensures the contract cannot be initialized outside of proxy deployment
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // --- Administration ---
    // Modifies contract parameters such as AccountingEngine or protocol token address
    // Forked from MakerDAO Flop.sol: function file(bytes32 what, address data) public auth { ... } (line ~76)
    // StableUnit adaptation: Updates AccountingEngine or protocolToken address, restricted to DAO role
    // @param what Parameter to modify ("accountingEngine" or "protocolToken")
    // @param data New contract address
    function modifyParameters(bytes32 what, address data) external onlyDAO {
        // Update AccountingEngine address for debt settlement
        if (what == "accountingEngine") {
            accountingEngine = IAccountingEngine(data);
        }
        // Update protocol token address (StablePro)
        if (what == "protocolToken") {
            protocolToken = address(data);
        }
        // Emit event to log address update
        emit ModifyParameters(what, data);
    }

    // MakerDAO Flop.sol: function file(bytes32 what, uint256 data) public auth { ... }
    // Modifies auction parameters ("discountType")
    // @param what Parameter to modify ("discountType")
    // @param data New parameter value
    function modifyParameters(bytes32 what, uint256 data) external onlyDAO {
        if (what == "discountType") {
            discountType = data;
        } else {
            revert("CollateralAuctionHouse/modify-unrecognized-param");
        }

        emit ModifyParameters(what, data);
    }

    // --- Auction Management ---
    // Starts a new debt auction, integrating Exchange module’s addOrder logic
    // StableUnit adaptation: Creates an order on the Exchange module to sell a token (e.g., USDT) for StablePro
    // @param incomeReceiver Address to receive the auction proceeds
    // @param amountToSell Amount of token to sell [rad]
    // @param tokenToSell Address of the token to sell
    // @return id Unique auction identifier
    function startAuction(
        address,
        uint256 amountToSell,
        address tokenToSell
    ) external onlyAuctionAccess returns (uint256 id) {
        // Ensure the contract is enabled to allow new auctions
        require(contractEnabled == 1, "DebtAuctionHouse/not-enabled");
        // Ensure non-zero amount to prevent null auctions
        require(amountToSell > 0, "DebtAuctionHouse/zero-collateral");

        IERC20(tokenToSell).safeApprove(address(exchange), amountToSell);

        // Create an order on the Exchange module to sell tokenToSell for StablePro
        id = exchange.addOrder(
            tokenToSell, // Token to sell (e.g., USDT)
            amountToSell, // Amount to sell
            protocolToken, // Token to receive (StablePro)
            address(accountingEngine), // Receiver of proceeds
            uint8(discountType), // Discount type for the auction
            address(0) // No additional recipient
        );

        // Store bid details for the auction
        bids[id] = Bid({
            tokenToSell: tokenToSell,
            amountToSell: amountToSell,
            sender: msg.sender
        });

        // Emit event to log auction start
        emit StartAuction(id, tokenToSell, amountToSell, msg.sender);
    }

    // Restarts an auction by extending the bid deadline
    // Forked from MakerDAO Flop.sol: function tick(uint id) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    function restartAuction(uint256 id) external {}

    // Increases the bid amount for a fixed collateral amount
    // Forked from MakerDAO Flop.sol: function tend(uint id, uint lot, uint bid) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    // @param collateralAmount Collateral amount (must match auction’s lot)
    // @param bid New bid amount in stablecoin [rad]
    function increaseBidSize(uint256 id, uint256 collateralAmount, uint256 bid) external {}

    // Decreases the collateral amount for a fixed bid
    // Forked from MakerDAO Flop.sol: function dent(uint id, uint lot, uint bid) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    // @param collateralAmount New collateral amount (must be less than current lot)
    // @param bid Bid amount (must match current bid and tab) [rad]
    function decreaseSoldAmount(uint256 id, uint256 collateralAmount, uint256 bid) external {}

    // Settles a completed auction, transferring StablePro to AccountingEngine
    // Forked from MakerDAO Flop.sol: function deal(uint id) public { ... }
    // StableUnit adaptation: Transfers StablePro balance to AccountingEngine and cancels auctioned debt
    // @param id Auction identifier
    function settleAuction(uint256 id) external {
        // Ensure the auction exists and is not already settled
        require(bids[id].amountToSell != 0, "DebtAuctionHouse/not-finished");
        // Retrieve the order details from the Exchange module
        Order memory order = exchange.getOrder(id);
        // Ensure the auction has been fully executed (no amount left to sell)
        require(order.amountLeft == 0, "DebtAuctionHouse/not-executed");

        // Get the StablePro balance of this contract
        uint256 stableProAmount = IERC20(protocolToken).balanceOf(address(this));

        // Transfer StablePro to AccountingEngine
        accountingEngine.joinStablePro(stableProAmount);

        // Cancel the auctioned debt using the StablePro received
        accountingEngine.cancelAuctionedDebtWithSurplus(stableProAmount);

        // Clear the bid data
        delete bids[id];
        // Emit event to log auction settlement
        emit SettleAuction(id);
    }

    // Cancels an auction during system shutdown, refunding the token to the sender
    // Forked from MakerDAO Flop.sol: function yank(uint id) public auth { ... } (line ~137)
    // StableUnit adaptation: Refunds tokenToSell to the sender via the Exchange module
    // @param id Auction identifier
    function cancelAuction(uint256 id) external onlyLiquidationAccess {
        // Ensure the contract is disabled (system shutdown)
        require(contractEnabled == 0, "DebtAuctionHouse/still-enabled");
        // Retrieve the order details from the Exchange module
        Order memory order = exchange.getOrder(id);
        // Ensure the auction has not been executed
        require(order.amountLeft != 0, "DebtAuctionHouse/executed");
        // Ensure the auction exists
        require(bids[id].amountToSell != 0, "DebtAuctionHouse/executed");

        // Cancel the order on the Exchange module
        exchange.cancelOrder(id);

        // Refund the remaining token amount to the sender
        IERC20(bids[id].tokenToSell).safeTransfer(bids[id].sender, order.amountLeft);

        // Emit event to log auction cancellation
        emit CancelAuction(id);
    }

    // Disables the contract, halting new auctions
    // StableUnit adaptation: Restricted to accounts with DisablerAccess role
    // @notice Only callable by accounts with DisablerAccess role
    function disableContract() external onlyAuctionAccess {
        // Set contract to disabled state
        contractEnabled = 0;
        // Emit event to log contract disablement
        emit DisableContract();
    }
}

