// Manages Safe liquidations in the StableUnit protocol
// Difference: Renamed to LiquidationEngine.sol for StableUnit, reflecting its role in StablePro liquidations. Adapted for Exchange-based auctions and global liquidator reward.
pragma solidity ^0.8.0;
// -  pragma solidity >=0.5.12;
// Difference: Updated to Solidity ^0.8.0 for improved safety and features like safe math by default.

// Import interface defining LiquidationEngine functions and structs
// + import (structs defined inline)
import "../interfaces/ILiquidationEngine.sol";
// Difference: StableUnit moves structs to ILiquidationEngine interface for modularity and clarity.

// Import SafeEngine interface for Safe operations
// + import (uses VatLike interface)
import "../interfaces/ISafeEngine.sol";
// Difference: StableUnit uses ISafeEngine for SafeEngine interactions, replacing MakerDAO's VatLike.

// Import CollateralJoin interface for collateral operations
// + import
import "../interfaces/ICollateralJoin.sol";
// Difference: StableUnit adds ICollateralJoin for collateral handling during auctions.

// Import Exchange interface for auctions
// + import (uses Kicker interface)
import "../../exchange/contracts/interfaces/IExchange.sol";
// Difference: StableUnit uses IExchange for auctions, replacing MakerDAO's Kicker.

// Import role-based access control
// + import (uses wards mapping)
import "../../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";
// Difference: StableUnit uses SuAuthenticated for role-based access control, replacing MakerDAO's wards.

// Import AccountingEngine interface for debt queuing
// + import (uses VowLike interface)
import "../interfaces/IAccountingEngine.sol";
// Difference: StableUnit uses IAccountingEngine, replacing MakerDAO's VowLike.

// Import safe math operations
// + import (uses inline math functions)
import {Math} from "../utils/Math.sol";
// Difference: StableUnit imports Math.sol for reusable safe math operations, replacing MakerDAO's inline math.

// Note: Custom events need to be defined for logging state changes (TODO)
// -  // FIXME: This contract was altered compared to the production version. It doesn't use LibNote anymore.
// Difference: Both note the removal of LibNote for event logging, but StableUnit explicitly plans custom events.
// CHANGED (from dog.sol): added Whitelist behaviour to add order correctly.
// Added SuAuthenticated instead of authorizedAccounts
// Added Math to move Math behaviour.
// Added interface and moved struct there.
// Added AddAuthorization call in constructor (can be removed).
// Added LiquidatorReward
// Moved Liquidator from CollateralType struct to global var, because it's one for all collaterals
// Calculate rewardCollateral in liquidateCDP
// Use addOrder instead of startAuction in liquidateCDP

/**
 * @title LiquidationEngine
 * @notice Manages Safe liquidations in the StableUnit protocol.
 * @dev Identifies undercollateralized Safes, confiscates collateral and debt, and auctions collateral via the Exchange
 *      module. Supports global and per-collateral-type liquidation limits, a liquidator reward, and a discount type for
 *      auctions. Integrates with SafeEngine for Safe operations and AccountingEngine for debt queuing. This contract is a
 *      fork of MakerDAO’s Dog.sol, adapted for StableUnit with Exchange-based auctions and a global liquidator.
 * @custom:usage
 * - **Initialization**: Deploy with access control and SafeEngine addresses.
 *   ```solidity
 *   LiquidationEngine liquidationEngine = new LiquidationEngine(accessControl, safeEngine);
 *   ```
 * - **Collateral Type Setup**: Configure liquidation parameters for a collateral type.
 *   ```solidity
 *   liquidationEngine.modifyParameters("ETH-A", "liquidationPenalty", penalty);
 *   liquidationEngine.modifyParameters("ETH-A", "liquidationLimit", limit);
 *   ```
 * - **Liquidation**: Liquidate an undercollateralized Safe.
 *   ```solidity
 *   uint orderId = liquidationEngine.liquidateCDP("ETH-A", safeAddress, incentivesReceiver, collateralJoin);
 *   ```
 * - **Parameter Modification**: Update global or contract-level parameters.
 *   ```solidity
 *   liquidationEngine.modifyParameters("GlobalLiquidationLimit", newLimit);
 *   liquidationEngine.modifyParameters("liquidator", newExchangeAddress);
 *   ```
 * - **Disabling**: Disable the contract to halt liquidations.
 *   ```solidity
 *   liquidationEngine.disableContract();
 *   ```
 * @custom:security Only authorized accounts (DAO, Admin, DisablerAccess, LiquidationAccess) can call state-changing functions.
 *                 Liquidations require sufficient limits and valid safety prices. Rewards are calculated to incentivize liquidators.
 * @custom:changes Renamed from Dog.sol to LiquidationEngine.sol. Replaced auction houses with Exchange module. Added global
 *                 liquidator and reward. Used SuAuthenticated for access control. Moved struct to ILiquidationEngine.
 * @custom:events Emits `AddAuthorization`, `DisableContract`, `ModifyParameters`, `LiquidateCDP`, `ReduceActiveLiquidationAmount`.
 */

// Manages Safe liquidations in the StableUnit protocol
// -  contract Cat {

interface CollateralAuctionHouseLike {
    function startAuction(
        bytes32 collateralType,
        address collateralJoin,
        address debtReceiver,
        uint256 collateralAmount,
        address incentivesReceiver
    ) external returns (uint256);

    function disableContract() external;
}
contract LiquidationEngine is SuAuthenticated, ILiquidationEngine, Math {
    // --- Data ---
    /// @notice Reference to the SafeEngine contract for Safe operations.
    // -  VatLike public vat;    // CDP Engine
    ISafeEngine public safeEngine; // CDP Engine
    // Difference: Renamed to safeEngine and uses ISafeEngine interface for type safety and clarity.

    /// @notice Maps collateral types to their liquidation configurations.
    // -  struct Ilk { address flip; uint256 chop; uint256 dunk; }
    // -  mapping (bytes32 => Ilk) public ilks;
    mapping (bytes32 => CollateralType) public collateralTypes;
    // Difference: Renamed to collateralTypes and uses CollateralType struct (defined in ILiquidationEngine). Removes flip (liquidator) field, as StableUnit uses a global liquidator.

    /// @notice Reference to the AccountingEngine for debt queuing.
    // -  VowLike public vow;    // Debt Engine
    IAccountingEngine public accountingEngine; // Debt Engine
    // Difference: Renamed to accountingEngine and uses IAccountingEngine interface for clarity.

    /// @notice Maximum StablePro debt for active auctions [rad].
    // -  uint256 public box;    // Max Dai out for liquidation [rad]
    uint256 public GlobalLiquidationLimit; // Max StablePro needed to cover debt+fees of active auctions [rad]
    // Difference: Renamed to GlobalLiquidationLimit for clarity, but functionality is identical.

    /// @notice Current StablePro debt for active auctions [rad].
    // -  uint256 public litter; // Balance of Dai out for liquidation [rad]
    uint256 public ActiveLiquidationAmount; // Amt StablePro needed to cover debt+fees of active auctions [rad]
    // Difference: Renamed to ActiveLiquidationAmount for clarity, but functionality is identical.

    /// @notice Reward percentage for liquidators [wad].
    // +
    uint256 public LiquidatorReward; // Reward in percent (1e18 == 1) for liquidation [wad]
    // Difference: StableUnit adds LiquidatorReward for global liquidator incentives, not present in MakerDAO.

    /// @notice Address of the Exchange module for auctions.
    // + (liquidator in Ilk struct)
    address public liquidator; // Liquidator, Exchange module
    // Difference: StableUnit uses a global liquidator (Exchange module) instead of per-collateral flip addresses.

    /// @notice Discount type for liquidation auctions (e.g., 0 for aggressive).
    // +
    uint8 public liquidationDiscountType; // DiscountType, that will be used in liquidations
    // Difference: StableUnit adds liquidationDiscountType for auction configuration.

    //@notice address for collateral auction house
    CollateralAuctionHouseLike public collateralAuctionHouse;

    // --- Init ---
    /**
     * @notice Initializes the LiquidationEngine with access control and SafeEngine addresses.
     * @dev Sets the SafeEngine reference, enables the contract, and sets default discount type.
     *      Emits a placeholder authorization event (can be removed).
     * @param accessControl Address of the access control singleton.
     * @param safeEngine_ Address of the SafeEngine contract.
     */
    // -  constructor(address vat_) public { ... }
    function initialize(address accessControl, address safeEngine_) initializer public {
        // -  wards[msg.sender] = 1;
        __suAuthenticatedInit(accessControl); // Set up role-based access control
        // -  vat = VatLike(vat_);
        safeEngine = ISafeEngine(safeEngine_); // Assign SafeEngine contract
        // -  live = 1;
        contractEnabled = 1; // Enable the contract
        liquidationDiscountType = 0; // Set default discount type to aggressive

        emit AddAuthorization(msg.sender); // Emit placeholder authorization event (can be removed)
        // Difference: StableUnit uses initializer for upgradeability, SuAuthenticated for access control, and adds liquidationDiscountType.
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    // +
    constructor() {
        _disableInitializers(); // Disable initializers for safety
        // Difference: StableUnit adds constructor for upgradeability, not present in MakerDAO.
    }

    // --- Disable ---
    /// @notice Flag indicating if the contract is enabled (1) or disabled (0).
    // -  uint256 public live;   // Active Flag
    uint public contractEnabled;
    // Difference: Renamed to contractEnabled for clarity, but functionality is identical.

    /**
     * @notice Disables the contract, halting liquidations.
     * @dev Sets `contractEnabled` to 0 and emits a placeholder event. Only callable by DisablerAccess.
     */
    // -  function cage() external auth { live = 0; }
    function disableContract() external onlyDisablerAccess {
        // -  live = 0;
        contractEnabled = 0; // Disable the contract

        collateralAuctionHouse.disableContract();
        emit DisableContract(); // Emit event for contract disablement
        // Difference: Renamed to disableContract, uses onlyDisablerAccess instead of auth, and plans to emit a custom event.
    }

    // --- Administration ---
    /**
     * @notice Modifies contract addresses.
     * @dev Updates AccountingEngine or Exchange module address. Only callable by DAO.
     * @param what Parameter to modify ("accountingEngine" or "liquidator").
     * @param data New contract address.
     */
    // -  function file(bytes32 what, address data) external auth { ... }
    function modifyParameters(bytes32 what, address data) external onlyDAO {
        // -  if (what == "vow") vow = VowLike(data);
        if (what == "accountingEngine") {
            accountingEngine = IAccountingEngine(data); // Update AccountingEngine
        } else if (what == "collateralAuctionHouse") {
            safeEngine.denySAFEModification(address(collateralAuctionHouse));
            collateralAuctionHouse = CollateralAuctionHouseLike(data);
            safeEngine.approveSAFEModification(data);
        } else if (what == "liquidator") { // -  else revert("Cat/file-unrecognized-param");
            liquidator = data; // Update liquidator (Exchange module)
        } else revert("LiquidationEngine/file-unrecognized-param"); // Revert for unrecognized parameter
        emit ModifyParameters(what, data); // Emit event for parameter modification
        // Difference: StableUnit renames vow to accountingEngine, adds liquidator, and uses onlyDAO instead of auth.
    }

    /**
     * @notice Modifies global parameters.
     * @dev Updates global liquidation limit or liquidator reward. Only callable by DAO.
     * @param what Parameter to modify ("GlobalLiquidationLimit" or "LiquidatorReward").
     * @param data New parameter value.
     */
    // -  function file(bytes32 what, uint256 data) external auth { ... }
    function modifyParameters(bytes32 what, uint256 data) external onlyDAO {
        // -  if (what == "box") box = data;
        if (what == "GlobalLiquidationLimit") GlobalLiquidationLimit = data; // Update global liquidation limit
        // -  else revert("Cat/file-unrecognized-param");
        else if (what == "LiquidatorReward") LiquidatorReward = data; // Update liquidator reward
        else revert("LiquidationEngine/file-unrecognized-param"); // Revert for unrecognized parameter
        emit ModifyParameters(what, data); // Emit event for parameter modification
        // Difference: StableUnit renames box to GlobalLiquidationLimit, adds LiquidatorReward, and uses onlyDAO.
    }

    /**
     * @notice Modifies collateral type parameters.
     * @dev Updates liquidation penalty or limit for a collateral type. Only callable by DAO.
     * @param collateralType Unique identifier for the collateral type.
     * @param what Parameter to modify ("liquidationPenalty" or "liquidationLimit").
     * @param data New parameter value.
     */
    // -  function file(bytes32 ilk, bytes32 what, uint256 data) external auth { ... }
    function modifyParameters(bytes32 collateralType, bytes32 what, uint256 data) external onlyDAO {
        // -  if (what == "chop") ilks[ilk].chop = data;
        if (what == "liquidationPenalty") {
            // + require
            require(data >= WAD, "LiquidationEngine/file-liquidationPenalty-lt-WAD"); // Ensure penalty is at least 1
            collateralTypes[collateralType].liquidationPenalty = data; // Update liquidation penalty
        }
        // -  else if (what == "dunk") ilks[ilk].dunk = data;
        else if (what == "liquidationLimit") collateralTypes[collateralType].liquidationLimit = data; // Update liquidation limit
        // -  else revert("Cat/file-unrecognized-param");
        else revert("LiquidationEngine/file-unrecognized-param"); // Revert for unrecognized parameter
        emit ModifyParameters(collateralType, what, data); // Emit event for parameter modification
        // Difference: StableUnit renames chop to liquidationPenalty and dunk to liquidationLimit, adds penalty check, and uses onlyDAO.
    }
    /**
     * @notice Retrieves the liquidation penalty for a collateral type.
     * @param collateralType Unique identifier for the collateral type.
     * @return Liquidation penalty [wad].
     */
    // +
    function liquidationPenalty(bytes32 collateralType) external view returns (uint256) {
        return collateralTypes[collateralType].liquidationPenalty; // Return liquidation penalty
        // Difference: StableUnit adds liquidationPenalty function for transparency.
    }

    /**
     * @notice Sets the discount type for liquidation auctions.
     * @dev Updates the auction discount type. Only callable by Admin.
     * @param newDiscountType New discount type (e.g., 0 for aggressive).
     */
    // +
    function setLiquidationDiscountType(uint8 newDiscountType) external onlyAdmin {
        liquidationDiscountType = newDiscountType; // Update discount type
        // Difference: StableUnit adds setLiquidationDiscountType for auction configuration.
        emit SetLiquidationDiscountType(newDiscountType);
    }

    /**
     * @notice Liquidates an undercollateralized Safe.
     * @dev Confiscates collateral and debt, rewards the liquidator, and auctions remaining collateral via Exchange.
     *      Pushes debt to AccountingEngine. Ensures limits and safety conditions are met.
     * @param collateralType Unique identifier for the collateral type.
     * @param cdp Address of the Safe to liquidate.
     * @param incentivesReceiver Address to receive liquidator reward.
     * @param collateralJoin Address of the CollateralJoin contract.
     * @return id Unique identifier of the Exchange order.
     */
    // -  function bite(bytes32 ilk, address urn) external returns (uint256 id) { ... }
    function liquidateCDP(
        bytes32 collateralType,
        address cdp,
        address incentivesReceiver,
        address collateralJoin
    ) external returns (uint256 id) {
        // -  require(live == 1, "Cat/not-live");
        require(contractEnabled == 1, "LiquidationEngine/not-contractEnabled"); // Ensure contract is enabled
        // -  (,uint256 rate,uint256 spot,,uint256 dust) = vat.ilks(ilk);
        // -  (uint256 ink, uint256 art) = vat.urns(ilk, urn);
        safeEngine.updateSafetyPrice(collateralType); // Refresh safety price from oracle
        (uint256 collateralAmount, uint256 generatedDebt) = safeEngine.safes(collateralType, cdp); // Load Safe state
        // -  Ilk memory milk = ilks[ilk];
        CollateralType memory _cType = collateralTypes[collateralType]; // Load collateral type
        uint256 deltaGeneratedDebt; // Debt to liquidate
        uint256 accumulatedRate; // Accumulated rate for debt calculation
        uint256 debtFloor; // Minimum debt requirement
        {
            // -  (,uint256 rate,uint256 spot,,uint256 dust) = vat.ilks(ilk);
            uint256 safetyPrice; // Safety price for collateral
            (, accumulatedRate,, debtFloor, safetyPrice,,,) = safeEngine.collateralTypes(collateralType); // Load collateral type data
            // -  require(spot > 0 && mul(ink, spot) < mul(art, rate), "Cat/not-unsafe");
            require(
                safetyPrice > 0 && _mul(collateralAmount, safetyPrice) < _mul(generatedDebt, accumulatedRate),
                "LiquidationEngine/not-unsafe"
            ); // Ensure Safe is undercollateralized
            // -  uint256 room = sub(box, litter);
            // -  require(litter < box && room >= dust, "Cat/liquidation-limit-hit");
            // Get the minimum value between:
            // 1) Remaining space in the general GlobalLiquidationLimit
            // 2) Remaining space in the collateral liquidationLimit
            require(
                GlobalLiquidationLimit > ActiveLiquidationAmount
                    && _cType.liquidationLimit > _cType.activeLiquidationAmount,
                "LiquidationEngine/liquidation-limit-hit"
            ); // Check liquidation limits
            // -  dart = min(art, mul(min(milk.dunk, room), WAD) / rate / milk.chop);
            uint256 room = min(
                GlobalLiquidationLimit - ActiveLiquidationAmount,
                _cType.liquidationLimit - _cType.activeLiquidationAmount
            ); // Calculate available liquidation capacity
            deltaGeneratedDebt = min(generatedDebt, _mul(room, WAD) / accumulatedRate / _cType.liquidationPenalty); // Calculate debt to liquidate
            // + partial liquidation logic
            if (generatedDebt > deltaGeneratedDebt) {
                if (_mul(generatedDebt - deltaGeneratedDebt, accumulatedRate) < debtFloor) {

                    // If the leftover Vault would be dusty, just liquidate it entirely.
                    // This will result in at least one of activeLiquidationAmount_i > liquidationLimit_i or
                    // ActiveLiquidationAmount > GlobalLiquidationLimit becoming true.
                    // The amount of excess will be bounded above by ceiling(debtFloor_i * liquidationPenalty_i / WAD).
                    // This deviation is assumed to be small compared to both liquidationLimit_i and
                    // GlobalLiquidationLimit, so that the extra amount of target StablePro over the limits
                    // intended is not of economic concern.
                    deltaGeneratedDebt = generatedDebt;
                    // Liquidate entire Safe if remainder is dusty
                } else {
                    require(
                        _mul(deltaGeneratedDebt, accumulatedRate) >= debtFloor,
                        "LiquidationEngine/dusty-auction-from-partial-liquidation"
                    ); // Ensure partial liquidation is non-dusty
                }
            }
        }
        // -  uint256 dink = min(ink, mul(ink, dart) / art);
        uint256 deltaCollateral = _mul(collateralAmount, deltaGeneratedDebt) / generatedDebt; // Calculate collateral to liquidate
        // -  require(dart > 0 && dink > 0, "Cat/null-auction");
        require(deltaCollateral > 0, "LiquidationEngine/null-auction"); // Ensure non-zero auction
        // -  require(dart <= 2**255 && dink <= 2**255, "Cat/overflow");
        require(deltaGeneratedDebt <= 2**255 && deltaCollateral <= 2**255, "LiquidationEngine/overflow"); // Prevent overflow
        // + reward calculation
        uint256 collateral2sell = deltaCollateral; // Calculate collateral to auction after reward
        // -  vat.grab(ilk, urn, address(this), address(vow), -int256(dink), -int256(dart));

         // Confiscate collateral to balance of liqiuidationEngine
        safeEngine.confiscateSAFECollateralAndDebt(
            collateralType,
            cdp,
            address(this),
            address(accountingEngine),
            -int256(collateral2sell),
            -int256(deltaGeneratedDebt)
        ); // Confiscate auction collateral and debt
        // -  vow.fess(mul(dart, rate));
        accountingEngine.pushDebtToQueue(_mul(deltaGeneratedDebt, accumulatedRate)); // Queue debt in AccountingEngine
        // -  uint256 tab = mul(mul(dart, rate), milk.chop) / WAD;
        // -  litter = add(litter, tab);
        // -  id = Kicker(milk.flip).kick({ urn: urn, gal: address(vow), tab: tab, lot: dink, bid: 0 });
        {
            id = _createOrder(_mul(deltaGeneratedDebt, accumulatedRate), collateralType, collateralJoin, collateral2sell, incentivesReceiver); // Create auction order
        }
        // -  emit Bite(ilk, urn, dink, dart, mul(dart, rate), milk.flip, id);
        emit LiquidateCDP(collateralType, cdp, deltaCollateral, deltaGeneratedDebt, _mul(deltaGeneratedDebt, accumulatedRate), address(this), id); // Emit liquidation event
        // Difference: Renamed to liquidateCDP, uses Exchange module instead of flip, adds liquidator reward, and includes partial liquidation logic.
    }

    // Creates an auction order for liquidated collateral
    // + (handled within bite)
    function _createOrder(uint256 due, bytes32 collateralType, address collateralJoin, uint256 collateral2sell, address incentivesReceiver) internal returns (uint256 id) {
        // -  uint256 tab = mul(mul(dart, rate), milk.chop) / WAD;
        CollateralType memory _cType = collateralTypes[collateralType]; // Load collateral type
                    // This calcuation will overflow if deltaGeneratedDebt*rate exceeds ~10^14
        uint256 amountToRaise = _mul(due, _cType.liquidationPenalty) / WAD; // Calculate amount to raise with penalty
        // -  litter = add(litter, tab);
        ActiveLiquidationAmount = _add(ActiveLiquidationAmount, amountToRaise); // Commented out
        collateralTypes[collateralType].activeLiquidationAmount =
                        _add(_cType.activeLiquidationAmount, amountToRaise); // Update collateral-specific liquidation amount
        // -  id = Kicker(milk.flip).kick({ urn: urn, gal: address(vow), tab: tab, lot: dink, bid: 0 });

        id = collateralAuctionHouse.startAuction(
            collateralType,
            collateralJoin,
            address(accountingEngine),
            collateral2sell,
            incentivesReceiver
        );
        /// Now we nee to use our "auction" with Exchange module, that mean we need to add order
        /// id is needed only for event, so it's not necessary to store it
        /// But we don't use amountToRaise (=minAmount), so in future we may need to use it
        /// Also we send some tokens to incentivesReceiver previously, so we don't need it more
    }

    /**
     * @notice Reduces active liquidation amounts after an auction completes.
     * @dev Updates global and collateral-specific liquidation amounts. Only callable by LiquidationAccess.
     * @param collateralType Unique identifier for the collateral type.
     * @param rad Amount to reduce [rad].
     */
    // -  function claw(uint256 rad) external auth { litter = sub(litter, rad); }
    function reduceActiveLiquidationAmount(bytes32 collateralType, uint256 rad) external onlyLiquidationAccess {
        // -  litter = sub(litter, rad);
        ActiveLiquidationAmount = _sub(ActiveLiquidationAmount, rad); // Reduce global liquidation amount
        collateralTypes[collateralType].activeLiquidationAmount =
                        _sub(collateralTypes[collateralType].activeLiquidationAmount, rad); // Reduce collateral-specific liquidation amount
        emit ReduceActiveLiquidationAmount(collateralType, rad); // Emit event for reduction
        // Difference: Renamed to reduceActiveLiquidationAmount, uses onlyLiquidationAccess, and updates both global and collateral-specific amounts.
    }
}

contracts/makerdao/externalContracts/Sur.content:
// SPDX-License-Identifier: AGPL-3.0-or-later

/// SurplusAuctionHouse.sol -- StableUnit surplus auction module

// Forked from MakerDAO's Flap.sol
// Copyright (C) 2020-2022 Dai Foundation
// Adapted for StableUnit with Reflexer naming and Exchange module functionality (IExchange.addOrder)

pragma solidity ^0.8.0;

import "../interfaces/IAccountingEngine.sol";
import {IExchange, Order} from "../../exchange/contracts/interfaces/IExchange.sol";
import "../../exchange/contracts/periphery/contracts/access-control/SuAuthenticated.sol";
import "../interfaces/ISafeEngine.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/ICoinJoin.sol";
import {Math} from "../utils/Math.sol";

contract SurplusAuctionHouse is SuAuthenticated, Math {
    using SafeERC20 for IERC20;

    // --- Data ---
    struct Bid {
        // Amount of protocol token to sell [rad]
        uint256 amountToSell;
        // Address of the auction initiator
        address sender;
    }

    // Bid data for each separate auction, mapping auction ID to Bid struct
    mapping (uint256 => Bid) public bids;

    // Flag to indicate if the contract is active (1 = enabled, 0 = disabled)
    uint256 public contractEnabled;

    // Address of the protocol's native token (StablePro)
    address public protocolToken;

    // Address of treasury
    address public treasury;

    // Reference to SafeEngine for collateral and stablecoin transfers
    ISafeEngine public safeEngine;

    ICoinJoin public coinJoin; // Adapter to join StablePro

    // --- Events ---
    // Emitted when a new auction starts
    event StartAuction(address treasury, uint256 amountToSell, address sender);

    // Emitted when an auction is settled
    event SettleAuction(uint256 id);
    // Emitted when an auction is canceled
    event CancelAuction(uint256 id);
    // Emitted when parameters are modified
    event ModifyParameters(bytes32 indexed parameter, bytes32 data);
    event ModifyParameters(bytes32 indexed parameter, uint256 data);
    event ModifyParameters(bytes32 indexed parameter, address data);
    // Emitted when the contract is disabled
    event DisableContract();

    // --- Initialization ---
    // Initializes the contract with access control, AccountingEngine, Exchange module, suDAO, and protocol token
    // Forked from MakerDAO Flap.sol: constructor(address vat_, address gem_) public { ... } (line ~54)
    // StableUnit adaptation: Replaces MakerDAO's Vat with AccountingEngine, adds suDAO, and integrates Exchange module
    // @param accessControl Address of the SuAuthenticated access control contract
    // @param protocolToken_ Address of the protocol's native token (StablePro)
    // @param treasury_ Address of the treasury

    function initialize(
        address accessControl,
        address protocolToken_,
        address safeEngine_,
        address coinJoin_,
        address treasury_
    ) public initializer {
        // Set up role-based access control (DAO, Admin, DisablerAccess, LiquidationAccess)
        __suAuthenticatedInit(accessControl);

        // Enable the contract by default
        contractEnabled = 1;

        // Set protocol token address (StablePro)
        protocolToken = protocolToken_;

        treasury = treasury_;

        safeEngine = ISafeEngine(safeEngine_);

        coinJoin = ICoinJoin(coinJoin_);

        safeEngine.approveSAFEModification(coinJoin_); // Approve CoinJoin for SafeEngine modifications
    }

    // Prevents initialization of the implementation contract for upgradeability safety
    // StableUnit addition: Ensures the contract cannot be initialized outside of proxy deployment
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // --- Administration ---
    // Modifies contract parameters such as AccountingEngine, suDAO, or protocol token address
    // Forked from MakerDAO Flap.sol: function file(bytes32 what, address data) public auth { ... } (line ~76)
    // StableUnit adaptation: Updates AccountingEngine, suDAO, or protocolToken address, restricted to DAO role
    // @param what Parameter to modify ("accountingEngine", "suDAO", or "protocolToken")
    // @param data New contract address
    function modifyParameters(bytes32 what, address data) external onlyDAO {
        // Update AccountingEngine address for surplus settlement

        if (what == "protocolToken") {
            protocolToken = address(data);
        }

        if (what == "treasury") {
            treasury = data;
        }

        if (what == "safeEngine") {
            safeEngine = ISafeEngine(data);
        }

        if (what == "coinJoin") {
            coinJoin = ICoinJoin(data);
        }

        // Emit event to log address update
        emit ModifyParameters(what, data);
    }

    // --- Auction Management ---
    // Starts a new surplus auction, integrating Exchange module’s addOrder logic
    // StableUnit adaptation: Creates an order on the Exchange module to sell StablePro for suDAO tokens
    // @param amountToSell Amount of protocol token to sell [rad]
    // @return id Unique auction identifier
    function startAuction(
        uint256 amountToSell,
        uint256
    ) external onlyAuctionAccess returns (uint256 id) {
        // Ensure the contract is enabled to allow new auctions
        require(contractEnabled == 1, "SurplusAuctionHouse/not-enabled");
        // Ensure non-zero amount to prevent null auctions
        require(amountToSell > 0, "SurplusAuctionHouse/zero-collateral");

        safeEngine.transferInternalCoins(msg.sender, address(this), amountToSell * 1e27);

        coinJoin.exitNoReward(address(this), amountToSell);

        IERC20(protocolToken).safeTransfer(treasury, amountToSell);
        // Emit event to log auction start
        emit StartAuction(treasury, amountToSell, msg.sender);
    }

    // Restarts an auction by extending the bid deadline
    // Forked from MakerDAO Flap.sol: function tick(uint id) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    function restartAuction(uint256 id) external {}

    // Increases the bid amount for a fixed collateral amount
    // Forked from MakerDAO Flap.sol: function tend(uint id, uint lot, uint bid) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    // @param collateralAmount Collateral amount (must match auction’s lot)
    // @param bid New bid amount in suDAO tokens [rad]
    function increaseBidSize(uint256 id, uint256 collateralAmount, uint256 bid) external {}

    // Decreases the collateral amount for a fixed bid
    // Forked from MakerDAO Flap.sol: function dent(uint id, uint lot, uint bid) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    // @param collateralAmount New collateral amount (must be less than current lot)
    // @param bid Bid amount (must match current bid and tab) [rad]
    function decreaseSoldAmount(uint256 id, uint256 collateralAmount, uint256 bid) external {}

    // Settles a completed auction, transferring suDAO tokens to AccountingEngine
    // Forked from MakerDAO Flap.sol: function deal(uint id) public { ... }
    // StableUnit adaptation: Not implemented, placeholder for future functionality
    // @param id Auction identifier
    function settleAuction(uint256 id) external {}

    // Cancels an auction during system shutdown, refunding StablePo to the sender
    // Forked from MakerDAO Flap.sol: function yank(uint id) public auth { ... } (line ~137)
    // StableUnit adaptation: Refunds StablePro to the sender via the Exchange module
    // @param id Auction identifier
    function cancelAuction(uint256 id) external onlyLiquidationAccess {}


    // Disables the contract, halting new auctions
    // StableUnit adaptation: Restricted to accounts with DisablerAccess role
    // @notice Only callable by accounts with DisablerAccess role
    function disableContract() external onlyDisablerAccess {
        // Set contract to disabled state
        contractEnabled = 0;
        // Emit event to log contract disablement
        emit DisableContract();
    }
}

contracts/makerdao/interfaces/IAccountin.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

/**
 * @title IAccountingEngine
 **/
interface IAccountingEngine {
    /* ==================== EVENTS ===============-===== */
    event StableProSwapped(uint256 amount);
    event DisableContract();

    event AuctionDebt(uint256 indexed id, uint256 totalOnAuctionDebt, uint256 debtBalance);
    event AuctionSurplus(uint256 indexed id, uint256 coinBalance);

    event Initialize(address indexed accountingEngine);
    event PushDebtToQueue(uint256 era, uint256 amountToRaise, uint256 totalQueuedDebt);
    event PopDebtFromQueue(uint256 era, uint256 totalQueuedDebt);
    event SettleDebt(uint256 rad, uint256 coinBalance, uint256 debtBalance);
    event CancelAuctionedDebtWithSurplus(uint256 rad, uint256 totalOnAuctionDebt);
    event JoinStablePro(uint256 amount, uint256 totalOnAuctionDebt);
    event GetRealStablePro(uint256 amount);
    event RefillStablecoin(address stablecoin, uint256 amount);
    event SwapStableProToStablecoin(address stablecoinToken, uint256 stableProAmountE18, uint256 stablecoinAmountEDecimal);
    event AddWhitelistStablecoin(address stablecoin);
    event RemoveWhitelistStablecoin(address stablecoin);
    event SetStablecoinToSwap(address stablecoin);
    event ModifyParameters(bytes32 what, uint256 data);
    event ModifyParameters(bytes32 what, address data);

    /* ==================== ERRORS ===============-===== */
    error ContractDisabled();
    error BadModifyParameters();
    error BadStablecoin(address asset);
    error BadRewardToken();
    error DebtNotZero();
    error SurplusNotZero();
    error InsufficientDebt();
    error InsufficientSurplus();
    error NotEnoughAsh();
    error PopDebtDelayNotFinished();
    error StablecoinNotExist(address asset);
    error StablecoinExists(address asset);

    /* ====================== VARS ===================== */

    function stablecoinToSwap() external view returns (address);

    /* ==================== MUTABLE METHODS ==================== */

    /**
     * @notice Push debt (that the system tries to cover with collateral auctions) to a queue
     * @dev Debt is locked in a queue to give the system enough time to auction collateral
     *      and gather surplus
     * @param amountToRaise Amount of debt to push
     */
    function pushDebtToQueue(uint amountToRaise) external;

    /**
     * @notice A block of debt can be popped from the queue after popDebtDelay seconds passed since it was
     *         added there
     * @param era Timestamp of the block of debt that should be popped out
     */
    function popDebtFromQueue(uint era) external;

    // Debt settlement
    /**
     * @notice Destroy an equal amount of coins and debt
     * @dev We can only destroy debt that is not locked in the queue and also not in a debt auction
     * @param rad Amount of coins/debt to destroy (number with 45 decimals)
    **/
    function settleDebt(uint rad) external;

    /**
     * @notice Use surplus coins to destroy debt that is/was in a debt auction
     * @param rad Amount of coins/debt to destroy (number with 45 decimals)
    **/
    function cancelAuctionedDebtWithSurplus(uint rad) external;

    /**
     * @notice Start a debt auction (we have debt = x$, token2sell with value of y$, where y >= x)
     * @dev We can only auction debt that is not already being auctioned and is not locked in the debt queue.
     * Firstly, we should donate token2sell (it's collateral or stablecoin) by refillStablecoin
    **/
    function auctionDebt(
        address token2sell,
        uint256 amountToSell,
        uint256 auctionDebt
    ) external returns (uint orderId);

    /**
     * @notice Start a surplus auction
     * @dev We can only auction surplus if we wait at least 'surplusAuctionDelay' seconds since the last
     *      auction trigger, if we keep enough surplus in the buffer and if there is no bad debt to settle
    **/
    function auctionSurplus(uint256 surplusAmount) external returns (uint orderId);

    /**
     * @notice Disable this contract (normally called by Global Settlement)
     * @dev When we disable, the contract tries to settle as much debt as possible (if there's any) with
            any surplus that's left in the system. After erasing debt, the contract will either transfer any
            remaining surplus right away (if disableCooldown == 0) or will only record the timestamp when
            it was disabled
    **/
    function disableContract() external;

    /* ================= Stablecoin Whitelist ================== */

    /**
     * @notice Add new stablecoin to whitelist of stablecoins protocol works with
    **/
    function addWhitelistStablecoin(address stablecoin) external;

    /**
     * @notice Remove stablecoin from whitelist of stablecoins protocol works with
    **/
    function removeWhitelistStablecoin(address stablecoin) external;

    /**
     * @notice Check if give token is in stablecoins whitelist
    **/
    function hasWhitelistStablecoin(address stablecoin) external view returns (bool);

    /**
     * @notice Change var stablecoinToSwap, that is used in liquidation process
    **/
    function setStablecoinToSwap(address stablecoin) external;

    /* =================== MANUAL FUNCTIONS ==================== */
    /**
     * @notice Admin function to join protocol using real StablePro (= join in coinJoin)
    **/
    function joinStablePro(uint256 amount) external;

    /**
     * @notice Admin function to get real StablePro tokens instead of records in protocol (= exit from coinJoin)
    **/
    function getRealStablePro(uint256 amount) external;

    /**
     * @notice Function to send stablecoin to AccountingEngine contract
    **/
    function refillStablecoin(address stablecoin, uint256 amount) external;

    /**
     * @notice Admin function to distribute surplus after getting it
    **/
    //function distributeSurplus(uint256 amount) external;

    /**
     * @notice Function to swap sender's stablePro to stablecoin that is in whitelist
    **/
    function swapStableProToStablecoin(address stablecoinToken, uint256 stableProAmountE18) external;

    function totalQueuedDebt() external view returns (uint256);

    function totalOnAuctionDebt() external view returns (uint256);
}

