// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.13;

/**
 * @title AddressSet
 * @notice Data structure for storing unique addresses with O(1) lookup
 * @dev Combines an array for iteration with a mapping for fast existence checks
 *      Used in Handler to manage the set of test actors (addresses)
 */
struct AddressSet {
    // Array of all unique addresses - provides ordered access and iteration capabilities
    // In Handler: Stores all test addresses that have interacted with the contract
    address[] addrs;

    // Mapping for O(1) existence check - prevents duplicates in the array
    // In Handler: Quickly determines if an address is already a test actor
    mapping(address => bool) saved;
}

/**
 * @title LibAddressSet
 * @notice Library providing operations for the AddressSet data structure
 * @dev Used by Handler to manage the pool of test actors for fuzzing operations
 */
library LibAddressSet {
    /**
     * @notice Adds an address to the set if not already present
     * @param s The AddressSet to modify
     * @param addr The address to add
     * @dev In Handler: Used when creating new test actors via createActor modifier
     *      Ensures each address is only added once to the actor pool
     */
    function add(AddressSet storage s, address addr) internal {
        if (!s.saved[addr]) {
            // Only add if not already in the set
            s.addrs.push(addr);
            s.saved[addr] = true;
        }
    }

    /**
     * @notice Checks if an address exists in the set
     * @param s The AddressSet to check
     * @param addr The address to look for
     * @return bool True if the address is in the set
     * @dev In Handler: Can be used to verify if an address is a recognized actor
     */
    function contains(AddressSet storage s, address addr) internal view returns (bool) {
        return s.saved[addr];
    }

    /**
     * @notice Returns the number of addresses in the set
     * @param s The AddressSet to count
     * @return uint256 The number of addresses
     * @dev In Handler: Used to track how many test actors have been created
     */
    function count(AddressSet storage s) internal view returns (uint256) {
        return s.addrs.length;
    }

    /**
     * @notice Selects a pseudo-random address from the set using a seed
     * @param s The AddressSet to select from
     * @param seed Random seed value to determine selection
     * @return address The selected address (or address(0) if set is empty)
     * @dev In Handler: Critical for fuzzing - selects random actors for operations
     *      Used in useActor modifier to randomly choose which address performs an action
     *      Also used to select random recipients for transfers, harvests, etc.
     */
    function rand(AddressSet storage s, uint256 seed) internal view returns (address) {
        if (s.addrs.length > 0) {
            // Convert any seed to a valid index using modulo
            return s.addrs[seed % s.addrs.length];
        } else {
            return address(0);
        }
    }

    /**
     * @notice Applies a function to every address in the set
     * @param s The AddressSet to iterate over
     * @param func The function to apply to each address
     * @dev In Handler: Enables batch operations across all actors
     *      Used for invariant checking that requires examining all actors
     *      Example: Verifying all actors have correct rewardDebt values
     */
    function forEach(AddressSet storage s, function(address) external func) internal {
        for (uint256 i; i < s.addrs.length; ++i) {
            func(s.addrs[i]);
        }
    }

    /**
     * @notice Reduces the set to a single value using an accumulator function
     * @param s The AddressSet to reduce
     * @param acc Initial accumulator value
     * @param func Function that takes current accumulator value and address, returns new accumulator
     * @return uint256 Final accumulated value
     * @dev In Handler: Used for complex aggregate calculations across all actors
     *      Example: Summing total pending rewards across all test addresses
     *      Example: Finding the maximum precision error across all accounts
     */
    function reduce(AddressSet storage s, uint256 acc, function(uint256,address) external returns (uint256) func)
        internal
        returns (uint256)
    {
        for (uint256 i; i < s.addrs.length; ++i) {
            acc = func(acc, s.addrs[i]);
        }
        return acc;
    }
}
