contract ArbitrageHelper is Initializable, IArbitrageHelper {
    // uint256(tokenA + tokenB) => fee for tokenA-tokenB pool (10000 / 3000 / 500)
    mapping (uint256 => uint24) public poolFees;

    ISwapRouter internal SWAP_ROUTER;
    address internal UNISWAP_FACTORY;
