pragma solidity ^0.8.0;

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-periphery/contracts/interfaces/IPeripheryImmutableState.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "../../access-control/SuAuthenticated.sol";
import "../../oracles/vendor/PoolAddress.sol";
import "../interfaces/IExchange.sol";
import "../interfaces/3rd-party/aave/IAavePool.sol";
import "../interfaces/IArbitrageHelperUniswap.sol";

// TODO: make swapBy***() internal and skip test with them
contract ArbitrageHelperUniswap is SuAuthenticated, IArbitrageHelperUniswap {
    ISwapRouter internal SWAP_ROUTER;
    IAavePool internal AAVE_POOL;
    IExchange internal EXCHANGE;

    function initialize(address _authControl, address _exchange, address _aavePool, address _swapRouter) public initializer {
        __SuAuthenticated_init(_authControl);

        EXCHANGE = IExchange(_exchange);
        AAVE_POOL = IAavePool(_aavePool);
        SWAP_ROUTER = ISwapRouter(_swapRouter);
    }

    // @notice Can use address(this) instead of recipient.
    // DOCS: https://docs.uniswap.org/contracts/v3/guides/swaps/single-swaps
    function swapByUniswap(address token2sell, uint256 amount2sell, uint256 minToken2buy, address recipient, bytes memory path) internal returns (uint256 amountOut) {
        ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
            path: path,
            recipient: recipient,
            deadline: block.timestamp,
            amountIn: amount2sell,
            amountOutMinimum: 0
        });

        // We need to approve {token2sell} to be used in Swap Router and send to this contract to swap it
        TransferHelper.safeApprove(token2sell, address(SWAP_ROUTER), amount2sell);
        if (recipient != address(this)) {
            TransferHelper.safeTransferFrom(token2sell, recipient, address(this), amount2sell);
        }

        // swap {token2sell} to {token2buy} and return the amount of the received {token2buy}
        amountOut = SWAP_ROUTER.exactInput(params);
    }

    function swapWithFlashLoan(
        uint256 orderId,
        address tokenA,
        address tokenB,
        uint256 tokenBAmount,
        bytes memory path
    ) public {
        bytes memory data = abi.encode(FlashCallbackData({
            orderId: orderId,
            tokenA: tokenA,
            tokenB: tokenB,
            tokenBAmount: tokenBAmount,
            recipient: msg.sender,
            path: path
        }));
        AAVE_POOL.flashLoanSimple(address(this), tokenB, tokenBAmount, data, 0);
    }

    // @notice This is callback function from aave. flashLoanSimple call (msg.sender).executeOperation(...) after transferring loan to sender
    function executeOperation(address asset, uint256 amount, uint256 fee, address sender, bytes calldata data) external returns (bool) {
        FlashCallbackData memory decoded = abi.decode(data, (FlashCallbackData));

        if (msg.sender != address(AAVE_POOL)) revert NotAPool(msg.sender);
        if (sender != address(this)) revert BadSender(sender);
        if (asset != decoded.tokenB || amount != decoded.tokenBAmount) revert BadDecodedData();

        uint256 maxOrderOutAmount = EXCHANGE.getExpectedOrderOutcome(decoded.orderId);
        uint256 amountToSend = maxOrderOutAmount > amount ? amount : maxOrderOutAmount;
        TransferHelper.safeTransfer(asset, decoded.recipient, amountToSend);

        (uint256 tokenAReceived, uint256 outAmount) = EXCHANGE.fillOrder(
            decoded.orderId,
            amountToSend,
            decoded.recipient,
            false
        );
        uint256 tokenBLeft = amountToSend - outAmount;

        TransferHelper.safeTransferFrom(decoded.tokenA, decoded.recipient, address(this), tokenAReceived);

        uint256 tokenBReceived = swapByUniswap(
            decoded.tokenA,
            tokenAReceived,
            outAmount + fee,
            address(this),
            decoded.path
        );

        uint256 swapProfit = tokenBReceived - amountToSend;
        if (swapProfit <= fee) revert SwapNotProfitable();

        // Send swapped {stablecoinToken} minus fee from flash-loan to liquidator (userProfit)
        TransferHelper.safeTransfer(asset, decoded.recipient, swapProfit - tokenBLeft - fee);

        // Send tokenBToRepay (Repay borrow)
        TransferHelper.safeApprove(asset, address(AAVE_POOL), amount + fee);
        return true;
    }

    uint256[45] private __gap;
}
