pragma solidity ^0.8.0;

/**
 * @title SuCdpRegistry
 * @notice This contract is needed to index all opened CDPs.
 * @dev It can be removed if there's more gas-efficient way to do that, such as graphQL, NFT-lps or other methods
 **/
interface ISuCdpRegistry {
    // @notice Collateral Debt Position
    struct CDP {
        address asset; // collateral token
        address owner; // borrower account
    }

    /* ===================== EVENTS ===================== */

    // @notice event emitted when a new CDP is created
    event Added(address indexed asset, address indexed owner);

    // @notice event emitted when a CDP is closed
    event Removed(address indexed asset, address indexed owner);

    /* ===================== ERRORS ===================== */

    error ZeroAddress();
    error ArgumentsLengthMismatch();

    /* ==================== METHODS ==================== */

    // @notice method to add or remove owner from cdpList[asset]
    function checkpoint ( address asset, address owner ) external;

    // @notice multiple checkpoint(...) call for the args arrays
    function batchCheckpoint ( address[] calldata assets, address[] calldata owners ) external;

    // @notice checkpoint in loop
    function batchCheckpointForAsset ( address asset, address[] calldata owners ) external;

    // @notice read-only function to get the list of all cdps
    function getAllCdps (  ) external view returns ( CDP[] memory r );

    // @notice read-only function to get the list of cdps for a given collateral
    function getCdpsByCollateral ( address asset ) external view returns ( CDP[] memory cdps );

    // @notice read-only function to get the list of all cdps by borrower
    function getCdpsByOwner ( address owner ) external view returns ( CDP[] memory r );

    // @notice total number of cdps
    function getCdpsCount (  ) external view returns ( uint256 totalCdpCount );

    // @notice number of cdps for a given collateral
    function getCdpsCountForCollateral ( address asset ) external view returns ( uint256 );

    // @notice alive means there are debts in the vault for this collateral of this borrower
    function isAlive ( address asset, address owner ) external view returns ( bool );

    // @notice listed means there are created cdps in this contract for this collateral of this borrower
    function isListed ( address asset, address owner ) external view returns ( bool );
}
