pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";

import "../interfaces/ISuPausable.sol";
import "../access-control/SuAuthenticated.sol";

/**
 * @dev Contract module which allows to implement an emergency stop mechanism that can be triggered only by an owner.
 */
contract SuPausable is SuAuthenticated, ContextUpgradeable, ISuPausable {
    mapping(address => bool) private _isCollateralPaused;
    mapping(address => bool) private _isContractPaused;
    bool private _isAllPaused;

    // @dev _isCollateralPaused and _isContractPaused are empty
    // that means that all collaterals and contracts are not paused.
    function initialize(address _authControl) public initializer {
        __SuAuthenticated_init(_authControl);
        _isAllPaused = false;
    }

    function pauseCollateral(address collateral) public onlyPauser {
        if (_isCollateralPaused[collateral]) revert AlreadyPaused();
        _isCollateralPaused[collateral] = true;
        emit PausedCollateral(_msgSender(), collateral);
    }

    function batchPauseCollateral(address[] calldata collaterals) public onlyPauser {
        for (uint i = 0; i < collaterals.length; i++) {
            pauseCollateral(collaterals[i]);
        }
    }

    function pauseContract(address _contract) public onlyPauser {
        if (_isContractPaused[_contract]) revert AlreadyPaused();
        _isContractPaused[_contract] = true;
        emit PausedContract(_msgSender(), _contract);
    }

    function pauseAll() public onlyPauser {
        _isAllPaused = true;
    }

    function unpauseCollateral(address collateral) public onlyPauser {
        if (!_isCollateralPaused[collateral]) revert NotPaused();
        _isCollateralPaused[collateral] = false;
        emit UnpausedCollateral(_msgSender(), collateral);
    }

    function batchUnpauseCollateral(address[] calldata collaterals) public onlyPauser {
        for (uint i = 0; i < collaterals.length; i++) {
            unpauseCollateral(collaterals[i]);
        }
    }

    function unpauseContract(address _contract) public onlyPauser {
        if (!_isContractPaused[_contract]) revert NotPaused();
        _isContractPaused[_contract] = false;
        emit UnpausedContract(_msgSender(), _contract);
    }

    function unpauseAll() public onlyPauser {
        _isAllPaused = false;
    }

    function isPaused(address collateral) public view virtual returns (bool) {
        return _isCollateralPaused[collateral] || _isContractPaused[msg.sender] || _isAllPaused;
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[45] private __gap;
}
