        if (block.number > rewardEndBlock) {
            return 0;
        }
        uint256 rewardsLeftToDistribute = rewardPerBlock * (rewardEndBlock - block.number);
        uint256 activeSupply = totalSupply() - rewardsLeftToDistribute;
