// SPDX-License-Identifier: BSL 1.1

/*
  Copyright 2020 Unit Protocol: Artem Zakharov (az@unit.xyz).
*/
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "hardhat/console.sol";

import "../interfaces/ISuOracle.sol";
import "../interfaces/ISuVault.sol";
import "../interfaces/ISuCdpRegistry.sol";
import "../interfaces/ISuManagerParameters.sol";
import "../interfaces/ISuVaultParameters.sol";
import "./SuManagerParameters.sol";

// finally found the managers mighty over the vault
// user does only interact with manager as proxy to the wallet
/// Yes
// there is only one manager for vault each moment
/// I'm sure, the code doesn't enforce this, could be multiple managers
// suggest to maintain only address of active manager instead of managers list
/// not sure why
contract SuManager is ReentrancyGuardUpgradeable, SuManagerParameters {
    ISuVault public vault;
    ISuManagerParameters public managerParameters;
    ISuCdpRegistry public cdpRegistry;

    ISuOracle public suOracle;

    // even triggered when user deposit collateral
    event Join(address indexed asset, address indexed owner, uint main, uint stablecoin);

    // event triggered when user withdraws collateral
    event Exit(address indexed asset, address indexed owner, uint main, uint stablecoin);

    // event triggered when user destroyes cdp
    event Destroyed(address indexed asset, address indexed owner, uint main, uint stablecoin);

    // event triggered when user didnt withdraw collateral before price goes down
    event LiquidationTriggered(address indexed asset, address indexed owner);

    /**
     * @dev Trigger when buyouts are happened
    **/
    event Buyout(address indexed asset, address indexed owner, address indexed buyer, uint amount, uint price);

    modifier checkpoint(address asset, address owner) {
        _;
        cdpRegistry.checkpoint(asset, owner);
    }

     // aggregation over composition
    function initialize(address _vault, address _suOracle, address _cdpRegistry) public initializer {
        __ReentrancyGuard_init();
        __SuManagerParameters_init(_vault);

        address _managerParameters = address(this);
        require(
            _managerParameters != address(0) &&
            _cdpRegistry != address(0),
                "SU Protocol: INVALID_ARGS"
        );

        require(_suOracle != address(0), "INVALID_ARGS: SU_ORACLE");

        // require(ISuVaultManagerParameters(_managerParameters).vaultParameters() != address(0), "INVALID_ARGS: VAULT_PARAMETERS");

        // require(ISuVaultParameters(ISuVaultManagerParameters(_managerParameters).vaultParameters()).vault() != address(0), "INVALID_ARGS: VAULT");

        // require(ISuVault(ISuVaultParameters(ISuVaultManagerParameters(_managerParameters).vaultParameters()).vault()).weth() != address(0), "INVALID_ARGS: VAULT_MANAGER");

        suOracle = ISuOracle(_suOracle);

        managerParameters = ISuManagerParameters(_managerParameters);

        // vault = ISuVault(ISuVaultParameters(ISuVaultManagerParameters(_managerParameters).vaultParameters()).vault());
        vault = ISuVault(_vault);

        cdpRegistry = ISuCdpRegistry(_cdpRegistry);
    }

    // wrapper ether only, no native
    receive() external payable {
        revert("SU Protocol: RESTRICTED");
    }

      // why checkpoint is needed? to check if depositing collateral allowed for user?
      // this function is called by user to deposit collateral and receive stablecoin
      // before calling this function user has to approve the Vault to take his collateral
      /// Yes,
    function join(address asset, uint assetAmountEDecimal, uint stablecoinAmountE18, uint256 lockupPeriodSeconds) public nonReentrant checkpoint(asset, msg.sender) {
        require(stablecoinAmountE18 != 0 || assetAmountEDecimal != 0, "SU Protocol: USELESS_TX");

        require(IERC20Metadata(asset).decimals() <= 18, "SU Protocol: NOT_SUPPORTED_DECIMALS");

        if (stablecoinAmountE18 == 0) {

            // why user deposit collateral but does not take stablecoin?
            // should be called in separate function
            /// Use doesn't want to get liquidate, so stakes more collateral
            vault.deposit(asset, msg.sender, assetAmountEDecimal, lockupPeriodSeconds);

        } else {

            if (assetAmountEDecimal != 0) {
                // deposit collateral to the wallet
                vault.deposit(asset, msg.sender, assetAmountEDecimal, lockupPeriodSeconds);
            }

            // issue stablecoin to the user
            // why stablecoinAmount is chosen by user?
            // it should be calculated from amount of collateral given
            vault.borrow(asset, msg.sender, stablecoinAmountE18);

            // maybe here we check that stablecoinAmount are corresponding to assetAmount?
            // how does user know correct values? probably by calling helper view function
            _ensurePositionCollateralization(asset, msg.sender);

        }

        // not allowed to left some dust after partial closure the position
        _ensureMinCollateralThreshold(asset, msg.sender);

        emit Join(asset, msg.sender, assetAmountEDecimal, stablecoinAmountE18);
    }

      // user can pay back the stablecoin and take his collateral
      // instead of passing both assetAmount and stablecoinAmount
      // better user just to pass one of them
      // also pass preferred rate and maybe acceptable diff percent
      // that's the purpose of passing both to protect user from rate fluctuations
    function exit(address asset, uint assetAmountEDecimal, uint stablecoinAmountE18) public nonReentrant checkpoint(asset, msg.sender) returns (uint) {

        // check liquidation is not triggered yet
        require(vault.liquidationBlock(asset, msg.sender) == 0, "SU Protocol: LIQUIDATING_POSITION");

        // not to pay more stablecoin than debt
        uint debtE18 = vault.debtsE18(asset, msg.sender);
        if (stablecoinAmountE18 > debtE18) { stablecoinAmountE18 = debtE18; }

        // calculate and charge fee
        uint feeE18 = vault.calculateFeeE18(asset, msg.sender, stablecoinAmountE18);

        bool destroyed = vault.liquidate(asset, msg.sender, msg.sender, assetAmountEDecimal, stablecoinAmountE18);

        // not allowed to left some dust after partial closure the position
        _ensureMinCollateralThreshold(asset, msg.sender);

        // burn stablecoin from the vault
        vault.repay(msg.sender, stablecoinAmountE18, feeE18);

        if (destroyed) {
            emit Destroyed(asset, msg.sender, assetAmountEDecimal, stablecoinAmountE18);
            return stablecoinAmountE18;
        }

        // TODO: check that withdrawals lead to update

        vault.update(asset, msg.sender);

        // make sure partial repayment is valid
        // but dont need to check this after full repayment?
        /// Yes, because divizion by 0
        _ensurePositionCollateralization(asset, msg.sender);

        emit Exit(asset, msg.sender, assetAmountEDecimal, stablecoinAmountE18);

        return stablecoinAmountE18;
    }

    // after partial repayment should be made sure its still collateralized enough
    function _ensurePositionCollateralization(address asset, address owner) internal view {
        // calculate value in usd from collateral position
        uint usdValueE18 = getCollateralUsdValueE18(asset, owner);

        // multiply value in usd to the collateral ratio, then divide by 1e18
        uint usdLimitE18 = usdValueE18 * managerParameters.initialCollateralRatioE18(asset) / 1e18;

        // make sure calculated allowed limit is higher than the actual debt
        require(vault.getTotalDebtE18(asset, owner) <= usdLimitE18, "SU Protocol: UNDERCOLLATERALIZED");
    }

    // after partial closure should be made sure it is not left too little amount of collateral
    function _ensureMinCollateralThreshold(address asset, address owner) internal view {
        // calculate value in usd from collateral position
        uint usdValueE18 = getCollateralUsdValueE18(asset, owner);

        require(usdValueE18 == 0 || usdValueE18 > managerParameters.minCollateralInUsdE18(), "SU Protocol: SMALL_COLLATERAL");
    }

    // * Now we return rate and calculate amount here
    function getCollateralUsdValueE18(address asset, address owner) public view returns (uint) {
        uint256 assetAmountEDecimal = vault.collateralsEDecimal(asset, owner);

        return suOracle.getUsdPrice1e18(asset) * assetAmountEDecimal / 1e18;
    }

     // is position allowed to be liquidated
    function _isLiquidatablePosition(
        address asset,
        address owner,
        uint usdValueE18
    ) internal view returns (bool) {
        // calculate current debt to be returned
        uint debtE18 = vault.getTotalDebtE18(asset, owner);

        if (debtE18 == 0) return false;
        console.log("LIMIT", debtE18, usdValueE18, managerParameters.liquidationRatioE18(asset));
        // make sure its not under collaterazied
        // should liquidation ration always be higher than 1 or allowed lower?
        return debtE18 * 1e18 / usdValueE18 >= managerParameters.liquidationRatioE18(asset);
    }

     // view function to check if position is liquidatable
    function isLiquidatablePosition(
        address asset,
        address owner
    ) public view returns (bool) {
        uint usdValueE18 = getCollateralUsdValueE18(asset, owner);

        return _isLiquidatablePosition(asset, owner, usdValueE18);
    }

     // view function to show utilization ratio
     // the same function can be used inside of _isLiquidatablePosition
    function utilizationRatioE18(
        address asset,
        address owner
    ) public view returns (uint) {
        uint debtE18 = vault.getTotalDebtE18(asset, owner);
        if (debtE18 == 0) return 0;

        uint usdValueE18 = getCollateralUsdValueE18(asset, owner);

        return debtE18 * 1e18 / usdValueE18;
    }

    /// @notice Marks a position as to be liquidated
    /// @param asset The address of the main collateral token of a position
    /// @param owner The owner of a position
    /** @dev
    Emits LiquidationTriggered event.
    Sets the current block as liquidationBlock for the position.
    Can be triggered only once for the position.
    */
    function triggerLiquidation(address asset, address owner) external {

        // check liquidation is not triggered yet
        require(vault.liquidationBlock(asset, owner) == 0, "SU Protocol: LIQUIDATING_POSITION");

        // calculate valut of collateral
        uint usdValue = getCollateralUsdValueE18(asset, owner);

        // and check if it can be liquidated
        require(_isLiquidatablePosition(asset, owner, usdValue), "SU Protocol: SAFE_POSITION");

        // summarize debt with accumulated fees
        vault.update(asset, owner);

        // then vault will trigger liquidation and auction begins
        vault.triggerLiquidation(asset, owner);

        emit LiquidationTriggered(asset, owner);
    }

    // will stablecoin paid by liquidator be burned immediately?
    /// Yes
    // how can cascading liquidation happen step by step?
    /// please see https://ratiofinance.medium.com/ratio-risk-lesson-2-cascading-liquidations-e91e04050f47
    /// @notice Liquidates a position's collateral completely
    /// @dev Supports a partial liquidation
    /// @param asset The address of the main collateral token of a position
    /// @param owner The owner of a position
    /// @param repayer The person who repaies by debt and recieves a position's collateral
    /// @param repaymentE18 The amount of repayment
    /// @param excessAndFeeE18 The amount of stablecoins which transfer to foundation
    /// @param assetAmountEDecimal The position's collateral which be recieved by repayer
    /// @return destroyed Returns "true" if the position completely liquidated
    function liquidateAndRepay(
        address asset,
        address owner,
        address repayer,
        uint repaymentE18,
        uint excessAndFeeE18,
        uint assetAmountEDecimal
    ) public nonReentrant checkpoint(asset, owner) onlyLiquidationAccess returns (bool destroyed) {
        // check if it can be liquidated
        require(vault.liquidationBlock(asset, owner) > 0, "SU Protocol: NOT_LIQUIDATING_POSITION");

        console.log("LIQUIDATE liquidate (assetAmount, repayment): ", assetAmountEDecimal, repaymentE18);
        destroyed = vault.liquidate(asset, owner, repayer, assetAmountEDecimal, repaymentE18);

        // not allowed to left some dust after partial closure the position
        _ensureMinCollateralThreshold(asset, msg.sender);

        // burn stablecoin from the vault
        console.log("REPAY repay (repayment, excessAndFee):", repaymentE18, excessAndFeeE18);
        vault.repay(repayer, repaymentE18, excessAndFeeE18);
    }

    /// @notice Liquidates a position
    /// @dev Supports a partial liquidation
    /// @param asset The address of the main collateral token of a position
    /// @param owner The owner of the collateral
    /// @param repayer The person who repaies by debt and transfers stablecoins to the foundation
    /// @param stablecoinsToRepaymentE18 The amount of stablecoins which will be burned as a debt repaymention
    /// @param assetAmountEDecimal The position's collateral which be recieved by repayer
    /// @return destroyed Returns "true" if the position completely liquidated
    function liquidate(
        address asset,
        address owner,
        address repayer,
        uint stablecoinsToRepaymentE18,
        uint assetAmountEDecimal
    ) public nonReentrant checkpoint(asset, owner) onlyLiquidationAccess returns (bool destroyed) {
        // check if it can be liquidated
        require(vault.liquidationBlock(asset, owner) > 0, "SU Protocol: NOT_LIQUIDATING_POSITION");

        console.log("LIQUIDATE liquidate (assetAmount, repayment): ", assetAmountEDecimal, stablecoinsToRepaymentE18);

        destroyed = vault.liquidate(asset, owner, repayer, assetAmountEDecimal, stablecoinsToRepaymentE18);

        // not allowed to left some dust after partial closure the position
        _ensureMinCollateralThreshold(asset, msg.sender);
    }

    /// @notice Finilizes the liquidation
    /// @param repayer The person who repaies by debt and transfers stablecoins to the foundation
    /// @param stablecoinsToRepaymentE18 The amount of stablecoins which will be burned as a debt repayment
    /// @param stablecoinsToFoundationE18 The amount of stablecoins which will be transfered to the foundation
    function repay(
        address repayer,
        uint stablecoinsToRepaymentE18,
        uint stablecoinsToFoundationE18
    ) public {
        console.log("REPAY repay (repayment, toFondation):", stablecoinsToRepaymentE18, stablecoinsToFoundationE18);

        // burn stablecoin from the vault
        vault.repay(repayer, stablecoinsToRepaymentE18, stablecoinsToFoundationE18);
    }

    /// @notice Returns information about a liquidating position
    /// @dev TODO: should rename to getPositionInfo()
    /// @param asset The address of the main collateral token of a position
    /// @param owner The owner of the collateral
    /// @return liquidationBlock The block number when the liquidation was triggered
    /// @return collateralEDecimal The amount of collateral
    /// @return debtE18 The amount of borrowed stablecoins
    function getPosition(
        address asset,
        address owner
    ) external view returns (
        uint liquidationBlock,
        uint collateralEDecimal,
        uint debtE18
    ) {
        liquidationBlock = vault.liquidationBlock(asset, owner);

        collateralEDecimal = vault.collateralsEDecimal(asset, owner);
        debtE18 = vault.debtsE18(asset, owner);
    }

    /// @notice Returns information about a liquidating position
    /// @param asset The address of the main collateral token of a position
    /// @param owner The owner of the collateral
    /// @return liquidationBlock The block number when the liquidation was triggered
    function getLiquidationBlock(
        address asset,
        address owner
    ) external view returns (uint256) {
        return vault.liquidationBlock(asset, owner);
    }

    // UI-helpers

    // calculate liquidation price
    // can be used inside of _isLiquidatablePosition
    function liquidationPriceE18(
        address asset,
        address owner
    ) external view returns (uint) {
        uint debtE18 = vault.getTotalDebtE18(asset, owner);
        if (debtE18 == 0) return type(uint256).max;

        // TODO initialCollateralRatioE18 or liquidationRatioE18?
        uint collateralLiqPriceE18 = debtE18 * 1e18 / (managerParameters.liquidationRatioE18(asset));

        require(IERC20Metadata(asset).decimals() <= 18, "SU Protocol: NOT_SUPPORTED_DECIMALS");

        return collateralLiqPriceE18 * 1e18 / vault.collateralsEDecimal(asset, owner) / 10 ** (18 - IERC20Metadata(asset).decimals());
    }

    /// @notice Returns Loan-To-Value in e18
    function getLTVE18(address asset, address owner) external view returns (uint256) {
        uint usdValueE18 = getCollateralUsdValueE18(asset, owner);
        if (usdValueE18 == 0) return 0;

        uint debtE18 = vault.debtsE18(asset, owner);

        return debtE18 * 1e18 / usdValueE18;
    }

    /// @notice Returns Available to Borrow
    /// we have invariant: (collateralAmountEDecimal * collateralPriceE18 / 1e18) * initialCollateralRatioE18 <= debtE18
    /// Has similar logic like in _ensurePositionCollateralization
    function getAvailableToBorrowE18(address asset, address owner) external view returns (uint256) {
        uint usdValueE18 = getCollateralUsdValueE18(asset, owner);
        uint usdLimitE18 = usdValueE18 * managerParameters.initialCollateralRatioE18(asset) / 1e18;

        uint totalDebtE18 = vault.getTotalDebtE18(asset, owner);

        if (usdLimitE18 <= totalDebtE18) return 0;

        return usdLimitE18 - totalDebtE18;
    }

    /// @notice Returns Available to Withdraw
    /// Has similar invariant like in getAvailableToBorrowE18
    function getAvailableToWithdrawE18(address asset, address owner) external view returns (uint256) {
        uint256 collateralAmountEDecimal = vault.collateralsEDecimal(asset, owner);
        uint256 collateralPriceE18 = suOracle.getUsdPrice1e18(asset);
        if (collateralPriceE18 == 0) return 0;
        uint256 debtE18 = vault.debtsE18(asset, owner);

        uint256 minCollateralAmountEDecimal = debtE18 * (10 ** IERC20Metadata(asset).decimals()) * 1e18 / (collateralPriceE18 * managerParameters.initialCollateralRatioE18(asset));

        return collateralAmountEDecimal - minCollateralAmountEDecimal;
    }
}

