contracts/lending/SuCollateralRegistry.s.content:
// SPDX-License-Identifier: BSL 1.1

/*
  Copyright 2020 Unit Protocol: Artem Zakharov (az@unit.xyz).
*/
pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;


import "./SuVaultParameters.sol";
import "../access-control/SuAccessControlSingleton.sol";

// [deprecated]
// contacts implements simple a enumeredSet of asset and used by CdpRegistry only
// can be change to https://docs.openzeppelin.com/contracts/4.x/api/utils#EnumerableSet
contract SuCollateralRegistry is SuAuthenticated {

    event CollateralAdded(address indexed asset);
    event CollateralRemoved(address indexed asset);

    // mapping of collateral to its index in collateralList
    // it allowed to find collateral by address in the list without loop
    mapping(address => uint) public collateralId;

    address[] public collateralList;

    // collateral registry is deployed after vaultParameters contract and it initializes list of collaterals
    function initialize(address _authControl, address[] memory assets) public initializer {
        __SuAuthenticated_init(_authControl);

        for (uint i = 0; i < assets.length; i++) {
            require(!isCollateral(assets[i]), "SU Protocol: ALREADY_EXIST");
            collateralList.push(assets[i]);
            collateralId[assets[i]] = i;
            emit CollateralAdded(assets[i]);
        }
    }

    // after deployment more collaterals can be added by managers
    function addCollateral(address asset) public onlyOwner {
        require(asset != address(0), "SU Protocol: ZERO_ADDRESS");

        require(!isCollateral(asset), "SU Protocol: ALREADY_EXIST");

        collateralId[asset] = collateralList.length;
        collateralList.push(asset);

        emit CollateralAdded(asset);
    }

    // as well collaterals can be removed
    function removeCollateral(address asset) public onlyOwner {
        require(asset != address(0), "SU Protocol: ZERO_ADDRESS");

        require(isCollateral(asset), "SU Protocol: DOES_NOT_EXIST");

        uint id = collateralId[asset];

        delete collateralId[asset];

        uint lastId = collateralList.length - 1;

        if (id != lastId) {
            address lastCollateral = collateralList[lastId];
            collateralList[id] = lastCollateral;
            collateralId[lastCollateral] = id;
        }

        collateralList.pop();

        emit CollateralRemoved(asset);
    }

    // check whether asset is collateral
    function isCollateral(address asset) public view returns(bool) {
        if (collateralList.length == 0) { return false; }
        return collateralId[asset] != 0 || collateralList[0] == asset;
    }

    // return all collateral assets
    function collaterals() external view returns (address[] memory) {
        return collateralList;
    }

    // return total number of collateral assets
    function collateralsCount() external view returns (uint) {
        return collateralList.length;
    }
}

contracts/mocks/MockV3Aggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

// FluxAggregator
// AggregatorV2V3Interface
contract MockV3Aggregator is AggregatorV3Interface {
  uint256 override constant public version = 0;

  uint8 override public decimals;
  int256 public latestAnswer;
  uint256 public latestTimestamp;
  uint256 public latestRound;

  mapping(uint256 => int256) public getAnswer;
  mapping(uint256 => uint256) public getTimestamp;
  mapping(uint256 => uint256) private getStartedAt;

  constructor(
    uint8 _decimals,
    int256 _initialAnswer
  ) {
    decimals = _decimals;
    updateAnswer(_initialAnswer);
  }

  function updateAnswer(
    int256 _answer
  ) public {
    latestAnswer = _answer;
    latestTimestamp = block.timestamp;
    latestRound++;
    getAnswer[latestRound] = _answer;
    getTimestamp[latestRound] = block.timestamp;
    getStartedAt[latestRound] = block.timestamp;
  }

  function updateRoundData(
    uint80 _roundId,
    int256 _answer,
    uint256 _timestamp,
    uint256 _startedAt
  ) public {
    latestRound = _roundId;
    latestAnswer = _answer;
    latestTimestamp = _timestamp;
    getAnswer[latestRound] = _answer;
    getTimestamp[latestRound] = _timestamp;
    getStartedAt[latestRound] = _startedAt;
  }

  function getRoundData(uint80 _roundId)
    external
    view
    override
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    )
  {
    return (
      _roundId,
      getAnswer[_roundId],
      getStartedAt[_roundId],
      getTimestamp[_roundId],
      _roundId
    );
  }

  function latestRoundData()
    external
    view
    override
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    )
  {
    return (
      uint80(latestRound),
      getAnswer[latestRound],
      getStartedAt[latestRound],
      getTimestamp[latestRound],
      uint80(latestRound)
    );
  }

  function description()
    external
    pure
    override
    returns (string memory)
  {
    return "";
  }  
}
