pragma solidity ^0.8.0;

import "./ISuOracle.sol";

interface ISuOracleAggregator is ISuOracle {
    /* ===================== STRUCT ===================== */
    struct AssetLimitation {
        uint256 lowerBound;
        uint256 upperBound;
    }

    /* ===================== EVENTS ===================== */
    event OracleLowerBoundWarning(address indexed asset, uint256 price, uint256 lowerBound);
    event OracleUpperBoundWarning(address indexed asset, uint256 price, uint256 upperBound);

    event OracleLowerBoundUpdated(address asset, uint256 newBound);
    event OracleUpperBoundUpdated(address asset, uint256 newBound);

    /* ===================== ERRORS ===================== */
    error NoOracleFound(address asset);
    error NoOracleImplementation();
    error BadOracleId();
    error PriceBelowLowerBound(uint256 price);
    error PriceAboveUpperBound(uint256 price);

    /* ====================== VARS ====================== */
    function assetToOracle (address asset) external view returns ( uint256 );
    function oracleImplementations (uint256 oracleId) external view returns ( ISuOracle );

    /* ==================== METHODS ==================== */

    /**
    * @notice Like getFiatPrice1e18 from ISuOracle, but also checks bounds and emit event if there is an error.
    * We should call it in non-view method where we need safePrice from Oracle.
    * @param asset - address of asset
    **/
    function getSafeFiatPrice1e18(address asset) external returns (uint256);

    /**
    * @notice Change lower bound of asset
    * @param asset - address of asset
    * @param newLowerBound - new value of lower bound
    **/
    function setLowerBound(address asset, uint256 newLowerBound) external;

    /**
    * @notice Change upper bound of asset
    * @param asset - address of asset
    * @param newUpperBound - new value of upper bound
    **/
    function setUpperBound(address asset, uint256 newUpperBound) external;

    /**
       * @notice assign address of oracle implementation to the oracleId
    * @param oracleId - number 0,1, etc to assign the oracle
    * @param oracleImplementation - an address with ISuOracle implementation contract
    **/
    function setOracleImplementation(uint256 oracleId, ISuOracle oracleImplementation) external;

    /**
    * @notice specify what oracleId should be used for each assets. Checks that oracleId has an implementation
    **/
    function setOracleIdForAssets(address[] memory assets, uint256 oracleId) external;

    /* ==================== VIEW METHODS ==================== */
    /**
    * @return true if oracle is set to this asset, false otherwise
    **/
    function hasPriceForAsset(address asset) external view returns(bool);
}
