pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../access-control/SuAuthenticated.sol";
import "../interfaces/adapters/IAuraAdapter.sol";
import "../vendor/aura/IAuraLPToken.sol";
import "../interfaces/adapters/IBalancerAdapter.sol";

/**
 * @title AuraAdapterWeighted
 * @notice Adapter for Aura LP token based on Balancer Weighted LP token.
 * @dev See ILPAdapter interface for full details.
 */
contract AuraAdapterWeighted is SuAuthenticated, IAuraAdapter {
    using SafeERC20 for IERC20;

    mapping(address => address) public lps; // Mapping from LP token to LP info
    IBalancerAdapter public ADAPTER; // Instance of Balancer Adapter to interact with Balancer pools.

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _authControl, address _balancerAdapter) public initializer {
        __suAuthenticatedInit(_authControl);
        ADAPTER = IBalancerAdapter(_balancerAdapter);
    }

    function registerLP(address lp) external onlyAdmin {
        address lpToken = IAuraLPToken(lp).asset();
        if (address(lpToken) == address(0)) revert IsNotLP(lp);
        if (lps[lp] != address(0)) revert AlreadyRegistered(lp);
        if (IERC20Metadata(lp).decimals() != 18) revert BadDecimals(lp);

        // To withdraw we need to approve Balancer Adapter to use our lp tokens
        IERC20(lpToken).approve(address(ADAPTER), type(uint).max);

        lps[lp] = lpToken;

        emit AuraLPRegistered(lp);
    }

    function revokeApprove(address lp) external onlyAdmin {
        uint256 allowance = IERC20(lp).allowance(address(this), address(ADAPTER));

        if (allowance > 0) {
            // We don't need forceApprove because we set allowance to 0
            IERC20(lp).safeApprove(address(ADAPTER), 0);
        }

        emit ApprovalRevoked(lp);
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return lps[asset] != address(0);
    }

    // @dev Aura Vault share price is the amount of assets you can get for that share multiplied by price of an asset
    function getFiatPrice1e18(address asset) external view returns (uint256) {
        address underlying = lps[asset];
        if (underlying == address(0)) revert IsNotLP(asset);

        uint8 auraShareDecimals = IERC20Metadata(asset).decimals();
        // In default balancerLpDecimals should be = 18
        uint8 balancerLpDecimals = IERC20Metadata(underlying).decimals();
        uint256 oneShare = 10 ** auraShareDecimals;

        // Get assets per share in asset's decimals
        uint256 assetsPerShare = IAuraLPToken(asset).convertToAssets(oneShare);
        // Get the price of underlying token (in 1e18)
        uint256 underlyingPrice = ADAPTER.getFiatPrice1e18(underlying);

        return (assetsPerShare * underlyingPrice) / (10 ** balancerLpDecimals);
    }

    function withdraw(
        address asset,
        uint256 amount,
        uint256 maxSlippageBps
    ) public returns (WithdrawResult[] memory results) {
        address lpToken = lps[asset];
        if (lpToken == address(0)) revert IsNotLP(asset);
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);

        uint256 receivedLpTokens = IAuraLPToken(asset).redeem(
            amount,
            address(this),
            address(this)
        );

        results = ADAPTER.withdrawTo(lpToken, receivedLpTokens, maxSlippageBps, msg.sender);
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for(uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}
