pragma solidity ^0.8.0;

import "./ILPAdapter.sol";

/**
 * @title IAuraAdapter
 * @notice Interface for AuraAdapterWeighted contract.
 */
interface IAuraAdapter is ILPAdapter {
    /* ========================= ERRORS ========================= */
    error WithdrawalFailed();
    error AlreadyRegistered(address asset);

    /* ========================= EVENTS ========================= */
    event AuraLPRegistered(address lp);
    event ApprovalRevoked(address lp);

    /* ==================== MUTABLE METHODS ==================== */
    /**
     * @notice Register the given LP token address and set the LP info.
     * @dev The function traverses to depth = 2, meaning if LP token {lp} contains {lp1} and {erc20Token1}
     *      as underlying tokens, where lp1 contains {erc20Token0} and {erc20Token2},
     *      then we register that {lp} has {erc20Token0}, {erc20Token2}, and {erc20Token1} as underlying tokens.
     * @param lp LP token to find and set the corresponding info.
     */
    function registerLP(address lp) external;

    /**
     * @notice Revoke the approval granted to Balancer Adapter for an LP token.
     * @param lp LP token address for which approval should be revoked.
     */
    function revokeApprove(address lp) external;
}","keccak256":"0xcd4cd6c3c82f40b118df343d316df4b40a638f806f4b872271b83498a9235adf","license":"BSL 1.1"},"contracts/exchange/contracts/periphery/contracts/interfaces/adapters/IBalancerAdapter.sol":{"content":"// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "./ILPAdapter.sol";
// if import "@balancer-labs/v2-interfaces/contracts/vault/IVault.sol" then we have a problem of
// re-declaration of IERC20, because balancer implement their own IERC20 (don't know why)
import "../../vendor/balancer/IVault.sol";

/**
 * @title IBalancerAdapter
 * @notice Interface for BalancerAdapterWeighted contract.
 */
interface IBalancerAdapter is ILPAdapter {
    struct LPInfoAdapter {
        bytes32 poolId;
        IVault vault;
        IERC20[] underlyingTokens;
    }

    /* ========================= ERRORS========================= */
    error NativeETHNotSupported();
    error SlippageExceeded(uint256 expectedValueUSD, uint256 receivedValueUSD);
    error InvalidSlippage();
    error NoPriceAfterRegister();
    error AlreadyRegistered(address);

    /* ========================= EVENTS ========================= */

    event UnderlyingTokensUpdated();
    event TokenRegistered(address);
    event Withdraw(address, uint256, address);

    /* ====================== VIEW METHODS ===================== */

    // solhint-disable max-line-length
    /**
     * @notice Verifies if stored underlying tokens match current pool tokens
     * @dev In the Balancer adapter, we store list of underlying tokens at lps[lp].underlyingTokens,
     *      however don't hardcode tokens for each pool, because weighted pools can be managed
     *      and their tokens may change over time, so hardcoding can prevent proper withdrawals and price calculations.
     *      Managed pool source code: https://github.com/balancer/balancer-v2-monorepo/blob/master/pkg/pool-weighted/contracts/managed/ManagedPoolSettings.sol#L608
     *      Example managed pool: https://polygonscan.com/address/0xC22bb237A5B8b7260190cb9e4998A9901a68af6f#code
     * @param lp Address of the LP token to check
     * @return bool Returns:
     *         - false if LP is not registered in adapter
     *         - false if stored and current token arrays have different lengths
     *         - false if any token addresses don't match at the same index
     *         - true if all tokens match in the same order
     */
    // solhint-enable max-line-length
    function checkUnderlyingTokens(address lp) external view returns (bool);

    /* ==================== MUTABLE METHODS ==================== */

    /**
      * @notice Register the given LP token address and set the LP info. We go to the depth = 2, that means, that if
      * LP token {lp} contains {lp1} and {erc20Token1} as underlying tokens,
      * where lp1 contains {erc20Token0} and {erc20Token2},
      * then we will register that {lp} has {erc20Token0}, {erc20Token2} and {erc20Token1} as underlying tokens.
      * @param lp LP token to find and set the corresponding info.
     **/
    function registerLP(address lp) external;

    /**
      * @notice Like common withdraw with slippage protection, but we can change receiver.
      * @dev Only supports ERC20 token withdrawals. Native ETH withdrawals are not supported.
      *      Use WETH for ETH-based pools.
      * @param asset - address of LP token to withdraw/unwrap
      * @param amount - amount of asset to withdraw/unwrap
      * @param maxSlippageBps Maximum allowed slippage in basis points (1 = 0.01%, 10000 = 100%)
      * @param receiver - receiver of underlying tokens
     **/
    function withdrawTo(
        address asset,
        uint256 amount,
        uint256 maxSlippageBps,
        address receiver
    ) external returns (WithdrawResult[] memory);

    /**
     * @notice Updates the stored underlying tokens at lps[lp].underlyingTokens
     * @param lp Address of the LP token to update
     */
    function updateUnderlyingTokens(address lp) external;
}","keccak256":"0xb122a0c1f9ca391226004f80690c030643bcbe4a51bf5b5fe06af9c600811e4e","license":"BSL 1.1"},"contracts/exchange/contracts/periphery/contracts/interfaces/adapters/ILPAdapter.sol":{"content":"// SPDX-License-Identifier: BSL 1.1
