pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../../vendor/balancer/WeightedPoolUserData.sol";
import "../../access-control/SuAuthenticated.sol";
import "../../interfaces/adapters/IBalancerAdapter.sol";
import "./BalancerOracleUtils.sol";

/**
 * @title BalancerAdapterWeighted
 * @notice Adapter for Balancer Weighted LP token.
 * @dev See IBalancerAdapter and ILPAdapter interface for full details.
 *      IMPORTANT: This adapter intentionally does not support native ETH withdrawals.
 *      Only ERC20 token withdrawals are supported. For ETH-based pools, use the wrapped version (WETH).
 */
contract BalancerAdapterWeighted is SuAuthenticated, BalancerOracleUtils, IBalancerAdapter {
    using SafeERC20 for IERC20;

    uint256 internal constant MAX_BPS = 10000; // 100% in basis points
    mapping(address => LPInfoAdapter) public lps; // Mapping from LP token to pool
    ISuOracle public ORACLE; // Oracle for fetching price information of underlying tokens.
    using WeightedPoolUserData for bytes;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _authControl, address _oracle) public initializer {
        __suAuthenticatedInit(_authControl);
        ORACLE = ISuOracle(_oracle);
    }

    function registerLP(address lp) external onlyAdmin {
        IVault vault = IWeightedPool(lp).getVault();
        if (address(vault) == address(0)) revert IsNotLP(lp);
        if (address(lps[lp].vault) != address(0)) revert AlreadyRegistered(lp);
        if (IERC20Metadata(lp).decimals() != 18) revert BadDecimals(lp);

        bytes32 poolId = IWeightedPool(lp).getPoolId();

        // Retrieve underlying tokens for the given pool ID.
        (IERC20[] memory tokens,,) = vault.getPoolTokens(poolId);

        // We don't work with native ETH, and Balancer pools only contain ERC20 tokens (WETH instead of native ETH).
        // Vault's getPoolTokens() returns actual ERC20 addresses - native ETH (sentinel 0x00) is never stored in pools,
        // only used as input/output flags in swap/join/exit operations.
        // No need for native ETH checks since pool composition is strictly ERC20:
        // https://github.com/balancer/docs-developers/blob/main/helpers/using-native-eth.md

        // Store pool details in the lps mapping.
        lps[lp].poolId = poolId;
        lps[lp].vault = vault;
        lps[lp].underlyingTokens = tokens;

        // make sure getFiatPrice1e18 does not revert and not return 0
        if(getFiatPrice1e18(lp) == 0) revert NoPriceAfterRegister();

        emit TokenRegistered(lp);
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return address(lps[asset].vault) != address(0);
    }

    function checkUnderlyingTokens(address lp) public view returns (bool) {
        if (!isAdapterLP(lp)) return false;

        LPInfoAdapter memory lpInfo = lps[lp];

        (IERC20[] memory currentTokens,,) = lpInfo.vault.getPoolTokens(lpInfo.poolId);

        if (currentTokens.length != lpInfo.underlyingTokens.length) return false;

        uint256 len = currentTokens.length;
        for (uint256 i = 0; i < len; i++) {
            if (address(currentTokens[i]) != address(lpInfo.underlyingTokens[i])) {
                return false;
            }
        }

        return true;
    }

    function updateUnderlyingTokens(address lp) public onlyAdmin {
        if (!isAdapterLP(lp)) revert IsNotLP(lp);

        LPInfoAdapter storage lpInfo = lps[lp];

        (IERC20[] memory currentTokens,,) = lpInfo.vault.getPoolTokens(lpInfo.poolId);

        lpInfo.underlyingTokens = currentTokens;

        emit UnderlyingTokensUpdated();
    }

    function getFiatPrice1e18(address asset) public view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        LPInfoAdapter memory lpInfo = lps[asset];

        // getTimeWeightedAverage is deprecated, so for any weighted LP token we should use this approximation
        return _getFiatPriceWeightedComplex(asset, lpInfo.poolId, lpInfo.vault, lpInfo.underlyingTokens, ORACLE);
    }

    /**
     * @notice Internal function to handle the withdrawal logic for LP tokens.
     * @dev This function interacts with Balancer's Vault to perform token withdrawals.
     *      IMPORTANT: This function intentionally does not support native ETH withdrawals.
     *      When working with pools containing ETH, use the wrapped version (WETH).
     *      This is a design decision to maintain consistent ERC20-based processing across all adapters.
     *      This check is in first step - registerLP() function
     * @param asset Address of the LP token to withdraw.
     * @param amount Amount of the LP token to withdraw.
     * @return results Array of `WithdrawResult` containing details of withdrawn tokens.
     */
    function _withdrawInternal(address asset, uint256 amount) internal returns (WithdrawResult[] memory results) {
        LPInfoAdapter memory lpInfo = lps[asset];
        VaultReentrancyLib.ensureNotInVaultContext(lpInfo.vault);
        uint256 nTokens = lpInfo.underlyingTokens.length;
        address vaultAddress = address(lpInfo.vault);
        if (
            asset != vaultAddress &&
            IERC20(asset).allowance(address(this), vaultAddress) < amount
        ) {
            IERC20(asset).approve(vaultAddress, amount);
        }

        uint256[] memory balancesBefore = new uint256[](nTokens);
        for (uint256 i = 0; i < nTokens; i++) {
            balancesBefore[i] = lpInfo.underlyingTokens[i].balanceOf(address(this));
        }

        IAsset[] memory assets = new IAsset[](nTokens);
        uint256[] memory minAmountsOut = new uint[](nTokens);
        for (uint256 i = 0; i < nTokens; i++) {
            assets[i] = IAsset(address(lpInfo.underlyingTokens[i]));
            minAmountsOut[i] = 0;
        }
        lpInfo.vault.exitPool(
            lpInfo.poolId,
            payable(address(this)),
            payable(address(this)),
            IVault.ExitPoolRequest({
                assets: assets,
                minAmountsOut: minAmountsOut,
                userData: abi.encode(WeightedPoolUserData.ExitKind.EXACT_BPT_IN_FOR_TOKENS_OUT, amount),
                toInternalBalance: false
            })
        );

        // For some LPs vault returns the first address in underlyingTokens as the LP token address
        if (address(lpInfo.underlyingTokens[0]) == asset) {
            results = new WithdrawResult[](nTokens - 1);
            for (uint256 i = 1; i < nTokens; i++) {
                uint256 underlineTokenAmount =
                    lpInfo.underlyingTokens[i].balanceOf(address(this)) - balancesBefore[i];
                results[i - 1] = WithdrawResult({
                    token: address(lpInfo.underlyingTokens[i]),
                    amount: underlineTokenAmount
                });
            }
        } else {
            results = new WithdrawResult[](nTokens);
            for (uint256 i = 0; i < nTokens; i++) {
                uint256 underlineTokenAmount =
                    lpInfo.underlyingTokens[i].balanceOf(address(this)) - balancesBefore[i];
                results[i] = WithdrawResult({
                    token: address(lpInfo.underlyingTokens[i]),
                    amount: underlineTokenAmount
                });
            }
        }
    }

    function withdrawTo(
        address asset, uint256 amount, uint256 maxSlippageBps, address receiver
    ) public returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        if (maxSlippageBps >= MAX_BPS) revert InvalidSlippage();
        // Calculate expected value of LP tokens being withdrawn and minimum acceptable value based on slippage
        uint256 lpTokenPrice = getFiatPrice1e18(asset);
        uint256 expectedValueUSD = (amount * lpTokenPrice) / 1e18;
        uint256 minValueUSD = (expectedValueUSD * (MAX_BPS - maxSlippageBps)) / MAX_BPS;

        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);

        WithdrawResult[] memory resultsExt = _withdrawInternal(asset, amount);

        uint256 receivedValueUSD;
        uint256 len = resultsExt.length;
        results = new WithdrawResult[](len);
        uint256 resultsIndex = 0;
        for (uint256 i = 0; i < len; i++) {
            address underlyingToken = resultsExt[i].token;
            if (underlyingToken != address(0)) {
                uint256 tokenAmount = resultsExt[i].amount;

                // Calculate USD value of received token
                uint256 tokenPrice = ORACLE.getFiatPrice1e18(underlyingToken);
                receivedValueUSD += (tokenAmount * tokenPrice) / 1e18;

                // Store result and transfer token
                IERC20(underlyingToken).safeTransfer(receiver, tokenAmount);

                results[resultsIndex++] = resultsExt[i];
            }
        }

        // Check slippage
        if (receivedValueUSD < minValueUSD) {
            revert SlippageExceeded(expectedValueUSD, receivedValueUSD);
        }

        emit Withdraw(asset, amount, receiver);
    }

    function withdraw(
        address asset,
        uint256 amount,
        uint256 maxSlippageBps
    ) external returns (WithdrawResult[] memory results) {
        return withdrawTo(asset, amount, maxSlippageBps, msg.sender);
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for(uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}
