pragma solidity ^0.8.0;

import "@prb/math/contracts/PRBMathSD59x18.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import "../../interfaces/adapters/IBalancerOracleUtils.sol";
import "../../interfaces/adapters/IWeightedPool.sol";
// import "@balancer-labs/v2-pool-utils/contracts/lib/VaultReentrancyLib.sol";
// We use custom copy of VaultReentrancyLib because Balancer need IVault that use his ERC20 implementation,
// so we need to copy IVault and VaultReentrancyLib
import "../../vendor/balancer/VaultReentrancyLib.sol";
import {ISuOracle} from "../../interfaces/oracle/ISuOracle.sol";

/**
 * @title BalancerOracle
 */
abstract contract BalancerOracleUtils is IBalancerOracleUtils {
    using PRBMathSD59x18 for *;

    function _getPoolInvariant(address asset) internal view returns (uint256) {
        // Try both old and new methods to get the invariant
        try IWeightedPool(asset).getLastPostJoinExitInvariant() returns (uint256 newInvariant) {
            return newInvariant;
        } catch {
            try IWeightedPool(asset).getLastInvariant() returns (uint256 oldInvariant) {
                return oldInvariant;
            } catch {
                revert PoolInvariantError();
            }
        }
    }

    /**
     * @notice To determine the supply there are several functions to get it:
     *         - getActualSupply: is used by the most recent versions of Weighted and Stable Pools.
     *         It accounts for pre-minted BPT as well as due protocol fees.
     *         - totalSupply: is used to be called only by older legacy pools since those doesn’t have pre-minted BPT.
     */
    function _getTotalPoolSupply(address pool) internal view returns (uint256) {
        try IWeightedPool(address(pool)).getActualSupply() returns (uint256 totalSupply) {
            return totalSupply;
        } catch {
            try IWeightedPool(pool).totalSupply() returns (uint256 oldTotalSupply) {
                return oldTotalSupply;
            } catch {
                revert TotalSupplyError();
            }
        }
    }

    // solhint-disable max-line-length
    /**
     * @notice Internal function to calculate the fiat price of a weighted LP token.
     * @dev Uses the Oracle to fetch prices of underlying tokens and computes the weighted price,
            assumes all tokens have <= 18 decimals. Base on:
            [Revest Finance Blog](https://revestfinance.medium.com/dev-blog-on-the-derivation-of-a-safe-price-formula-for-balancer-pool-tokens-33e8993455d0)
     * @dev Balancer has a known read-only reentrancy, which can lead to price manipulation attacks:
            - Oracle with the same implementation was hacked using this bug: [Sentiment Blog](https://hackmd.io/@ sentimentxyz/SJCySo1z2)
            - This bug is kinda old, most of the vulnerable pools were redeployed or has low TVL now. But it is still
            recommended to have reentrancy checks.
            - Calling ensureNotInVaultContext(vault) will perform a noop call to the Vault that will fail if an attacker
            is attempting a read-only reentrancy attack.
     * @return The fiat price of the LP token scaled to 1e18.
     */
    // solhint-enable max-line-length
    function _getFiatPriceWeightedComplex(
        address asset,
        bytes32 poolId,
        IVault vault,
        IERC20[] memory underlyingTokens,
        ISuOracle oracle
    ) internal view returns (uint256) {
        VaultReentrancyLib.ensureNotInVaultContext(vault);

        (IERC20[] memory tokens, uint256[] memory balances, ) = vault.getPoolTokens(poolId);
        uint256[] memory weights = IWeightedPool(asset).getNormalizedWeights();

        int256 totalPi = PRBMathSD59x18.fromInt(1e18);
        uint256[] memory prices = new uint256[](tokens.length);

        uint256 len = tokens.length;
        for (uint256 i = 0; i < len; i++) {
            if (weights[i] == 0) revert ZeroWeight();

            address underlyingTokenAddress = address(underlyingTokens[i]);
            uint8 decimals = IERC20Metadata(underlyingTokenAddress).decimals();
            if (decimals > 18) revert DecimalsTooHigh();
            uint256 decimalAdjustment = 10**(18 - decimals);

            balances[i] = balances[i] * decimalAdjustment;
            prices[i] = oracle.getFiatPrice1e18(underlyingTokenAddress) / decimalAdjustment;

            int256 val = int256(prices[i]).div(int256(weights[i]));
            int256 indivPi = val.pow(int256(weights[i]));

            totalPi = totalPi.mul(indivPi);
        }

        int256 invariant = int256(_getPoolInvariant(asset));
        int256 numerator = totalPi.mul(invariant);
        int256 totalSupply = int256(_getTotalPoolSupply(asset));

        return uint256(numerator.toInt().div(totalSupply));
    }
}
