pragma solidity ^0.8.0;

import "../../../interfaces/IRewardChefV2.sol";
import "../../../exchange/contracts/periphery/contracts/access-control/SuAuthenticatedNonUpgradeable.sol";
import "../../interfaces/ICoin.sol";
import "../../interfaces/ICoinJoin.sol";
import "../../interfaces/ICollateralJoin.sol";
import "../../interfaces/ISafeEngine.sol";
import {ProxyOwner} from "../../utils/ProxyOwner.sol";

// CHANGED: in dss all joins contracts are in join.sol. We split and rename them to read this better.
// In CoinJoin (DaiJoin) we added events and integration with rewardChef during join and exit.
// Added SuAuthenticated instead of authorizedAccounts.

contract CoinJoin is ICoinJoin, SuAuthenticatedNonUpgradeable, ProxyOwner {
    address public rewardChef;
    ISafeEngine public safeEngine;
    // Coin created by the system; this is the external, ERC-20 representation, not the internal 'coinBalance'
    ICoin public systemCoin;
    mapping (bytes32 => address) public collateralAddress; // CollateralType => address of collateral

    constructor(address accessControlSingleton, address safeEngine_, address systemCoin_, address rewardChef_)
        SuAuthenticatedNonUpgradeable(accessControlSingleton) public {
        contractEnabled = 1;
        safeEngine = ISafeEngine(safeEngine_);
        systemCoin = ICoin(systemCoin_);
        rewardChef = rewardChef_;
    }

    uint public contractEnabled;
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0;
        emit DisableContract();
    }

    uint constant ONE = 10 ** 27;
    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x);
    }

    function updateRewardChef(address rewardChef_) external onlyDAO {
        rewardChef = rewardChef_;
        emit UpdateRewardChef();
    }

    function initializeCollateralType(bytes32 _collateralType, address _collateralAddress) external onlyAdmin {
        collateralAddress[_collateralType] = _collateralAddress;
    }

    function join(bytes32 collateralType, address user, address cdp, uint wad) external onlyProxyOwner(user) {
        safeEngine.transferInternalCoins(address(this), cdp, mul(ONE, wad));
        systemCoin.burn(msg.sender, wad);

        address colAddr = collateralAddress[collateralType];
        (bool success,) = rewardChef.call(
            abi.encodeWithSignature("decreaseAmount(address,address,uint256)", colAddr, user, wad)
        );
        if (!success) {
            emit DecreaseAmountFailed(colAddr, user, wad);
        }

        emit Join(cdp, wad);
    }
    function joinNoReward(address cdp, uint wad) external {
        safeEngine.transferInternalCoins(address(this), cdp, mul(ONE, wad));
        systemCoin.burn(msg.sender, wad);
        emit JoinNoReward(cdp, wad);
    }

    function exit(
        bytes32 collateralType, address user, uint256 lockupPeriodSeconds, address cdp, uint wad
    ) external onlyProxyOwner(user) {
        require(contractEnabled == 1, "CoinJoin/not-contractEnabled");
        safeEngine.transferInternalCoins(msg.sender, address(this), mul(ONE, wad));
        systemCoin.mint(cdp, wad);

        address colAddr = collateralAddress[collateralType];
        uint256 userLockupPeriod = IRewardChefV2(rewardChef).getUserLockupPeriodsSeconds(colAddr, user);
        if (userLockupPeriod != 0 && userLockupPeriod != lockupPeriodSeconds) revert BadLockupPeriod();
        (bool success,) = rewardChef.call(
            abi.encodeWithSignature(
                "increaseAmount(address,address,uint256,uint256)", colAddr, user, wad, lockupPeriodSeconds
            )
        );
        if (!success) {
            emit IncreaseAmountFailed(colAddr, user, wad, 1);
        }

        emit Exit(cdp, wad);
    }

    function exitNoReward(address cdp, uint wad) external {
        require(contractEnabled == 1, "CoinJoin/not-contractEnabled");
        safeEngine.transferInternalCoins(msg.sender, address(this), mul(ONE, wad));
        systemCoin.mint(cdp, wad);
        emit ExitNoReward(cdp, wad);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return interfaceId == type(ICoinJoin).interfaceId || interfaceId == type(IERC165).interfaceId || ACCESS_CONTROL_SINGLETON.supportsInterface(interfaceId);
    }
}

