pragma solidity ^0.8.0;

import {IDisable} from "./IDisable.sol";
import {ICoin} from "./ICoin.sol";

/**
 * @title ITaxCollector
 **/
interface ICoinJoin is IDisable {
    /* ==================== EVENTS ==================== */
    event Join(address indexed usr, uint256 wad);
    event JoinNoReward(address indexed usr, uint256 wad);
    event Exit(address indexed usr, uint256 wad);
    event ExitNoReward(address indexed usr, uint256 wad);
    event UpdateRewardChef();
    event DecreaseAmountFailed(address indexed collateralType, address indexed user, uint amount);
    event IncreaseAmountFailed(
        address indexed collateralType,
        address indexed user,
        uint amount,
        uint256 lockupPeriodSeconds
    );

    /* ===================== ERRORS ==================== */
    error BadLockupPeriod();

    /* ====================== VARS ===================== */
    function systemCoin() external returns (ICoin);

    /* ==================== METHODS ==================== */

    /**
    * @notice Join pegged-coins in the system
    * @dev Exited coins have 18 decimals but inside the system they have 45 (rad) decimals.
           When we join, the amount (wad) is multiplied by 10**27 (ray).
           Can only be called by authorized contracts.
    * @param collateralType Collateral that will be used for borrow
    * @param user Account that call borrow/repay function and will receive rewards in RewardChef
    * @param cdp Account that will receive the joined coins
    * @param wad Amount of external coins to join (18 decimal number)
    **/
    function join(bytes32 collateralType, address user, address cdp, uint wad) external;

    /**
    * @notice Simple version of join(). Don't have Rewards, it is useful for our smart-contracts.
    * @param cdp Account that will receive the joined coins
    * @param wad Amount of external coins to join (18 decimal number)
    **/
    function joinNoReward(address cdp, uint wad) external;

    /**
    * @notice Exit pegged-coins from the system and inside 'Coin.sol'
    * @dev Inside the system, coins have 45 (rad) decimals but outside they have 18 decimals (wad).
           When we exit, we specify a wad amount of coins and then the contract automatically multiplies
           wad by 10**27 to move the correct 45 decimal coin amount to this adapter.
           Can only be called by authorized contracts.
    * @param collateralType Collateral that will be used for repay
    * @param user Account that call borrow/repay function and will receive rewards in RewardChef
    * @param lockupPeriodSeconds Lockup period for rewardChef that calculate user rewards
    * @param cdp Account that will receive the exited coins
    * @param wad Amount of internal coins to join (18 decimal number that will be multiplied by ray)
    **/
    function exit(bytes32 collateralType, address user, uint256 lockupPeriodSeconds, address cdp, uint wad) external;

    /**
    * @notice Simple version of exit(). Don't have Rewards, it is useful for our smart-contracts.
    * @param cdp Account that will receive the joined coins
    * @param wad Amount of external coins to join (18 decimal number)
    **/
    function exitNoReward(address cdp, uint wad) external;

    /**
    * @notice We use rewardChef to increaseAmount/decreaseAmount during join and exit,
    * so admin should be able to change RewardChef contract's address.
    * @param rewardChef New address of RewardChef contract.
    **/
    function updateRewardChef(address rewardChef) external;

    /**
    * @notice We need to save mapping of collateralType => collateralAddress to work with RewardChef.
    * @param _collateralType Collateral type as bytes32, that we use in our protocol.
    * @param _collateralAddress Address of collateral
    **/
    function initializeCollateralType(bytes32 _collateralType, address _collateralAddress) external;
}
