abstract contract ProxyOwner {
    error InvalidProxyOwner();
    error InvalidProxy();

    // User through Proxy call exit function, so only owner can be the real user
    modifier onlyProxyOwner(address user) {
        // Check if msg.sender is contract
        if (msg.sender.code.length == 0) revert InvalidProxy();

        // Check if msg.sender's contract can get owner
        (bool success, bytes memory data) = msg.sender.call(abi.encodeWithSignature("owner()"));
        if (!success) revert InvalidProxy();
        address proxyOwner = abi.decode(data, (address));

        // Check Proxy owner is user
        if (proxyOwner != user) revert InvalidProxyOwner();
        _;
    }
