pragma solidity ^0.8.0;

import "../../interfaces/ICollateralJoin.sol";
import "../../interfaces/ISafeEngine.sol";
import "../../../exchange/contracts/periphery/contracts/access-control/SuAuthenticatedNonUpgradeable.sol";

// CHANGED: In CollateralJoin (GemJoin) I only added events.
// Added SuAuthenticated instead of authorizedAccounts

/// @dev This adapter assumes that the collateral has decimals < 18
// we use logic like in GemJoin5 from dss-gem-joins
contract CollateralJoinDecimals is ICollateralJoin, SuAuthenticatedNonUpgradeable {
    ISafeEngine public safeEngine;
    bytes32 public collateralType;
    IERC20Metadata public collateral;      // Actual collateral token contract
    uint public decimals;        // How many decimals the collateral token has

    constructor(address accessControlSingleton, address safeEngine_, bytes32 collateralType_, address collateral_)
        SuAuthenticatedNonUpgradeable(accessControlSingleton) public {
        contractEnabled = 1;
        safeEngine = ISafeEngine(safeEngine_);
        collateralType = collateralType_;
        collateral = IERC20Metadata(collateral_);
        decimals = collateral.decimals();
    }

    uint public contractEnabled;
    function disableContract() external onlyDisablerAccess {
        contractEnabled = 0;
        emit DisableContract();
    }

    function join(address usr, uint amt) external {
        require(contractEnabled == 1, "CollateralJoin/not-contractEnabled");

        uint wad = amt * (10 ** (18 - decimals));
        require(int(wad) >= 0, "CollateralJoin/overflow"); // ?

        safeEngine.modifyCollateralBalance(collateralType, usr, int(wad));
        require(collateral.transferFrom(msg.sender, address(this), amt), "CollateralJoin/failed-transfer");
        emit Join(usr, amt);
    }

    function exit(address usr, uint amt) external {
        uint wad = amt * (10 ** (18 - decimals));

        require(wad <= 2 ** 255, "CollateralJoin/overflow");
        safeEngine.modifyCollateralBalance(collateralType, msg.sender, -int(wad));
        require(collateral.transfer(usr, amt), "CollateralJoin/failed-transfer");
        emit Exit(usr, amt);
    }
}
