pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../../access-control/SuAuthenticated.sol";
import "../../interfaces/adapters/ICurveAdapter.sol";
import "../../vendor/curve/ICurveLP.sol";
import "./CurveReentrancyCheck.sol";

// solhint-disable max-line-length
/**
 * @title CurveAdapter
 * @notice Adapter for Curve LP token.
 * @dev This adapter does not support pools containing native ETH as an underlying token.
 *      Also adapter support LP tokens with <= 4 underlying tokens
 *      (it seems that Curve don't have Stable and V2 pools with more amount of underlying tokens).
 *      See ILPAdapter interface for full details.
 *      We copied some logic from Silo CurveNPAPTokensPriceProvider for V2 and CurvePAPTokensPriceProvider for Stable:
 *      https://github.com/silo-finance/silo-core-v1/blob/master/contracts/priceProviders/curveLPTokens/nonPeggedAssetsPools/CurveNPAPTokensPriceProviderETH.sol
 */
// solhint-enable max-line-length
contract CurveAdapter is SuAuthenticated, ICurveAdapter, CurveReentrancyCheck {
    using SafeERC20 for IERC20;

    mapping(address => CurveLPInfo) public lps; // Mapping from LP token to pool
    // Some pools don't have lp_price() function, so Curve suggest to use manual oracle for that
    // For such tokens we'll use this mapping
    mapping(address => address) public lpsWithoutLpPrice;
    ISuOracle public ORACLE;
    uint256 internal constant MAX_BPS = 10000; // 100% in basis points
    bytes4 constant REMOVE_LIQUIDITY_SELECTOR = bytes4(keccak256("remove_liquidity(uint256,uint256[])"));

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _authControl, address _oracle) public initializer {
        __suAuthenticatedInit(_authControl);
        ORACLE = ISuOracle(_oracle);
    }

    function registerLP(address lp, address lpPool, uint256 nTokens, CurvePoolType poolType) external onlyAdmin {
        if (nTokens > 4) revert UnsupportedTokenCount(nTokens);
        bool wasRegistered = lps[lp].pool != address(0);
        // If admin make mistake in this function, for example he pass wrong nTokens, poolType or lpPool address,
        // then he should be able to fix it by repeating register call with correct arguments.
        // if (wasRegistered) revert AlreadyRegistered(lp);

        // Store pool details in the lps mapping.
        if (IERC20Metadata(lp).decimals() != 18) revert BadDecimals(lp);
        lps[lp].pool = lpPool;
        lps[lp].poolType = poolType;
        lps[lp].underlyingTokens = new address[](nTokens);
        for (uint256 i = 0; i < nTokens; ++i) {
            address token = ICurveLP(lpPool).coins(i);
            if (token == address(0) || token == 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE) {
                revert NativeETHNotSupported();
            }
            lps[lp].underlyingTokens[i] = token;
        }

        if (wasRegistered) {
            emit TokenRegisteredRepeat(lp, lpPool, nTokens, poolType);
        } else {
            emit TokenRegistered(lp, lpPool, nTokens, poolType);
        }
    }

    function updateLpPriceProvider(address lp, address priceProvider) external onlyAdmin {
        if (!isAdapterLP(lp)) revert IsNotLP(lp);

        lpsWithoutLpPrice[lp] = priceProvider;

        emit PriceProviderUpdated(lp, priceProvider);
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return lps[asset].pool != address(0);
    }

    /// @inheritdoc ICurveReentrancyCheck
    function setReentrancyVerificationConfig(
        address _pool, uint128 _gasLimit, N_COINS _nCoins
    ) external virtual onlyAdmin {
        _setReentrancyVerificationConfig(_pool, _gasLimit, _nCoins);
    }

    /// @notice Get the minimum price among all underlying tokens in a Curve LP pool
    /// @param _lpToken Curve LP Token address
    /// @return minPrice Minimum price among pool coins, denominated in fiat with 18 decimals.
    function _lpTokenPoolCoinsMinPrice(address _lpToken) internal view returns (uint256 minPrice) {
        CurveLPInfo memory lpInfo = lps[_lpToken];
        uint256 length = lpInfo.underlyingTokens.length;

        minPrice = 0;
        for(uint256 i = 0; i < length; i++) {
            address underlyingToken = lpInfo.underlyingTokens[i];
            uint256 decimals = IERC20Metadata(underlyingToken).decimals();
            // We should unify all prices to find minimal
            // We don't need to check if it's 0, because it will be reverted in _getFiatPriceStablePool
            // If decimals != 18: price base = (36 - d) + d - 18 = 18, where d = decimals
            uint256 price = decimals == 18
                ? ORACLE.getFiatPrice1e18(underlyingToken)
                : ORACLE.getFiatPrice1e18(underlyingToken) * 10**decimals / 1e18;

            if (i == 0) {
                minPrice = price;
            } else {
                minPrice = price < minPrice ? price : minPrice;
            }
        }
    }

    /**
     * @notice Internal function to get the fiat price of a stable Curve LP token.
     * @dev Uses the virtual price of the pool to determine the fiat value.
     * @param asset Address of the LP token.
     * @return price The fiat price of the LP token scaled to 1e18.
     */
    function _getFiatPriceStablePool(address asset) internal view returns (uint256 price) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        CurveLPInfo memory lpInfo = lps[asset];

        uint256 minPrice = _lpTokenPoolCoinsMinPrice(asset);
        uint256 virtualPrice = ICurveLP(lpInfo.pool).get_virtual_price();

        // `_lpToken` price calculation
        // Expect a `virtualPrice` to be a value close to 10 ** 18.
        // So, to have an overflow here a `minPrice` value must be approximately > 10 ** 59.
        // About the price calculation algorithm:
        // https://news.curve.fi/chainlink-oracles-and-curve-pools/
        // As result in base we'll have 18 + 18 - 18 = 18
        price = minPrice * virtualPrice / 1e18;
        if (price == 0) revert ZeroPrice(); // Zero price is unacceptable
    }

    /**
     * @notice Internal function to get the fiat price of a Curve V2 pool LP token.
     * @dev Uses the `lp_price` function of the pool to determine the fiat value.
     * @param asset Address of the LP token.
     * @return price The fiat price of the LP token scaled to 1e18.
     */
    function _getFiatPricePoolV2(address asset) internal view returns (uint256 price) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        CurveLPInfo memory lpInfo = lps[asset];
        address provider = lpsWithoutLpPrice[asset] == address(0) ? lpInfo.pool : lpsWithoutLpPrice[asset];

        // lp_price() function returns the price in the local quote token (indexed at 0), not in USD.
        // To calculate the price in fiat, the oracle must multiply the returned value by the token price.
        uint256 lpPrice = ICurveLP(provider).lp_price();
        address firstCoin = lps[asset].underlyingTokens[0];
        uint256 decimals = IERC20Metadata(firstCoin).decimals();
        uint256 coinPrice = ORACLE.getFiatPrice1e18(firstCoin); // in base of 10^(36-decimals)

        price = lpPrice * coinPrice / 1e18; // decimals here: 18 + 36 - d - 18 = 36 - d
        if (decimals != 18) {
            price = price * 10**decimals / 1e18; // if d != 18 => (36 - d) + d - 18 = 18
        }
        if (price == 0) revert ZeroPrice();
    }

    function getFiatPrice1e18(address asset) external view returns (uint256) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        CurveLPInfo memory lpInfo = lps[asset];

        if (isLocked(lpInfo.pool)) revert ReentrantLockError();

        if (lpInfo.poolType == CurvePoolType.Stable) {
            return _getFiatPriceStablePool(asset);
        }
        if (lpInfo.poolType == CurvePoolType.V2) {
            return _getFiatPricePoolV2(asset);
        }

        // If pool type is CurvePoolType.Default, unknown or not set, revert with an error.
        revert LPOracleNotReady();
    }

    function _safeRemoveLiquidity(address pool, uint256 amount, uint256 nTokens) internal {
        // If regular array version fails, try fixed array version with no return value
        if (nTokens == 2) {
            try ICurveLP(pool).remove_liquidity(amount, [uint256(0), 0]) {
                return;
            } catch {}
        } else if (nTokens == 3) {
            try ICurveLP(pool).remove_liquidity(amount, [uint256(0), 0, 0]) {
                return;
            } catch {}
        } else if (nTokens == 4) {
            try ICurveLP(pool).remove_liquidity(amount, [uint256(0), 0, 0, 0]) {
                return;
            } catch {}
        }

        // If we get here, try the dynamic array version with return value
        try ICurveLP(pool).remove_liquidity(amount, new uint256[](nTokens)) {
            return;
        } catch {
            revert RemoveLiquidityError();
        }
    }

    /**
     * @notice Withdraw LP tokens with slippage protection using basis points
     * @param asset The LP token address
     * @param amount The amount of LP tokens to withdraw
     * @param maxSlippageBps Maximum allowed slippage in basis points (1 = 0.01%)
     * @return results Array of withdrawal results
     */
    function withdraw(
        address asset,
        uint256 amount,
        uint256 maxSlippageBps
    ) public returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        if (maxSlippageBps >= MAX_BPS) revert InvalidSlippage();

        // Calculate expected value of LP tokens being withdrawn
        uint256 lpTokenPrice = this.getFiatPrice1e18(asset);
        uint256 expectedValueUSD = (amount * lpTokenPrice) / 1e18;
        // Calculate minimum acceptable value based on slippage
        uint256 minValueUSD = (expectedValueUSD * (MAX_BPS - maxSlippageBps)) / MAX_BPS;

        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);
        CurveLPInfo memory lpInfo = lps[asset];

        uint256 nTokens = lpInfo.underlyingTokens.length;
        results = new WithdrawResult[](nTokens);

        // Approve the Curve pool if necessary to enable withdrawal.
        if (asset != lpInfo.pool && IERC20(asset).allowance(address(this), lpInfo.pool) < amount) {
            IERC20(asset).approve(lpInfo.pool, amount);
        }

        // Record balances of underlying tokens before liquidity removal.
        uint256[] memory balancesBefore = new uint256[](nTokens);
        for (uint256 i = 0; i < nTokens; ++i) {
            // Due to the check in registerLP we are sure that there is no native ETH here
            balancesBefore[i] = IERC20(lpInfo.underlyingTokens[i]).balanceOf(address(this));
        }

        // Remove liquidity from the pool based on the number of tokens.
        _safeRemoveLiquidity(lpInfo.pool, amount, nTokens);

        // Calculate results, actual received amounts, and total USD value in one loop
        uint256 receivedValueUSD;
        for (uint256 i = 0; i < nTokens; ++i) {
            address underlyingToken = lpInfo.underlyingTokens[i];
            uint256 underlineTokenAmount = IERC20(underlyingToken).balanceOf(address(this)) - balancesBefore[i];
            results[i] = WithdrawResult({ token: underlyingToken, amount: underlineTokenAmount });

            // Calculate USD value - uses basic token amount directly as per ISuOracle interface
            uint256 tokenPrice = ORACLE.getFiatPrice1e18(underlyingToken);
            receivedValueUSD += (underlineTokenAmount * tokenPrice) / 1e18;

            // Transfer the obtained tokens to the sender.
            IERC20(underlyingToken).safeTransfer(msg.sender, underlineTokenAmount);
        }

        // Check slippage
        if (receivedValueUSD < minValueUSD) {
            revert SlippageExceeded(expectedValueUSD, receivedValueUSD);
        }

        emit Withdraw(asset, amount);
    }

    function emergencyWithdraw(address[] calldata tokens, address receiver) external onlyAdmin {
        if (receiver == address(0)) revert ZeroRecipient();

        for(uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(receiver, balance);
                emit EmergencyWithdraw(token, balance, receiver);
            }
        }
    }

    uint256[50] private __gap;
}
